mapper.select:
SELECT CASE WHEN M1.RENTAL_CAN_CT > 0 THEN '01' WHEN M1.SPAY_RSG_CT > 0 THEN '02' WHEN M1.RENTAL_BOO_CAN_CT > 0 THEN '03' WHEN M1.SPAY_BOO_CAN_CT > 0 THEN '04' WHEN M1.RENTAL_EXN_CT > 0 THEN '05' WHEN M1.MSH_SPR_CT > 0 THEN '06' END AS inqrDvCd , C2.CNTR_NO || '-' || C2.CNTR_SN AS CNTR_DTL_NO /* 계약상세번호 */ , P1.PD_NM AS PD_CLSF_NM , P1.PD_CD AS PD_CLSF_ID , G2.HOO_PRTNR_NM AS ICHR_MNGER_NM /* 담당매니저 */ , G2.HOO_PRTNR_NO AS ICHR_MNGER_NO , G2.DGR2_LEVL_DG_PRTNR_NM AS ICHR_BRMGR_NM /* 담당지점장 */ , G2.DGR2_LEVL_DG_PRTNR_NO AS ICHR_BRMGR_NO , C1.CNTR_CNFM_DTM /* 계약일 */ , C3.IST_DT /* 설치일 */ , S5.CNTR_CH_RCP_DTM /* 취소요청일 */ , C2.CNTR_PD_ENDDT AS CNTR_CAN_DTM /* 취소일 */ , '' AS CNTR_PD_ENDDT /* 만료일 */ , C2.CNTR_PD_STRTDT /* 사용일 */ , C1.CNTR_RCP_FSH_DTM /* 멤버십접수일 */ , C1.CNTR_CNFM_APR_DTM /* 멤버십가입일 */ , C1.CNTR_CAN_RSON_CD /* 취소사유 */ , S3.BZRNO , CASE WHEN (SELECT COUNT(1) FROM TB_SSCT_SELL_LM_OJ_IZ WHERE TO_CHAR(ADD_MONTHS(TO_DATE(#{perfYm}, 'YYYYMM'), -1), 'YYYYMM') BETWEEN VL_STRT_DTM AND VL_END_DTM AND SELL_LM_RLS_DT IS NULL AND ( SELL_LM_PRTNR_NO = C1.SELL_PRTNR_NO OR (SELL_LM_CNTR_NO = C2.CNTR_NO AND SELL_LM_CNTR_SN = C2.CNTR_SN) OR SELL_LM_BZRNO = S3.BZRNO )) > 0 THEN 'Y' ELSE 'N' END AS b2bYn , DECODE(C1.SELL_PRTNR_NO, NULL, 'N', 'Y') AS SELL_OG_YN , G1.PRTNR_KNM AS SELL_PRTNR_KNM , C1.SELL_PRTNR_NO , DECODE(S4.FEE_PERF_EXCD_DV_CD, '03' , 'Y', 'N') AS PERF_EXCD_OJ_YN /* 제외대상 */ , CASE WHEN S4.FEE_PERF_EXCD_DV_CD = '03' AND TO_CHAR(ADD_MONTHS(TO_DATE(#{perfYm}, 'YYYYMM'), -1), 'YYYYMM') BETWEEN S4.APY_STRT_YM AND S4.APY_END_YM THEN 'Y' ELSE 'N' END AS APY_OJ_YN FROM TB_FEAM_WELS_ACC_NINC_MM_DTL M1 INNER JOIN TB_SSCT_CNTR_BAS C1 /*계약기본*/ ON M1.CNTR_NO = C1.CNTR_NO AND M1.DTA_DL_YN = 'N' AND C1.DTA_DL_YN = 'N' INNER JOIN TB_SSCT_CNTR_DTL C2 /*계약상세*/ ON M1.CNTR_NO = C2.CNTR_NO AND M1.CNTR_SN = C2.CNTR_SN AND C2.DTA_DL_YN = 'N' INNER JOIN TB_SSCT_CNTR_WELLS_DTL C3 /* WELLS계약상세 */ ON M1.CNTR_NO = C3.CNTR_NO AND M1.CNTR_SN = C3.CNTR_SN AND C3.DTA_DL_YN = 'N' INNER JOIN TB_OGBS_MM_PRTNR_IZ G1 /*월파트너내역*/ ON M1.PERF_YM = G1.BASE_YM AND M1.PRTNR_NO = G1.PRTNR_NO AND M1.OG_TP_CD = G1.OG_TP_CD AND G1.DTA_DL_YN = 'N' INNER JOIN TB_OGBS_MM_OG_IZ G2 /*월조직내역*/ ON G1.BASE_YM = G2.BASE_YM AND G1.OG_ID = G2.OG_ID AND G2.DTA_DL_YN = 'N' INNER JOIN TB_PDBS_PD_BAS P1 /*상품기본*/ ON C2.BASE_PD_CD = P1.PD_CD AND P1.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_SSOP_PSPC_CST_BAS S3 /* 가망고객기본 */ ON C1.PSPC_CST_ID = S3.PSPC_CST_ID AND S3.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_FEDD_FEE_PERF_EXCD_OJ_BAS S4 /* 수수료실적제외대상기본 */ ON M1.CNTR_NO = S4.CNTR_NO AND M1.CNTR_SN = S4.CNTR_SN AND S4.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_SSCT_CNTR_CH_RCP_BAS S5 /* 계약변경접수기본 */ ON C1.CNTR_CH_RCP_ID = S5.CNTR_CH_RCP_ID AND S5.CNTR_CH_TP_CD = '402' AND S5.DTA_DL_YN = 'N' WHERE 1=1 AND M1.PERF_YM = #{perfYm} AND M1.FEE_TCNT_DV_CD = #{feeTcntDvCd} AND M1.OG_TP_CD = #{ogTpCd}
AND G2.DGR1_LEVL_OG_ID = #{dgr1LevlOgId}
AND G2.DGR2_LEVL_OG_ID = #{dgr2LevlOgId}
AND G2.DGR3_LEVL_OG_ID = #{dgr3LevlOgId}
AND M1.PRTNR_NO = #{prtnrNo}
/* 렌탈 */ AND M1.RENTAL_CAN_CT > 0
/* 일시불 */ AND M1.SPAY_RSG_CT > 0
/* 렌탈예약취소 */ AND M1.RENTAL_BOO_CAN_CT > 0
/* 일시불예약취소 */ AND M1.SPAY_BOO_CAN_CT > 0
/* 렌탈만료 */ AND M1.RENTAL_EXN_CT > 0
/* 멤버십이탈 */ AND M1.MSH_SPR_CT > 0

SELECT CASE WHEN M1.RENTAL_NW_CT > 0 THEN '01' WHEN M1.SPAY_NW_CT > 0 THEN '02' END AS inqrDvCd , C2.CNTR_NO || '-' || C2.CNTR_SN AS CNTR_DTL_NO /* 계약상세번호 */ , P1.PD_NM AS PD_CLSF_NM /* 상품명 */ , P1.PD_CD AS PD_CLSF_ID /* 상품코드 */ , C1.CNTR_CNFM_DTM /* 계약일 */ , C3.IST_DT /* 설치일 */ , C1.CNTR_CAN_DTM /* 취소일 */ , DECODE(W1.MCHN_CH_YN, 'Y', W1.MCHN_CH_TP_CD, '') AS MCHN_CH_TP_CD /* 기변유형 */ , DECODE(C1.SELL_PRTNR_NO, NULL, 'N', 'Y') AS SELL_OG_YN , G1.PRTNR_KNM AS SELL_PRTNR_KNM , C1.SELL_PRTNR_NO , W1.BOO_SELL_YN /* 예약여부 */ , DECODE(S4.FEE_PERF_EXCD_DV_CD, '03' , 'Y', 'N') AS PERF_EXCD_OJ_YN /* 제외대상 */ , CASE WHEN S4.FEE_PERF_EXCD_DV_CD = '03' AND #{perfYm} BETWEEN S4.APY_STRT_YM AND S4.APY_END_YM THEN 'Y' ELSE 'N' END AS APY_OJ_YN FROM TB_FEAM_WELS_ACC_NINC_MM_DTL M1 INNER JOIN TB_FEAM_WELS_NRCTR_MM_CL W1 /* 웰스순주문계약월마감 */ ON M1.PERF_YM = W1.BASE_YM AND M1.PERF_YM = W1.PERF_YM AND M1.FEE_TCNT_DV_CD = W1.FEE_TCNT_DV_CD AND M1.CNTR_NO = W1.CNTR_NO AND M1.CNTR_SN = W1.CNTR_SN AND M1.CO_CD = W1.CO_CD AND M1.OG_TP_CD = W1.OG_TP_CD AND M1.PRTNR_NO = W1.PRTNR_NO AND M1.DTA_DL_YN = 'N' AND W1.DTA_DL_YN = 'N' INNER JOIN TB_SSCT_CNTR_BAS C1 /*계약기본*/ ON M1.CNTR_NO = C1.CNTR_NO AND C1.DTA_DL_YN = 'N' INNER JOIN TB_SSCT_CNTR_DTL C2 /*계약상세*/ ON M1.CNTR_NO = C2.CNTR_NO AND M1.CNTR_SN = C2.CNTR_SN AND C2.DTA_DL_YN = 'N' INNER JOIN TB_SSCT_CNTR_WELLS_DTL C3 /* WELLS계약상세 */ ON M1.CNTR_NO = C3.CNTR_NO AND M1.CNTR_SN = C3.CNTR_SN AND C3.DTA_DL_YN = 'N' INNER JOIN TB_OGBS_MM_PRTNR_IZ G1 /*월파트너내역*/ ON M1.PERF_YM = G1.BASE_YM AND M1.PRTNR_NO = G1.PRTNR_NO AND M1.OG_TP_CD = G1.OG_TP_CD AND G1.DTA_DL_YN = 'N' INNER JOIN TB_OGBS_MM_OG_IZ G2 /*월조직내역*/ ON G1.BASE_YM = G2.BASE_YM AND G1.OG_ID = G2.OG_ID AND G2.DTA_DL_YN = 'N' INNER JOIN TB_PDBS_PD_BAS P1 /*상품기본*/ ON C2.BASE_PD_CD = P1.PD_CD AND P1.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_FEDD_FEE_PERF_EXCD_OJ_BAS S4 /* 수수료실적제외대상기본 */ ON M1.CNTR_NO = S4.CNTR_NO AND M1.CNTR_SN = S4.CNTR_SN AND S4.DTA_DL_YN = 'N' WHERE M1.FEE_TCNT_DV_CD = #{feeTcntDvCd} AND M1.PERF_YM = #{perfYm} AND M1.OG_TP_CD = #{ogTpCd}
AND G2.DGR1_LEVL_OG_ID = #{dgr1LevlOgId} /* 조직1레벨 */
AND G2.DGR2_LEVL_OG_ID = #{dgr2LevlOgId} /* 조직2레벨 */
AND G2.DGR3_LEVL_OG_ID = #{dgr3LevlOgId} /* 조직3레벨 */
AND M1.PRTNR_NO = #{prtnrNo} /* 파트너번호 */
/* 렌탈 */ AND M1.RENTAL_NW_CT > 0
/* 일시불 */ AND M1.SPAY_NW_CT > 0

/* 개인 */ SELECT G2.OG_CD AS inqrDvCd /* 유형구분코드 */ , G1.PRTNR_KNM /* 파트너명 */ , G1.PRTNR_NO /* 파트너번호 */ , NVL(W1.RENTAL_CAN_CT, 0) AS RENTAL_CAN_CT /* 렌탈취소건수 */ , NVL(W1.SPAY_RSG_CT, 0) AS SPAY_RSG_CT /* 일시불해지건수 */ , NVL(W1.MSH_SPR_CT, 0) AS MSH_SPR_CT /* 멤버십이탈건수 */ , NVL(W1.RENTAL_EXN_CT, 0) AS RENTAL_EXN_CT /* 렌탈만료건수 */ , NVL(W1.RENTAL_BOO_CAN_CT, 0) AS RENTAL_BOO_CAN_CT /* 렌탈예약취소건수 */ , NVL(W1.SPAY_BOO_CAN_CT, 0) AS SPAY_BOO_CAN_CT /* 일시불예약취소건수 */ , NVL(W1.RENTAL_CAN_CT, 0) + NVL(W1.SPAY_RSG_CT, 0) + NVL(W1.MSH_SPR_CT, 0) + NVL(W1.RENTAL_EXN_CT, 0) + NVL(W1.RENTAL_BOO_CAN_CT, 0) + NVL(W1.SPAY_BOO_CAN_CT, 0) AS CAN_TOT_CT /* 취소건수계 */ , NVL(W1.RENTAL_NW_CT, 0) AS RENTAL_NW_CT /* 렌탈신규건수 */ , NVL(W1.SPAY_NW_CT, 0) AS SPAY_NW_CT /* 일시불신규건수 */ , NVL(W1.RENTAL_NW_CT, 0) + NVL(W1.SPAY_NW_CT, 0) AS NW_TOT_CT /* 신규건수계 */ , NVL(W1.RENTAL_NW_CT, 0) + NVL(W1.SPAY_NW_CT, 0) - NVL(W1.RENTAL_CAN_CT, 0) - NVL(W1.SPAY_RSG_CT, 0) - NVL(W1.MSH_SPR_CT, 0) - NVL(W1.RENTAL_EXN_CT, 0) - NVL(W1.RENTAL_BOO_CAN_CT, 0) - NVL(W1.SPAY_BOO_CAN_CT, 0) AS ACC_NINC_YN /* 계정순증계 */
SELECT CASE WHEN #{aggregateTpCd} = '02' THEN MAX(G2.DGR3_LEVL_OG_CD) WHEN #{aggregateTpCd} = '03' THEN MAX(G2.DGR2_LEVL_OG_CD) WHEN #{aggregateTpCd} = '04' THEN MAX(G2.DGR1_LEVL_OG_CD) END AS inqrDvCd /* 유형구분코드 */ , CASE WHEN #{aggregateTpCd} = '02' THEN MAX(G2.DGR3_LEVL_DG_PRTNR_NM) WHEN #{aggregateTpCd} = '03' THEN MAX(G2.DGR2_LEVL_DG_PRTNR_NM) WHEN #{aggregateTpCd} = '04' THEN MAX(G2.DGR1_LEVL_DG_PRTNR_NM) END AS PRTNR_KNM /* 파트너명 */ , CASE WHEN #{aggregateTpCd} = '02' THEN MAX(G2.DGR3_LEVL_DG_PRTNR_NO) WHEN #{aggregateTpCd} = '03' THEN MAX(G2.DGR2_LEVL_DG_PRTNR_NO) WHEN #{aggregateTpCd} = '04' THEN MAX(G2.DGR1_LEVL_DG_PRTNR_NO) END AS PRTNR_NO /* 파트너번호 */ , NVL(SUM(W1.RENTAL_CAN_CT), 0) AS RENTAL_CAN_CT /* 렌탈취소건수 */ , NVL(SUM(W1.SPAY_RSG_CT), 0) AS SPAY_RSG_CT /* 일시불해지건수 */ , NVL(SUM(W1.MSH_SPR_CT), 0) AS MSH_SPR_CT /* 멤버십이탈건수 */ , NVL(SUM(W1.RENTAL_EXN_CT), 0) AS RENTAL_EXN_CT /* 렌탈만료건수 */ , NVL(SUM(W1.RENTAL_BOO_CAN_CT), 0) AS RENTAL_BOO_CAN_CT /* 렌탈예약취소건수 */ , NVL(SUM(W1.SPAY_BOO_CAN_CT), 0) AS SPAY_BOO_CAN_CT /* 일시불예약취소건수 */ , NVL(SUM(W1.RENTAL_CAN_CT), 0) + NVL(SUM(W1.SPAY_RSG_CT), 0) + NVL(SUM(W1.MSH_SPR_CT), 0) + NVL(SUM(W1.RENTAL_EXN_CT), 0) + NVL(SUM(W1.RENTAL_BOO_CAN_CT), 0) + NVL(SUM(W1.SPAY_BOO_CAN_CT), 0) AS CAN_TOT_CT /* 취소건수계 */ , NVL(SUM(W1.RENTAL_NW_CT), 0) AS RENTAL_NW_CT /* 렌탈신규건수 */ , NVL(SUM(W1.SPAY_NW_CT), 0) AS SPAY_NW_CT /* 일시불신규건수 */ , NVL(SUM(W1.RENTAL_NW_CT), 0) + NVL(SUM(W1.SPAY_NW_CT), 0) AS NW_TOT_CT /* 신규건수계 */ , NVL(SUM(W1.RENTAL_NW_CT), 0) + NVL(SUM(W1.SPAY_NW_CT), 0) - NVL(SUM(W1.RENTAL_CAN_CT), 0) - NVL(SUM(W1.SPAY_RSG_CT), 0) - NVL(SUM(W1.MSH_SPR_CT), 0) - NVL(SUM(W1.RENTAL_EXN_CT), 0) - NVL(SUM(W1.RENTAL_BOO_CAN_CT), 0) - NVL(SUM(W1.SPAY_BOO_CAN_CT), 0) AS ACC_NINC_YN /* 계정순증계 */
FROM TB_FEAM_WELS_ACC_NINC_MM_CL W1 /* 웰스계정순증월마감 */ INNER JOIN TB_OGBS_MM_PRTNR_IZ G1 /*월파트너내역*/ ON W1.PERF_YM = G1.BASE_YM AND W1.PRTNR_NO = G1.PRTNR_NO AND W1.OG_TP_CD = G1.OG_TP_CD INNER JOIN TB_OGBS_MM_OG_IZ G2 /*월조직내역*/ ON G1.BASE_YM = G2.BASE_YM AND G1.OG_ID = G2.OG_ID AND G2.DTA_DL_YN = 'N' WHERE W1.FEE_TCNT_DV_CD = #{feeTcntDvCd} /* 차수 */ AND W1.PERF_YM = #{perfYm} /* 실적년월 */ AND W1.OG_TP_CD = #{ogTpCd}
AND G2.DGR1_LEVL_OG_ID = #{dgr1LevlOgId} /* 조직1레벨 */
AND G2.DGR2_LEVL_OG_ID = #{dgr2LevlOgId} /* 조직2레벨 */
AND G2.DGR3_LEVL_OG_ID = #{dgr3LevlOgId} /* 조직3레벨 */
AND W1.PRTNR_NO = #{prtnrNo} /* 파트너번호 */
/* 지점 */ GROUP BY G2.DGR3_LEVL_OG_CD
/* 지역단 */ GROUP BY G2.DGR2_LEVL_OG_CD
/* 총력단 */ GROUP BY G2.DGR1_LEVL_OG_CD
