mapper.select:
SELECT A1.OG_TP_CD /* 조직유형코드 */ , A2.OG_CD /* 조직코드 */ , A1.PRTNR_NO /* 파트너번호 */ , A2.PRTNR_KNM /* 파트너명 */ , CASE WHEN A1.SELL_TP_CD = '1' THEN '01' /* 일시불 */ WHEN A1.SELL_TP_CD = '2' THEN '02' /* 렌탈/리스 */ WHEN A1.SELL_TP_CD = '3' THEN '06' /* 멤버십 */ WHEN A1.SELL_TP_CD = '6' THEN '04' /* 정기배송 */ WHEN A1.SELL_TP_CD = '05' THEN '05' /* 재약정 */ END AS SELL_TP_CD /* 판매유형코드 */ , A1.FEE_PDCT_TP_CD /* 수수료제품유형코드 */ , A1.CNTR_NO || '-' || A1.CNTR_SN AS CNTR_NO /* 계약번호, 계약일련번호 */ , A1.COPN_DV_CD /* 법인격구분코드 */ , A1.PD_NM /* 상품명 */ , A1.BASE_PD_CD /* 기준상품코드 */ , A1.PKG_CD /* 패키지코드 */ , A1.PKG_SN /* 패키지일련번호 */ , A1.CNTR_PTRM /* 계약기간 */ , A1.STPL_PTRM /* 약정기간 */ , A1.CNTR_CNFM_DTM /* 계약확정일시 */ , A1.SL_DT /* 매출일자 */ , A1.CAN_DT /* 취소일자 */ , A1.REQD_DT /* 철거일자 */ , A1.SELL_AMT /* 판매금액 */ FROM (
/* 판매유형 = 05 : 재약정 */ SELECT
/* 구분 = 01 : 접수, 02 : 예약 */ SUBSTR(T1.CNTR_RCP_FSH_DTM, 0, 6) AS BASE_YM /* 기준년월 */
/* 구분 = 03 : 매출 */ SUBSTR(T2.CNTR_PD_STRTDT, 0, 6) AS BASE_YM /* 기준년월 */
, T1.SELL_OG_TP_CD AS OG_TP_CD /* 조직유형코드 */ , T1.SELL_PRTNR_NO AS PRTNR_NO /* 파트너번호 */ , T1.COPN_DV_CD /* 법인격구분코드 */ , SUBSTR(T1.CNTR_RCP_FSH_DTM, 0, 8) AS CNTR_CNFM_DTM /* 계약접수완료일시 */ , T2.SELL_TP_CD /* 판매유형코드 */ , T2.CNTR_NO /* 계약번호 */ , T2.CNTR_SN /* 계약일련번호 */ , T2.BASE_PD_CD /* 기준상품코드 */ , T2.HGR_PD_CD AS PKG_CD /* 상위상품코드 */ , T7.CNTR_CST_GRP_ID AS PKG_SN /* 계약고객그룹ID */ , T2.CNTR_PTRM /* 계약기간 */ , T2.STPL_PTRM /* 약정기간 */ , SUBSTR(T2.CNTR_PD_STRTDT, 0, 8) AS SL_DT /* 매출일자 */ , CASE WHEN T2.CNTR_DTL_STAT_CD IN ('301', '303') THEN T2.CNTR_PD_ENDDT ELSE '' END AS CAN_DT /* 취소일자 */ , CASE WHEN T2.SELL_TP_CD = '6' THEN NVL((NVL(T2.FNL_AMT, 0)- NVL(T2.CNTRAM_DSC_AMT, 0)) / DECODE(T2.SV_PRD, 0, NULL, T2.SV_PRD), 0) ELSE T2.SELL_AMT END AS SELL_AMT /* 판매금액 */ , T3.PD_NM /* 상품명 */ , CASE WHEN T1.SELL_OG_TP_CD = 'W01' THEN T5.FEE_PDCT_TP_CD2 WHEN T1.SELL_OG_TP_CD IN ('W02', 'W03', 'W04', 'W05') THEN T5.FEE_PDCT_TP_CD1 ELSE '' END AS FEE_PDCT_TP_CD /* 수수료제품유형코드 */ , T6.REQD_DT /* 철거일자 */ FROM TB_SSCT_CNTR_BAS T1 /* 계약기본 */ INNER JOIN TB_SSCT_CNTR_DTL T2 /* 계약상세 */ ON T1.CNTR_NO = T2.CNTR_NO /* 계약번호 */ AND T2.DTA_DL_YN = 'N' INNER JOIN TB_PDBS_PD_BAS T3 /* 상품기본 */ ON T2.BASE_PD_CD = T3.PD_CD /* 상품코드 */ AND T3.DTA_DL_YN = 'N' INNER JOIN TB_PDBS_PD_CLSF_BAS T4 /* 상품분류기본 */ ON T3.PD_CLSF_ID = T4.PD_CLSF_ID /* 상품분류ID */ AND T4.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_FEAM_WELS_FEE_PDCT_TP_BAS T5 /* 웰스수수료제품유형기본 */ ON T2.BASE_PD_CD = T5.BASE_PD_CD /* 기준상품코드 */ AND SUBSTR(#{rcpDtFrom}, 0, 6) BETWEEN T5.APY_STRT_YM AND T5.APY_END_YM /* 적용시작년월, 적용종료년월 */ AND T5.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_SSCT_CNTR_WELLS_DTL T6 /* 계약WELLS상세 */ ON T2.CNTR_NO = T6.CNTR_NO /* 계약번호 */ AND T2.CNTR_SN = T6.CNTR_SN /* 계약일련번호 */ AND T6.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_SSCT_CNTR_REL T7 /* 계약관계 */ ON T7.BASE_DTL_CNTR_NO = T2.CNTR_NO /* 기준상세계약번호 */ AND T7.BASE_DTL_CNTR_SN = T2.CNTR_SN /* 기준상세계약일련번호 */ AND T7.CNTR_REL_DTL_CD = '22W' /* 계약관계상세코드 = 22W : 패키지상품 */ AND SUBSTR(T1.CNTR_RCP_FSH_DTM, 0, 8) BETWEEN SUBSTR(T7.VL_STRT_DTM, 0 ,8) AND SUBSTR(T7.VL_END_DTM, 0, 8) /* 유효시작일시, 유효종료일시 */ AND T7.DTA_DL_YN = 'N' WHERE 1=1 AND T1.DTA_DL_YN = 'N'
/* 구분 = 01 : 접수, 02 : 예약 */ AND SUBSTR(T1.CNTR_RCP_FSH_DTM, 0, 8) BETWEEN #{rcpDtFrom} AND #{rcpDtTo} /* 계약접수완료일시 */ /* 구분 = 02 : 예약 */ AND T2.BOO_SELL_TP_CD IS NOT NULL /* 예약판매유형코드 */
/* 구분 = 03 : 매출 */ AND T2.CNTR_PD_STRTDT BETWEEN #{rcpDtFrom} AND #{rcpDtTo} /* 계약상품시작일자 */ AND T2.BOO_SELL_TP_CD IS NULL /* 예약판매유형코드 */
/* 조직구분 = W99 : 기타 */ AND T1.SELL_OG_TP_CD NOT IN ('W01', 'W02', 'W03' ,'W04', 'W05') /* 판매조직유형코드 */
AND T1.SELL_OG_TP_CD = #{ogTpCd} /* 판매조직유형코드 */
AND T2.SELL_TP_CD IN ('1', '2', '3', '6')
/* 판매유형 = 01 : 일시불 */ AND T2.SELL_TP_CD = '1' /* 판매유형코드 = 1 : 일시불 */
/* 판매유형 = 02 : 렌탈/리스 */ AND T2.SELL_TP_CD = '2' /* 판매유형코드 = 2 : 렌탈/리스 */
/* 판매유형 = 06 : 멤버십 */ AND T2.SELL_TP_CD = '3' /* 판매유형코드 = 3 : 멤버십 */
/* 판매유형 = 04 : 정기배송 */ AND T2.SELL_TP_CD = '6' /* 판매유형코드 = 6 : 정기배송 */
AND T1.SELL_PRTNR_NO = #{prtnrNo} /* 파트너번호 */
/* 판매유형 = '' : 전체, 05 : 재약정 */
/* 판매유형 = '' : 전체 */ UNION ALL
SELECT
/* 구분 = 01 : 접수, 02 : 예약 */ SUBSTR(T1.STPL_RCP_DTM, 0, 6) AS BASE_YM /* 기준년월 */
/* 구분 = 03 : 매출 */ SUBSTR(T1.STPL_CNFM_DTM, 0, 6) AS BASE_YM /* 기준년월 */
, T1.RCP_OG_TP_CD AS OG_TP_CD /* 조직유형코드 */ , T1.RCP_PRTNR_NO AS PRTNR_NO /* 파트너번호 */ , T2.COPN_DV_CD /* 법인격구분코드 */
/* 구분 = 01 : 접수, 02 : 예약 */ , SUBSTR(T1.STPL_RCP_DTM, 0, 8) AS CNTR_CNFM_DTM /* 계약확정일시 */
/* 구분 = 03 : 매출 */ , SUBSTR(T1.STPL_CNFM_DTM, 0, 8) AS CNTR_CNFM_DTM /* 계약확정일시 */
, '05' AS SELL_TP_CD /* 판매유형코드 = 5 : 재약정 */ , T1.CNTR_NO /* 계약번호 */ , T1.CNTR_SN /* 계약일련번호 */ , T3.BASE_PD_CD /* 기준상품코드 */ , T3.HGR_PD_CD AS PKG_CD /* 상위상품코드 */ , T8.CNTR_CST_GRP_ID AS PKG_SN /* 계약고객그룹ID */ , T3.CNTR_PTRM /* 계약기간 */ , T3.STPL_PTRM /* 약정기간 */ , SUBSTR(T3.CNTR_PD_STRTDT, 0, 8) AS SL_DT /* 매출일자 */ , T1.STPL_CAN_DTM AS CAN_DT /* 취소일자 */ , NVL(T3.FNL_AMT, 0) - NVL(T1.STPL_DSC_AMT, 0) SELL_AMT /* 판매금액 */ , T4.PD_NM /* 상품명 */ , CASE WHEN T2.SELL_OG_TP_CD = 'W01' THEN T6.FEE_PDCT_TP_CD2 ELSE T6.FEE_PDCT_TP_CD1 END AS FEE_PDCT_TP_CD /* 수수료제품유형코드 */ , T7.REQD_DT /* 철거일자 */ FROM TB_SSCT_RENTAL_RSTL_IZ T1 /* 렌탈재약정내역 */ INNER JOIN TB_SSCT_CNTR_BAS T2 /* 계약기본 */ ON T1.CNTR_NO = T2.CNTR_NO /* 계약번호 */ AND T2.DTA_DL_YN = 'N' INNER JOIN TB_SSCT_CNTR_DTL T3 /* 계약상세 */ ON T1.CNTR_NO = T3.CNTR_NO /* 계약번호 */ AND T1.CNTR_SN = T3.CNTR_SN /* 계약일련번호 */ AND T3.DTA_DL_YN = 'N' INNER JOIN TB_PDBS_PD_BAS T4 /* 상품기본 */ ON T3.BASE_PD_CD = T4.PD_CD /* 상품코드 */ AND T4.DTA_DL_YN = 'N' INNER JOIN TB_PDBS_PD_CLSF_BAS T5 /* 상품분류기본 */ ON T4.PD_CLSF_ID = T5.PD_CLSF_ID /* 상품분류ID */ AND T5.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_FEAM_WELS_FEE_PDCT_TP_BAS T6 /* 웰스수수료제품유형기본 */ ON T3.BASE_PD_CD = T6.BASE_PD_CD /* 기준상품코드 */ AND SUBSTR(#{rcpDtFrom}, 0, 6) BETWEEN T6.APY_STRT_YM AND T6.APY_END_YM /* 적용시작년월, 적용종료년월 */ AND T6.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_SSCT_CNTR_WELLS_DTL T7 /* 계약WELLS상세 */ ON T3.CNTR_NO = T7.CNTR_NO /* 계약번호 */ AND T3.CNTR_SN = T7.CNTR_SN /* 계약일련번호 */ AND T7.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_SSCT_CNTR_REL T8 /* 계약관계 */ ON T8.BASE_DTL_CNTR_NO = T1.CNTR_NO /* 기준상세계약번호 */ AND T8.BASE_DTL_CNTR_SN = T1.CNTR_SN /* 기준상세계약일련번호 */ AND T8.CNTR_REL_DTL_CD = '22W' /* 계약관계상세코드 = 22W : 패키지상품 */ AND SUBSTR(T1.STPL_RCP_DTM, 0, 8) BETWEEN SUBSTR(T8.VL_STRT_DTM, 0 ,8) AND SUBSTR(T8.VL_END_DTM, 0, 8) /* 유효시작일시, 유효종료일시 */ AND T8.DTA_DL_YN = 'N' WHERE 1=1 AND T1.DTA_DL_YN = 'N' AND T1.STPL_TP_CD = 'A' /* 약정유형코드 */
/* 구분 = 01 : 접수, 02 : 예약 */ AND SUBSTR(T1.STPL_RCP_DTM, 0, 8) BETWEEN #{rcpDtFrom} AND #{rcpDtTo} /* 약정접수일시 */ /* 구분 = 02 : 예약 */ AND T3.BOO_SELL_TP_CD IS NOT NULL /* 예약판매유형코드 */
/* 구분 = 03 : 매출 */ AND SUBSTR(T1.STPL_CNFM_DTM, 0, 8) BETWEEN #{rcpDtFrom} AND #{rcpDtTo} /* 약정확정일시 */
/* 조직구분 = W99 : 기타 */ AND T1.RCP_OG_TP_CD NOT IN ('W01', 'W02', 'W03' ,'W04', 'W05') /* 판매조직유형코드 */
AND T1.RCP_OG_TP_CD = #{ogTpCd} /* 판매조직유형코드 */
AND T1.RCP_PRTNR_NO = #{prtnrNo} /* 파트너번호 */
) A1 LEFT OUTER JOIN ( SELECT T1.BASE_YM /* 기준년월 */ , T1.OG_TP_CD /* 조직유형코드 */ , T1.OG_CD /* 조직코드 */ , T2.PRTNR_NO /* 파트너번호 */ , T2.PRTNR_KNM /* 파트너명 */ , T1.DGR1_LEVL_OG_ID /* 1차레벨조직ID */ , T1.DGR2_LEVL_OG_ID /* 2차레벨조직ID */ , T1.DGR3_LEVL_OG_ID /* 3차레벨조직ID */ , T1.DGR4_LEVL_OG_ID /* 4차레벨조직ID */ , T1.DGR5_LEVL_OG_ID /* 5차레벨조직ID */ , T1.DGR1_LEVL_DG_PRTNR_NO /* 1차레벨대표파트너번호 */ , T1.DGR2_LEVL_DG_PRTNR_NO /* 2차레벨대표파트너번호 */ , T1.DGR3_LEVL_DG_PRTNR_NO /* 3차레벨대표파트너번호 */ , T1.DGR4_LEVL_DG_PRTNR_NO /* 4차레벨대표파트너번호 */ , T1.DGR5_LEVL_DG_PRTNR_NO /* 5차레벨대표파트너번호 */ , T1.DGR1_LEVL_DG_PRTNR_NM /* 1차레벨대표파트너명 */ , T1.DGR2_LEVL_DG_PRTNR_NM /* 2차레벨대표파트너명 */ , T1.DGR3_LEVL_DG_PRTNR_NM /* 3차레벨대표파트너명 */ , T1.DGR4_LEVL_DG_PRTNR_NM /* 4차레벨대표파트너명 */ , T1.DGR5_LEVL_DG_PRTNR_NM /* 5차레벨대표파트너명 */ FROM TB_OGBS_MM_OG_IZ T1 /* 월조직내역 */ INNER JOIN TB_OGBS_MM_PRTNR_IZ T2 /* 월파트너내역 */ ON T1.BASE_YM = T2.BASE_YM /* 기준년월 */ AND T1.OG_TP_CD = T2.OG_TP_CD /* 조직유형코드 */ AND T1.OG_ID = T2.OG_ID /* 조직ID */ AND T2.DTA_DL_YN = 'N' WHERE 1=1 AND T1.DTA_DL_YN = 'N' AND T1.BASE_YM = SUBSTR(#{rcpDtFrom}, 0, 6) /* 기준년월 */
/* 조직구분 = W99 : 기타 */ AND T1.OG_TP_CD NOT IN ('W01', 'W02', 'W03' ,'W04', 'W05') /* 조직유형코드 */
AND T1.OG_TP_CD = #{ogTpCd} /* 조직유형코드 */
) A2 ON A1.BASE_YM = A2.BASE_YM AND A1.OG_TP_CD = A2.OG_TP_CD AND A1.PRTNR_NO = A2.PRTNR_NO WHERE 1=1
AND A2.DGR1_LEVL_OG_ID = #{ogLevl1}
AND A2.DGR2_LEVL_OG_ID = #{ogLevl2}
AND A2.DGR3_LEVL_OG_ID = #{ogLevl3}
AND A2.DGR4_LEVL_OG_ID = #{ogLevl4}
AND A2.DGR5_LEVL_OG_ID = #{ogLevl5}
AND A1.PRTNR_NO = #{prtnrNo} /* 파트너번호 */
AND (A1.CAN_DT BETWEEN #{cancDtFrom} AND #{cancDtTo} OR A1.CAN_DT BETWEEN #{cancDtTo} AND #{cancDtFrom})/* 취소일자 */
AND A1.CAN_DT >= #{cancDtFrom} /* 취소일자 */
AND A1.CAN_DT <= #{cancDtTo} /* 취소일자 */
AND (A1.BASE_PD_CD BETWEEN #{pdCdFrom} AND #{pdCdTo} OR A1.BASE_PD_CD BETWEEN #{pdCdTo} AND #{pdCdFrom}) /* 상품코드 */
AND A1.BASE_PD_CD >= #{pdCdFrom} /* 상품코드 */
AND A1.BASE_PD_CD <= #{pdCdTo} /* 상품코드 */
AND (A1.PKG_CD BETWEEN #{pkgCdFrom} AND #{pkgCdTo} OR A1.PKG_CD BETWEEN #{pkgCdTo} AND #{pkgCdFrom}) /* 패키지코드 */
AND A1.PKG_CD >= #{pkgCdFrom} /* 패키지코드 */
AND A1.PKG_CD <= #{pkgCdTo} /* 패키지코드 */
/* 수수료제품유형코드 */ AND A1.FEE_PDCT_TP_CD = #{feePdctTpCd} /* 수수료제품유형코드 */
ORDER BY A1.OG_TP_CD, A2.OG_CD, A1.PRTNR_NO

SELECT A1.OG_TP_CD /* 조직유형코드 */ , A2.OG_CD /* 조직코드 */ , A1.PRTNR_NO /* 파트너번호 */ , A2.PRTNR_KNM /* 파트너명 */ , CASE WHEN NVL(A1.RSTL_YN, 'N') = 'Y' THEN '05' /* 재약정 */ WHEN A1.SELL_TP_CD = '1' THEN '01' /* 일시불 */ WHEN A1.SELL_TP_CD = '2' THEN '02' /* 렌탈/리스 */ WHEN A1.SELL_TP_CD = '3' THEN '03' /* 멤버십 */ WHEN A1.SELL_TP_CD = '6' THEN '04' /* 정기배송 */ ELSE A1.SELL_TP_CD END AS SELL_TP_CD /* 판매유형코드 */ , A1.FEE_PDCT_TP_CD /* 수수료제품유형코드 */ , A1.CNTR_NO || '-' || A1.CNTR_SN AS CNTR_NO /* 계약번호, 계약일련번호 */ , A1.COPN_DV_CD /* 법인격구분코드 */ , A1.PD_NM /* 상품명 */ , A1.BASE_PD_CD /* 기준상품코드 */ , A1.PKG_CD /* 패키지코드 */ , A1.PKG_SN /* 패키지일련번호 */ , A1.CNTR_PTRM /* 계약기간 */ , A1.STPL_PTRM /* 약정기간 */ , A1.CNTR_CNFM_DTM /* 계약확정일시 */ , A1.SL_DT /* 매출일자 */ , A1.CAN_DT /* 취소일자 */ , A1.REQD_DT /* 철거일자 */ , A1.SELL_AMT /* 판매금액 */ FROM ( SELECT T1.BASE_YM /* 기준년월 */ , T1.OG_TP_CD /* 조직유형코드 */ , T1.PRTNR_NO /* 파트너번호 */ , T1.CNTR_NO /* 계약번호 */ , T1.CNTR_SN /* 계약일련번호 */ , T1.SL_DT /* 매출일자 */ , T1.CAN_DT /* 취소일자 */ , T1.RSTL_YN /* 재약정여부 */ , T2.COPN_DV_CD /* 법인격구분코드 */ , SUBSTR(T2.CNTR_CNFM_DTM, 0, 8) AS CNTR_CNFM_DTM /* 계약확정일시 */ , T3.SELL_TP_CD /* 판매유형코드 */ , T3.BASE_PD_CD /* 기준상품코드 */ , T3.HGR_PD_CD AS PKG_CD /* 상위상품코드 */ , T8.CNTR_CST_GRP_ID AS PKG_SN /* 계약고객그룹ID */ , T3.CNTR_PTRM /* 계약기간 */ , T3.STPL_PTRM /* 약정기간 */ , T3.SELL_AMT /* 판매금액 */ , T4.PD_NM /* 상품명 */ , CASE WHEN T2.SELL_OG_TP_CD = 'W01' THEN T6.FEE_PDCT_TP_CD2 ELSE T6.FEE_PDCT_TP_CD1 END AS FEE_PDCT_TP_CD /* 수수료제품유형코드 */ , T7.REQD_DT /* 철거일자 */ FROM TB_FEAM_WELS_NRCTR_MM_CL T1 /* 웰스순주문계약월마감 */ INNER JOIN TB_SSCT_CNTR_BAS T2 /* 계약기본 */ ON T1.CNTR_NO = T2.CNTR_NO /* 계약번호 */ AND T2.DTA_DL_YN = 'N' INNER JOIN TB_SSCT_CNTR_DTL T3 /* 계약상세 */ ON T1.CNTR_NO = T3.CNTR_NO /* 계약번호 */ AND T1.CNTR_SN = T3.CNTR_SN /* 계약일련번호 */ AND T3.DTA_DL_YN = 'N' INNER JOIN TB_PDBS_PD_BAS T4 /* 상품기본 */ ON T3.BASE_PD_CD = T4.PD_CD /* 상품코드 */ AND T4.DTA_DL_YN = 'N' INNER JOIN TB_PDBS_PD_CLSF_BAS T5 /* 상품분류기본 */ ON T4.PD_CLSF_ID = T5.PD_CLSF_ID /* 상품분류ID */ AND T5.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_FEAM_WELS_FEE_PDCT_TP_BAS T6 /* 웰스수수료제품유형기본 */ ON T3.BASE_PD_CD = T6.BASE_PD_CD /* 기준상품코드 */ AND SUBSTR(#{perfYm}, 0, 6) BETWEEN T6.APY_STRT_YM AND T6.APY_END_YM /* 적용시작년월, 적용종료년월 */ AND T6.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_SSCT_CNTR_WELLS_DTL T7 /* 계약WELLS상세 */ ON T3.CNTR_NO = T7.CNTR_NO /* 계약번호 */ AND T3.CNTR_SN = T7.CNTR_SN /* 계약일련번호 */ AND T7.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_SSCT_CNTR_REL T8 /* 계약관계 */ ON T8.BASE_DTL_CNTR_NO = T1.CNTR_NO /* 기준상세계약번호 */ AND T8.BASE_DTL_CNTR_SN = T1.CNTR_SN /* 기준상세계약일련번호 */ AND T8.CNTR_REL_DTL_CD = '22W' /* 계약관계상세코드 = 22W : 패키지상품 */ AND SUBSTR(T2.CNTR_CNFM_DTM, 0, 8) BETWEEN SUBSTR(T8.VL_STRT_DTM, 0 ,8) AND SUBSTR(T8.VL_END_DTM, 0, 8) /* 유효시작일시, 유효종료일시 */ AND T8.DTA_DL_YN = 'N' WHERE 1=1 AND T1.DTA_DL_YN = 'N' AND NVL(T1.ACC_NINC_YN, 'N') = 'N' /* 계정순증여부 */ AND T1.CNTR_PERF_CRT_DV_CD = '01' /* 계약실적생성구분코드 = 01 : 순주문 */ AND T1.BASE_YM = #{perfYm} /* 기준년월 */ AND T1.PERF_YM = #{perfYm} /* 실적년월 */
/* 조직구분 = W99 : 기타 */ AND T1.OG_TP_CD NOT IN ('W01', 'W02', 'W03' ,'W04', 'W05') /* 판매조직유형코드 */
AND T1.OG_TP_CD = #{ogTpCd} /* 조직유형코드 */
AND T1.FEE_TCNT_DV_CD = #{feeTcntDvCd} /* 수수료차수구분코드 */ ) A1 LEFT OUTER JOIN ( SELECT T1.BASE_YM /* 기준년월 */ , T1.OG_TP_CD /* 조직유형코드 */ , T1.OG_CD /* 조직코드 */ , T2.PRTNR_NO /* 파트너번호 */ , T2.PRTNR_KNM /* 파트너명 */ FROM TB_OGBS_MM_OG_IZ T1 /* 월조직내역 */ INNER JOIN TB_OGBS_MM_PRTNR_IZ T2 /* 월파트너내역 */ ON T1.BASE_YM = T2.BASE_YM /* 기준년월 */ AND T1.OG_TP_CD = T2.OG_TP_CD /* 조직유형코드 */ AND T1.OG_ID = T2.OG_ID /* 조직ID */ AND T2.DTA_DL_YN = 'N' WHERE 1=1 AND T1.DTA_DL_YN = 'N' AND T1.BASE_YM = #{perfYm} /* 기준년월 */
/* 조직구분 = W99 : 기타 */ AND T1.OG_TP_CD NOT IN ('W01', 'W02', 'W03' ,'W04', 'W05') /* 판매조직유형코드 */
AND T1.OG_TP_CD = #{ogTpCd} /* 조직유형코드 */
) A2 ON A1.BASE_YM = A2.BASE_YM /* 기준년월 */ AND A1.OG_TP_CD = A2.OG_TP_CD /* 조직유형코드 */ AND A1.PRTNR_NO = A2.PRTNR_NO /* 파트너번호 */ WHERE 1=1 ORDER BY A1.OG_TP_CD, A2.OG_CD, A1.PRTNR_NO

SELECT A.OG_TP_CD /* 조직유형코드 */ , NVL(SUM(A.SPAY_CNT), 0) + NVL(SUM(A.RENT_CNT), 0) + NVL(SUM(A.MSH_CNT), 0) + NVL(SUM(A.RGLR_CNT), 0) + NVL(SUM(A.RSTL_CNT), 0) AS TOT_CT /* 총 건수 */ , NVL(SUM(A.SPAY_CNT), 0) AS SPAY_CNT /* 일시불 건수 */ , NVL(SUM(A.RENT_CNT), 0) AS RENT_CNT /* 렌탈/리스 건수 */ , NVL(SUM(A.MSH_CNT), 0) AS MSH_CNT /* 홈케어멤버십 건수 */ , NVL(SUM(A.RGLR_CNT), 0) AS RGLR_CNT /* 정기배송 건수 */ , NVL(SUM(A.RSTL_CNT), 0) AS RSTL_CNT /* 재약정 건수 */ , NVL(SUM(A.ENVR_CNT), 0) AS ENVR_CNT /* 환경 건수 */ , NVL(SUM(A.WELSF_CNT), 0) AS WELSF_CNT /* 웰스팜 건수 */ , NVL(SUM(A.BH_CNT), 0) AS BH_CNT /* BH 건수 */ , NVL(SUM(A.CAPSL_CNT), 0) AS CAPSL_CNT /* 캡슐 건수 */ , NVL(SUM(A.HOME_CARE_CNT), 0) AS HOME_CARE_CNT /* 홈케어 건수 */ , NVL(SUM(A.CSMB_CNT), 0) AS CSMB_CNT /* 소모품 건수 */ , NVL(SUM(A.ACSR_CNT), 0) AS ACSR_CNT /* 부속품 건수 */ , NVL(SUM(A.NOPD_CNT), 0) AS NOPD_CNT /* 미등록유형 건수 */ FROM ( SELECT CASE WHEN (T1.OG_TP_CD = 'W01' OR T1.OG_TP_CD = 'W02' OR T1.OG_TP_CD = 'W03' OR T1.OG_TP_CD = 'W04' OR T1.OG_TP_CD = 'W05') THEN T1.OG_TP_CD ELSE 'W99' END AS OG_TP_CD , T1.BASE_YM , CASE WHEN T3.SELL_TP_CD = '1' AND NVL(T5.CNTR_NO, 'N') = 'N' THEN 1 ELSE 0 END SPAY_CNT /* 일시불 건수 */ , CASE WHEN T3.SELL_TP_CD = '2' AND NVL(T5.CNTR_NO, 'N') = 'N' THEN 1 ELSE 0 END RENT_CNT /* 렌탈/리스 건수 */ , CASE WHEN T3.SELL_TP_CD = '3' AND T3.SELL_TP_DTL_CD = '33' AND NVL(T5.CNTR_NO, 'N') = 'N' THEN 1 ELSE 0 END MSH_CNT /* 홈케어멤버십 건수 */ , CASE WHEN T3.SELL_TP_CD = '6' AND NVL(T5.CNTR_NO, 'N') = 'N' THEN 1 ELSE 0 END RGLR_CNT /* 정기배송 건수 */ , CASE WHEN NVL(T5.CNTR_NO, 'N') != 'N' THEN 1 ELSE 0 END RSTL_CNT /* 재약정 건수 */ , CASE WHEN T1.OG_TP_CD = 'W01' AND T4.FEE_PDCT_TP_CD2 = 'A' THEN 1 WHEN T4.FEE_PDCT_TP_CD1 = 'A' THEN 1 END AS ENVR_CNT /* 환경 건수 */ , CASE WHEN T1.OG_TP_CD = 'W01' AND T4.FEE_PDCT_TP_CD2 = 'B' THEN 1 WHEN T1.OG_TP_CD != 'W01' AND T4.FEE_PDCT_TP_CD1 = 'B' THEN 1 END AS WELSF_CNT /* 웰스팜 건수 */ , CASE WHEN T1.OG_TP_CD = 'W01' AND T4.FEE_PDCT_TP_CD2 = 'C' THEN 1 WHEN T1.OG_TP_CD != 'W01' AND T4.FEE_PDCT_TP_CD1 = 'C' THEN 1 END AS BH_CNT /* BH 건수 */ , CASE WHEN T1.OG_TP_CD = 'W01' AND T4.FEE_PDCT_TP_CD2 = 'D' THEN 1 WHEN T1.OG_TP_CD != 'W01' AND T4.FEE_PDCT_TP_CD1 = 'D' THEN 1 END AS CAPSL_CNT /* 캡슐 건수 */ , CASE WHEN T1.OG_TP_CD = 'W01' AND T4.FEE_PDCT_TP_CD2 = 'E' THEN 1 WHEN T1.OG_TP_CD != 'W01' AND T4.FEE_PDCT_TP_CD1 = 'E' THEN 1 END AS HOME_CARE_CNT /* 홈케어 건수 */ , CASE WHEN T1.OG_TP_CD = 'W01' AND T4.FEE_PDCT_TP_CD2 = 'F' THEN 1 WHEN T1.OG_TP_CD != 'W01' AND T4.FEE_PDCT_TP_CD1 = 'F' THEN 1 END AS CSMB_CNT /* 소모품 건수 */ , CASE WHEN T1.OG_TP_CD = 'W01' AND T4.FEE_PDCT_TP_CD2 = 'G' THEN 1 WHEN T1.OG_TP_CD != 'W01' AND T4.FEE_PDCT_TP_CD1 = 'G' THEN 1 END AS ACSR_CNT /* 부속품 건수 */ , CASE WHEN T4.FEE_PDCT_TP_CD1 IS NULL AND T4.FEE_PDCT_TP_CD2 IS NULL THEN 1 END AS NOPD_CNT /* 미등록유형 */ FROM TB_FEAM_WELS_NRCTR_MM_CL T1 /* 웰스순주문계약월마감 */ INNER JOIN TB_SSCT_CNTR_BAS T2 /* 계약기본 */ ON T1.CNTR_NO = T2.CNTR_NO /* 계약번호 */ AND T2.DTA_DL_YN = 'N' INNER JOIN TB_SSCT_CNTR_DTL T3 /* 계약상세 */ ON T1.CNTR_NO = T3.CNTR_NO /* 계약번호 */ AND T1.CNTR_SN = T3.CNTR_SN /* 계약일련번호 */ AND T3.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_FEAM_WELS_FEE_PDCT_TP_BAS T4 /* 웰스수수료제품유형기본 */ ON T3.BASE_PD_CD = T4.BASE_PD_CD /* 기준상품코드 */ AND #{perfYm} BETWEEN T4.APY_STRT_YM AND T4.APY_END_YM /* 적용시작년월, 적용종료년월 */ AND T4.DTA_DL_YN = 'N' LEFT OUTER JOIN ( SELECT T1.CNTR_NO /* 계약번호 */ , T1.CNTR_SN /* 계약일련번호 */ FROM TB_SSCT_RENTAL_RSTL_IZ T1 /* 렌탈재약정내역 */ WHERE 1=1 AND T1.DTA_DL_YN = 'N' AND T1.FEE_ACKMT_BASE_AMT > 0 /* 수수료인정기준금액 */ AND SUBSTR(T1.STPL_STRTDT, 0, 6) = #{perfYm} /* 약정시작일자 */
/* 조직구분 = W99 : 기타 */ AND T1.RCP_OG_TP_CD NOT IN ('W01', 'W02', 'W03' ,'W04', 'W05') /* 판매조직유형코드 */
AND T1.RCP_OG_TP_CD = #{ogTpCd} /* 판매조직유형코드 */
) T5 ON T1.CNTR_NO = T5.CNTR_NO /* 계약번호 */ AND T1.CNTR_SN = T5.CNTR_SN /* 계약일련번호 */ WHERE 1=1 AND T1.DTA_DL_YN = 'N' AND NVL(T1.ACC_NINC_YN, 'N') = 'N' /* 계정순증여부 */ AND T1.CNTR_PERF_CRT_DV_CD = '01' /* 계약실적생성구분코드 = 01 : 순주문 */ AND T1.BASE_YM = #{perfYm} /* 기준년월 */ AND T1.PERF_YM = #{perfYm} /* 실적년월 */
/* 조직구분 = W99 : 기타 */ AND T1.OG_TP_CD NOT IN ('W01', 'W02', 'W03' ,'W04', 'W05') /* 조직유형코드 */
AND T1.OG_TP_CD = #{ogTpCd} /* 조직유형코드 */
AND T1.FEE_TCNT_DV_CD = #{feeTcntDvCd} /* 수수료차수구분코드 */ ) A WHERE 1=1 GROUP BY A.OG_TP_CD ORDER BY A.OG_TP_CD
