mapper.select:
SELECT T.DGR2_LEVL_OG_NM AS OG2_LV , T.DGR3_LEVL_OG_NM AS OG3_LV , T.PRTNR_NO AS SEQUENCE_NUMBER /* 번호 !!!*/ , T.PRTNR_KNM AS EMPL_NM /* 성명 !!!*/ , T.SELL_TP_CD AS SEL_TYPE /* 판매유형 !!!*/ , T.FEE_PERF_TP_CD AS PDCT_TP /* 제품유형 !!!*/ , T.RGLR_SPP_PRC_DV_CD AS PRC_TP /* 가격유형 !!!*/ , T.MCHN_CH_TP_CD AS CHDVC_TP /* 기변유형 !!!*/ , T.FEE_PDCT_TP_CD AS FEE /* 수수료 실적유형 !!!*/ , T.CNTR_DTL_CD AS CNTR_DTL_NO /* 계약상세번호 !!!*/ , T.LCCGUB AS CST_DV /* 고객구분 !!!*/ , T.PD_NM AS PRDT_NM /* 상품명 !!!*/ , T.PD_CD AS PRDT_CODE /* 상품코드 !!!*/ , T.LCETC3 AS PD_DC_CLASS /* 할인구분 !!!*/ , T.LCETC4 AS DISC_CODE /* 할인유형 !!!*/ , T.PMOT_TP_CD AS DSC_SYST /* 할인제도 !!!*/ , T.LCST04 AS COMBI_DV /* 결합구분 !!!*/ , T.LCMONT AS ISTM /* 할부 !!!*/ , T.STPL_PTRM AS STPL_MCNT /* 약정개월 !!!*/ , T.LCFLG3 AS MNGT_PRD /* 관리주기 !!!*/ , T.LCPAMT AS PD_ACC_RSLT /* 인정실적 !!!*/ , T.LCBAMT AS BASE_PRC /* 기준가격 !!!*/ , T.HOMCARE AS HOME_CARE /* 홈케어 !!!*/ , T.HOMCARE3_YEAR AS HCR_MSH_Y3 /* 홈케어멤버십3년 !!!!*/ , T.LCST13 AS FXAM_YN /* 정액여부 !!!*/ , T.LEASE_YN AS FNN_LEASE /* 금융리스 ???? 일단임시 */ , T.LCPCNT AS ELHM_ACKMT_CT /* 가정 인정건수 !!!*/ , T.NEW_LCPCNT AS NW_SELL_CT /* 신규판매건수 ???? 일단임시*/ , T.BS_DAESANG AS OBJ /* BS대상 !!!!*/ , T.RSTL_YN AS RECOMMITMENT /* 재약정여부 !!!*/ , T.LCCRTT AS CNTR_DATE /* 계약일자 !!!*/ , T.LCSLET AS SL_DT /* 매출일자 !!!*/ , T.LCCANT AS CANC_DT /* 취소일자 !!!*/ , T.LCDELDT AS DEM_DT /* 철거일자???? 일단 임시*/ , T.AKDBON AS BRMGR_NO /* 지점장번호 !!!*/ , T.AKDBNM AS BRMGR_FNM /* 지점장성명 !!!*/ , T.LCAMT1 AS RTLFE /* 렌탈료 !!!*/ , T.LCCK02 AS PMOT_NO /* 프로모션번호!!!!! */ , T.LCRCDE AS PKG_PD_NO /*패키지 상품번호!!!!!*/ , '0' AS PKG_SN /* 패키지 일련번호!!!!! */ , T.INTM_ORDR_NO AS MCHN_PRTNR /* 기기고객코드!!!! */ , T.INTM_ORDR_PRDT AS MCHN_PD /* 기기상품코드!!! */ , NVL(T.EX_YN,'N') AS PERF_EXCD /*실적제외등록여부!!! */ FROM ( SELECT MO.DGR2_LEVL_OG_NM , MO.DGR3_LEVL_OG_NM , MP.PRTNR_NO /* 번호 !!!*/ , MP.PRTNR_KNM /* 성명 !!!*/ , CD.SELL_TP_CD /* 판매유형 !!!*/ , PF.FEE_PERF_TP_CD /* 제품유형 !!!*/ , CASE WHEN CD.SELL_TP_CD = '6' THEN PSP.RGLR_SPP_PRC_DV_CD END AS RGLR_SPP_PRC_DV_CD /* 가격유형 !!!*/ , MC.MCHN_CH_TP_CD /* 기변유형 !!!*/ , PF.FEE_PDCT_TP_CD /* 수수료 실적유형 !!!*/ , CD.CNTR_NO||CD.CNTR_SN AS CNTR_DTL_CD /* 계약상세번호 !!!*/ , CASE WHEN CB.COPN_DV_CD = '2' THEN '사업' ELSE '개인' END AS LCCGUB /* 고객구분 !!!*/ , PP.PD_NM /* 상품명 !!!*/ , PP.PD_CD /* 상품코드 !!!*/ , CD.SELL_DSC_DV_CD AS LCETC3 /* 할인구분 !!!*/ , CD.SELL_DSC_TP_CD AS LCETC4 /* 할인유형 !!!*/ , CASE WHEN CD.SELL_TP_CD = '6' THEN CP.PMOT_TP_CD END AS PMOT_TP_CD /* 할인제도 !!!*/ , CASE WHEN CD.SELL_TP_CD = '6' AND PSP.RGLR_SPP_PRC_DV_CD = 4 THEN 'Y' END AS LCST04 /* 결합구분 !!!*/ , CD.ISTM_MCN AS LCMONT /* 할부 !!!*/ , CD.STPL_PTRM /* 약정개월 !!!*/ , CD.SV_PRD AS LCFLG3 /* 관리주기 !!!*/ , CD.ACKMT_PERF_AMT AS LCPAMT /* 인정실적 !!!*/ , CD.FEE_ACKMT_BASE_AMT AS LCBAMT /* 기준가격 !!!*/ , CASE WHEN PF.FEE_PDCT_TP_CD = 'E' THEN ( CASE WHEN CD.SELL_TP_CD = '6' THEN (CD.SELL_AMT- CD.CNTRAM_DSC_AMT )/ CD.SV_PRD ELSE CD.SELL_AMT END ) ELSE 0 END AS HOMCARE /* 홈케어 !!!*/ , CASE WHEN CD.SELL_TP_CD = '3' AND CD.STPL_PTRM = '36' THEN 'Y' ELSE ' ' END AS HOMCARE3_YEAR /* 홈케어멤버십3년 !!!!*/ , CD.FEE_FXAM_YN AS LCST13 /* 정액여부 !!!*/ , CASE WHEN CD.SELL_TP_DTL_CD IN ('22','24') THEN 'Y' ELSE 'N' END AS LEASE_YN /* 금융리스 !!! */ , CASE WHEN CD.SELL_TP_CD IN ('1', '6') THEN CD.FEE_ACKMT_CT WHEN CD.SELL_TP_CD = '2' THEN ( CASE WHEN NVL(MC.OJ_CNTR_NO, '0') != '0' AND MC.MCHN_CH_TP_CD IN ('1','18') THEN 0 ELSE CD.FEE_ACKMT_CT END ) WHEN CD.SELL_TP_CD = '3' THEN ( CASE WHEN CD.CNTRW_TP_CD = '5' AND CD.SELL_TP_DTL_CD = '33' AND CP.PMOT_CD = 'P1' THEN 1 ELSE 0 END ) END AS LCPCNT /* 가정 인정건수 !!!*/ , 0 AS NEW_LCPCNT /* 신규판매건수 ???? 일단임시*/ , CASE WHEN WS.ROW_SCNT = 1 THEN 'Y' END AS BS_DAESANG /* BS대상 !!!!*/ , CD.RSTL_YN /* 재약정여부 !!!*/ , CB.CNTR_CNFM_DTM AS LCCRTT /* 계약일자 !!!*/ , SC.SL_RCOG_DT AS LCSLET /* 매출일자 !!!*/ , CD.CNTR_PD_ENDDT AS LCCANT /* 취소일자 !!!*/ , WD.REQD_DT AS LCDELDT /* 철거일자!!!*/ , MO.DGR3_LEVL_DG_PRTNR_NO AS AKDBON /* 지점장번호 !!!*/ , MO.DGR3_LEVL_DG_PRTNR_NM AS AKDBNM /* 지점장성명 !!!*/ , CD.SELL_AMT AS LCAMT1 /* 렌탈료 !!!*/ , CASE WHEN CD.SELL_TP_CD = '2' THEN CP.PMOT_CD ELSE '0' END AS LCCK02 /* 프로모션번호!!!!! */ , CASE WHEN CD.SELL_TP_CD = '2' THEN CD.HGR_PD_CD ELSE '0' END AS LCRCDE /*패키지 상품번호!!!!!*/ , CASE WHEN CD.SELL_TP_CD = '6' THEN SUBSTR(CB.CNTR_NO,2,4) ||'-'||SUBSTR(CB.CNTR_NO,6,7) END AS INTM_ORDR_NO /* 기기고객코드!!!! */ , CASE WHEN CD.SELL_TP_CD = '6' THEN PP.PD_CD END AS INTM_ORDR_PRDT/* 기기상품코드!!! */ , CASE WHEN EX.EX_SCNT = 1 THEN 'Y' END AS EX_YN /*실적제외등록여부!!! */ FROM TB_SSCT_CNTR_BAS CB INNER JOIN TB_SSCT_CNTR_DTL CD ON CB.CNTR_NO = CD.CNTR_NO
AND CD.SELL_TP_CD IN ('1','2','3','6')
AND 1 = 2 /* 재약정은 따로 조회*/
AND CD.SELL_TP_CD = #{sellTpCd}
/* 예약 */ AND CD.BOO_SELL_TP_CD = '2'
/* 매출 */ AND NVL(CD.BOO_SELL_TP_CD,0) != '2'
AND CD.HGR_PD_CD >= #{pkgStrtCd}
AND CD.HGR_PD_CD <= #{pkgEndCd}
/* 매출 */ INNER JOIN TB_CBCL_WELLS_SL_CNFM_BAS SC /*T WELLS매출확정기본 T*/ ON CD.CNTR_NO = SC.CNTR_NO AND CD.CNTR_SN = SC.CNTR_SN AND SC.SL_RCOG_DT BETWEEN #{strtDt} AND #{endDt} /* 매출 확정일자 */
/* 전체 */ LEFT OUTER JOIN TB_CBCL_WELLS_SL_CNFM_BAS SC /*T WELLS매출확정기본 T*/ ON CD.CNTR_NO = SC.CNTR_NO AND CD.CNTR_SN = SC.CNTR_SN
INNER JOIN TB_OGBS_MM_PRTNR_IZ MP /* 월파트너 */ ON CB.SELL_PRTNR_NO = MP.PRTNR_NO AND CB.SELL_OG_TP_CD = MP.OG_TP_CD AND MP.BASE_YM = SUBSTR(#{strtDt},0,6)
AND MP.PRTNR_NO = #{prtnrNo}
INNER JOIN TB_OGBS_MM_OG_IZ MO /* 월조직 */ ON MP.BASE_YM = MO.BASE_YM AND MP.OG_ID = MO.OG_ID AND MP.OG_TP_CD = MO.OG_TP_CD
AND MO.DGR1_LEVL_OG_ID = #{og1LevlId}
AND MO.DGR2_LEVL_OG_ID = #{og2LevlId}
AND MO.DGR3_LEVL_OG_ID = #{og3LevlId}
INNER JOIN TB_PDBS_PD_BAS PP ON CD.BASE_PD_CD = PP.PD_CD
AND PP.PD_CD >= #{pdStrtCd}
AND PP.PD_CD <= #{pdEndCd}
INNER JOIN TB_FEAM_WELS_FEE_PDCT_TP_BAS PD ON CD.BASE_PD_CD = PD.BASE_PD_CD AND SUBSTR(#{strtDt},0,6) BETWEEN APY_STRT_YM AND APY_END_YM INNER JOIN TB_FEAM_WELS_FEE_PERF_TP_BAS PF ON PD.FEE_PDCT_TP_CD1 = PF.FEE_PDCT_TP_CD AND CD.SELL_TP_CD = PF.SELL_TP_CD
AND PF.FEE_PDCT_TP_CD = #{pdctTpCd}
AND PF.FEE_PERF_TP_CD = #{feePerfCd}
LEFT OUTER JOIN TB_PDBS_SDING_PRC_BAS PSP ON CD.BASE_PD_CD = PSP.PDCT_PD_CD AND CB.CNTR_CNFM_DTM BETWEEN PSP.APY_STRTDT AND PSP.APY_ENDDT LEFT OUTER JOIN TB_SSCT_MCHN_CH_IZ MC ON CD.CNTR_NO = MC.BASE_CNTR_NO AND CD.CNTR_SN = MC.BASE_CNTR_SN LEFT OUTER JOIN TB_SSCT_CNTR_PMOT_IZ CP ON CD.CNTR_NO = CP.DTL_CNTR_NO AND CD.CNTR_SN = CP.DTL_CNTR_SN LEFT OUTER JOIN TB_SSCT_CNTR_WELLS_DTL WD ON CD.CNTR_NO = WD.CNTR_NO AND CD.CNTR_SN = WD.CNTR_SN LEFT OUTER JOIN LATERAL ( SELECT COUNT(1) AS ROW_SCNT FROM TB_FEAM_WELS_SV_FEE_BAS A WHERE A.BASE_PD_CD = CD.BASE_PD_CD AND A.VST_MCN = 0 AND SUBSTR(CB.CNTR_CNFM_DTM,0,6) BETWEEN A.APY_STRT_YM AND A.APY_END_YM )WS ON 1=1 LEFT OUTER JOIN LATERAL ( SELECT COUNT(1) AS EX_SCNT FROM TB_FEDD_FEE_PERF_EXCD_OJ_BAS A WHERE A.CNTR_NO = CD.CNTR_NO AND A.CNTR_SN = CD.CNTR_SN AND A.FEE_PERF_EXCD_DV_CD = '01' )EX ON 1=1 WHERE CB.SELL_OG_TP_CD = 'W03'
AND CB.CNTR_CNFM_DTM BETWEEN #{strtDt} AND #{endDt} /* 계약확정일시 */
AND CB.CNTR_CAN_DTM >= #{cancStrtDt}
AND CB.CNTR_CAN_DTM <= #{cancEndDt}
UNION SELECT MO.DGR2_LEVL_OG_NM , MO.DGR3_LEVL_OG_NM , MP.PRTNR_NO /* 번호 !!!*/ , MP.PRTNR_KNM /* 성명 !!!*/ , CD.SELL_TP_CD /* 판매유형 !!!*/ , PF.FEE_PERF_TP_CD /* 제품유형 !!!*/ , '' AS RGLR_SPP_PRC_DV_CD /* 가격유형 !!!*/ , MC.MCHN_CH_TP_CD /* 기변유형 !!!*/ , PF.FEE_PDCT_TP_CD /* 수수료 실적유형 !!!*/ , CD.CNTR_NO||CD.CNTR_SN AS CNTR_DTL_CD /* 계약상세번호 !!!*/ , CASE WHEN CB.COPN_DV_CD = '2' THEN '사업' ELSE '개인' END AS LCCGUB /* 고객구분 !!!*/ , PP.PD_NM /* 상품명 !!!*/ , PP.PD_CD /* 상품코드 !!!*/ , CD.SELL_DSC_DV_CD AS LCETC3 /* 할인구분 !!!*/ , CD.SELL_DSC_TP_CD AS LCETC4 /* 할인유형 !!!*/ , '' AS PMOT_TP_CD /* 할인제도 !!!*/ , '' AS LCST04 /* 결합구분 !!!*/ , CD.ISTM_MCN AS LCMONT /* 할부 !!!*/ , CD.STPL_PTRM /* 약정개월 !!!*/ , CD.SV_PRD AS LCFLG3 /* 관리주기 !!!*/ , CD.ACKMT_PERF_AMT AS LCPAMT /* 인정실적 !!!*/ , CD.FEE_ACKMT_BASE_AMT AS LCBAMT /* 기준가격 !!!*/ , 0 AS HOMCARE /* 홈케어 !!!*/ , '' AS HOMCARE3_YEAR /* 홈케어멤버십3년 !!!!*/ , CD.FEE_FXAM_YN AS LCST13 /* 정액여부 !!!*/ , CASE WHEN CD.SELL_TP_DTL_CD IN ('22','24') THEN 'Y' ELSE 'N' END AS LEASE_YN /* 금융리스 !!! */ , CD.FEE_ACKMT_CT AS LCPCNT /* 가정 인정건수 !!!*/ , 0 AS NEW_LCPCNT /* 신규판매건수 ???? 일단임시*/ , CASE WHEN WS.ROW_SCNT = 1 THEN 'Y' END AS BS_DAESANG /* BS대상 !!!!*/ , CD.RSTL_YN /* 재약정여부 !!!*/ , RI.STPL_STRTDT AS LCCRTT /* 계약일자 !!!*/ , SC.SL_RCOG_DT AS LCSLET /* 매출일자 !!!*/ , CD.CNTR_PD_ENDDT AS LCCANT /* 취소일자 !!!*/ , WD.REQD_DT AS LCDELDT /* 철거일자!!!*/ , MO.DGR3_LEVL_DG_PRTNR_NO AS AKDBON /* 지점장번호 !!!*/ , MO.DGR3_LEVL_DG_PRTNR_NM AS AKDBNM /* 지점장성명 !!!*/ , CD.SELL_AMT AS LCAMT1 /* 렌탈료 !!!*/ , '0' AS LCCK02 /* 프로모션번호!!!!! */ , '' AS LCRCDE /*패키지 상품번호!!!!!*/ , '' AS INTM_ORDR_NO /* 기기고객코드!!!! */ , '' AS INTM_ORDR_PRDT/* 기기상품코드!!! */ , CASE WHEN EX.EX_SCNT = 1 THEN 'Y' END AS EX_YN /*실적제외등록여부!!! */ FROM TB_SSCT_RENTAL_RSTL_IZ RI INNER JOIN TB_SSCT_CNTR_BAS CB ON RI.CNTR_NO = CB.CNTR_NO INNER JOIN TB_SSCT_CNTR_DTL CD ON RI.CNTR_NO = CD.CNTR_NO AND RI.CNTR_SN = CD.CNTR_SN AND CD.SELL_TP_CD = '2' /* 예약 */ AND CD.BOO_SELL_TP_CD = '2'
/* 매출 */ AND NVL(CD.BOO_SELL_TP_CD,0) != '2'
AND CD.HGR_PD_CD >= #{pkgStrtCd}
AND CD.HGR_PD_CD <= #{pkgEndCd}
/* 매출 */ INNER JOIN TB_CBCL_WELLS_SL_CNFM_BAS SC /*T WELLS매출확정기본 T*/ ON RI.CNTR_NO = SC.CNTR_NO AND RI.CNTR_SN = SC.CNTR_SN AND SC.SL_RCOG_DT BETWEEN #{strtDt} AND #{endDt} /* 매출 확정일자 */
/* 전체 */ LEFT OUTER JOIN TB_CBCL_WELLS_SL_CNFM_BAS SC /*T WELLS매출확정기본 T*/ ON RI.CNTR_NO = SC.CNTR_NO AND RI.CNTR_SN = SC.CNTR_SN
INNER JOIN TB_OGBS_MM_PRTNR_IZ MP /* 월파트너 */ ON RI.RCP_PRTNR_NO = MP.PRTNR_NO AND RI.RCP_OG_TP_CD = MP.OG_TP_CD AND MP.BASE_YM = SUBSTR(#{strtDt},0,6)
AND MP.PRTNR_NO = #{prtnrNo}
INNER JOIN TB_OGBS_MM_OG_IZ MO /* 월조직 */ ON MP.BASE_YM = MO.BASE_YM AND MP.OG_ID = MO.OG_ID AND MP.OG_TP_CD = MO.OG_TP_CD
AND MO.DGR1_LEVL_OG_ID = #{og1LevlId}
AND MO.DGR2_LEVL_OG_ID = #{og2LevlId}
AND MO.DGR3_LEVL_OG_ID = #{og3LevlId}
INNER JOIN TB_PDBS_PD_BAS PP ON CD.BASE_PD_CD = PP.PD_CD
AND PP.PD_CD >= #{pdStrtCd}
AND PP.PD_CD <= #{pdEndCd}
INNER JOIN TB_FEAM_WELS_FEE_PDCT_TP_BAS PD ON CD.BASE_PD_CD = PD.BASE_PD_CD AND SUBSTR(#{strtDt},0,6) BETWEEN APY_STRT_YM AND APY_END_YM INNER JOIN TB_FEAM_WELS_FEE_PERF_TP_BAS PF ON PD.FEE_PDCT_TP_CD1 = PF.FEE_PDCT_TP_CD AND CD.SELL_TP_CD = PF.SELL_TP_CD
AND PF.FEE_PDCT_TP_CD = #{pdctTpCd}
AND PF.FEE_PERF_TP_CD = #{feePerfCd}
LEFT OUTER JOIN TB_PDBS_SDING_PRC_BAS PSP ON CD.BASE_PD_CD = PSP.PDCT_PD_CD AND RI.STPL_STRTDT BETWEEN PSP.APY_STRTDT AND PSP.APY_ENDDT LEFT OUTER JOIN TB_SSCT_MCHN_CH_IZ MC ON RI.CNTR_NO = MC.BASE_CNTR_NO AND RI.CNTR_SN = MC.BASE_CNTR_SN LEFT OUTER JOIN TB_SSCT_CNTR_PMOT_IZ CP ON RI.CNTR_NO = CP.DTL_CNTR_NO AND RI.CNTR_SN = CP.DTL_CNTR_SN LEFT OUTER JOIN TB_SSCT_CNTR_WELLS_DTL WD ON RI.CNTR_NO = WD.CNTR_NO AND RI.CNTR_SN = WD.CNTR_SN LEFT OUTER JOIN LATERAL ( SELECT COUNT(1) AS ROW_SCNT FROM TB_FEAM_WELS_SV_FEE_BAS A WHERE A.BASE_PD_CD = CD.BASE_PD_CD AND A.VST_MCN = 0 AND SUBSTR(RI.STPL_STRTDT,0,6) BETWEEN A.APY_STRT_YM AND A.APY_END_YM )WS ON 1=1 LEFT OUTER JOIN LATERAL ( SELECT COUNT(1) AS EX_SCNT FROM TB_FEDD_FEE_PERF_EXCD_OJ_BAS A WHERE A.CNTR_NO = RI.CNTR_NO AND A.CNTR_SN = RI.CNTR_SN AND A.FEE_PERF_EXCD_DV_CD = '01' )EX ON 1=1 WHERE CB.SELL_OG_TP_CD = 'W03'
AND RI.STPL_STRTDT BETWEEN #{strtDt} AND #{endDt} /* 계약확정일시 */
AND RI.STPL_CAN_DTM >= #{cancStrtDt}
AND RI.STPL_CAN_DTM <= #{cancEndDt}
)T

SELECT T3.DGR2_LEVL_OG_NM AS OG2_LV , T3.DGR3_LEVL_OG_NM AS OG3_LV , T2.PRTNR_NO , T2.PRTNR_KNM , T1.CNTR_NO||T1.CNTR_SN AS CNTR_DTL_NO , T1.RCPDT , T1.SL_DT , T1.CAN_DT , T5.FEE_PDCT_TP_CD1 AS FEE_PDCT_TP_CD , T1.PD_CD , T6.FEE_PERF_TP_CD AS FEE_PERF_TP_CD , T4.PD_NM , T1.ACKMT_PERF_CT , T1.MCHN_CH_TP_CD AS MCHN_CH_TP_CD FROM TB_FEAM_WELS_NRCTR_MM_CL T1 INNER JOIN TB_OGBS_MM_PRTNR_IZ T2 /* 월파트너 */ ON T1.PRTNR_NO = T2.PRTNR_NO AND T1.OG_TP_CD = T2.OG_TP_CD AND T1.BASE_YM = T2.BASE_YM
AND T2.PRTNR_NO = #{prtnrNo}
INNER JOIN TB_OGBS_MM_OG_IZ T3 /* 월조직 */ ON T2.BASE_YM = T3.BASE_YM AND T2.OG_ID = T3.OG_ID AND T2.OG_TP_CD = T3.OG_TP_CD
AND T3.DGR1_LEVL_OG_ID = #{og1LevlId}
AND T3.DGR2_LEVL_OG_ID = #{og2LevlId}
AND T3.DGR3_LEVL_OG_ID = #{og3LevlId}
INNER JOIN TB_PDBS_PD_BAS T4 ON T1.PD_CD = T4.PD_CD INNER JOIN TB_FEAM_WELS_FEE_PDCT_TP_BAS T5 ON T1.PD_CD = T5.BASE_PD_CD AND #{perfYm} BETWEEN APY_STRT_YM AND APY_END_YM INNER JOIN TB_FEAM_WELS_FEE_PERF_TP_BAS T6 ON T5.FEE_PDCT_TP_CD1 = T6.FEE_PDCT_TP_CD AND T1.SELL_TP_CD = T6.SELL_TP_CD WHERE T1.BASE_YM = #{perfYm} AND T1.FEE_TCNT_DV_CD = #{feeTcntDvCd} AND T1.OG_TP_CD = 'W03' AND T1.CNTR_PERF_CRT_DV_CD = '01'

SELECT T.OG_ID AS BLG/* 소속 !!!*/ , T.PRTNR_NO AS SEQUENCE_NUMBER /* 번호 !!!*/ , T.PRTNR_KNM AS EMPL_NM /* 성명 !!!*/ , T.SELL_TP_CD AS SEL_TYPE /* 판매유형 !!!*/ , T.FEE_PERF_TP_CD AS PDCT_TP /* 제품유형 !!!*/ , T.RGLR_SPP_PRC_DV_CD AS PRC_TP /* 가격유형 !!!*/ , T.MCHN_CH_TP_CD AS CHDVC_TP /* 기변유형 !!!*/ , T.FEE_PDCT_TP_CD AS FEE /* 수수료 실적유형 !!!*/ , T.CNTR_DTL_CD AS CNTR_DTL_NO /* 계약상세번호 !!!*/ , T.LCCGUB AS CST_DV /* 고객구분 !!!*/ , T.PD_NM AS PRDT_NM /* 상품명 !!!*/ , T.PD_CD AS PRDT_CODE /* 상품코드 !!!*/ , T.LCETC3 AS PD_DC_CLASS /* 할인구분 !!!*/ , T.LCETC4 AS DISC_CODE /* 할인유형 !!!*/ , T.PMOT_TP_CD AS DSC_SYST /* 할인제도 !!!*/ , T.LCST04 AS COMBI_DV /* 결합구분 !!!*/ , T.LCMONT AS ISTM /* 할부 !!!*/ , T.STPL_PTRM AS STPL_MCNT /* 약정개월 !!!*/ , T.LCFLG3 AS MNGT_PRD /* 관리주기 !!!*/ , T.LCPAMT AS PD_ACC_RSLT /* 인정실적 !!!*/ , T.LCBAMT AS BASE_PRC /* 기준가격 !!!*/ , T.HOMCARE AS HOME_CARE /* 홈케어 !!!*/ , T.HOMCARE3_YEAR AS HCR_MSH_Y3 /* 홈케어멤버십3년 !!!!*/ , T.LCST13 AS FXAM_YN /* 정액여부 !!!*/ , T.LEASE_YN AS FNN_LEASE /* 금융리스 ???? 일단임시 */ , T.LCPCNT AS ELHM_ACKMT_CT /* 가정 인정건수 !!!*/ , T.NEW_LCPCNT AS NW_SELL_CT /* 신규판매건수 ???? 일단임시*/ , T.BS_DAESANG AS OBJ /* BS대상 !!!!*/ , T.RSTL_YN AS RECOMMITMENT /* 재약정여부 !!!*/ , SUBSTR(T.LCCRTT,1,8) AS CNTR_DATE /* 계약일자 !!!*/ , SUBSTR(T.LCSLET,1,8) AS SL_DT /* 매출일자 !!!*/ , SUBSTR(T.LCCANT,1,8) AS CANC_DT /* 취소일자 !!!*/ , SUBSTR(T.LCDELDT,1,8) AS DEM_DT /* 철거일자???? 일단 임시*/ , T.AKDBON AS BRMGR_NO /* 지점장번호 !!!*/ , T.AKDBNM AS BRMGR_FNM /* 지점장성명 !!!*/ , T.LCAMT1 AS RTLFE /* 렌탈료 !!!*/ , T.LCCK02 AS PMOT_NO /* 프로모션번호!!!!! */ , T.LCRCDE AS PKG_PD_NO /*패키지 상품번호!!!!!*/ , '0' AS PKG_SN /* 패키지 일련번호!!!!! */ , T.INTM_ORDR_NO AS MCHN_PRTNR /* 기기고객코드!!!! */ , T.INTM_ORDR_PRDT AS MCHN_PD /* 기기상품코드!!! */ , NVL(T.EX_YN,'N') AS PERF_EXCD /*실적제외등록여부!!! */ FROM ( SELECT MP.OG_ID /* 소속 !!!*/ , MP.PRTNR_NO /* 번호 !!!*/ , MP.PRTNR_KNM /* 성명 !!!*/ , CD.SELL_TP_CD /* 판매유형 !!!*/ , PF.FEE_PERF_TP_CD /* 제품유형 !!!*/ , CASE WHEN CD.SELL_TP_CD = '6' THEN PSP.RGLR_SPP_PRC_DV_CD END AS RGLR_SPP_PRC_DV_CD /* 가격유형 !!!*/ , MC.MCHN_CH_TP_CD /* 기변유형 !!!*/ , PF.FEE_PDCT_TP_CD /* 수수료 실적유형 !!!*/ , CD.CNTR_NO||CD.CNTR_SN AS CNTR_DTL_CD /* 계약상세번호 !!!*/ , CASE WHEN CB.COPN_DV_CD = '2' THEN '사업' ELSE '개인' END AS LCCGUB /* 고객구분 !!!*/ , PP.PD_NM /* 상품명 !!!*/ , PP.PD_CD /* 상품코드 !!!*/ , CD.SELL_DSC_DV_CD AS LCETC3 /* 할인구분 !!!*/ , CD.SELL_DSC_TP_CD AS LCETC4 /* 할인유형 !!!*/ , CASE WHEN CD.SELL_TP_CD = '6' THEN CP.PMOT_TP_CD END AS PMOT_TP_CD /* 할인제도 !!!*/ , CASE WHEN CD.SELL_TP_CD = '6' AND PSP.RGLR_SPP_PRC_DV_CD = 4 THEN 'Y' END AS LCST04 /* 결합구분 !!!*/ , CD.ISTM_MCN AS LCMONT /* 할부 !!!*/ , CD.STPL_PTRM /* 약정개월 !!!*/ , CD.SV_PRD AS LCFLG3 /* 관리주기 !!!*/ , CD.ACKMT_PERF_AMT AS LCPAMT /* 인정실적 !!!*/ , CD.FEE_ACKMT_BASE_AMT AS LCBAMT /* 기준가격 !!!*/ , CASE WHEN PF.FEE_PDCT_TP_CD = 'E' THEN ( CASE WHEN CD.SELL_TP_CD = '6' THEN (CD.SELL_AMT- CD.CNTRAM_DSC_AMT )/ CD.SV_PRD ELSE CD.SELL_AMT END ) ELSE 0 END AS HOMCARE /* 홈케어 !!!*/ , CASE WHEN CD.SELL_TP_CD = '3' AND CD.STPL_PTRM = '36' THEN 'Y' ELSE ' ' END AS HOMCARE3_YEAR /* 홈케어멤버십3년 !!!!*/ , CD.FEE_FXAM_YN AS LCST13 /* 정액여부 !!!*/ , CASE WHEN CD.SELL_TP_DTL_CD IN ('22','24') THEN 'Y' ELSE 'N' END AS LEASE_YN /* 금융리스 !!! */ , CASE WHEN CD.SELL_TP_CD IN ('1', '6') THEN CD.FEE_ACKMT_CT WHEN CD.SELL_TP_CD = '2' THEN ( CASE WHEN NVL(MC.OJ_CNTR_NO, '0') != '0' AND MC.MCHN_CH_TP_CD IN ('1','18') THEN 0 ELSE CD.FEE_ACKMT_CT END ) WHEN CD.SELL_TP_CD = '3' THEN ( CASE WHEN CD.CNTRW_TP_CD = '5' AND CD.SELL_TP_DTL_CD = '33' AND CP.PMOT_CD = 'P1' THEN 1 ELSE 0 END ) END AS LCPCNT /* 가정 인정건수 !!!*/ , 0 AS NEW_LCPCNT /* 신규판매건수 ???? 일단임시*/ , CASE WHEN WS.ROW_SCNT = 1 THEN 'Y' END AS BS_DAESANG /* BS대상 !!!!*/ , CD.RSTL_YN /* 재약정여부 !!!*/ , CB.CNTR_CNFM_DTM AS LCCRTT /* 계약일자 !!!*/ , SC.SL_RCOG_DT AS LCSLET /* 매출일자 !!!*/ , CB.CNTR_CAN_DTM AS LCCANT /* 취소일자 !!!*/ , WD.REQD_DT AS LCDELDT /* 철거일자!!!*/ , MO.DGR3_LEVL_DG_PRTNR_NO AS AKDBON /* 지점장번호 !!!*/ , MO.DGR3_LEVL_DG_PRTNR_NM AS AKDBNM /* 지점장성명 !!!*/ , CD.SELL_AMT AS LCAMT1 /* 렌탈료 !!!*/ , CASE WHEN CD.SELL_TP_CD = '2' THEN CP.PMOT_CD ELSE '0' END AS LCCK02 /* 프로모션번호!!!!! */ , CASE WHEN CD.SELL_TP_CD = '2' THEN CD.HGR_PD_CD ELSE '0' END AS LCRCDE /*패키지 상품번호!!!!!*/ , CASE WHEN CD.SELL_TP_CD = '6' THEN SUBSTR(CB.CNTR_NO,2,4) ||'-'||SUBSTR(CB.CNTR_NO,6,7) END AS INTM_ORDR_NO /* 기기고객코드!!!! */ , CASE WHEN CD.SELL_TP_CD = '6' THEN PP.PD_CD END AS INTM_ORDR_PRDT/* 기기상품코드!!! */ , CASE WHEN EX.EX_SCNT = 1 THEN 'Y' END AS EX_YN /*실적제외등록여부!!! */ FROM TB_SSCT_CNTR_BAS CB INNER JOIN TB_SSCT_CNTR_DTL CD ON CB.CNTR_NO = CD.CNTR_NO
AND CD.SELL_TP_CD IN ('1','2','3','6')
AND 1 = 2 /* 재약정은 따로 조회*/
AND CD.SELL_TP_CD = #{sellTpCd}
/* 예약 */ AND CD.BOO_SELL_TP_CD = '2'
/* 매출 */ AND NVL(CD.BOO_SELL_TP_CD,0) != '2'
AND CD.HGR_PD_CD >= #{pkgStrtCd}
AND CD.HGR_PD_CD <= #{pkgEndCd}
/* 매출 */ INNER JOIN TB_CBCL_WELLS_SL_CNFM_BAS SC /*T WELLS매출확정기본 T*/ ON CD.CNTR_NO = SC.CNTR_NO AND CD.CNTR_SN = SC.CNTR_SN AND SC.SL_RCOG_DT BETWEEN #{strtDt} AND #{endDt} /* 매출 확정일자 */
/* 전체 */ LEFT OUTER JOIN TB_CBCL_WELLS_SL_CNFM_BAS SC /*T WELLS매출확정기본 T*/ ON CD.CNTR_NO = SC.CNTR_NO AND CD.CNTR_SN = SC.CNTR_SN
INNER JOIN TB_OGBS_MM_PRTNR_IZ MP /* 월파트너 */ ON CB.SELL_PRTNR_NO = MP.PRTNR_NO AND CB.SELL_OG_TP_CD = MP.OG_TP_CD AND MP.BASE_YM = SUBSTR(#{strtDt},0,6)
AND MP.PRTNR_NO = #{prtnrNo}
INNER JOIN TB_OGBS_MM_OG_IZ MO /* 월조직 */ ON MP.BASE_YM = MO.BASE_YM AND MP.OG_ID = MO.OG_ID AND MP.OG_TP_CD = MO.OG_TP_CD
AND MO.DGR1_LEVL_OG_ID = #{og1LevlId}
AND MO.DGR2_LEVL_OG_ID = #{og2LevlId}
AND MO.DGR3_LEVL_OG_ID = #{og3LevlId}
INNER JOIN TB_PDBS_PD_BAS PP ON CD.BASE_PD_CD = PP.PD_CD
AND PP.PD_CD >= #{pdStrtCd}
AND PP.PD_CD <= #{pdEndCd}
INNER JOIN TB_FEAM_WELS_FEE_PDCT_TP_BAS PD ON CD.BASE_PD_CD = PD.BASE_PD_CD AND SUBSTR(#{strtDt},0,6) BETWEEN APY_STRT_YM AND APY_END_YM INNER JOIN TB_FEAM_WELS_FEE_PERF_TP_BAS PF ON PD.FEE_PDCT_TP_CD1 = PF.FEE_PDCT_TP_CD AND CD.SELL_TP_CD = PF.SELL_TP_CD
AND PF.FEE_PDCT_TP_CD = #{pdctTpCd}
AND PF.FEE_PERF_TP_CD = #{feePerfCd}
LEFT OUTER JOIN TB_PDBS_SDING_PRC_BAS PSP ON CD.BASE_PD_CD = PSP.PDCT_PD_CD AND CB.CNTR_CNFM_DTM BETWEEN PSP.APY_STRTDT AND PSP.APY_ENDDT LEFT OUTER JOIN TB_SSCT_MCHN_CH_IZ MC ON CD.CNTR_NO = MC.BASE_CNTR_NO AND CD.CNTR_SN = MC.BASE_CNTR_SN LEFT OUTER JOIN TB_SSCT_CNTR_PMOT_IZ CP ON CD.CNTR_NO = CP.DTL_CNTR_NO AND CD.CNTR_SN = CP.DTL_CNTR_SN LEFT OUTER JOIN TB_SSCT_CNTR_WELLS_DTL WD ON CD.CNTR_NO = WD.CNTR_NO AND CD.CNTR_SN = WD.CNTR_SN LEFT OUTER JOIN LATERAL ( SELECT COUNT(1) AS ROW_SCNT FROM TB_FEAM_WELS_SV_FEE_BAS A WHERE A.BASE_PD_CD = CD.BASE_PD_CD AND A.VST_MCN = 0 AND SUBSTR(CB.CNTR_CNFM_DTM,0,6) BETWEEN A.APY_STRT_YM AND A.APY_END_YM )WS ON 1=1 LEFT OUTER JOIN LATERAL ( SELECT COUNT(1) AS EX_SCNT FROM TB_FEDD_FEE_PERF_EXCD_OJ_BAS A WHERE A.CNTR_NO = CD.CNTR_NO AND A.CNTR_SN = CD.CNTR_SN AND A.FEE_PERF_EXCD_DV_CD = '01' )EX ON 1=1 WHERE CB.SELL_OG_TP_CD = 'W02'
AND SUBSTR(CB.CNTR_CNFM_DTM,1,8) BETWEEN #{strtDt} AND #{endDt} /* 계약확정일시 */
AND SUBSTR(CB.CNTR_CAN_DTM,1,8) >= #{cancStrtDt}
AND SUBSTR(CB.CNTR_CAN_DTM,1,8) <= #{cancEndDt}
UNION SELECT MP.OG_ID /* 소속 !!!*/ , MP.PRTNR_NO /* 번호 !!!*/ , MP.PRTNR_KNM /* 성명 !!!*/ , CD.SELL_TP_CD /* 판매유형 !!!*/ , PF.FEE_PERF_TP_CD /* 제품유형 !!!*/ , '' AS RGLR_SPP_PRC_DV_CD /* 가격유형 !!!*/ , MC.MCHN_CH_TP_CD /* 기변유형 !!!*/ , PF.FEE_PDCT_TP_CD /* 수수료 실적유형 !!!*/ , CD.CNTR_NO||CD.CNTR_SN AS CNTR_DTL_CD /* 계약상세번호 !!!*/ , CASE WHEN CB.COPN_DV_CD = '2' THEN '사업' ELSE '개인' END AS LCCGUB /* 고객구분 !!!*/ , PP.PD_NM /* 상품명 !!!*/ , PP.PD_CD /* 상품코드 !!!*/ , CD.SELL_DSC_DV_CD AS LCETC3 /* 할인구분 !!!*/ , CD.SELL_DSC_TP_CD AS LCETC4 /* 할인유형 !!!*/ , '' AS PMOT_TP_CD /* 할인제도 !!!*/ , '' AS LCST04 /* 결합구분 !!!*/ , CD.ISTM_MCN AS LCMONT /* 할부 !!!*/ , CD.STPL_PTRM /* 약정개월 !!!*/ , CD.SV_PRD AS LCFLG3 /* 관리주기 !!!*/ , CD.ACKMT_PERF_AMT AS LCPAMT /* 인정실적 !!!*/ , CD.FEE_ACKMT_BASE_AMT AS LCBAMT /* 기준가격 !!!*/ , 0 AS HOMCARE /* 홈케어 !!!*/ , '' AS HOMCARE3_YEAR /* 홈케어멤버십3년 !!!!*/ , CD.FEE_FXAM_YN AS LCST13 /* 정액여부 !!!*/ , CASE WHEN CD.SELL_TP_DTL_CD IN ('22','24') THEN 'Y' ELSE 'N' END AS LEASE_YN /* 금융리스 !!! */ , CD.FEE_ACKMT_CT AS LCPCNT /* 가정 인정건수 !!!*/ , 0 AS NEW_LCPCNT /* 신규판매건수 ???? 일단임시*/ , CASE WHEN WS.ROW_SCNT = 1 THEN 'Y' END AS BS_DAESANG /* BS대상 !!!!*/ , CD.RSTL_YN /* 재약정여부 !!!*/ , RI.STPL_STRTDT AS LCCRTT /* 계약일자 !!!*/ , SC.SL_RCOG_DT AS LCSLET /* 매출일자 !!!*/ , CB.CNTR_CAN_DTM AS LCCANT /* 취소일자 !!!*/ , WD.REQD_DT AS LCDELDT /* 철거일자!!!*/ , MO.DGR3_LEVL_DG_PRTNR_NO AS AKDBON /* 지점장번호 !!!*/ , MO.DGR3_LEVL_DG_PRTNR_NM AS AKDBNM /* 지점장성명 !!!*/ , CD.SELL_AMT AS LCAMT1 /* 렌탈료 !!!*/ , '0' AS LCCK02 /* 프로모션번호!!!!! */ , '' AS LCRCDE /*패키지 상품번호!!!!!*/ , '' AS INTM_ORDR_NO /* 기기고객코드!!!! */ , '' AS INTM_ORDR_PRDT/* 기기상품코드!!! */ , CASE WHEN EX.EX_SCNT = 1 THEN 'Y' END AS EX_YN /*실적제외등록여부!!! */ FROM TB_SSCT_RENTAL_RSTL_IZ RI INNER JOIN TB_SSCT_CNTR_BAS CB ON RI.CNTR_NO = CB.CNTR_NO INNER JOIN TB_SSCT_CNTR_DTL CD ON RI.CNTR_NO = CD.CNTR_NO AND RI.CNTR_SN = CD.CNTR_SN AND CD.SELL_TP_CD = '2' /* 예약 */ AND CD.BOO_SELL_TP_CD = '2'
/* 매출 */ AND NVL(CD.BOO_SELL_TP_CD,0) != '2'
AND CD.HGR_PD_CD >= #{pkgStrtCd}
AND CD.HGR_PD_CD <= #{pkgEndCd}
/* 매출 */ INNER JOIN TB_CBCL_WELLS_SL_CNFM_BAS SC /*T WELLS매출확정기본 T*/ ON RI.CNTR_NO = SC.CNTR_NO AND RI.CNTR_SN = SC.CNTR_SN AND SUBSTR(SC.SL_RCOG_DT,1,8) BETWEEN #{strtDt} AND #{endDt} /* 매출 확정일자 */
/* 전체 */ LEFT OUTER JOIN TB_CBCL_WELLS_SL_CNFM_BAS SC /*T WELLS매출확정기본 T*/ ON RI.CNTR_NO = SC.CNTR_NO AND RI.CNTR_SN = SC.CNTR_SN
INNER JOIN TB_OGBS_MM_PRTNR_IZ MP /* 월파트너 */ ON RI.RCP_PRTNR_NO = MP.PRTNR_NO AND RI.RCP_OG_TP_CD = MP.OG_TP_CD AND MP.BASE_YM = SUBSTR(#{strtDt},0,6)
AND MP.PRTNR_NO = #{prtnrNo}
INNER JOIN TB_OGBS_MM_OG_IZ MO /* 월조직 */ ON MP.BASE_YM = MO.BASE_YM AND MP.OG_ID = MO.OG_ID AND MP.OG_TP_CD = MO.OG_TP_CD
AND MO.DGR1_LEVL_OG_ID = #{og1LevlId}
AND MO.DGR2_LEVL_OG_ID = #{og2LevlId}
AND MO.DGR3_LEVL_OG_ID = #{og3LevlId}
INNER JOIN TB_PDBS_PD_BAS PP ON CD.BASE_PD_CD = PP.PD_CD
AND PP.PD_CD >= #{pdStrtCd}
AND PP.PD_CD <= #{pdEndCd}
INNER JOIN TB_FEAM_WELS_FEE_PDCT_TP_BAS PD ON CD.BASE_PD_CD = PD.BASE_PD_CD AND SUBSTR(#{strtDt},0,6) BETWEEN APY_STRT_YM AND APY_END_YM INNER JOIN TB_FEAM_WELS_FEE_PERF_TP_BAS PF ON PD.FEE_PDCT_TP_CD1 = PF.FEE_PDCT_TP_CD AND CD.SELL_TP_CD = PF.SELL_TP_CD
AND PF.FEE_PDCT_TP_CD = #{pdctTpCd}
AND PF.FEE_PERF_TP_CD = #{feePerfCd}
LEFT OUTER JOIN TB_PDBS_SDING_PRC_BAS PSP ON CD.BASE_PD_CD = PSP.PDCT_PD_CD AND RI.STPL_STRTDT BETWEEN PSP.APY_STRTDT AND PSP.APY_ENDDT LEFT OUTER JOIN TB_SSCT_MCHN_CH_IZ MC ON RI.CNTR_NO = MC.BASE_CNTR_NO AND RI.CNTR_SN = MC.BASE_CNTR_SN LEFT OUTER JOIN TB_SSCT_CNTR_PMOT_IZ CP ON RI.CNTR_NO = CP.DTL_CNTR_NO AND RI.CNTR_SN = CP.DTL_CNTR_SN LEFT OUTER JOIN TB_SSCT_CNTR_WELLS_DTL WD ON RI.CNTR_NO = WD.CNTR_NO AND RI.CNTR_SN = WD.CNTR_SN LEFT OUTER JOIN LATERAL ( SELECT COUNT(1) AS ROW_SCNT FROM TB_FEAM_WELS_SV_FEE_BAS A WHERE A.BASE_PD_CD = CD.BASE_PD_CD AND A.VST_MCN = 0 AND SUBSTR(RI.STPL_STRTDT,0,6) BETWEEN A.APY_STRT_YM AND A.APY_END_YM )WS ON 1=1 LEFT OUTER JOIN LATERAL ( SELECT COUNT(1) AS EX_SCNT FROM TB_FEDD_FEE_PERF_EXCD_OJ_BAS A WHERE A.CNTR_NO = RI.CNTR_NO AND A.CNTR_SN = RI.CNTR_SN AND A.FEE_PERF_EXCD_DV_CD = '01' )EX ON 1=1 WHERE CB.SELL_OG_TP_CD = 'W02'
AND SUBSTR(RI.STPL_STRTDT,1,8) BETWEEN #{strtDt} AND #{endDt} /* 계약확정일시 */
AND SUBSTR(RI.STPL_CAN_DTM,1,8) >= #{cancStrtDt}
AND SUBSTR(RI.STPL_CAN_DTM,1,8) <= #{cancEndDt}
)T

SELECT T3.DGR1_LEVL_OG_NM AS OG1_LV , T3.DGR2_LEVL_OG_NM AS OG2_LV , T3.DGR3_LEVL_OG_NM AS OG3_LV , T2.PRTNR_NO , T2.PRTNR_KNM , T1.CNTR_NO||T1.CNTR_SN AS CNTR_DTL_NO , T1.RCPDT , T1.SL_DT , T1.CAN_DT , T5.FEE_PDCT_TP_CD1 AS FEE_PDCT_TP_CD , T1.PD_CD , T6.FEE_PERF_TP_CD , T4.PD_NM , T1.ACKMT_PERF_CT , T1.BFSVC_PRD_CD , T1.MCHN_CH_TP_CD FROM TB_FEAM_WELS_NRCTR_MM_CL T1 INNER JOIN TB_OGBS_MM_PRTNR_IZ T2 /* 월파트너 */ ON T1.PRTNR_NO = T2.PRTNR_NO AND T1.OG_TP_CD = T2.OG_TP_CD AND T1.BASE_YM = T2.BASE_YM
AND T2.PRTNR_NO = #{prtnrNo}
INNER JOIN TB_OGBS_MM_OG_IZ T3 /* 월조직 */ ON T2.BASE_YM = T3.BASE_YM AND T2.OG_ID = T3.OG_ID AND T2.OG_TP_CD = T3.OG_TP_CD
AND T3.DGR1_LEVL_OG_ID = #{og1LevlId}
AND T3.DGR2_LEVL_OG_ID = #{og2LevlId}
AND T3.DGR3_LEVL_OG_ID = #{og3LevlId}
INNER JOIN TB_PDBS_PD_BAS T4 ON T1.PD_CD = T4.PD_CD INNER JOIN TB_FEAM_WELS_FEE_PDCT_TP_BAS T5 ON T1.PD_CD = T5.BASE_PD_CD AND #{perfYm} BETWEEN APY_STRT_YM AND APY_END_YM INNER JOIN TB_FEAM_WELS_FEE_PERF_TP_BAS T6 ON T5.FEE_PDCT_TP_CD1 = T6.FEE_PDCT_TP_CD AND T1.SELL_TP_CD = T6.SELL_TP_CD WHERE T1.BASE_YM = #{perfYm} AND T1.FEE_TCNT_DV_CD = #{feeTcntDvCd} AND T1.OG_TP_CD = 'W02' AND T1.CNTR_PERF_CRT_DV_CD = '01'

SELECT T2.OG_ID , M.PRTNR_NO , T2.PRTNR_KNM , T2.PSTN_DV_CD , MAX(M.EH_CNT) AS EH_CNT , MAX(M.EX_CNT) AS EX_CNT , MAX(M.ET_CNT) AS ET_CNT , MAX(M.UP_CNT) AS UP_CNT , MAX(M.EH_CNT)+ MAX(M.EX_CNT)+ MAX(M.ET_CNT)+ MAX(M.UP_CNT) AS TOT_CNT , MAX(M.EH_AMT) AS EH_AMT , MAX(M.EX_AMT) AS EX_AMT , MAX(M.ET_AMT) AS ET_AMT , MAX(M.UP_AMT) AS UP_AMT , MAX(M.EH_AMT)+ MAX(M.EX_AMT)+ MAX(M.ET_AMT)+ MAX(M.UP_AMT) AS TOT_AMT , MAX(M.EH_CNT) AS EH_PERF_CNT , MAX(M.EH_AMT1) AS EH_AMT1 , MAX(M.EH_AMT2) AS EH_AMT2 , MAX(M.EH_MC_CNT) AS EH_MC_CNT , MAX(M.FXAM_CNT) AS FXAM_CNT , MAX(M.LPK_CNT) AS LPK_CNT , MAX(M.HMB_CNT) AS HMB_CNT FROM ( SELECT T.PRTNR_NO , NVL(SUM(EH_CNT),0) AS EH_CNT , NVL(SUM(EX_CNT),0) AS EX_CNT , NVL(SUM(ET_CNT),0) AS ET_CNT , NVL(SUM(UP_CNT),0) AS UP_CNT , 0 AS EH_AMT , 0 AS EX_AMT , 0 AS ET_AMT , 0 AS UP_AMT , 0 AS EH_AMT1 , 0 AS EH_AMT2 , 0 AS EH_MC_CNT , 0 AS FXAM_CNT , 0 AS LPK_CNT , 0 AS HMB_CNT FROM ( SELECT T1.PRTNR_NO , CASE WHEN T3.FEE_PERF_TP_CD = 'EH' THEN T1.ACKMT_PERF_CT END AS EH_CNT , CASE WHEN T3.FEE_PERF_TP_CD = 'EX' THEN T1.ACKMT_PERF_CT END AS EX_CNT , CASE WHEN T3.FEE_PERF_TP_CD = 'ET' THEN T1.ACKMT_PERF_CT END AS ET_CNT , CASE WHEN T3.FEE_PERF_TP_CD = 'UP' THEN T1.ACKMT_PERF_CT END AS UP_CNT FROM TB_FEAM_WELS_NRCTR_MM_CL T1 INNER JOIN TB_FEAM_WELS_FEE_PDCT_TP_BAS T2 ON T1.PD_CD = T2.BASE_PD_CD AND #{perfYm} BETWEEN APY_STRT_YM AND APY_END_YM INNER JOIN TB_FEAM_WELS_FEE_PERF_TP_BAS T3 ON T2.FEE_PDCT_TP_CD1 = T3.FEE_PDCT_TP_CD AND T1.SELL_TP_CD = T3.SELL_TP_CD WHERE T1.BASE_YM = #{perfYm} AND T1.OG_TP_CD = 'W02' AND T1.FEE_TCNT_DV_CD = #{feeTcntDvCd} AND T1.CNTR_PERF_CRT_DV_CD = '01' AND T1.ACKMT_PERF_CT > 0 )T GROUP BY T.PRTNR_NO UNION SELECT T.PRTNR_NO , 0 AS EH_CNT , 0 AS EX_CNT , 0 AS ET_CNT , 0 AS UP_CNT , NVL(SUM(EH_AMT),0) AS EH_AMT , NVL(SUM(EX_AMT),0) AS EX_AMT , NVL(SUM(ET_AMT),0) AS ET_AMT , NVL(SUM(UP_AMT),0) AS UP_AMT , 0 AS EH_AMT1 , 0 AS EH_AMT2 , 0 AS EH_MC_CNT , 0 AS FXAM_CNT , 0 AS LPK_CNT , 0 AS HMB_CNT FROM ( SELECT T1.PRTNR_NO , CASE WHEN T3.FEE_PERF_TP_CD = 'EH' THEN T1.ACKMT_PERF_AMT END AS EH_AMT , CASE WHEN T3.FEE_PERF_TP_CD = 'EX' THEN T1.ACKMT_PERF_AMT END AS EX_AMT , CASE WHEN T3.FEE_PERF_TP_CD = 'ET' THEN T1.ACKMT_PERF_AMT END AS ET_AMT , CASE WHEN T3.FEE_PERF_TP_CD = 'UP' THEN T1.ACKMT_PERF_AMT END AS UP_AMT FROM TB_FEAM_WELS_NRCTR_MM_CL T1 INNER JOIN TB_FEAM_WELS_FEE_PDCT_TP_BAS T2 ON T1.PD_CD = T2.BASE_PD_CD AND #{perfYm} BETWEEN APY_STRT_YM AND APY_END_YM INNER JOIN TB_FEAM_WELS_FEE_PERF_TP_BAS T3 ON T2.FEE_PDCT_TP_CD1 = T3.FEE_PDCT_TP_CD AND T1.SELL_TP_CD = T3.SELL_TP_CD WHERE T1.BASE_YM = #{perfYm} AND T1.OG_TP_CD = 'W02' AND T1.FEE_TCNT_DV_CD = #{feeTcntDvCd} AND T1.CNTR_PERF_CRT_DV_CD = '01' AND T1.ACKMT_PERF_AMT > 0 )T GROUP BY T.PRTNR_NO UNION SELECT T.PRTNR_NO , 0 AS EH_CNT , 0 AS EX_CNT , 0 AS ET_CNT , 0 AS UP_CNT , 0 AS EH_AMT , 0 AS EX_AMT , 0 AS ET_AMT , 0 AS UP_AMT , NVL(SUM(EH_AMT1),0) AS EH_AMT1 , NVL(SUM(EH_AMT2),0) AS EH_AMT2 , NVL(SUM(EH_MC_CNT),0) AS EH_MC_CNT , NVL(SUM(FXAM_CNT),0) AS FXAM_CNT , NVL(SUM(LPK_CNT),0) AS LPK_CNT , NVL(SUM(HMB_CNT),0) AS HMB_CNT FROM ( SELECT T1.PRTNR_NO , CASE WHEN T1.PERF_ATC_CD = 'W00P00003' THEN T1.PERF_VAL END AS EH_AMT1 /* 가전렌탈 */ , CASE WHEN T1.PERF_ATC_CD = 'W00P00004' THEN T1.PERF_VAL END AS EH_AMT2 /* 가전일시불 */ , CASE WHEN T1.PERF_ATC_CD = 'W02P00104' THEN T1.PERF_VAL END AS EH_MC_CNT /* 기변건수 */ , CASE WHEN T1.PERF_ATC_CD IN ('W00P00005','W00P00007') THEN 1 END AS FXAM_CNT /* 환경정액 , 환경외정액 */ , CASE WHEN T1.PERF_ATC_CD = 'W02P00105' THEN 1 END AS LPK_CNT /* 라이브팩 */ , CASE WHEN T1.PERF_ATC_CD = 'W00P00081' THEN 1 END AS HMB_CNT /* 홈케어멤버십 */ FROM TB_FEAM_NTORP_PERF_MM_CL T1 WHERE T1.BASE_YM = #{perfYm} AND T1.OG_TP_CD = 'W02' AND T1.FEE_TCNT_DV_CD = #{feeTcntDvCd} AND T1.PERF_AGRG_CRT_DV_CD = '201' /* M추진단순주문실적생성 */ AND T1.PERF_DV_CD = '0' /* 개인 */ AND T1.PERF_ATC_CD IN ('W00P00003','W00P00004','W02P00104','W00P00005','W00P00007','W02P00105','W00P00081') /* 실적코드 */ AND T1.PERF_VAL > 0 )T GROUP BY T.PRTNR_NO )M INNER JOIN TB_OGBS_MM_PRTNR_IZ T2 /* 월파트너 */ ON M.PRTNR_NO = T2.PRTNR_NO AND T2.OG_TP_CD = 'W02' AND T2.BASE_YM = #{perfYm}
AND T2.PRTNR_NO = #{prtnrNo}
AND T2.PSTN_DV_CD IN ('7','15')
AND T2.PSTN_DV_CD = #{rsbDvCd}
INNER JOIN TB_OGBS_MM_OG_IZ T3 /* 월조직 */ ON T2.BASE_YM = T3.BASE_YM AND T2.OG_ID = T3.OG_ID AND T2.OG_TP_CD = T3.OG_TP_CD
AND T3.DGR1_LEVL_OG_ID = #{og1LevlId}
AND T3.DGR2_LEVL_OG_ID = #{og2LevlId}
AND T3.DGR3_LEVL_OG_ID = #{og3LevlId}
GROUP BY T2.OG_ID, M.PRTNR_NO, T2.PRTNR_KNM, T2.PSTN_DV_CD

SELECT T.OG1_LV /* 총괄단 !!!*/ , T.OG2_LV /* 지역단 !!!*/ , T.OG3_LV /* 지점 !!!*/ , T.PRTNR_NO AS SEQUENCE_NUMBER /* 번호 !!!*/ , T.PRTNR_KNM AS EMPL_NM /* 성명 !!!*/ , T.SELL_TP_CD AS SEL_TYPE /* 판매유형 !!!*/ , T.FEE_PERF_TP_CD AS PDCT_TP /* 제품유형 !!!*/ , T.MCHN_CH_TP_CD AS CHDVC_TP /* 기변유형 !!!*/ , T.FEE_PDCT_TP_CD AS FEE /* 수수료 실적유형 !!!*/ , T.CNTR_DTL_CD AS CNTR_DTL_NO /* 계약상세번호 !!!*/ , T.LCCGUB AS CST_DV /* 고객구분 !!!*/ , T.PD_NM AS PRDT_NM /* 상품명 !!!*/ , T.PD_CD AS PRDT_CODE /* 상품코드 !!!*/ , T.SV_PD_TP_CD AS USWY /* 용도!!! */ , T.LCETC3 AS PD_DC_CLASS /* 할인구분 !!!*/ , T.LCETC4 AS DISC_CODE /* 할인유형 !!!*/ , T.PMOT_TP_CD AS DSC_SYST /* 할인제도 !!!*/ , T.LCST04 AS COMBI_DV /* 결합구분 !!!*/ , T.DP_PERF /* 입금실적 !!!*/ , T.LCMONT AS ISTM /* 할부 !!!*/ , T.HOMCARE AS HOME_CARE /* 홈케어 !!!*/ , T.HOMCARE3_YEAR AS HCR_MSH_Y3 /* 홈케어멤버십3년 !!!!*/ , T.LCST13 AS FXAM_YN /* 정액여부 !!!*/ , T.LEASE_YN AS FNN_LEASE /* 금융리스 ???? 일단임시 */ , T.RSTL_YN AS RECOMMITMENT /* 재약정여부 !!!*/ , T.LCCRTT AS CNTR_DATE /* 계약일자 !!!*/ , T.LCSLET AS SL_DT /* 매출일자 !!!*/ , T.LCCANT AS CANC_DT /* 취소일자 !!!*/ , T.AKDBON AS BRMGR_NO /* 지점장번호 !!!*/ , T.AKDBNM AS BRMGR_FNM /* 지점장성명 !!!*/ , T.LCAMT1 AS RTLFE /* 렌탈료 !!!*/ , T.STPL_PTRM AS STPL_MCNT /* 약정개월 !!!*/ , T.LCFLG3 AS MNGT_PRD /* 관리주기 !!!*/ , T.LCPAMT AS PD_ACC_RSLT /* 인정실적 !!!*/ , T.LCCK02 AS PMOT_NO /* 프로모션번호!!!!! */ , '0' AS PKG_SN /* 패키지 일련번호!!!!! */ , T.LCRCDE AS PKG_NO /*패키지 번호!!!!!*/ , T2.BASE_YM AS NTOR_MM , T.INTM_ORDR_PRDT AS MCHN_PD /* 기기코드!!! */ , NVL(T.EX_YN,'N') AS PERF_EXCD /*실적제외등록여부!!! */ , SUBSTR(T.CNTR_DT,0,6) AS BIZ_RGST_MM /*업무등록월 !!!*/ , TRUNC(MONTHS_BETWEEN(SYSDATE,TO_DATE(T.CNTR_DT,'YYYY-MM-DD'))) AS BIZ_RGST_NM /*업무등록차월 !!!*/ FROM ( SELECT MO.DGR1_LEVL_OG_NM AS OG1_LV /* 총괄단 !!!*/ , MO.DGR2_LEVL_OG_NM AS OG2_LV /* 지역단 !!!*/ , MO.DGR3_LEVL_OG_NM AS OG3_LV /* 지점 !!!*/ , MP.PRTNR_NO /* 번호 !!!*/ , MP.PRTNR_KNM /* 성명 !!!*/ , CD.SELL_TP_CD /* 판매유형 !!!*/ , PF.FEE_PERF_TP_CD /* 제품유형 !!!*/ , MC.MCHN_CH_TP_CD /* 기변유형 !!!*/ , PF.FEE_PDCT_TP_CD , CD.CNTR_NO||CD.CNTR_SN AS CNTR_DTL_CD /* 계약상세번호 !!!*/ , CASE WHEN CB.COPN_DV_CD = '2' THEN '사업' ELSE '개인' END AS LCCGUB /* 고객구분 !!!*/ , PP.PD_NM /* 상품명 !!!*/ , PP.PD_CD /* 상품코드 !!!*/ , CD.SELL_DSC_DV_CD AS LCETC3 /* 할인구분 !!!*/ , CD.SELL_DSC_TP_CD AS LCETC4 /* 할인유형 !!!*/ , CASE WHEN CD.SELL_TP_CD = '6' THEN CP.PMOT_TP_CD END AS PMOT_TP_CD /* 할인제도 !!!*/ , CASE WHEN CD.SELL_TP_CD = '6' AND PSP.RGLR_SPP_PRC_DV_CD = 4 THEN 'Y' END AS LCST04 /* 결합구분 !!!*/ , CPC.FNL_VAL AS DP_PERF /* 입금실적 */ , CD.ISTM_MCN AS LCMONT /* 할부 !!!*/ , CD.STPL_PTRM /* 약정개월 !!!*/ , CD.SV_PRD AS LCFLG3 /* 관리주기 !!!*/ , CD.ACKMT_PERF_AMT AS LCPAMT /* 인정실적 !!!*/ , CASE WHEN PF.FEE_PDCT_TP_CD = 'E' THEN ( CASE WHEN CD.SELL_TP_CD = '6' THEN (CD.SELL_AMT- CD.CNTRAM_DSC_AMT )/ CD.SV_PRD ELSE CD.SELL_AMT END ) ELSE 0 END AS HOMCARE /* 홈케어 !!!*/ , CASE WHEN CD.SELL_TP_CD = '3' AND CD.STPL_PTRM = '36' THEN 'Y' ELSE ' ' END AS HOMCARE3_YEAR /* 홈케어멤버십3년 !!!!*/ , CD.FEE_FXAM_YN AS LCST13 /* 정액여부 !!!*/ , CASE WHEN CD.SELL_TP_DTL_CD IN ('22','24') THEN 'Y' ELSE 'N' END AS LEASE_YN /* 금융리스 !!! */ , CD.RSTL_YN /* 재약정여부 !!!*/ , CB.CNTR_CNFM_DTM AS LCCRTT /* 계약일자 !!!*/ , SC.SL_RCOG_DT AS LCSLET /* 매출일자 !!!*/ , CD.CNTR_PD_ENDDT AS LCCANT /* 취소일자 !!!*/ , MO.DGR3_LEVL_DG_PRTNR_NO AS AKDBON /* 지점장번호 !!!*/ , MO.DGR3_LEVL_DG_PRTNR_NM AS AKDBNM /* 지점장성명 !!!*/ , CD.SELL_AMT AS LCAMT1 /* 렌탈료 !!!*/ , CASE WHEN CD.SELL_TP_CD = '2' THEN CP.PMOT_CD ELSE '0' END AS LCCK02 /* 프로모션번호!!!!! */ , CASE WHEN CD.SELL_TP_CD = '2' THEN CD.HGR_PD_CD ELSE '0' END AS LCRCDE /*패키지 상품번호!!!!!*/ , CASE WHEN CD.SELL_TP_CD = '6' THEN PP.PD_CD END AS INTM_ORDR_PRDT/* 기기코드!!! */ , CASE WHEN EX.EX_SCNT = 1 THEN 'Y' END AS EX_YN /*실적제외등록여부!!! */ , MP.BASE_YM , MP.OG_TP_CD , MP.CNTR_DT , PP.SV_PD_TP_CD FROM TB_SSCT_CNTR_BAS CB INNER JOIN TB_SSCT_CNTR_DTL CD ON CB.CNTR_NO = CD.CNTR_NO
AND CD.SELL_TP_CD IN ('1','2','3','6')
AND 1 = 2 /* 재약정은 따로 조회*/
AND CD.SELL_TP_CD = #{sellTpCd}
/* 예약 */ AND CD.BOO_SELL_TP_CD = '2'
/* 매출 */ AND NVL(CD.BOO_SELL_TP_CD,0) != '2'
AND CD.HGR_PD_CD >= #{pkgStrtCd}
AND CD.HGR_PD_CD <= #{pkgEndCd}
/* 매출 */ INNER JOIN TB_CBCL_WELLS_SL_CNFM_BAS SC /*T WELLS매출확정기본 T*/ ON CD.CNTR_NO = SC.CNTR_NO AND CD.CNTR_SN = SC.CNTR_SN AND SC.SL_RCOG_DT BETWEEN #{strtDt} AND #{endDt} /* 매출 확정일자 */
/* 전체 */ LEFT OUTER JOIN TB_CBCL_WELLS_SL_CNFM_BAS SC /*T WELLS매출확정기본 T*/ ON CD.CNTR_NO = SC.CNTR_NO AND CD.CNTR_SN = SC.CNTR_SN
INNER JOIN TB_OGBS_MM_PRTNR_IZ MP /* 월파트너 */ ON CB.SELL_PRTNR_NO = MP.PRTNR_NO AND CB.SELL_OG_TP_CD = MP.OG_TP_CD AND MP.BASE_YM = SUBSTR(#{strtDt},0,6)
AND MP.PRTNR_NO = #{prtnrNo}
INNER JOIN TB_OGBS_MM_OG_IZ MO /* 월조직 */ ON MP.BASE_YM = MO.BASE_YM AND MP.OG_ID = MO.OG_ID AND MP.OG_TP_CD = MO.OG_TP_CD
AND MO.DGR1_LEVL_OG_ID = #{og1LevlId}
AND MO.DGR2_LEVL_OG_ID = #{og2LevlId}
AND MO.DGR3_LEVL_OG_ID = #{og3LevlId}
INNER JOIN TB_PDBS_PD_BAS PP ON CD.BASE_PD_CD = PP.PD_CD
AND PP.PD_CD >= #{pdStrtCd}
AND PP.PD_CD <= #{pdEndCd}
INNER JOIN TB_FEAM_WELS_FEE_PDCT_TP_BAS PD ON CD.BASE_PD_CD = PD.BASE_PD_CD AND SUBSTR(#{strtDt},0,6) BETWEEN APY_STRT_YM AND APY_END_YM INNER JOIN TB_FEAM_WELS_FEE_PERF_TP_BAS PF ON PD.FEE_PDCT_TP_CD1 = PF.FEE_PDCT_TP_CD AND CD.SELL_TP_CD = PF.SELL_TP_CD
AND PF.FEE_PDCT_TP_CD = #{pdctTpCd}
AND PF.FEE_PERF_TP_CD = #{feePerfCd}
LEFT OUTER JOIN TB_PDBS_SDING_PRC_BAS PSP ON CD.BASE_PD_CD = PSP.PDCT_PD_CD AND CB.CNTR_CNFM_DTM BETWEEN PSP.APY_STRTDT AND PSP.APY_ENDDT LEFT OUTER JOIN TB_SSCT_MCHN_CH_IZ MC ON CD.CNTR_NO = MC.BASE_CNTR_NO AND CD.CNTR_SN = MC.BASE_CNTR_SN LEFT OUTER JOIN TB_SSCT_CNTR_PMOT_IZ CP ON CD.CNTR_NO = CP.DTL_CNTR_NO AND CD.CNTR_SN = CP.DTL_CNTR_SN LEFT OUTER JOIN LATERAL ( SELECT COUNT(1) AS ROW_SCNT FROM TB_FEAM_WELS_SV_FEE_BAS A WHERE A.BASE_PD_CD = CD.BASE_PD_CD AND A.VST_MCN = 0 AND SUBSTR(CB.CNTR_CNFM_DTM,0,6) BETWEEN A.APY_STRT_YM AND A.APY_END_YM )WS ON 1=1 LEFT OUTER JOIN LATERAL ( SELECT COUNT(1) AS EX_SCNT FROM TB_FEDD_FEE_PERF_EXCD_OJ_BAS A WHERE A.CNTR_NO = CD.CNTR_NO AND A.CNTR_SN = CD.CNTR_SN AND A.FEE_PERF_EXCD_DV_CD = '01' )EX ON 1=1 LEFT OUTER JOIN TB_SSCT_CNTR_PRC_CMPT_IZ CPC ON CD.CNTR_NO = CPC.CNTR_NO AND CD.CNTR_SN = CPC.CNTR_SN WHERE CB.SELL_OG_TP_CD = 'W01'
AND CB.CNTR_CNFM_DTM BETWEEN #{strtDt} AND #{endDt} /* 계약확정일시 */
AND CB.CNTR_CAN_DTM >= #{cancStrtDt}
AND CB.CNTR_CAN_DTM <= #{cancEndDt}
UNION SELECT MO.DGR1_LEVL_OG_NM AS OG1_LV /* 총괄단 !!!*/ , MO.DGR2_LEVL_OG_NM AS OG2_LV /* 지역단 !!!*/ , MO.DGR3_LEVL_OG_NM AS OG3_LV /* 지점 !!!*/ , MP.PRTNR_NO /* 번호 !!!*/ , MP.PRTNR_KNM /* 성명 !!!*/ , CD.SELL_TP_CD /* 판매유형 !!!*/ , PF.FEE_PERF_TP_CD /* 제품유형 !!!*/ , MC.MCHN_CH_TP_CD /* 기변유형 !!!*/ , PF.FEE_PDCT_TP_CD , CD.CNTR_NO||CD.CNTR_SN AS CNTR_DTL_CD /* 계약상세번호 !!!*/ , CASE WHEN CB.COPN_DV_CD = '2' THEN '사업' ELSE '개인' END AS LCCGUB /* 고객구분 !!!*/ , PP.PD_NM /* 상품명 !!!*/ , PP.PD_CD /* 상품코드 !!!*/ , CD.SELL_DSC_DV_CD AS LCETC3 /* 할인구분 !!!*/ , CD.SELL_DSC_TP_CD AS LCETC4 /* 할인유형 !!!*/ , '' AS PMOT_TP_CD /* 할인제도 !!!*/ , '' AS LCST04 /* 결합구분 !!!*/ , CPC.FNL_VAL AS DP_PERF /* 입금실적 */ , CD.ISTM_MCN AS LCMONT /* 할부 !!!*/ , CD.STPL_PTRM /* 약정개월 !!!*/ , CD.SV_PRD AS LCFLG3 /* 관리주기 !!!*/ , CD.ACKMT_PERF_AMT AS LCPAMT /* 인정실적 !!!*/ , 0 AS HOMCARE /* 홈케어 !!!*/ , '' AS HOMCARE3_YEAR /* 홈케어멤버십3년 !!!!*/ , CD.FEE_FXAM_YN AS LCST13 /* 정액여부 !!!*/ , CASE WHEN CD.SELL_TP_DTL_CD IN ('22','24') THEN 'Y' ELSE 'N' END AS LEASE_YN /* 금융리스 !!! */ , CD.RSTL_YN /* 재약정여부 !!!*/ , RI.STPL_STRTDT AS LCCRTT /* 계약일자 !!!*/ , SC.SL_RCOG_DT AS LCSLET /* 매출일자 !!!*/ , CD.CNTR_PD_ENDDT AS LCCANT /* 취소일자 !!!*/ , MO.DGR3_LEVL_DG_PRTNR_NO AS AKDBON /* 지점장번호 !!!*/ , MO.DGR3_LEVL_DG_PRTNR_NM AS AKDBNM /* 지점장성명 !!!*/ , CD.SELL_AMT AS LCAMT1 /* 렌탈료 !!!*/ , '0' AS LCCK02 /* 프로모션번호!!!!! */ , '' AS LCRCDE /*패키지 상품번호!!!!!*/ , '' AS INTM_ORDR_PRDT/* 기기코드!!! */ , CASE WHEN EX.EX_SCNT = 1 THEN 'Y' END AS EX_YN /*실적제외등록여부!!! */ , MP.BASE_YM , MP.OG_TP_CD , MP.CNTR_DT , PP.SV_PD_TP_CD FROM TB_SSCT_RENTAL_RSTL_IZ RI INNER JOIN TB_SSCT_CNTR_BAS CB ON RI.CNTR_NO = CB.CNTR_NO INNER JOIN TB_SSCT_CNTR_DTL CD ON RI.CNTR_NO = CD.CNTR_NO AND RI.CNTR_SN = CD.CNTR_SN AND CD.SELL_TP_CD = '2' /* 예약 */ AND CD.BOO_SELL_TP_CD = '2'
/* 매출 */ AND NVL(CD.BOO_SELL_TP_CD,0) != '2'
AND CD.HGR_PD_CD >= #{pkgStrtCd}
AND CD.HGR_PD_CD <= #{pkgEndCd}
/* 매출 */ INNER JOIN TB_CBCL_WELLS_SL_CNFM_BAS SC /*T WELLS매출확정기본 T*/ ON RI.CNTR_NO = SC.CNTR_NO AND RI.CNTR_SN = SC.CNTR_SN AND SC.SL_RCOG_DT BETWEEN #{strtDt} AND #{endDt} /* 매출 확정일자 */
/* 전체 */ LEFT OUTER JOIN TB_CBCL_WELLS_SL_CNFM_BAS SC /*T WELLS매출확정기본 T*/ ON RI.CNTR_NO = SC.CNTR_NO AND RI.CNTR_SN = SC.CNTR_SN
INNER JOIN TB_OGBS_MM_PRTNR_IZ MP /* 월파트너 */ ON RI.RCP_PRTNR_NO = MP.PRTNR_NO AND RI.RCP_OG_TP_CD = MP.OG_TP_CD AND MP.BASE_YM = SUBSTR(#{strtDt},0,6)
AND MP.PRTNR_NO = #{prtnrNo}
INNER JOIN TB_OGBS_MM_OG_IZ MO /* 월조직 */ ON MP.BASE_YM = MO.BASE_YM AND MP.OG_ID = MO.OG_ID AND MP.OG_TP_CD = MO.OG_TP_CD
AND MO.DGR1_LEVL_OG_ID = #{og1LevlId}
AND MO.DGR2_LEVL_OG_ID = #{og2LevlId}
AND MO.DGR3_LEVL_OG_ID = #{og3LevlId}
INNER JOIN TB_PDBS_PD_BAS PP ON CD.BASE_PD_CD = PP.PD_CD
AND PP.PD_CD >= #{pdStrtCd}
AND PP.PD_CD <= #{pdEndCd}
INNER JOIN TB_FEAM_WELS_FEE_PDCT_TP_BAS PD ON CD.BASE_PD_CD = PD.BASE_PD_CD AND SUBSTR(#{strtDt},0,6) BETWEEN APY_STRT_YM AND APY_END_YM INNER JOIN TB_FEAM_WELS_FEE_PERF_TP_BAS PF ON PD.FEE_PDCT_TP_CD1 = PF.FEE_PDCT_TP_CD AND CD.SELL_TP_CD = PF.SELL_TP_CD
AND PF.FEE_PDCT_TP_CD = #{pdctTpCd}
AND PF.FEE_PERF_TP_CD = #{feePerfCd}
LEFT OUTER JOIN TB_PDBS_SDING_PRC_BAS PSP ON CD.BASE_PD_CD = PSP.PDCT_PD_CD AND RI.STPL_STRTDT BETWEEN PSP.APY_STRTDT AND PSP.APY_ENDDT LEFT OUTER JOIN TB_SSCT_MCHN_CH_IZ MC ON RI.CNTR_NO = MC.BASE_CNTR_NO AND RI.CNTR_SN = MC.BASE_CNTR_SN LEFT OUTER JOIN TB_SSCT_CNTR_PMOT_IZ CP ON RI.CNTR_NO = CP.DTL_CNTR_NO AND RI.CNTR_SN = CP.DTL_CNTR_SN LEFT OUTER JOIN LATERAL ( SELECT COUNT(1) AS ROW_SCNT FROM TB_FEAM_WELS_SV_FEE_BAS A WHERE A.BASE_PD_CD = CD.BASE_PD_CD AND A.VST_MCN = 0 AND SUBSTR(RI.STPL_STRTDT,0,6) BETWEEN A.APY_STRT_YM AND A.APY_END_YM )WS ON 1=1 LEFT OUTER JOIN LATERAL ( SELECT COUNT(1) AS EX_SCNT FROM TB_FEDD_FEE_PERF_EXCD_OJ_BAS A WHERE A.CNTR_NO = RI.CNTR_NO AND A.CNTR_SN = RI.CNTR_SN AND A.FEE_PERF_EXCD_DV_CD = '01' )EX ON 1=1 LEFT OUTER JOIN TB_SSCT_CNTR_PRC_CMPT_IZ CPC ON CD.CNTR_NO = CPC.CNTR_NO AND CD.CNTR_SN = CPC.CNTR_SN WHERE CB.SELL_OG_TP_CD = 'W01'
AND RI.STPL_STRTDT BETWEEN #{strtDt} AND #{endDt} /* 계약확정일시 */
AND RI.STPL_CAN_DTM >= #{cancStrtDt}
AND RI.STPL_CAN_DTM <= #{cancEndDt}
)T LEFT OUTER JOIN TB_FEAM_WELS_NRCTR_MM_CL T2 ON T.BASE_YM = T2.BASE_YM AND T.OG_TP_CD = T2.OG_TP_CD AND T.PRTNR_NO = T2.PRTNR_NO AND T.CNTR_DTL_CD = T2.CNTR_NO||T2.CNTR_SN AND T2.FEE_TCNT_DV_CD = #{feeTcntDvCd} AND T2.CNTR_PERF_CRT_DV_CD = '01'

SELECT T3.DGR1_LEVL_OG_NM AS OG1_LV , T3.DGR2_LEVL_OG_NM AS OG2_LV , T3.DGR3_LEVL_OG_NM AS OG3_LV , T2.PRTNR_NO , T2.PRTNR_KNM , T1.CNTR_NO||T1.CNTR_SN AS CNTR_DTL_NO , T1.RCPDT , T1.SL_DT , T1.CAN_DT , T5.FEE_PDCT_TP_CD1 AS FEE_PDCT_TP_CD , T1.PD_CD , T6.FEE_PERF_TP_CD AS FEE_PERF_TP_CD , T4.PD_NM , T1.ACKMT_PERF_CT , T1.BFSVC_PRD_CD , T1.MCHN_CH_TP_CD AS MCHN_CH_TP_CD FROM TB_FEAM_WELS_NRCTR_MM_CL T1 INNER JOIN TB_OGBS_MM_PRTNR_IZ T2 /* 월파트너 */ ON T1.PRTNR_NO = T2.PRTNR_NO AND T1.OG_TP_CD = T2.OG_TP_CD AND T1.BASE_YM = T2.BASE_YM
AND T2.PRTNR_NO = #{prtnrNo}
INNER JOIN TB_OGBS_MM_OG_IZ T3 /* 월조직 */ ON T2.BASE_YM = T3.BASE_YM AND T2.OG_ID = T3.OG_ID AND T2.OG_TP_CD = T3.OG_TP_CD
AND T3.DGR1_LEVL_OG_ID = #{og1LevlId}
AND T3.DGR2_LEVL_OG_ID = #{og2LevlId}
AND T3.DGR3_LEVL_OG_ID = #{og3LevlId}
INNER JOIN TB_PDBS_PD_BAS T4 ON T1.PD_CD = T4.PD_CD INNER JOIN TB_FEAM_WELS_FEE_PDCT_TP_BAS T5 ON T1.PD_CD = T5.BASE_PD_CD AND #{perfYm} BETWEEN APY_STRT_YM AND APY_END_YM INNER JOIN TB_FEAM_WELS_FEE_PERF_TP_BAS T6 ON T5.FEE_PDCT_TP_CD1 = T6.FEE_PDCT_TP_CD AND T1.SELL_TP_CD = T6.SELL_TP_CD WHERE T1.BASE_YM = #{perfYm} AND T1.FEE_TCNT_DV_CD = #{feeTcntDvCd} AND T1.OG_TP_CD = 'W01' AND T1.CNTR_PERF_CRT_DV_CD = '01'

SELECT T2.OG_ID , M.PRTNR_NO , T2.PRTNR_KNM , T2.PSTN_DV_CD , MAX(M.EH_CNT) AS EH_CNT , MAX(M.EX_CNT) AS EX_CNT , MAX(M.ET_CNT) AS ET_CNT , MAX(M.UP_CNT) AS UP_CNT , MAX(M.EH_CNT)+ MAX(M.EX_CNT)+ MAX(M.ET_CNT)+ MAX(M.UP_CNT) AS TOT_CNT , MAX(M.EH_AMT) AS EH_AMT , MAX(M.EX_AMT) AS EX_AMT , MAX(M.ET_AMT) AS ET_AMT , MAX(M.UP_AMT) AS UP_AMT , MAX(M.EH_AMT)+ MAX(M.EX_AMT)+ MAX(M.ET_AMT)+ MAX(M.UP_AMT) AS TOT_AMT FROM ( SELECT T.PRTNR_NO , NVL(SUM(EH_CNT),0) AS EH_CNT , NVL(SUM(EX_CNT),0) AS EX_CNT , NVL(SUM(ET_CNT),0) AS ET_CNT , NVL(SUM(UP_CNT),0) AS UP_CNT , 0 AS EH_AMT , 0 AS EX_AMT , 0 AS ET_AMT , 0 AS UP_AMT FROM ( SELECT T1.PRTNR_NO , CASE WHEN T3.FEE_PERF_TP_CD = 'EH' THEN T1.ACKMT_PERF_CT END AS EH_CNT , CASE WHEN T3.FEE_PERF_TP_CD = 'EX' THEN T1.ACKMT_PERF_CT END AS EX_CNT , CASE WHEN T3.FEE_PERF_TP_CD = 'ET' THEN T1.ACKMT_PERF_CT END AS ET_CNT , CASE WHEN T3.FEE_PERF_TP_CD = 'UP' THEN T1.ACKMT_PERF_CT END AS UP_CNT FROM TB_FEAM_WELS_NRCTR_MM_CL T1 INNER JOIN TB_FEAM_WELS_FEE_PDCT_TP_BAS T2 ON T1.PD_CD = T2.BASE_PD_CD AND #{perfYm} BETWEEN APY_STRT_YM AND APY_END_YM INNER JOIN TB_FEAM_WELS_FEE_PERF_TP_BAS T3 ON T2.FEE_PDCT_TP_CD1 = T3.FEE_PDCT_TP_CD AND T1.SELL_TP_CD = T3.SELL_TP_CD WHERE T1.BASE_YM = #{perfYm} AND T1.OG_TP_CD = 'W01' AND T1.FEE_TCNT_DV_CD = #{feeTcntDvCd} AND T1.CNTR_PERF_CRT_DV_CD = '01' AND T1.ACKMT_PERF_CT > 0 )T GROUP BY T.PRTNR_NO UNION SELECT T.PRTNR_NO , 0 AS EH_CNT , 0 AS EX_CNT , 0 AS ET_CNT , 0 AS UP_CNT , NVL(SUM(EH_AMT),0) AS EH_AMT , NVL(SUM(EX_AMT),0) AS EX_AMT , NVL(SUM(ET_AMT),0) AS ET_AMT , NVL(SUM(UP_AMT),0) AS UP_AMT FROM ( SELECT T1.PRTNR_NO , CASE WHEN T3.FEE_PERF_TP_CD = 'EH' THEN T1.ACKMT_PERF_AMT END AS EH_AMT , CASE WHEN T3.FEE_PERF_TP_CD = 'EX' THEN T1.ACKMT_PERF_AMT END AS EX_AMT , CASE WHEN T3.FEE_PERF_TP_CD = 'ET' THEN T1.ACKMT_PERF_AMT END AS ET_AMT , CASE WHEN T3.FEE_PERF_TP_CD = 'UP' THEN T1.ACKMT_PERF_AMT END AS UP_AMT FROM TB_FEAM_WELS_NRCTR_MM_CL T1 INNER JOIN TB_FEAM_WELS_FEE_PDCT_TP_BAS T2 ON T1.PD_CD = T2.BASE_PD_CD AND #{perfYm} BETWEEN APY_STRT_YM AND APY_END_YM INNER JOIN TB_FEAM_WELS_FEE_PERF_TP_BAS T3 ON T2.FEE_PDCT_TP_CD1 = T3.FEE_PDCT_TP_CD AND T1.SELL_TP_CD = T3.SELL_TP_CD WHERE T1.BASE_YM = #{perfYm} AND T1.OG_TP_CD = 'W01' AND T1.FEE_TCNT_DV_CD = #{feeTcntDvCd} AND T1.CNTR_PERF_CRT_DV_CD = '01' AND T1.ACKMT_PERF_AMT > 0 )T GROUP BY T.PRTNR_NO )M INNER JOIN TB_OGBS_MM_PRTNR_IZ T2 /* 월파트너 */ ON M.PRTNR_NO = T2.PRTNR_NO AND T2.OG_TP_CD = 'W01' AND T2.BASE_YM = #{perfYm}
AND T2.PRTNR_NO = #{prtnrNo}
AND T2.PSTN_DV_CD IN ('7','15')
AND T2.PSTN_DV_CD = #{rsbDvCd}
INNER JOIN TB_OGBS_MM_OG_IZ T3 /* 월조직 */ ON T2.BASE_YM = T3.BASE_YM AND T2.OG_ID = T3.OG_ID AND T2.OG_TP_CD = T3.OG_TP_CD
AND T3.DGR1_LEVL_OG_ID = #{og1LevlId}
AND T3.DGR2_LEVL_OG_ID = #{og2LevlId}
AND T3.DGR3_LEVL_OG_ID = #{og3LevlId}
GROUP BY T2.OG_ID, M.PRTNR_NO, T2.PRTNR_KNM, T2.PSTN_DV_CD

mapper.update:
UPDATE TB_FEAM_NTOR_MM_CL
   SET NTOR_CNFM_STAT_CD = '02' /*순주문확정상태코드 01-임시저장, 02-확정*/

 WHERE BASE_YM = #{perfYm}
   AND FEE_TCNT_DV_CD = #{feeTcntDvCd}
   AND PERF_AGRG_CRT_DV_CD = #{perfAgrgCrtDvCd}

UPDATE TB_FEAM_NTOR_MM_CL
   SET NTOR_CNFM_STAT_CD = '01' /*순주문확정상태코드 01-임시저장, 02-확정*/

 WHERE BASE_YM = #{perfYm}
   AND FEE_TCNT_DV_CD = #{feeTcntDvCd}
   AND PERF_AGRG_CRT_DV_CD = #{perfAgrgCrtDvCd}
