AND MO.DGR1_LEVL_OG_ID = #{og1LevlId}
AND MO.DGR2_LEVL_OG_ID = #{og2LevlId}
AND MO.DGR3_LEVL_OG_ID = #{og3LevlId}
INNER JOIN TB_PDBS_PD_BAS PP ON CD.BASE_PD_CD = PP.PD_CD INNER JOIN TB_FEAM_WELS_FEE_PDCT_TP_BAS PD ON CD.BASE_PD_CD = PD.BASE_PD_CD AND T1.BASE_YM BETWEEN APY_STRT_YM AND APY_END_YM INNER JOIN TB_FEAM_WELS_FEE_PERF_TP_BAS PF ON PD.FEE_PDCT_TP_CD1 = PF.FEE_PDCT_TP_CD AND CD.SELL_TP_CD = PF.SELL_TP_CD LEFT OUTER JOIN TB_PDBS_SDING_PRC_BAS PSP ON CD.BASE_PD_CD = PSP.PDCT_PD_CD AND RI.STPL_STRTDT BETWEEN PSP.APY_STRTDT AND PSP.APY_ENDDT LEFT OUTER JOIN TB_SSCT_MCHN_CH_IZ MC ON RI.CNTR_NO = MC.BASE_CNTR_NO AND RI.CNTR_SN = MC.BASE_CNTR_SN LEFT OUTER JOIN LATERAL ( SELECT A.PMOT_CD , A.PMOT_TP_CD , RANK() OVER (PARTITION BY (A.DTL_CNTR_NO || A.DTL_CNTR_SN) ORDER BY CNTR_PMOT_ID DESC) AS RK_NUM FROM TB_SSCT_CNTR_PMOT_IZ A WHERE A.DTL_CNTR_NO = RI.CNTR_NO AND A.DTL_CNTR_SN = RI.CNTR_SN )CP /* T프로모션 계약내역T */ ON 1=1 AND CP.RK_NUM = 1 LEFT OUTER JOIN LATERAL ( SELECT COUNT(1) AS ROW_SCNT FROM TB_FEAM_WELS_SV_FEE_BAS A WHERE A.BASE_PD_CD = CD.BASE_PD_CD AND A.VST_MCN = 0 AND SUBSTR(RI.STPL_STRTDT,1,6) BETWEEN A.APY_STRT_YM AND A.APY_END_YM )WS ON 1=1 LEFT OUTER JOIN LATERAL ( SELECT COUNT(1) AS EX_SCNT FROM TB_FEDD_FEE_PERF_EXCD_OJ_BAS A WHERE A.CNTR_NO = RI.CNTR_NO AND A.CNTR_SN = RI.CNTR_SN AND A.FEE_PERF_EXCD_DV_CD = '01' )EX ON 1=1 LEFT OUTER JOIN TB_SSCT_CNTR_PRC_CMPT_IZ CPC ON CD.CNTR_NO = CPC.CNTR_NO AND CD.CNTR_SN = CPC.CNTR_SN WHERE T1.BASE_YM = #{perfYm} AND T1.FEE_TCNT_DV_CD = #{feeTcntDvCd} AND T1.OG_TP_CD = 'W01' AND T1.CNTR_PERF_CRT_DV_CD = '01' AND NVL(T1.ACC_NINC_YN,'N') = 'N' AND T1.RSTL_YN = 'Y' AND T1.DTA_DL_YN = 'N' )T LEFT OUTER JOIN TB_FEAM_WELS_NRCTR_MM_CL T2 ON T.BASE_YM = T2.BASE_YM AND T.OG_TP_CD = T2.OG_TP_CD AND T.PRTNR_NO = T2.PRTNR_NO AND T.CNTR_DTL_CD = T2.CNTR_NO||T2.CNTR_SN AND T2.FEE_TCNT_DV_CD = #{feeTcntDvCd} AND T2.CNTR_PERF_CRT_DV_CD = '01' AND NVL(T2.ACC_NINC_YN,'N') = 'N'

SELECT T2.OG_CD AS OG_ID , M.PRTNR_NO , T2.PRTNR_KNM , T2.PSTN_DV_CD , MAX(M.EH_CNT) AS EH_CNT , MAX(M.EX_CNT) AS EX_CNT , MAX(M.ET_CNT) AS ET_CNT , MAX(M.UP_CNT) AS UP_CNT , MAX(M.EH_CNT)+ MAX(M.EX_CNT)+ MAX(M.ET_CNT)+ MAX(M.UP_CNT) AS TOT_CNT , MAX(M.EH_AMT) AS EH_AMT , MAX(M.EX_AMT) AS EX_AMT , MAX(M.ET_AMT) AS ET_AMT , MAX(M.UP_AMT) AS UP_AMT , MAX(M.EH_AMT)+ MAX(M.EX_AMT)+ MAX(M.ET_AMT)+ MAX(M.UP_AMT) AS TOT_AMT FROM ( SELECT T.PRTNR_NO , NVL(SUM(EH_CNT),0) AS EH_CNT , NVL(SUM(EX_CNT),0) AS EX_CNT , NVL(SUM(ET_CNT),0) AS ET_CNT , NVL(SUM(UP_CNT),0) AS UP_CNT , 0 AS EH_AMT , 0 AS EX_AMT , 0 AS ET_AMT , 0 AS UP_AMT FROM ( SELECT T1.PRTNR_NO , CASE WHEN T3.FEE_PERF_TP_CD = 'EH' THEN T2.FEE_ACKMT_CT END AS EH_CNT , CASE WHEN T3.FEE_PERF_TP_CD = 'EX' THEN T2.FEE_ACKMT_CT END AS EX_CNT , CASE WHEN T3.FEE_PERF_TP_CD = 'ET' THEN T2.FEE_ACKMT_CT END AS ET_CNT , CASE WHEN T3.FEE_PERF_TP_CD = 'UP' THEN T2.FEE_ACKMT_CT END AS UP_CNT FROM TB_FEAM_WELS_NRCTR_MM_CL T1 INNER JOIN TB_SSCT_CNTR_DTL T2 ON T2.CNTR_NO = T1.CNTR_NO AND T2.CNTR_SN = T1.CNTR_SN INNER JOIN TB_FEAM_WELS_FEE_PDCT_TP_BAS T2 ON T1.PD_CD = T2.BASE_PD_CD AND #{perfYm} BETWEEN APY_STRT_YM AND APY_END_YM INNER JOIN TB_FEAM_WELS_FEE_PERF_TP_BAS T3 ON T2.FEE_PDCT_TP_CD1 = T3.FEE_PDCT_TP_CD AND T1.SELL_TP_CD = T3.SELL_TP_CD WHERE T1.BASE_YM = #{perfYm} AND T1.OG_TP_CD = 'W01' AND T1.FEE_TCNT_DV_CD = #{feeTcntDvCd} AND T1.CNTR_PERF_CRT_DV_CD = '01' AND T2.FEE_ACKMT_CT > 0 AND NVL(T1.ACC_NINC_YN,'N') = 'N' )T GROUP BY T.PRTNR_NO UNION ALL SELECT T.PRTNR_NO , 0 AS EH_CNT , 0 AS EX_CNT , 0 AS ET_CNT , 0 AS UP_CNT , NVL(SUM(EH_AMT),0) AS EH_AMT , NVL(SUM(EX_AMT),0) AS EX_AMT , NVL(SUM(ET_AMT),0) AS ET_AMT , NVL(SUM(UP_AMT),0) AS UP_AMT FROM ( SELECT T1.PRTNR_NO , CASE WHEN T3.FEE_PERF_TP_CD = 'EH' THEN T1.ACKMT_PERF_AMT END AS EH_AMT , CASE WHEN T3.FEE_PERF_TP_CD = 'EX' THEN T1.ACKMT_PERF_AMT END AS EX_AMT , CASE WHEN T3.FEE_PERF_TP_CD = 'ET' THEN T1.ACKMT_PERF_AMT END AS ET_AMT , CASE WHEN T3.FEE_PERF_TP_CD = 'UP' THEN T1.ACKMT_PERF_AMT END AS UP_AMT FROM TB_FEAM_WELS_NRCTR_MM_CL T1 INNER JOIN TB_FEAM_WELS_FEE_PDCT_TP_BAS T2 ON T1.PD_CD = T2.BASE_PD_CD AND #{perfYm} BETWEEN APY_STRT_YM AND APY_END_YM INNER JOIN TB_FEAM_WELS_FEE_PERF_TP_BAS T3 ON T2.FEE_PDCT_TP_CD1 = T3.FEE_PDCT_TP_CD AND T1.SELL_TP_CD = T3.SELL_TP_CD WHERE T1.BASE_YM = #{perfYm} AND T1.OG_TP_CD = 'W01' AND T1.FEE_TCNT_DV_CD = #{feeTcntDvCd} AND T1.CNTR_PERF_CRT_DV_CD = '01' AND T1.ACKMT_PERF_AMT > 0 AND NVL(T1.ACC_NINC_YN,'N') = 'N' )T GROUP BY T.PRTNR_NO )M INNER JOIN TB_OGBS_MM_PRTNR_IZ T2 /* 월파트너 */ ON M.PRTNR_NO = T2.PRTNR_NO AND T2.OG_TP_CD = 'W01' AND T2.BASE_YM = #{perfYm}
AND T2.PRTNR_NO = #{prtnrNo}
AND T2.PSTN_DV_CD IN ('7','15')
AND T2.PSTN_DV_CD = #{rsbDvCd}
INNER JOIN TB_OGBS_MM_OG_IZ T3 /* 월조직 */ ON T2.BASE_YM = T3.BASE_YM AND T2.OG_ID = T3.OG_ID AND T2.OG_TP_CD = T3.OG_TP_CD
AND T3.DGR1_LEVL_OG_ID = #{og1LevlId}
AND T3.DGR2_LEVL_OG_ID = #{og2LevlId}
AND T3.DGR3_LEVL_OG_ID = #{og3LevlId}
GROUP BY T2.OG_CD, M.PRTNR_NO, T2.PRTNR_KNM, T2.PSTN_DV_CD

mapper.update:
UPDATE TB_FEAM_NTOR_MM_CL
   SET NTOR_CNFM_STAT_CD = '02' /*순주문확정상태코드 01-임시저장, 02-확정*/

 WHERE BASE_YM = #{perfYm}
   AND FEE_TCNT_DV_CD = #{feeTcntDvCd}
   AND PERF_AGRG_CRT_DV_CD = #{perfAgrgCrtDvCd}

UPDATE TB_FEAM_NTOR_MM_CL
   SET NTOR_CNFM_STAT_CD = '01' /*순주문확정상태코드 01-임시저장, 02-확정*/

 WHERE BASE_YM = #{perfYm}
   AND FEE_TCNT_DV_CD = #{feeTcntDvCd}
   AND PERF_AGRG_CRT_DV_CD = #{perfAgrgCrtDvCd}

mapper.select:
SELECT COUNT(T1.PD_CD) AS P_CNT
  FROM TB_FEAM_WELS_NRCTR_MM_CL T1
 WHERE T1.BASE_YM = #{perfYm}
   AND T1.FEE_TCNT_DV_CD = #{feeTcntDvCd}
   AND T1.OG_TP_CD = #{ogTpCd}
   AND T1.CNTR_PERF_CRT_DV_CD = '01' /* 순주문 */
   AND NVL(T1.ACC_NINC_YN,'N') = 'N'
   AND NOT EXISTS ( SELECT 1 FROM TB_FEAM_WELS_FEE_PDCT_TP_BAS X
                     WHERE T1.PD_CD = X.BASE_PD_CD
                       AND #{perfYm} BETWEEN X.APY_STRT_YM AND X.APY_END_YM
                  )

SELECT PR.OG_NM , PR.PRTNR_NO , PR.PRTNR_KNM , WN.CNTR_NO , WN.RCPDT , WN.SL_DT , WN.CAN_DT , PT.FEE_PERF_TP_CD , WN.PD_CD , PD.PD_NM , WN.ACKMT_PERF_AMT , WN.ACKMT_PERF_CT , CASE WHEN WN.MCHN_CH_TP_CD = '0' THEN '' ELSE WN.MCHN_CH_TP_CD END AS MCHN_CH_TP_CD FROM TB_FEAM_WELS_NRCTR_MM_CL WN /* 웰스순주문계약월마감 */ JOIN TB_OGBS_MM_PRTNR_IZ PR /* 월파트너내역 */ ON PR.BASE_YM = WN.BASE_YM AND PR.OG_TP_CD = WN.OG_TP_CD AND PR.PRTNR_NO = WN.PRTNR_NO JOIN TB_OGBS_MM_OG_IZ OG /* 월조직내역 */ ON OG.BASE_YM = PR.BASE_YM AND OG.OG_ID = PR.OG_ID JOIN TB_FEAM_WELS_FEE_PDCT_TP_BAS PB /* 웰스수수료제품유형기본 */ ON PB.BASE_PD_CD = WN.PD_CD AND WN.BASE_YM BETWEEN PB.APY_STRT_YM AND PB.APY_END_YM JOIN TB_FEAM_WELS_FEE_PERF_TP_BAS PT /* 웰스수수료실적유형기본 */ ON PB.FEE_PDCT_TP_CD1 = PT.FEE_PDCT_TP_CD AND WN.SELL_TP_CD = PT.SELL_TP_CD JOIN TB_PDBS_PD_BAS PD /* 상품기본 */ ON PD.PD_CD = WN.PD_CD WHERE WN.BASE_YM = #{perfYm} AND WN.OG_TP_CD = 'W03' AND WN.FEE_TCNT_DV_CD = #{feeTcntDvCd} AND WN.CO_CD = '2000' AND WN.DTA_DL_YN = 'N'
AND WN.PRTNR_NO = #{prtnrNo}
AND OG.DGR1_LEVL_OG_ID = #{og1LevlId}
AND OG.DGR2_LEVL_OG_ID = #{og2LevlId}
AND OG.DGR3_LEVL_OG_ID = #{og3LevlId}
