INSERT INTO TB_FEAM_NTORP_CNTR_MM_CL( BASE_YM , PERF_YM , FEE_TCNT_DV_CD , PERF_AGRG_CRT_DV_CD , PERF_ATC_CD , CO_CD , OG_TP_CD , PRTNR_NO , PERF_DV_CD , CNTR_NO , CNTR_SN , CNTR_PERF_CRT_DV_CD , PERF_VAL , REDF_ADSB_OJ_PERF_VAL
) SELECT #{baseYm} AS BASE_YM , PERF_YM , FEE_TCNT_DV_CD , #{perfAgrgCrtDvCd} AS PERF_AGRG_CRT_DV_CD , PERF_ATC_CD , CO_CD , OG_TP_CD , PRTNR_NO , '0' AS PERF_DV_CD , CNTR_NO , CNTR_SN , CNTR_PERF_CRT_DV_CD , PERF_VAL , REDF_ADSB_OJ_PERF_VAL
FROM (SELECT T1.PERF_YM , T1.FEE_TCNT_DV_CD , T2.PERF_ATC_CD , T2.CO_CD , T1.OG_TP_CD , T1.PRTNR_NO , T1.CNTR_NO , T1.CNTR_SN , T1.CNTR_PERF_CRT_DV_CD , 0 AS PERF_VAL , T2.PERF_VAL AS REDF_ADSB_OJ_PERF_VAL , ROW_NUMBER() OVER (PARTITION BY T1.PERF_YM , T1.FEE_TCNT_DV_CD , T2.PERF_ATC_CD , T2.CO_CD , T1.OG_TP_CD , T1.PRTNR_NO , T1.CNTR_NO , T1.CNTR_SN ORDER BY T2.BASE_YM DESC , T2.PERF_AGRG_CRT_DV_CD DESC) AS RN FROM (SELECT PERF_YM , FEE_TCNT_DV_CD , CNTR_NO , CNTR_SN , OG_TP_CD , PRTNR_NO , CNTR_PERF_CRT_DV_CD , REDF_ADSB_OJ_AMT FROM TB_FEAM_WELS_NRCTR_MM_CL WHERE BASE_YM = #{baseYm} AND CNTR_PERF_CRT_DV_CD = #{cntrPerfCrtDvCd} -- 되물림 AND PERF_TP_CD NOT IN ('10', '11') -- 연체, 장기보류는 별도 처리 AND OG_TP_CD = #{ogTpCd}) T1 INNER JOIN TB_FEAM_NTORP_CNTR_MM_CL T2 ON (T1.PERF_YM = T2.PERF_YM AND T1.FEE_TCNT_DV_CD = T2.FEE_TCNT_DV_CD AND T1.CNTR_NO = T2.CNTR_NO AND T1.CNTR_SN = T2.CNTR_SN AND T1.OG_TP_CD = T2.OG_TP_CD AND T1.PRTNR_NO = T2.PRTNR_NO) WHERE T2.BASE_YM BETWEEN T1.PERF_YM AND TO_CHAR(ADD_MONTHS(TO_DATE(#{baseYm}, 'YYYYMM'), -1), 'YYYYMM') AND T2.PERF_ATC_CD IN
#{perfAtcCd}
) WHERE RN = 1

INSERT INTO TB_FEAM_NTORP_CNTR_MM_CL( BASE_YM , PERF_YM , FEE_TCNT_DV_CD , PERF_AGRG_CRT_DV_CD , PERF_ATC_CD , CO_CD , OG_TP_CD , PRTNR_NO , PERF_DV_CD , CNTR_NO , CNTR_SN , CNTR_PERF_CRT_DV_CD , PERF_VAL , REDF_ADSB_OJ_PERF_VAL
) SELECT T2.FEE_REDF_YM AS BASE_YM , T1.BASE_YM AS PERF_YM , '02' AS FEE_TCNT_DV_CD , #{perfAgrgCrtDvCd} AS PERF_AGRG_CRT_DV_CD , #{perfAtcCd} AS PERF_ATC_CD , '2000' AS CO_CD , #{ogTpCd} AS OG_TP_CD , T1.PRTNR_NO , '0' AS PERF_DV_CD , T1.CNTR_NO , T1.CNTR_SN , '02' AS CNTR_PERF_CRT_DV_CD , 0 AS PERF_VAL , COUNT(1) AS REDF_ADSB_OJ_PERF_VAL
FROM TB_IFIN_LIF_ALNC_FEE_CNTR_IZ T1 INNER JOIN TB_IFIN_LIF_ALNC_FEE_CNTR_IZ T2 ON (T1.LIF_CNTR_NO = T2.LIF_CNTR_NO AND T1.OG_TP_CD = T2.OG_TP_CD) LEFT OUTER JOIN LATERAL (SELECT SUM(CASE WHEN T3.LIF_CNTR_STAT_CD = '2' THEN 1 ELSE -1 END) AS REDF_ADSB_SUM_CNT FROM TB_IFIN_LIF_ALNC_FEE_CNTR_IZ T3 WHERE T1.LIF_CNTR_NO = T3.LIF_CNTR_NO AND T1.OG_TP_CD = T3.OG_TP_CD AND T3.LIF_CNTR_STAT_CD IN ('2', '3')) T3 ON (1=1) WHERE T2.FEE_REDF_YM = ${baseYm} AND T2.LIF_CNTR_STAT_CD = '1' /* 취소 */ AND T1.LIF_CNTR_STAT_CD = '0' AND NVL(T1.FEE_DSB_YM, '0') > '0' AND T1.OG_TP_CD = #{ogTpCd} AND T2.CNFM_YN = 'Y' AND NVL(T1.FEE_DSB_YM, '0') < NVL(T2.FEE_REDF_YM,'0') AND NVL(REDF_ADSB_SUM_CNT, 0) = 0 GROUP BY T2.FEE_REDF_YM, T1.BASE_YM, T1.PRTNR_NO, T1.CNTR_NO, T1.CNTR_SN

INSERT INTO TB_FEAM_NTORP_CNTR_MM_CL( BASE_YM , PERF_YM , FEE_TCNT_DV_CD , PERF_AGRG_CRT_DV_CD , PERF_ATC_CD , CO_CD , OG_TP_CD , PRTNR_NO , PERF_DV_CD , CNTR_NO , CNTR_SN , CNTR_PERF_CRT_DV_CD , PERF_VAL , REDF_ADSB_OJ_PERF_VAL
) SELECT T2.FEE_REDF_YM AS BASE_YM , T1.BASE_YM AS PERF_YM , '02' AS FEE_TCNT_DV_CD , #{perfAgrgCrtDvCd} AS PERF_AGRG_CRT_DV_CD , #{perfAtcCd} AS PERF_ATC_CD , '2000' AS CO_CD , #{ogTpCd} AS OG_TP_CD , T1.PRTNR_NO , '0' AS PERF_DV_CD , T1.CNTR_NO , T1.CNTR_SN , '02' AS CNTR_PERF_CRT_DV_CD , 0 AS PERF_VAL , COUNT(1) AS REDF_ADSB_OJ_PERF_VAL
FROM TB_IFIN_LIF_ALNC_FEE_CNTR_IZ T1 INNER JOIN TB_IFIN_LIF_ALNC_FEE_CNTR_IZ T2 ON (T1.LIF_CNTR_NO = T2.LIF_CNTR_NO AND T1.OG_TP_CD = T2.OG_TP_CD) LEFT OUTER JOIN LATERAL (SELECT SUM(CASE WHEN T3.LIF_CNTR_STAT_CD = '2' THEN 1 ELSE -1 END) AS REDF_ADSB_SUM_CNT FROM TB_IFIN_LIF_ALNC_FEE_CNTR_IZ T3 WHERE T1.LIF_CNTR_NO = T3.LIF_CNTR_NO AND T1.OG_TP_CD = T3.OG_TP_CD AND T3.LIF_CNTR_STAT_CD IN ('2', '3')) T3 ON (1=1) WHERE T2.FEE_REDF_YM = ${baseYm} AND T2.LIF_CNTR_STAT_CD = '1' /* 취소 */ AND T1.LIF_CNTR_STAT_CD = '0' AND NVL(T1.FEE_DSB_YM, '0') > '0' AND T1.OG_TP_CD = #{ogTpCd} AND T2.CNFM_YN = 'Y' AND T1.LIF_CNTR_OC_TN = 1 AND T1.PRTNR_NO != T1.BRMGR_PRTNR_NO AND NVL(REDF_ADSB_SUM_CNT, 0) = 0 AND NVL(T1.FEE_DSB_YM, '0') < NVL(T2.FEE_REDF_YM,'0') GROUP BY T2.FEE_REDF_YM, T1.BASE_YM, T1.PRTNR_NO, T1.CNTR_NO, T1.CNTR_SN
