mapper.select:
SELECT T1.BASE_YM , NVL(C0.CD_CNTN, T1.CO_CD) CO_CD_NM /* 소속 - T1.CO_CD */ , T2.PRTNR_KNM /* 핀매자 */ , T1.PRTNR_NO /* 번호 */ , T1.CNTR_NO /* 계약상세번호 */ , T4.CST_KNM /* 고객성명 */ , T5.BASE_PD_CD /* 상품코드 */ , T6.PD_NM /* 상품명 */ , NVL(C1.CD_CNTN, T7.SELL_DSC_DV_CD) SELL_DSC_DV_CD_NM /* 할인구분 - T7.DSC_DV_CD => SELL_DSC_DV_CD ??? */ , CASE WHEN T7.SELL_DSC_DV_CD = '5' THEN NVL(C2.CD_CNTN, T7.SELL_DSCR_CD) --판매할인율코드 ELSE TO_CHAR(T7.RECAP_DUTY_PTRM_N) --유상의무기간수 END SELL_DSCR_CD_NM /* 할인유형 - T7.DSC_TP_CD => SELL_DSCR_CD RECAP_DUTY_PTRM_N ??? */ , NVL(C3.CD_CNTN, T7.SELL_DSC_TP_CD) SELL_DSC_TP_CD_NM /* 할인제도 - T7.PMOT_CD => SELL_DSC_TP_CD ??? */ , (SELECT MAX('1') FROM TB_SSCT_CNTR_REL X WHERE X.BASE_CNTR_NO = T1.CNTR_NO AND X.CNTR_UNIT_TP_CD = '10' -- 계약 AND X.CNTR_REL_TP_CD = '20' -- 연계상품 AND X.CNTR_REL_DTL_CD = '214' -- 원주문-정기배송 ) AS REL_PD_CD_NM /* 결합구분 */ , NVL(C5.CD_CNTN, T7.PMOT_USWY_DV_CD) PMOT_USWY_DV_CD_NM /* 용도구분 - T7.PMOT_USWY_DV_CD ??? */ , CASE WHEN T7.PMOT_USWY_DV_CD = '1' THEN '1-일반' WHEN T7.PMOT_USWY_DV_CD = '2' THEN '2-업소' ELSE '' END MG_NM /* 관리유형 - ??? */ , NVL(C4.CD_CNTN, T7.BFSVC_PRD_CD) BFSVC_PRD_CD_NM /* 방문주기 - T7.BFSVC_PRD_CD */ , T7.RCPDT /* 접수일자 */ , T7.SL_DT /* 매출일자 */ , T7.CAN_DT /* 취소일자 */ , T1.PERF_VAL /* 수수료 - 실적항목코드(W04P00001-기본수수료금액)의 금액 */ , CASE WHEN T7.ACKMT_PERF_CT IS NULL AND T7.ACKMT_PERF_AMT > 0 THEN 1 ELSE T7.ACKMT_PERF_CT END ACKMT_PERF_CT /* 신규건수 -> 인정건수(Null인 경우 인정실적이 있으면 1로 치환) */ FROM TB_FEAM_NTORP_CNTR_MM_CL T1 /* 순주문파트너계약월마감 */ INNER JOIN TB_OGBS_MM_PRTNR_IZ T2 /* 월파트너 */ ON T2.BASE_YM = T1.BASE_YM AND T2.OG_TP_CD = T1.OG_TP_CD /* 2023.05.02 조건 추가 */ AND T2.PRTNR_NO = T1.PRTNR_NO INNER JOIN TB_SSCT_CNTR_BAS T3 /* 계약기본 */ ON T3.CNTR_NO = T1.CNTR_NO INNER JOIN TB_CUBS_CST_BAS T4 /* 고객기본 */ ON T4.CST_NO = T3.CNTR_CST_NO INNER JOIN TB_SSCT_CNTR_DTL T5 /* 계약상세 */ ON T5.CNTR_NO = T1.CNTR_NO AND T5.CNTR_SN = T1.CNTR_SN INNER JOIN TB_PDBS_PD_BAS T6 /* 상품기본 */ ON T6.PD_CD = T5.BASE_PD_CD LEFT OUTER JOIN TB_FEAM_WELS_NRCTR_MM_CL T7 /* 웰스순주문계약월마감 */ ON T7.BASE_YM = T1.BASE_YM AND T7.PERF_YM = T1.PERF_YM AND T7.FEE_TCNT_DV_CD = T1.FEE_TCNT_DV_CD AND T7.CNTR_NO = T1.CNTR_NO AND T7.CNTR_SN = T1.CNTR_SN AND T7.CNTR_PERF_CRT_DV_CD = T1.CNTR_PERF_CRT_DV_CD
AND T7.RCPDT BETWEEN #{strtYm} AND #{endYm}
AND T7.CAN_DT BETWEEN #{cancelStrtYm} AND #{cancelEndYm}
LEFT OUTER JOIN T_CMZ_CD_D C0 /* 공통코드 - 업체명 */ ON C0.TENANT_ID = #{session.tenantId} --웰스 AND C0.CD_ID = 'CO_CD' --업체명 AND C0.CD_VLD_VAL = T1.CO_CD LEFT OUTER JOIN T_CMZ_CD_D C1 /* 공통코드 - 할인구분 */ ON C1.TENANT_ID = #{session.tenantId} --웰스 AND C1.CD_ID = 'SELL_DSC_DV_CD' --할인구분 AND C1.CD_VLD_VAL = T7.SELL_DSC_DV_CD LEFT OUTER JOIN T_CMZ_CD_D C2 /* 공통코드 - 할인유형 */ ON C2.TENANT_ID = #{session.tenantId} --웰스 AND C2.CD_ID = 'SELL_DSCR_CD' --할인유형 AND C2.CD_VLD_VAL = T7.SELL_DSCR_CD LEFT OUTER JOIN T_CMZ_CD_D C3 /* 공통코드 - 할인제도 */ ON C3.TENANT_ID = #{session.tenantId} --웰스 AND C3.CD_ID = 'SELL_DSC_TP_CD' --할인제도 AND C3.CD_VLD_VAL = T7.SELL_DSC_TP_CD LEFT OUTER JOIN T_CMZ_CD_D C4 /* 공통코드 - 방문주기 */ ON C4.TENANT_ID = #{session.tenantId} --웰스 AND C4.CD_ID = 'BFSVC_PRD_CD' --방문주기 AND C4.CD_VLD_VAL = T7.BFSVC_PRD_CD LEFT OUTER JOIN T_CMZ_CD_D C5 /* 공통코드 - 용도구분 */ ON C5.TENANT_ID = #{session.tenantId} --웰스 AND C5.CD_ID = 'PMOT_USWY_DV_CD' --용도구분 AND C5.CD_VLD_VAL = T7.PMOT_USWY_DV_CD WHERE 1=1 AND T1.BASE_YM = #{perfYm} AND T1.PERF_AGRG_CRT_DV_CD = '401' /* 실적집계생성구분코드 : ???-B2B수수료실적생성 */ AND T1.OG_TP_CD = 'W04' /* 조직유형코드 : W04-온라인B2B */ AND T1.PERF_ATC_CD = 'W04P00001' /* 실적항목코드 : W04P00001-기본수수료금액 */ AND T1.PERF_DV_CD = '0' /* 실적구분코드 : 0-개인판매 */ AND T1.CNTR_PERF_CRT_DV_CD = '01' /* 계약실적생성구분코드 : 01-순주문집계 */ AND T1.DTA_DL_YN = 'N'

mapper.update:
MERGE INTO TB_FEAM_FEE_CALC_BAS T1 /* 수수료계산기본 */ USING ( SELECT #{baseYm} AS BASE_YM /* 기준년월 */ , #{perfYm} AS PERF_YM /* 실적년월 */ , #{ojDsbYm} AS OJ_DSB_YM /* 대상지급년월 */ , #{coCd} AS CO_CD /* 회사코드 */ , #{ogTpCd} AS OG_TP_CD /* 조직유형코드 */ , #{prtnrNo} AS PRTNR_NO /* 파트너번호 */ , #{feeCd} AS FEE_CD /* 수수료코드 */ , #{dtaCrtFeeCd} AS DTA_CRT_FEE_CD /* 데이터생성수수료코드 */ , #{feeTcntDvCd} AS FEE_TCNT_DV_CD /* 수수료차수구분코드 */ , #{spmtDsbDvCd} AS SPMT_DSB_DV_CD /* 추가지급구분코드 */ , #{feeCalcTpCd} AS FEE_CALC_TP_CD /* 수수료계산유형코드 */ , #{feeCalcAmt} AS FEE_CALC_AMT /* 수수료계산금액 */ , #{feeCtrCnfmAmt} AS FEE_CTR_CNFM_AMT /* 수수료조정확정금액 */ , #{feeCtrRsonCn} AS FEE_CTR_RSON_CN /* 수수료조정사유내용 */ , #{feeCtrOgTpCd} AS FEE_CTR_OG_TP_CD /* 수수료조정조직유형코드 */ , #{feeCtrPrtnrNo} AS FEE_CTR_PRTNR_NO /* 수수료조정파트너번호 */ , #{feeCalcVarbVal} AS FEE_CALC_VARB_VAL /* 수수료계산변수값 */ , #{fnlFeeYn} AS FNL_FEE_YN /* 최종수수료여부 */ , #{dtaDlYn} AS DTA_DL_YN /* 데이터삭제여부 */ FROM DUAL ) T2 ON ( T1.BASE_YM = T2.BASE_YM /* 기준년월 */ AND T1.PERF_YM = T2.PERF_YM /* 실적년월 */ AND T1.OJ_DSB_YM = T2.OJ_DSB_YM /* 대상지급년월 */ AND T1.CO_CD = T2.CO_CD /* 회사코드 */ AND T1.OG_TP_CD = T2.OG_TP_CD /* 조직유형코드 */ AND T1.PRTNR_NO = T2.PRTNR_NO /* 파트너번호 */ AND T1.FEE_CD = T2.FEE_CD /* 수수료코드 */ AND T1.DTA_CRT_FEE_CD = T2.DTA_CRT_FEE_CD /* 데이터생성수수료코드 */ AND T1.FEE_TCNT_DV_CD = T2.FEE_TCNT_DV_CD /* 수수료차수구분코드 */ AND T1.SPMT_DSB_DV_CD = T2.SPMT_DSB_DV_CD /* 추가지급구분코드 */ AND T1.FEE_CALC_TP_CD = T2.FEE_CALC_TP_CD /* 수수료계산유형코드 */ ) WHEN MATCHED THEN UPDATE SET FEE_CALC_AMT = T2.FEE_CALC_AMT , FEE_CTR_CNFM_AMT = T2.FEE_CTR_CNFM_AMT
WHEN NOT MATCHED THEN INSERT ( BASE_YM , PERF_YM , OJ_DSB_YM , CO_CD , OG_TP_CD , PRTNR_NO , FEE_CD , DTA_CRT_FEE_CD , FEE_TCNT_DV_CD , SPMT_DSB_DV_CD , FEE_CALC_TP_CD , FEE_CALC_AMT , FEE_CTR_CNFM_AMT , FEE_CTR_RSON_CN , FEE_CTR_OG_TP_CD , FEE_CTR_PRTNR_NO , FEE_CALC_VARB_VAL , FNL_FEE_YN , DTA_DL_YN
) VALUES ( T2.BASE_YM , T2.PERF_YM , T2.OJ_DSB_YM , T2.CO_CD , T2.OG_TP_CD , T2.PRTNR_NO , T2.FEE_CD , T2.DTA_CRT_FEE_CD , T2.FEE_TCNT_DV_CD , T2.SPMT_DSB_DV_CD , T2.FEE_CALC_TP_CD , T2.FEE_CALC_AMT , T2.FEE_CTR_CNFM_AMT , T2.FEE_CTR_RSON_CN , T2.FEE_CTR_OG_TP_CD , T2.FEE_CTR_PRTNR_NO , T2.FEE_CALC_VARB_VAL , T2.FNL_FEE_YN , T2.DTA_DL_YN
)

MERGE INTO TB_FEAM_FEE_DDTN_IZ T1 /* 수수료공제내역 */ USING ( SELECT #{ddtnYm} AS DDTN_YM /* 공제년월 */ , #{coCd} AS CO_CD /* 회사코드 */ , #{ogTpCd} AS OG_TP_CD /* 조직유형코드 */ , #{prtnrNo} AS PRTNR_NO /* 파트너번호 */ , #{feeDdtnTpCd} AS FEE_DDTN_TP_CD /* 수수료공제유형코드 */ , #{feeTcntDvCd} AS FEE_TCNT_DV_CD /* 수수료차수구분코드 */ , #{spmtDsbDvCd} AS SPMT_DSB_DV_CD /* 추가지급구분코드 */ , #{feeDdtnCrtCd} AS FEE_DDTN_CRT_CD /* 수수료공제생성코드 */ , #{feeDdctam} AS FEE_DDCTAM /* 수수료공제금액 */ , #{feeCtrRsonCn} AS FEE_CTR_RSON_CN /* 수수료조정사유내용 */ , #{feeDdtnCnfmAmt} AS FEE_DDTN_CNFM_AMT /* 수수료공제확정금액 */ , #{feeCtrOgTpCd} AS FEE_CTR_OG_TP_CD /* 수수료조정조직유형코드 */ , #{feeCtrPrtnrNo} AS FEE_CTR_PRTNR_NO /* 수수료조정파트너번호 */ , #{dtaDlYn} AS DTA_DL_YN /* 데이터삭제여부 */ FROM DUAL ) T2 ON ( T1.DDTN_YM = T2.DDTN_YM /* 공제년월 */ AND T1.CO_CD = T2.CO_CD /* 회사코드 */ AND T1.OG_TP_CD = T2.OG_TP_CD /* 조직유형코드 */ AND T1.PRTNR_NO = T2.PRTNR_NO /* 파트너번호 */ AND T1.FEE_DDTN_TP_CD = T2.FEE_DDTN_TP_CD /* 수수료공제유형코드 */ AND T1.FEE_TCNT_DV_CD = T2.FEE_TCNT_DV_CD /* 수수료차수구분코드 */ AND T1.SPMT_DSB_DV_CD = T2.SPMT_DSB_DV_CD /* 추가지급구분코드 */ AND T1.FEE_DDTN_CRT_CD = T2.FEE_DDTN_CRT_CD /* 수수료공제생성코드 */ ) WHEN MATCHED THEN UPDATE SET FEE_DDTN_CNFM_AMT = T2.FEE_DDTN_CNFM_AMT , FEE_CTR_RSON_CN = T2.FEE_CTR_RSON_CN
WHEN NOT MATCHED THEN INSERT ( DDTN_YM , CO_CD , OG_TP_CD , PRTNR_NO , FEE_DDTN_TP_CD , FEE_TCNT_DV_CD , SPMT_DSB_DV_CD , FEE_DDTN_CRT_CD , FEE_DDCTAM , FEE_CTR_RSON_CN , FEE_DDTN_CNFM_AMT , FEE_CTR_OG_TP_CD , FEE_CTR_PRTNR_NO , DTA_DL_YN
) VALUES ( T2.DDTN_YM , T2.CO_CD , T2.OG_TP_CD , T2.PRTNR_NO , T2.FEE_DDTN_TP_CD , T2.FEE_TCNT_DV_CD , T2.SPMT_DSB_DV_CD , T2.FEE_DDTN_CRT_CD , T2.FEE_DDCTAM , T2.FEE_CTR_RSON_CN , T2.FEE_DDTN_CNFM_AMT , T2.FEE_CTR_OG_TP_CD , T2.FEE_CTR_PRTNR_NO , T2.DTA_DL_YN
)

mapper.insert:
INSERT INTO TB_FEAM_NTOR_MM_CL ( -- 순주문파트너월마감 BASE_YM , FEE_TCNT_DV_CD , PERF_AGRG_CRT_DV_CD , NTOR_CNFM_STAT_CD , DTA_DL_YN
) SELECT #{perfYm} AS BASE_YM -- 실행조건 : UI실적년월 , '02' AS FEE_TCNT_DV_CD -- 2차 , '401' AS PERF_AGRG_CRT_DV_CD -- B2B수수료실적생성 , '01' AS NTOR_CNFM_STAT_CD -- 임시저장 , 'N' AS DTA_DL_YN
FROM DUAL

INSERT INTO TB_FEAM_NTORP_CNTR_MM_CL ( -- 순주문파트너 계약월마감 BASE_YM , PERF_YM , FEE_TCNT_DV_CD , PERF_AGRG_CRT_DV_CD , PERF_ATC_CD , CO_CD , OG_TP_CD , PRTNR_NO , PERF_DV_CD , CNTR_NO , CNTR_SN , CNTR_PERF_CRT_DV_CD , PERF_VAL , DTA_DL_YN
) SELECT T1.BASE_YM , T1.PERF_YM , T1.FEE_TCNT_DV_CD , '401' AS PERF_AGRG_CRT_DV_CD -- B2B수수료실적생성 , 'W04P00001' AS PERF_ATC_CD -- B2B기본수수료 , '2000' AS CO_CD -- 교원프라퍼티 ??? , T1.OG_TP_CD , T1.PRTNR_NO , '0' AS PERF_DV_CD -- 개인판매 , T1.CNTR_NO , T1.CNTR_SN , '01' AS CNTR_PERF_CRT_DV_CD -- 순주문집계 (01:순주문집계, 02:되물림생성, 03:재지급생성) , T1.ACKMT_PERF_AMT AS PERF_VAL , 'N' AS DTA_DL_YN
FROM TB_FEAM_WELS_NRCTR_MM_CL T1 WHERE T1.BASE_YM = #{perfYm} AND T1.FEE_TCNT_DV_CD = '02' -- 2차 AND T1.OG_TP_CD = 'W04' -- 온라인총판

INSERT INTO TB_FEAM_NTORP_PERF_MM_CL ( -- 순주문파트너 실적월마감 BASE_YM , PERF_YM , FEE_TCNT_DV_CD , PERF_AGRG_CRT_DV_CD , PERF_ATC_CD , CO_CD , OG_TP_CD , PRTNR_NO , PERF_DV_CD , PERF_VAL , DTA_DL_YN
) SELECT T1.BASE_YM , T1.PERF_YM , T1.FEE_TCNT_DV_CD , T1.PERF_AGRG_CRT_DV_CD , T1.PERF_ATC_CD , T1.CO_CD , T1.OG_TP_CD , T1.PRTNR_NO , T1.PERF_DV_CD , SUM(T1.PERF_VAL) AS PERF_VAL , 'N' AS DTA_DL_YN
FROM TB_FEAM_NTORP_CNTR_MM_CL T1 WHERE T1.BASE_YM = #{perfYm} AND T1.FEE_TCNT_DV_CD = '02' -- 2차 AND T1.PERF_AGRG_CRT_DV_CD = '401' -- B2B수수료실적생성 AND T1.PERF_ATC_CD = 'W04P00001' -- B2B기본수수료 -- AND T1.CO_CD = '2000' -- 회사코드가 여러개가 될수도 있으니까 일단 주석처리하자. AND T1.OG_TP_CD = 'W04' -- 온라인B2B AND T1.PERF_DV_CD = '0' -- 개인판매 GROUP BY T1.BASE_YM , T1.PERF_YM , T1.FEE_TCNT_DV_CD , T1.PERF_AGRG_CRT_DV_CD , T1.PERF_ATC_CD , T1.CO_CD , T1.OG_TP_CD , T1.PRTNR_NO , T1.PERF_DV_CD

INSERT INTO TB_FEAM_NTORP_PERF_MM_CL ( -- 순주문파트너 실적월마감 BASE_YM , PERF_YM , FEE_TCNT_DV_CD , PERF_AGRG_CRT_DV_CD , PERF_ATC_CD , CO_CD , OG_TP_CD , PRTNR_NO , PERF_DV_CD , PERF_VAL , DTA_DL_YN
) SELECT T1.BASE_YM , T1.PERF_YM , T1.FEE_TCNT_DV_CD , T1.PERF_AGRG_CRT_DV_CD , T1.PERF_ATC_CD , T1.CO_CD , T1.OG_TP_CD , T3.DGR1_LEVL_DG_PRTNR_NO , '2' AS PERF_DV_CD /* 지국(지점)실적 */ , SUM(T1.PERF_VAL) AS PERF_VAL , 'N' AS DTA_DL_YN
FROM TB_FEAM_NTORP_PERF_MM_CL T1 INNER JOIN TB_OGBS_MM_PRTNR_IZ T2 ON T2.BASE_YM = T1.BASE_YM AND T2.OG_TP_CD = T1.OG_TP_CD AND T2.PRTNR_NO = T1.PRTNR_NO -- INNER JOIN -- 월조직내역 들어오면 주석 풀자. LEFT OUTER JOIN TB_OGBS_MM_OG_IZ T3 ON T3.BASE_YM = T2.BASE_YM AND T3.OG_ID = T2.OG_ID WHERE T1.BASE_YM = #{perfYm} AND T1.FEE_TCNT_DV_CD = '02' -- 2차 AND T1.PERF_AGRG_CRT_DV_CD = '401' -- B2B수수료실적생성 AND T1.PERF_ATC_CD = 'W04P00001' -- B2B기본수수료 -- AND T1.CO_CD = '2000' -- 회사코드가 여러개가 될수도 있으니까 일단 주석처리하자. AND T1.OG_TP_CD = 'W04' -- 온라인B2B AND T1.PERF_DV_CD = '0' -- 개인판매 GROUP BY T1.BASE_YM , T1.PERF_YM , T1.FEE_TCNT_DV_CD , T1.PERF_AGRG_CRT_DV_CD , T1.PERF_ATC_CD , T1.CO_CD , T1.OG_TP_CD , T3.DGR1_LEVL_DG_PRTNR_NO
