AS FEE_W060020 /*기타 수당*/ , NVL(FEE_W060021, 0) AS FEE_W060021 /*철거/철회 수당*/ , NVL(FEE_W060022, 0) AS FEE_W060022 /*판매권유 수당*/ , NVL(FEE_W060023, 0) AS FEE_W060023 /*토요근무 수당*/ , NVL(FEE_W060024, 0) AS FEE_W060024 /*휴무당직 수당*/ , NVL(FEE_W060025, 0) AS FEE_W060025 /*강의 수당*/ , NVL(FEE_W060026, 0) AS FEE_W060026 /*도서방문수당*/ , NVL(FEE_W060027, 0) AS FEE_W060027 /*기술숙련수당*/ , NVL(FEE_W060028, 0) AS FEE_W060028 /*조장 수당*/ , NVL(FEE_W060001 + FEE_W060002 + FEE_W060003 + FEE_W060004 + FEE_W060005 + FEE_W060006 + FEE_W060007 + FEE_W060008 + FEE_W060009 + FEE_W060010 + FEE_W060011 + FEE_W060012 + FEE_W060017 + FEE_W060018, 0) AS TOT_FEE /*조정전수당합계*/ , NVL(FEE_W060001 + FEE_W060003 + FEE_W060005 + FEE_W060007 + FEE_W060002 + FEE_W060004 + FEE_W060006 + FEE_W060008 + FEE_W060009 + FEE_W060010 + FEE_W060011 + FEE_W060012 + FEE_W060017 + FEE_W060018, 0) AS TOT_AF_FEE /*조정후수당합계*/ , NVL(FEE_W060019 + FEE_W060020 + FEE_W060021 + FEE_W060022 + FEE_W060024 + FEE_W060025 + FEE_W060027 + FEE_W060028 + FEE_W060023 + FEE_W060026, 0) AS TOT_FEE_ETC /*부가수당합계*/ , NVL(FEE_W060019 + FEE_W060020 + FEE_W060021 + FEE_W060022 + FEE_W060024 + FEE_W060025 + FEE_W060026 + FEE_W060027 + FEE_W060028 + FEE_W060023, 0) AS DSB_OJ_AMT /*지급대상금액*/ , (CASE WHEN FEE_W060022 > 0 THEN FEE_W060022/5000 ELSE 0 END) AS FEE_W060022_CNT /*판매권유수당 건수*/ , (CASE WHEN FEE_W060024 > 0 THEN FEE_W060024/20000 ELSE 0 END) AS FEE_W060024_CNT /*휴무당직수당 건수*/ , (CASE WHEN FEE_W060025 > 0 THEN FEE_W060025/10000 ELSE 0 END) AS FEE_W060025_CNT /*강의수당 건수*/ , (CASE WHEN FEE_W060026 > 0 THEN FEE_W060026/10000 ELSE 0 END) AS FEE_W060026_CNT /*도서방문수당 건수*/ , (CASE WHEN FEE_W060023 > 0 THEN FEE_W060023/2000 ELSE 0 END) AS FEE_W060023_CNT /*토요근무수당 건수*/ , (CASE WHEN CNR_AW_CNFM_DTM IS NULL THEN (CASE WHEN #{session.rsbCd} = '171' THEN 'Y' /*센터장*/ ELSE 'N' END) ELSE 'N' END) AS EDIT_YN /*수정가능여부*/ /* , (CASE WHEN CNR_AW_CNFM_DTM IS NULL THEN (CASE WHEN #G_DEG_CD# ='0' THEN 'Y' WHEN ('55' = #G_DEG_CD#) THEN 'Y' WHEN (OG_CD = '71365' AND '56'= #G_DEG_CD#) THEN 'Y' ELSE 'N' END) ELSE 'N' END) AS EDIT_YN --수정가능여부 */ , CNR_AW_CNFM_DTM /*센터확정일시*/ , '-' AS NOTE FROM ( SELECT OG_ID , OG_NM , OG_CD , BASE_YM , PRTNR_KNM , PRTNR_NO , PSTN_DV_CD , RSB_DV_CD , PERF_W06P00001 , PERF_W06P00004 , PERF_W06P00005 , PERF_W06P00006 , PERF_W06P00007 , PERF_W06P00008 , PERF_W06P00009 , PERF_W06P00010 , PERF_W06P00025 , PERF_W06P00015 , PERF_W06P00018 , FEE_CD , FEE_CALC_AMT , CNR_AW_CNFM_DTM FROM ( SELECT T2.OG_ID , T2.OG_NM , T2.OG_CD , T2.BASE_YM , T1.PRTNR_KNM , T1.PRTNR_NO , T1.PSTN_DV_CD , T1.RSB_DV_CD , T3.PERF_ATC_CD , T3.PERF_VAL , T4.FEE_CD , NVL(T4.FEE_CTR_CNFM_AMT, T4.FEE_CALC_AMT) AS FEE_CALC_AMT , T5.CNR_AW_CNFM_DTM FROM TB_OGBS_MM_PRTNR_IZ T1 /*월파트너내역*/ INNER JOIN TB_OGBS_MM_OG_IZ T2 /*월조직내역*/ ON T1.BASE_YM = T2.BASE_YM AND T1.OG_TP_CD = T2.OG_TP_CD AND T1.OG_ID = T2.OG_ID AND T1.OG_CD = T2.OG_CD AND T2.DTA_DL_YN = 'N' AND T2.OG_LEVL_DV_CD = '1' /*센터*/ AND T2.CLO_YN = 'N' /*조직폐쇄여부*/ LEFT OUTER JOIN TB_FEAM_EGER_PERF_MM_CL T3 /*엔지니어실적월마감*/ ON T1.BASE_YM = T3.BASE_YM AND T1.BASE_YM = T3.PERF_YM AND T1.OG_TP_CD = T3.OG_TP_CD AND T1.PRTNR_NO = T3.PRTNR_NO AND T1.OG_CD = T3.OG_CD AND T3.FEE_TCNT_DV_CD = '02' AND T3.PERF_AGRG_CRT_DV_CD = '601'/*실적집계생성구분코드 601-엔지니어실적*/ AND T3.PERF_ATC_CD IN ('W06P00001', 'W06P00004', 'W06P00005', 'W06P00006', 'W06P00007', 'W06P00008', 'W06P00009', 'W06P00010', 'W06P00025', 'W06P00015', 'W06P00018') AND T3.CO_CD = '2000' LEFT OUTER JOIN TB_FEAM_EGER_AW_CALC_BAS T4 /*엔지니어수당기본*/ ON T4.DTA_DL_YN = 'N' AND T3.BASE_YM = T4.BASE_YM AND T3.BASE_YM = T4.PERF_YM AND T3.BASE_YM = T4.OJ_DSB_YM AND T3.OG_TP_CD = T4.OG_TP_CD AND T3.PRTNR_NO = T4.PRTNR_NO AND T3.OG_CD = T4.OG_CD AND T3.CO_CD = T4.CO_CD AND T3.FEE_TCNT_DV_CD = T4.FEE_TCNT_DV_CD AND T4.FEE_CD IN ('W060001', 'W060002', 'W060003', 'W060004', 'W060005', 'W060006', 'W060007', 'W060008', 'W060009', 'W060010' , 'W060011', 'W060012', 'W060017', 'W060018', 'W060019', 'W060020', 'W060021', 'W060022' , 'W060023', 'W060024', 'W060025', 'W060026', 'W060027', 'W060028') AND T4.SPMT_DSB_DV_CD = '01' AND T4.FNL_FEE_YN = 'N' LEFT OUTER JOIN TB_FEAM_EGER_AW_CNFM_BAS T5 /*엔지니어수당확정기본*/ ON T5.DTA_DL_YN = 'N' AND T1.BASE_YM = T5.BASE_YM AND T1.BASE_YM = T5.PERF_YM AND T1.BASE_YM = T5.OJ_DSB_YM AND T1.OG_TP_CD = T5.OG_TP_CD AND T1.PRTNR_NO = T5.PRTNR_NO AND T1.OG_CD = T5.OG_CD AND T5.CO_CD = '2000' WHERE T1.DTA_DL_YN = 'N' AND T1.BASE_YM = #{perfYm} AND T1.OG_TP_CD = 'W06'
AND T1.PRTNR_NO = #{prtnrNo}
AND T2.OG_ID = #{ogLevlDvCd1}
AND T1.RSB_DV_CD IN ('W0601', 'W0602', 'W0603')
AND T1.RSB_DV_CD = #{rsbDvCd}
) PIVOT ( SUM(PERF_VAL) FOR PERF_ATC_CD IN ( 'W06P00001' AS PERF_W06P00001 , 'W06P00004' AS PERF_W06P00004 , 'W06P00005' AS PERF_W06P00005 , 'W06P00006' AS PERF_W06P00006 , 'W06P00007' AS PERF_W06P00007 , 'W06P00008' AS PERF_W06P00008 , 'W06P00009' AS PERF_W06P00009 , 'W06P00010' AS PERF_W06P00010 , 'W06P00025' AS PERF_W06P00025 , 'W06P00015' AS PERF_W06P00015 , 'W06P00018' AS PERF_W06P00018 ) ) ) PIVOT ( SUM(FEE_CALC_AMT) FOR FEE_CD IN ( 'W060001' AS FEE_W060001 , 'W060002' AS FEE_W060002 , 'W060003' AS FEE_W060003 , 'W060004' AS FEE_W060004 , 'W060005' AS FEE_W060005 , 'W060006' AS FEE_W060006 , 'W060007' AS FEE_W060007 , 'W060008' AS FEE_W060008 , 'W060009' AS FEE_W060009 , 'W060010' AS FEE_W060010 , 'W060011' AS FEE_W060011 , 'W060012' AS FEE_W060012 , 'W060017' AS FEE_W060017 , 'W060018' AS FEE_W060018 , 'W060019' AS FEE_W060019 , 'W060020' AS FEE_W060020 , 'W060021' AS FEE_W060021 , 'W060022' AS FEE_W060022 , 'W060023' AS FEE_W060023 , 'W060024' AS FEE_W060024 , 'W060025' AS FEE_W060025 , 'W060026' AS FEE_W060026 , 'W060027' AS FEE_W060027 , 'W060028' AS FEE_W060028 ) ) ORDER BY OG_NM, PRTNR_KNM

SELECT OG_ID , OG_NM , OG_CD , BASE_YM , PRTNR_KNM , PRTNR_NO , PSTN_DV_CD , RSB_DV_CD , NVL(FEE_W060031, 0) AS FEE_W060031 /*업적수당*/ , NVL(FEE_W060032, 0) AS FEE_W060032 /*자격수당*/ , NVL(FEE_W060031 + FEE_W060032, 0) AS TOT_FEE FROM ( SELECT T2.OG_ID , T2.OG_NM , T2.OG_CD , T2.BASE_YM , T1.PRTNR_KNM , T1.PRTNR_NO , T1.PSTN_DV_CD , T1.RSB_DV_CD , T3.FEE_CD , NVL(T3.FEE_CTR_CNFM_AMT, T3.FEE_CALC_AMT) AS FEE_CALC_AMT FROM TB_OGBS_MM_PRTNR_IZ T1 /*월파트너내역*/ INNER JOIN TB_OGBS_MM_OG_IZ T2 /*월조직내역*/ ON T1.BASE_YM = T2.BASE_YM AND T1.OG_TP_CD = T2.OG_TP_CD AND T1.OG_ID = T2.OG_ID AND T1.OG_CD = T2.OG_CD AND T2.DTA_DL_YN = 'N' AND T2.OG_LEVL_DV_CD = '1' /*센터*/ AND T2.CLO_YN = 'N' /*조직폐쇄여부*/ LEFT OUTER JOIN TB_FEAM_EGER_AW_CALC_BAS T3 /*엔지니어수당기본*/ ON T3.DTA_DL_YN = 'N' AND T1.BASE_YM = T3.BASE_YM AND T1.BASE_YM = T3.PERF_YM AND T1.BASE_YM = T3.OJ_DSB_YM AND T1.OG_TP_CD = T3.OG_TP_CD AND T1.PRTNR_NO = T3.PRTNR_NO AND T1.OG_CD = T3.OG_CD AND T3.CO_CD = '2000' AND T3.FEE_CD IN ('W060031', 'W060032') AND T3.FEE_TCNT_DV_CD = '02' AND T3.SPMT_DSB_DV_CD = '01' WHERE T1.DTA_DL_YN = 'N' AND T1.BASE_YM = #{perfYm} AND T1.OG_TP_CD = 'W06'
AND T1.PRTNR_NO = #{prtnrNo}
AND T2.OG_ID = #{ogLevlDvCd1}
AND T1.RSB_DV_CD IN ('W0603', 'W0604')
AND T1.RSB_DV_CD = #{rsbDvCd}
) PIVOT ( SUM(FEE_CALC_AMT) FOR FEE_CD IN ( 'W060031' AS FEE_W060031 , 'W060032' AS FEE_W060032 ) ) ORDER BY OG_NM, PRTNR_KNM

mapper.update:
MERGE INTO TB_FEAM_EGER_AW_CALC_BAS T1 /* 엔지니어수당계산기본 */ USING ( SELECT #{perfYm} AS BASE_YM /* 기준년월 */ , #{perfYm} AS PERF_YM /* 실적년월 */ , #{perfYm} AS OJ_DSB_YM /* 대상지급년월 */ , '2000' AS CO_CD /* 회사코드 */ , 'W06' AS OG_TP_CD /* 조직유형코드 */ , #{prtnrNo} AS PRTNR_NO /* 파트너번호 */ , #{ogCd} AS OG_CD , #{feeCd} AS FEE_CD /* 수수료코드 */ , #{feeCd} AS DTA_CRT_FEE_CD /*데이터생성수수료코드*/ , '02' AS FEE_TCNT_DV_CD /* 수수료차수구분코드 */ , #{feeAmt} AS FEE_CALC_AMT /* 수수료계산금액 */ , NVL(#{feeCtrCnfmAmt}, #{feeAmt}) AS FEE_CTR_CNFM_AMT /* 수수료조정확정금액 */ , '엔지니어 수당 조정' AS FEE_CTR_RSON_CN /* 수수료조정사유내용 */ , 'N' AS FNL_FEE_YN /* 최종수수료여부 */ , 'N' AS DTA_DL_YN /* 데이터삭제여부 */ , '01' AS SPMT_DSB_DV_CD /* 추가지급여부 */ FROM DUAL ) T2 ON (T1.BASE_YM = T2.BASE_YM /* 기준년월 */ AND T1.PERF_YM = T2.PERF_YM /* 실적년월 */ AND T1.OJ_DSB_YM = T2.OJ_DSB_YM /* 대상지급년월 */ AND T1.CO_CD = T2.CO_CD /* 회사코드 */ AND T1.OG_TP_CD = T2.OG_TP_CD /* 조직유형코드 */ AND T1.PRTNR_NO = T2.PRTNR_NO /* 파트너번호 */ AND T1.OG_CD = T2.OG_CD /*조직코드*/ AND T1.FEE_CD = T2.FEE_CD /* 수수료코드 */ AND T1.FEE_TCNT_DV_CD = T2.FEE_TCNT_DV_CD /* 수수료차수구분코드 */ ) WHEN MATCHED THEN UPDATE SET DTA_CRT_FEE_CD = T2.DTA_CRT_FEE_CD , FEE_CALC_AMT = T2.FEE_CALC_AMT , FEE_CTR_CNFM_AMT = T2.FEE_CTR_CNFM_AMT , FEE_CTR_RSON_CN = T2.FEE_CTR_RSON_CN , FNL_FEE_YN = T2.FNL_FEE_YN , DTA_DL_YN = T2.DTA_DL_YN
WHEN NOT MATCHED THEN INSERT ( BASE_YM , PERF_YM , OJ_DSB_YM , CO_CD , OG_TP_CD , PRTNR_NO , OG_CD , FEE_CD , DTA_CRT_FEE_CD , FEE_TCNT_DV_CD , FEE_CALC_AMT , FEE_CTR_CNFM_AMT , FEE_CTR_RSON_CN , FNL_FEE_YN , DTA_DL_YN , SPMT_DSB_DV_CD
) VALUES ( T2.BASE_YM , T2.PERF_YM , T2.OJ_DSB_YM , T2.CO_CD , T2.OG_TP_CD , T2.PRTNR_NO , T2.OG_CD , T2.FEE_CD , T2.DTA_CRT_FEE_CD , T2.FEE_TCNT_DV_CD , T2.FEE_CALC_AMT , T2.FEE_CTR_CNFM_AMT , T2.FEE_CTR_RSON_CN , T2.FNL_FEE_YN , T2.DTA_DL_YN , T2.SPMT_DSB_DV_CD
)

mapper.select:
SELECT COUNT(*) AS CNT
  FROM TB_FEAM_EGER_AW_CNFM_BAS
 WHERE BASE_YM = #{baseYm}
   AND PERF_YM = #{baseYm}
   AND OJ_DSB_YM = #{baseYm}
   AND CO_CD = '2000'
   AND OG_TP_CD = 'W06'
   AND OG_CD = #{ogCd}
   /*AND CNR_AW_CNFM_DTM IS NOT NULL*/

   AND HDOF_AW_CNFM_DTM IS NOT NULL

   AND DTA_DL_YN = 'N'

mapper.insert:
INSERT INTO TB_FEAM_EGER_AW_CNFM_BAS (  /*엔지니어수당확정기본*/
     BASE_YM
     , PERF_YM
     , OJ_DSB_YM
     , CO_CD
     , OG_TP_CD
     , OG_CD
     , PRTNR_NO
     , CNR_AW_CNFM_DTM
     , DTA_DL_YN         /* 데이터삭제여부 */

)
VALUES (
    #{baseYm}
    , #{baseYm}
    , #{baseYm}
    , '2000'
    , 'W06'
    , #{ogCd}
    , #{prtnrNo}
    , TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
    , 'N'

)

mapper.update:
        MERGE INTO TB_FEAM_EGER_AW_CNFM_BAS
             USING DUAL
                ON (BASE_YM = #{baseYm}
					AND PERF_YM = #{baseYm}
					AND OJ_DSB_YM = #{baseYm}
					AND CO_CD = '2000'
					AND OG_TP_CD = 'W06'
					AND DTA_DL_YN = 'N'
				)
        WHEN MATCHED THEN
             UPDATE SET HDOF_AW_CNFM_DTM = TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')

        WHEN NOT MATCHED THEN
             INSERT
                  ( BASE_YM
					, PERF_YM
					, OJ_DSB_YM
					, CO_CD
					, OG_TP_CD
					, OG_CD
					, PRTNR_NO
					, HDOF_AW_CNFM_DTM
					, DTA_DL_YN

                  ) VALUES
                  ( #{baseYm}
					, #{baseYm}
					, #{baseYm}
					, '2000'
					, 'W06'
					, #{ogCd}
					, #{prtnrNo}
					, TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
					, 'N'

                  )

UPDATE TB_FEAM_EGER_AW_CNFM_BAS
   SET CNR_AW_CNFM_DTM = NULL

 WHERE 1=1
   AND BASE_YM = #{baseYm}
   AND PERF_YM = #{baseYm}
   AND OJ_DSB_YM = #{baseYm}
   AND CO_CD = '2000'
   AND OG_TP_CD = 'W06'
   AND OG_CD = #{ogCd}
   AND DTA_DL_YN = 'N'
