mapper.select:
SELECT C.BASE_YM AS MNGT_YM , C.OG_NM AS BELONG , C.PRTNR_KNM AS EMPL_NM , C.PRTNR_NO , C.CNTR_DT AS RGST_YM , B.HMST_GD_VAL AS CL_DV_CD , C.DGR3_LEVL_DG_PRTNR_NM AS BRMGR_EMPL_NM , C.DGR3_LEVL_DG_PRTNR_NO AS BRMGR_PRTNR_NO , '저장' AS SAVE , '상세보기' AS POINT_HISTORY FROM (SELECT A.BASE_YM /*관리년월*/ , A.OG_NM , A.PRTNR_KNM , A.PRTNR_NO , A.CNTR_DT , B.DGR3_LEVL_DG_PRTNR_NO , B.DGR3_LEVL_DG_PRTNR_NM FROM TB_OGBS_MM_PRTNR_IZ A INNER JOIN TB_OGBS_MM_OG_IZ B ON A.BASE_YM = B.BASE_YM AND A.OG_ID = B.OG_ID WHERE A.BASE_YM = #{mngtYm} AND A.OG_TP_CD = 'W03' /*홈마스터*/ AND (A.PSTN_DV_CD = 15 OR (A.PSTN_DV_CD = 7 AND RSB_DV_CD = 'W0301')) AND A.OG_CD LIKE 'V%'
AND A.PRTNR_NO = #{prtnrNo}
AND A.PRTNR_KNM LIKE '%' || #{emplNm} || '%'
)C LEFT OUTER JOIN (SELECT B.PRTNR_NO , B.HMST_GD_VAL FROM TB_FEAM_HMST_GD_BAS B WHERE 1 = 1 AND MNGT_YM = #{mngtYm} )B ON C.PRTNR_NO = B.PRTNR_NO WHERE 1 = 1 ORDER BY C.OG_NM

mapper.insert:
MERGE INTO TB_FEAM_HMST_GD_BAS A
USING DUAL
  ON( A.PRTNR_NO = #{prtnrNo}
  AND A.MNGT_YM = #{mngtYm})
 WHEN MATCHED THEN
UPDATE SET A.HMST_GD_VAL = #{clDvCd}
     , A.FNL_MDFC_DTM = TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
     , A.FNL_MDFC_USR_ID = #{session.userId}
     , A.FNL_MDFC_PRG_ID = 'KSS_WELLS'
     , A.FNL_MDFC_DEPT_ID = #{session.ogId}
WHEN NOT MATCHED THEN
INSERT(A.MNGT_YM
     , A.PRTNR_NO
     , A.HMST_GD_VAL
     , A.FST_RGST_DTM
     , A.FST_RGST_USR_ID
     , A.FST_RGST_PRG_ID
     , A.FST_RGST_DEPT_ID
     , A.FNL_MDFC_DTM
     , A.FNL_MDFC_USR_ID
     , A.FNL_MDFC_PRG_ID
     , A.FNL_MDFC_DEPT_ID
)
VALUES(#{mngtYm}
     , #{prtnrNo}
     , #{clDvCd}

)

mapper.delete:
DELETE
  FROM TB_FEAM_HMST_GD_IZ
 WHERE 1 = 1
   AND MNGT_YM = #{mngtYm}
   AND PRTNR_NO = #{prtnrNo}
