mapper.select:
SELECT T1.BASE_YM /* 발생월 (기준년월) */ , CASE WHEN T1.LIF_CNTR_STAT_CD = '0' THEN '수수료' WHEN T1.LIF_CNTR_STAT_CD = '1' THEN '되물림' WHEN T1.LIF_CNTR_STAT_CD = '2' THEN '연체' WHEN T1.LIF_CNTR_STAT_CD = '3' THEN '재지급' ELSE 'Error' END CNTR_STAT , T3.OG_CD /* 소속 (지점CD) */ , T1.BRMGR_PRTNR_NO /* 사번 - 지점장 */ , T3.PRTNR_KNM /* 성명 - 지점장 */ , T3.RSB_DV_CD /* 직책명 - 지점장 */ , COUNT(1) AS BRCH_CT /* 건수 - 지점 */ , TF.BASE_FEE * COUNT(1) AS BRCH_AMT /* 금액 - 지점 (40000 * CNT) */ , T1.FEE_DSB_YM /* 수수료 지급월 */ , T1.FEE_REDF_YM /* 수수료 되물림월 */ , CASE WHEN T1.ET_CNFM_DV_CD = '00' THEN 'Y' ELSE 'N' END ET_CNFM_DV_NM /* 라이프 확정 */ FROM TB_IFIN_LIF_ALNC_FEE_CNTR_IZ T1 /* 라이프제휴수수료계약내역 */ INNER JOIN TB_SSCT_CNTR_BAS T2 /* 계약기본 20230502 - 조건절을 위해 태이블 추가 */ ON T2.CNTR_NO = T1.CNTR_NO INNER JOIN TB_OGBS_MM_PRTNR_IZ T3 /* 월파트너 */ ON T1.BASE_YM = T3.BASE_YM AND T1.BRMGR_PRTNR_NO = T3.PRTNR_NO AND T3.OG_TP_CD = T2.SELL_OG_TP_CD INNER JOIN ( SELECT TO_NUMBER(SUBSTR(CALF_CN, INSTR(CALF_CN, '*', 1, 1) + 1, LENGTHB(CALF_CN) - INSTR(CALF_CN, '*', 1, 1))) BASE_FEE FROM TB_FEAM_FEE_BASE_DTL WHERE FEE_CD = 'W010022' /* W010021(15직급개인), W010022(조직) */ AND TO_CHAR(SYSDATE, 'YYYYMM') BETWEEN APY_STRT_YM AND APY_END_YM) TF /* 상조 FEE 기준금액 */ ON 1=1 INNER JOIN ( SELECT LIF_CNTR_NO --라이프계약번호 , BRMGR_PRTNR_NO --지점장 파트너 번호 FROM TB_IFIN_LIF_ALNC_FEE_CNTR_IZ WHERE BASE_YM = #{baseYm} AND LIF_CNTR_STAT_CD = '1' AND CAN_DT <> SUBSTR(CNTR_DT,1,6) GROUP BY LIF_CNTR_NO, BRMGR_PRTNR_NO ) T7 /* 기지급을 구하기 위한 지급회수 COUNT */ ON T1.LIF_CNTR_NO = T7.LIF_CNTR_NO AND T1.BRMGR_PRTNR_NO = T7.BRMGR_PRTNR_NO WHERE 1=1 AND T1.LIF_CNTR_STAT_CD = '0' AND T1.FLPYM_TN = 1 AND T1.PRTNR_NO != T1.BRMGR_PRTNR_NO
AND T1.CNTR_DT >= #{strtDt} AND T1.CNTR_DT <= #{endDt}
AND CASE WHEN T1.LIF_PD_CD = '2178' THEN '429' ELSE '599' END = #{pdCd}
AND T1.DTA_DL_YN = 'N' GROUP BY T1.BASE_YM , T1.LIF_CNTR_STAT_CD , T3.OG_CD , T1.BRMGR_PRTNR_NO , T3.PRTNR_KNM , T3.RSB_DV_CD , T1.FEE_DSB_YM , T1.FEE_REDF_YM , TF.BASE_FEE , T1.ET_CNFM_DV_CD

mapper.update:
UPDATE TB_IFIN_LIF_ALNC_FEE_CNTR_IZ A
   SET A.CNFM_YN        = 'Y'
    , A.FEE_REDF_YM     = #{baseYm}

WHERE  A.BASE_YM           = #{baseYm}
  AND  A.ET_CNFM_DV_CD     = '00'
  AND  A.LIF_CNTR_STAT_CD  = '2'

UPDATE TB_IFIN_LIF_ALNC_FEE_CNTR_IZ A
   SET A.CNFM_YN          = 'Y'
     , A.FEE_DSB_YM       = #{baseYm}

 WHERE A.BASE_YM           = #{baseYm}
   AND A.ET_CNFM_DV_CD     = '00'
   AND A.LIF_CNTR_STAT_CD  = '3'
