SELECT PRTNR_NO , FEE_ATC_VAL AS B_AKSD05 /* P조직 기준일 이전수수료 */ , DSB_YM FROM TB_FEAM_FEE_DSB_DTL_IZ WHERE DSB_YM < #{perfYm} AND DSB_YM >= '201904' AND OG_TP_CD = 'W02' /*M조직*/ AND FEE_TCNT_DV_CD = #{feeTcntDvCd} /*차수구분*/
AND PRTNR_NO = #{prtnrNo}
AND FEE_CD IN ('W020080', 'W020081', 'W020082') )T1 LEFT OUTER JOIN ( SELECT PRTNR_NO , MIN(UPGR_YM) AS UPGR_YM FROM TB_OGPS_TOPMR_PLAR_APLC_IZ WHERE OG_TP_CD = 'W01' AND QLF_DV_CD = '1' /* 수석플래너 */ AND UPGR_MCN > 0 /* 승급개월수가 0 이상, 강등되지 않은 */ AND UPGR_DMTN_DV_CD = '1' /* 승급 */ AND MNGT_YM IN (#{perfYm}, TO_CHAR(ADD_MONTHS(TO_DATE(#{perfYm},'YYYYMM'),-1),'YYYYMM')) /* 실적월, 실적 전월 조회 */ GROUP BY PRTNR_NO ) T2 ON T2.PRTNR_NO = T1.PRTNR_NO WHERE T1.DSB_YM < NVL(T2.UPGR_YM,'000001') /*수석플래너등록 이전*/ GROUP BY T1.PRTNR_NO )MM ON A.PRTNR_NO = MM.PRTNR_NO WHERE A.BASE_YM = #{perfYm} AND A.OG_TP_CD = 'W01' /*P조직*/
AND A.PRTNR_NO = #{prtnrNo}
AND A.PSTN_DV_CD = 7

SELECT A.OG_CD AS OG_NM /*소속*/ , A.PRTNR_NO /*번호*/ , A.PRTNR_KNM /*성명*/ , A.RSB_DV_CD /*직책*/ , NVL(P_CNT.AKCUIL,0) AS AKCUIL /*미팅일수*/ , CASE WHEN A.PSTN_DV_CD = 15 AND NVL(A.QLF_DV_CD , '2') = '2' THEN '플래너' WHEN A.PSTN_DV_CD = 15 AND NVL(A.QLF_DV_CD , '2') = '1' THEN '수석플래너' WHEN A.PSTN_DV_CD = 7 THEN '지점장' ELSE '' END AS JAGYUK /*수수료월 자격*/ , CASE WHEN F.PRTNR_NO IS NOT NULL THEN CASE WHEN F.PSTN_DV_CD = 15 AND NVL(F.QLF_DV_CD , '2') = '2' THEN '플래너' WHEN F.PSTN_DV_CD = 15 AND NVL(F.QLF_DV_CD , '2') = '1' THEN '수석플래너' WHEN F.PSTN_DV_CD = 7 THEN '지점장' WHEN F.PSTN_DV_CD = 4 THEN '단장' WHEN F.PSTN_DV_CD = 2 THEN '총괄단장장' END ELSE '해약' END AS N_JAGYUK1 /* 자격(M+1) */ , G.IS11EDU /* 플래너스타트업 */ , G.IS17EDU /* 수석(플래너)실전 */ , SUBSTR(A.CNTR_DT,1,6) AS CNTR_DT/*등록기준월*/ , SUBSTR(A.FST_CNTR_DT,1,6) AS FST_CNTR_DT/*최초업무등록월*/ , SUBSTR(A.RCNTR_DT,1,6) AS RCNTR_DT/*재등록월*/ , SUBSTR(A.FNL_CLTN_DT,1,6) AS FNL_CLTN_DT/*최종해약월*/ , SUBSTR(A.CLTN_DT,1,6) AS CLTN_DT/*업무해약월*/ , NVL(P_CNT.RUNCNT,0) AS RUNCNT /* 실활동인원 */ , NVL(P_CNT.GADCNT,0) AS GADCNT /* 가동인원 */ , CASE WHEN A.PSTN_DV_CD = 7 THEN CASE WHEN A.PRFMT_DT > 0 AND A.PRFMT_DMTN_DV_CD = 0 THEN SUBSTR(A.PRFMT_DT,1,6) ELSE CASE WHEN AA.PRFMT_DT > 0 AND AA.PRFMT_DMTN_DV_CD = 0 THEN SUBSTR(AA.PRFMT_DT,1,6) ELSE '' END END ELSE SUBSTR(A.PRFMT_DT,1,6) END AS AKDRIY /* 승진월 */ , SUBSTR(PAI.UPGR_YM,1,6) AS AKSTYM /*수석승급월*/ , H.CV_DT AS LCECAYMD /*웰스매니저 개시일 */ , NVL(MM.B_AKSD05,0) AS B_AKSD05 /* 정착수수료 = 이전 정착수수료*/ , NVL(B.AKCDA0, 0) AS AKCDA0 /* 가전 */ , NVL(B.AKCDA1, 0) AS AKCDA1 /* 가전외 */ , NVL(B.AKCDA2, 0) AS AKCDA2 /* 기변 */ , NVL(EE.P_CNT, 0) AS AKSQ01 /* 당월 상조 */ , NVL(EE.G_CNT, 0) AS N_AKSQ01 /* 상조 유지 */ , NVL(B1.AKCDA0, 0) AS AKCDAG0 /* 지점 가전 */ , NVL(B1.AKCDA1, 0) AS AKCDAG1 /* 지점 가전외 */ , NVL(B1.AKSQ02, 0) AS AKSQG02 /* 지점 상조 */ , NVL(B.AKCDA3, 0) AS AKCDA3 /* 환경(정액) */ , NVL(B.AKCDA4, 0) AS AKCDA4 /* 환경외(정액) */ , NVL(B.AKDEQ5, 0) AS AKDEQ5 /* 라이브팩 건수 */ , NVL(B.EC5AMT, 0) AS EC5AMT /* 홈케어 판매가 */ , NVL(B.MPRODUCT,0) AS MPRODUCT /* 관리상품 판매건수 */ , NVL(D.AKSD23, 0) AS AKSD23 /* 정액수수료 */ , NVL(D.AKSD24, 0) AS AKSD24 /* 라이브팩 수당 */ , NVL(D.AKSD25, 0) AS AKSD25 /* 홈케어 수장 */ , NVL(D.AKSD01, 0) AS AKSD01 /* 개인비례 가전 */ , NVL(D.AKSD02, 0) AS AKSD02 /* 개인비례 가전외 */ , NVL(D.AKSD03, 0) AS AKSD03 /* 개인 판매장려 */ , NVL(D.AKSD21, 0) AS AKSD21 /* 개인상조 */ , NVL(D.AKSD04, 0) AS AKSD04 /* 미팅 */ , NVL(D.AKSD05, 0) AS AKSD05 /* 정착 */ , NVL(D.AKSD20, 0) AS AKSD20 /* 재지급 */ , NVL(D.AKSD30, 0) AS AKSD30 /* 기타지원 */ , NVL(D.AKSD11, 0) AS AKSD11 /* 가전조직비례 */ , NVL(D.AKSD12, 0) AS AKSD12 /* 가전조직외 비례 */ , NVL(D.AKSD13, 0) AS AKSD13 /* 조직판매장려 */ , NVL(D.AKSD16, 0) AS AKSD16 /* 조직배출1 */ , NVL(D.AKSD17, 0) AS AKSD17 /* 조직배출2 */ , NVL(D.AKSD15, 0) AS AKSD15 /* 신설 */ , NVL(D.AKSD18, 0) AS AKSD18 /* 승진수수료 */ , NVL(D.AKSD22, 0) AS AKSD22 /* 지점장상조 */ , NVL(C.INTBS_AMT, 0) AS AKSD99 /* 과표합계 */ , NVL(C.DDCTAM, 0) AS DDCTAM /* 공제 */ , NVL(C.DSB_OJ_AMT, 0) AS DSB_OJ_AMT /* 실지급 */ FROM TB_OGBS_MM_PRTNR_IZ A LEFT OUTER JOIN TB_OGBS_MM_PRTNR_IZ AA ON AA.OG_TP_CD = 'W02' AND AA.PRTNR_NO = A.PRTNR_NO AND AA.BASE_YM = ( SELECT MAX(X.BASE_YM) FROM TB_OGBS_MM_PRTNR_IZ X WHERE X.OG_TP_CD = 'W02' AND X.PRTNR_NO = AA.PRTNR_NO ) INNER JOIN TB_OGBS_MM_OG_IZ O ON A.BASE_YM = O.BASE_YM AND A.OG_ID = O.OG_ID
AND O.DGR1_LEVL_OG_ID = #{ogLevl1Id}
AND O.DGR2_LEVL_OG_ID = #{ogLevl2Id}
AND O.DGR3_LEVL_OG_ID = #{ogLevl3Id}
LEFT OUTER JOIN ( SELECT M.PRTNR_NO , M.OG_TP_CD , SUM(AKCDA0) AS AKCDA0 , SUM(AKCDA1) AS AKCDA1 , SUM(AKCDA2) AS AKCDA2 , SUM(AKCDA3) AS AKCDA3 , SUM(AKCDA4) AS AKCDA4 , SUM(AKDEQ5) AS AKDEQ5 , SUM(EC5AMT) AS EC5AMT , SUM(MPRODUCT) AS MPRODUCT FROM ( SELECT AK17.PRTNR_NO , AK17.OG_TP_CD , CASE WHEN AK17.PERF_ATC_CD = 'W01P00001' THEN PERF_VAL END AS AKCDA0 /* 가전 */ , CASE WHEN AK17.PERF_ATC_CD = 'W01P00004' THEN PERF_VAL END AS AKCDA1 /* 가전외 */ , CASE WHEN AK17.PERF_ATC_CD = 'W00P00028' THEN PERF_VAL END AS AKCDA2 /* 기변 */ , CASE WHEN AK17.PERF_ATC_CD = 'W00P00029' THEN PERF_VAL END AS AKCDA3 /* 환경(정액) */ , CASE WHEN AK17.PERF_ATC_CD = 'W00P00030' THEN PERF_VAL END AS AKCDA4 /* 환경외(정액) */ , CASE WHEN AK17.PERF_ATC_CD = 'W00P00009' THEN PERF_VAL END AS AKDEQ5 /* 라이브팩 건수 */ , CASE WHEN AK17.PERF_ATC_CD = 'W01P00016' THEN PERF_VAL END AS EC5AMT /* 홈케어 판매가 */ , CASE WHEN AK17.PERF_ATC_CD = 'W01P00018' THEN PERF_VAL END AS MPRODUCT /* 관리상품 판매건수 */ FROM TB_FEAM_NTORP_PERF_MM_CL AK17 /*순주문 파트너 실적 월마감*/ WHERE AK17.FEE_TCNT_DV_CD = #{feeTcntDvCd} AND AK17.BASE_YM = #{perfYm} AND AK17.PERF_DV_CD = 0 /*개인판매*/ AND AK17.PERF_AGRG_CRT_DV_CD = '101' /* P추진단순주문실적생성 */
AND AK17.PRTNR_NO = #{prtnrNo}
AND AK17.OG_TP_CD = 'W01' /*웰스 P조직*/ )M GROUP BY M.PRTNR_NO, M.OG_TP_CD )B ON A.PRTNR_NO = B.PRTNR_NO AND A.OG_TP_CD = B.OG_TP_CD LEFT OUTER JOIN ( SELECT M.PRTNR_NO , M.OG_TP_CD , SUM(AKCDA0) AS AKCDA0 , SUM(AKCDA1) AS AKCDA1 , SUM(AKSQ02) AS AKSQ02 FROM ( SELECT AK17.PRTNR_NO , AK17.OG_TP_CD , CASE WHEN AK17.PERF_ATC_CD = 'W01P00001' THEN PERF_VAL END AS AKCDA0 /* 가전 */ , CASE WHEN AK17.PERF_ATC_CD = 'W01P00004' THEN PERF_VAL END AS AKCDA1 /* 가전외 */ , CASE WHEN AK17.PERF_ATC_CD = 'W01P00034' THEN PERF_VAL END AS AKSQ02 /* 조직 상조실적 */ FROM TB_FEAM_NTORP_PERF_MM_CL AK17 /*순주문 파트너 실적 월마감*/ WHERE AK17.FEE_TCNT_DV_CD = #{feeTcntDvCd} AND AK17.BASE_YM = #{perfYm} AND AK17.PERF_DV_CD = 2 AND AK17.PERF_AGRG_CRT_DV_CD = '101' /* P추진단순주문실적생성 */
AND AK17.PRTNR_NO = #{prtnrNo}
AND AK17.OG_TP_CD = 'W01' /*웰스 P조직*/ )M GROUP BY M.PRTNR_NO, M.OG_TP_CD )B1 ON A.PRTNR_NO = B1.PRTNR_NO AND A.OG_TP_CD = B1.OG_TP_CD LEFT OUTER JOIN TB_OGPS_TOPMR_PLAR_APLC_IZ PAI ON PAI.MNGT_YM = #{perfYm} AND PAI.OG_TP_CD = 'W01' AND PAI.QLF_DV_CD = '1' /* 수석플래너 */ AND PAI.UPGR_MCN > 0 /* 승급개월수가 0 이상, 강등되지 않은 */ AND PAI.UPGR_DMTN_DV_CD = '1' /* 승급 */ AND PAI.PRTNR_NO = A.PRTNR_NO LEFT OUTER JOIN TB_FEAM_FEE_DSB_IZ C ON A.PRTNR_NO = C.PRTNR_NO AND A.BASE_YM = C.DSB_YM AND A.OG_TP_CD = C.OG_TP_CD AND C.FEE_TCNT_DV_CD = #{feeTcntDvCd} LEFT OUTER JOIN ( SELECT PRTNR_NO , DSB_YM , CO_CD , OG_TP_CD , FEE_TCNT_DV_CD , SPMT_DSB_DV_CD , NVL(SUM(AKSD01),0) AS AKSD01 /* 개인비례 가전 */ , NVL(SUM(AKSD02),0) AS AKSD02 /* 개인비례 가전외 */ , NVL(SUM(AKSD03),0) AS AKSD03 /* 개인 판매장려 */ , NVL(SUM(AKSD21),0) AS AKSD21 /* 개인상조 */ , NVL(SUM(AKSD04),0) AS AKSD04 /* 미팅 */ , NVL(SUM(AKSD05),0) AS AKSD05 /* 정착 */ , NVL(SUM(AKSD20),0) AS AKSD20 /* 재지급 */ , NVL(SUM(AKSD30),0) AS AKSD30 /* 기타수수료 */ , NVL(SUM(AKSD24),0) AS AKSD24 /* 라이브팩 수당 */ , NVL(SUM(AKSD25),0) AS AKSD25 /* 홈케어 수장 */ , NVL(SUM(AKSD11),0) AS AKSD11 /* 조직비례 가전 */ , NVL(SUM(AKSD12),0) AS AKSD12 /* 조직비례 가전 외 */ , NVL(SUM(AKSD13),0) AS AKSD13 /* 조직 판매장려 */ , NVL(SUM(AKSD16),0) AS AKSD16 /* 조직배출1 */ , NVL(SUM(AKSD17),0) AS AKSD17 /* 조직배출2 */ , NVL(SUM(AKSD15),0) AS AKSD15 /* 신설 */ , NVL(SUM(AKSD22),0) AS AKSD22 /* 지점장상조 */ , NVL(SUM(AKSD23),0) AS AKSD23 /* 정액수수료 */ , NVL(SUM(AKSD18),0) AS AKSD18 /* 승진수수료 */ FROM ( SELECT PRTNR_NO , DSB_YM , CO_CD , OG_TP_CD , FEE_TCNT_DV_CD , SPMT_DSB_DV_CD , CASE WHEN (FEE_CD = 'W010001' OR FEE_CD = 'W010101') THEN FEE_ATC_VAL ELSE 0 END AS AKSD01 /* 개인비례 가전 */ , CASE WHEN (FEE_CD = 'W010002' OR FEE_CD = 'W010102') THEN FEE_ATC_VAL ELSE 0 END AS AKSD02 /* 개인비례 가전외 */ , CASE WHEN (FEE_CD = 'W010003' OR FEE_CD = 'W010103') THEN FEE_ATC_VAL ELSE 0 END AS AKSD03 /* 개인 판매장려 */ , CASE WHEN (FEE_CD = 'W010021' OR FEE_CD = 'W010121') THEN FEE_ATC_VAL ELSE 0 END AS AKSD21 /* 개인상조 */ , CASE WHEN FEE_CD = 'W010004' THEN FEE_ATC_VAL ELSE 0 END AS AKSD04 /* 미팅 */ , CASE WHEN (FEE_CD = 'W010005' OR FEE_CD = 'W010105') THEN FEE_ATC_VAL ELSE 0 END AS AKSD05 /* 정착 */ , CASE WHEN (FEE_CD = 'W010020' OR FEE_CD = 'W010120') THEN FEE_ATC_VAL ELSE 0 END AS AKSD20 /* 재지급 */ , CASE WHEN (FEE_CD = 'W010030' OR FEE_CD = 'W010130') THEN FEE_ATC_VAL ELSE 0 END AS AKSD30 /* 기타수수료 */ , CASE WHEN (FEE_CD = 'W010024' OR FEE_CD = 'W010124') THEN FEE_ATC_VAL ELSE 0 END AS AKSD24 /* 라이브팩 수당 */ , CASE WHEN (FEE_CD = 'W010025' OR FEE_CD = 'W010125') THEN FEE_ATC_VAL ELSE 0 END AS AKSD25 /* 홈케어 수장 */ , CASE WHEN FEE_CD = 'W010011' THEN FEE_ATC_VAL ELSE 0 END AS AKSD11 /* 조직비례 가전 */ , CASE WHEN FEE_CD = 'W010012' THEN FEE_ATC_VAL ELSE 0 END AS AKSD12 /* 조직비례 가전 외 */ , CASE WHEN FEE_CD = 'W010013' THEN FEE_ATC_VAL ELSE 0 END AS AKSD13 /* 조직 판매장려 */ , CASE WHEN FEE_CD = 'W010016' THEN FEE_ATC_VAL ELSE 0 END AS AKSD16 /* 조직배출1 */ , CASE WHEN FEE_CD = 'W010017' THEN FEE_ATC_VAL ELSE 0 END AS AKSD17 /* 조직배출2 */ , CASE WHEN FEE_CD = 'W010015' THEN FEE_ATC_VAL ELSE 0 END AS AKSD15 /* 신설 */ , CASE WHEN FEE_CD = 'W010022' THEN FEE_ATC_VAL ELSE 0 END AS AKSD22 /* 지점장상조 */ , CASE WHEN (FEE_CD = 'W010023' OR FEE_CD = 'W010123') THEN FEE_ATC_VAL ELSE 0 END AS AKSD23 /* 정액수수료 */ , CASE WHEN (FEE_CD = 'W010018' OR FEE_CD = 'W010118') THEN FEE_ATC_VAL ELSE 0 END AS AKSD18 /* 승진수수료 */ FROM TB_FEAM_FEE_DSB_DTL_IZ WHERE DSB_YM = #{perfYm} AND OG_TP_CD = 'W01' AND FEE_TCNT_DV_CD = #{feeTcntDvCd} AND FEE_ATC_DV_CD = '01'
AND PRTNR_NO = #{prtnrNo}
AND FEE_CD IN ('W010001', 'W010101', 'W010002', 'W010102', 'W010003', 'W010103', 'W010021', 'W010121' , 'W010004', 'W010005', 'W010105', 'W010020', 'W010120', 'W010030', 'W010130', 'W010024' , 'W010124', 'W010025', 'W010125', 'W010011', 'W010012', 'W010013', 'W010016', 'W010017' , 'W010015', 'W010022', 'W010023', 'W010123', 'W010018', 'W010118') ) GROUP BY PRTNR_NO, DSB_YM, CO_CD, OG_TP_CD, FEE_TCNT_DV_CD, SPMT_DSB_DV_CD )D ON C.PRTNR_NO = D.PRTNR_NO AND C.DSB_YM = D.DSB_YM AND C.OG_TP_CD = D.OG_TP_CD AND C.FEE_TCNT_DV_CD = D.FEE_TCNT_DV_CD AND C.CO_CD = D.CO_CD AND C.SPMT_DSB_DV_CD = D.SPMT_DSB_DV_CD LEFT OUTER JOIN ( SELECT Z.PRTNR_NO , SUM(Z.P_CNT) AS P_CNT , SUM(Z.G_CNT) AS G_CNT FROM ( SELECT PRTNR_NO , COUNT(1) AS P_CNT , 0 AS G_CNT FROM TB_IFIN_LIF_ALNC_FEE_CNTR_IZ A /* 상조 건수*/ WHERE BASE_YM = #{perfYm} AND CAN_DT IS NULL AND FLPYM_TN = 1 AND LIF_CNTR_STAT_CD = '0' GROUP BY PRTNR_NO UNION ALL SELECT PRTNR_NO , 0 AS P_CNT , COUNT(1) AS G_CNT FROM TB_IFIN_LIF_ALNC_FEE_CNTR_IZ A WHERE BASE_YM = #{perfYm} AND CAN_DT IS NULL AND FLPYM_TN > 1 AND LIF_CNTR_STAT_CD = '0' GROUP BY PRTNR_NO ) Z GROUP BY Z.PRTNR_NO ) EE ON ( A.PRTNR_NO = EE.PRTNR_NO ) LEFT OUTER JOIN TB_OGBS_MM_PRTNR_IZ F ON A.PRTNR_NO = F.PRTNR_NO AND A.OG_TP_CD = F.OG_TP_CD AND F.BASE_YM = TO_CHAR(ADD_MONTHS(TO_DATE(#{perfYm}, 'YYYYMM'), 1),'YYYYMM' ) LEFT OUTER JOIN ( SELECT M.PRTNR_NO , SUM(RUNCNT) AS RUNCNT , SUM(GADCNT) AS GADCNT , SUM(AKCUIL) AS AKCUIL FROM ( SELECT A.PRTNR_NO , CASE WHEN A.PERF_ATC_CD = 'W01P00020' THEN PERF_VAL END AS RUNCNT /* 실활동인원 */ , CASE WHEN A.PERF_ATC_CD = 'W01P00035' THEN PERF_VAL END AS GADCNT /* 가동인원 */ , CASE WHEN A.PERF_ATC_CD = 'W01P00012' THEN PERF_VAL END AS AKCUIL /* 미팅참석일수 */ FROM TB_FEAM_PRTNR_PERF_MM_CL A /*순주문 파트너 실적 월마감*/ WHERE A.BASE_YM = #{perfYm} AND A.FEE_TCNT_DV_CD = #{feeTcntDvCd} AND A.PERF_AGRG_CRT_DV_CD IN ('101','103') /* P추진단순주문실적생성, P조직미팅참석집계 */ AND A.PERF_ATC_CD IN ('W01P00020', 'W01P00035', 'W01P00012') AND A.CO_CD = '2000' AND A.OG_TP_CD = 'W01' /*웰스 P조직*/
AND A.PRTNR_NO = #{prtnrNo}
AND A.PERF_VAL > 0 )M GROUP BY M.PRTNR_NO ) P_CNT ON A.PRTNR_NO = P_CNT.PRTNR_NO LEFT OUTER JOIN ( SELECT PRTNR_NO , MAX(IS11EDU) AS IS11EDU , MAX(IS17EDU) AS IS17EDU FROM ( SELECT PRTNR_NO , CASE WHEN EDUC_CRSE_NO = '14' THEN EDUC_CRSE_CRT_BASE_YM END AS IS11EDU , CASE WHEN EDUC_CRSE_NO = '19' THEN EDUC_CRSE_CRT_BASE_YM END AS IS17EDU FROM TB_PSCA_MCPTN_EDUC_IZ WHERE EDUC_CRSE_CRT_BASE_YM = #{perfYm} AND OG_TP_CD = 'W01' AND EDUC_CRSE_NO IN ('14','19') AND EDUC_CPC_ACKMT_YN = 'Y' ) GROUP BY PRTNR_NO )G ON A.PRTNR_NO = G.PRTNR_NO LEFT OUTER JOIN ( SELECT PRTNR_NO , MIN(STRTDT) AS CV_DT FROM TB_OGPS_PLAR_QLF_CH_IZ /*플래너 자격 변경내역*/ WHERE QLF_DV_CD = '3' /* 웰스매니저 */ AND DTA_DL_YN = 'N'
AND PRTNR_NO = #{prtnrNo}
GROUP BY PRTNR_NO )H ON A.PRTNR_NO = H.PRTNR_NO LEFT OUTER JOIN ( SELECT T1.PRTNR_NO , NVL(SUM(T1.B_AKSD05),0) AS B_AKSD05 FROM ( SELECT PRTNR_NO , FEE_ATC_VAL AS B_AKSD05 /* P조직 기준일 이전수수료 */ , DSB_YM FROM TB_FEAM_FEE_DSB_DTL_IZ WHERE DSB_YM < #{perfYm} AND OG_TP_CD = 'W01' /*P조직*/ AND FEE_TCNT_DV_CD = '02' /*차수구분=2차확정*/
AND PRTNR_NO = #{prtnrNo}
AND FEE_CD IN ('W010005', 'W010105') UNION ALL SELECT PRTNR_NO , FEE_ATC_VAL AS B_AKSD05 /* P조직 기준일 이전수수료 */ , DSB_YM FROM TB_FEAM_FEE_DSB_DTL_IZ WHERE DSB_YM < #{perfYm} AND DSB_YM >= '201904' AND OG_TP_CD = 'W02' /*M조직*/ AND FEE_TCNT_DV_CD = '02' /*차수구분*/
AND PRTNR_NO = #{prtnrNo}
AND FEE_CD IN ('W020080', 'W020081', 'W020082') )T1 LEFT OUTER JOIN ( SELECT PRTNR_NO , MIN(UPGR_YM) AS UPGR_YM FROM TB_OGPS_TOPMR_PLAR_APLC_IZ WHERE OG_TP_CD = 'W01' AND QLF_DV_CD = '1' /* 수석플래너 */ AND UPGR_MCN > 0 /* 승급개월수가 0 이상, 강등되지 않은 */ AND UPGR_DMTN_DV_CD = '1' /* 승급 */ AND MNGT_YM IN (#{perfYm}, TO_CHAR(ADD_MONTHS(TO_DATE(#{perfYm},'YYYYMM'),-1),'YYYYMM')) /* 실적월, 실적 전월 조회 */ GROUP BY PRTNR_NO ) T2 ON T2.PRTNR_NO = T1.PRTNR_NO WHERE T1.DSB_YM < NVL(T2.UPGR_YM,'000001') /*수석플래너등록 이전*/ GROUP BY T1.PRTNR_NO )MM ON A.PRTNR_NO = MM.PRTNR_NO WHERE A.BASE_YM = #{perfYm} AND A.OG_TP_CD = 'W01' /*P조직*/
AND A.PRTNR_NO = #{prtnrNo}
AND ( A.PSTN_DV_CD = 7 OR A.PSTN_DV_CD = 15 )
