/* com.kyowon.sms.wells.web.fee.calculation.mapper.WfebOutcomeAllowanceMapper.selectOutcomeAllowancesManagerThisMonth */ SELECT C.OG_CD /* 소속 */ , C.OG_NM /* 소속명 */ , C.PRTNR_KNM /* 성명 */ , C.PRTNR_NO /* 번호 */ , C.DTRC_N /* 지국수(당월) */ , C.BASE_DTRC_N /* 지국수(고정) */ , C.BRCH_NINC_CT /* 지점순증 */ , C.TRG_CT /* 목표 */ , C.PERF_CT /* 실적 */ , C.TRG_ACHV_RT /* 달성률 */ , C.NW_SELL_ACC_CT /* 신규판매 */ , C.PUR_SPR_ACC_CT /* 순수이탈 */ , C.ACC_NINC_CT /* 계정순증 */ , C.FEE_ACKMT_CT /* 인정건수 */ , C.ELHM_EXCP_PERF_AMT /* 가전외 금액 */ , C.ELHM_EXCP_CT /* 가전외 인정건수 */ , C.REDF_CT /* 되물림 건수 */ , C.PERF_AGG_AMT /* 누계 */ , C.MNGR_DANG_GD /* 관리자위험 등급 */ , C.WM_ACL_ACTI_CT /* 웰스매니저 실활동 수 */ , C.BFSVC_ACL_ACTI_CT /* 프리매니저 실활동 수 */ , C.ACL_ACTI_BRCH_AV_CT /* 실활동(지평) */ , C.ACTV_BRCH_N /* 활성지점 */ , NVL(AT.AW_AMT,0) * C.PERF_AGG_AMT - C.MNGR_DANG_GD AS TRG_ACHV_AW_AMT /* 목표달성 수당(인정건수) = 누계 X 목표달성수당 테이블(달성율 and 계정순증) 금액 - 관리자 위험지수 차감액 */ , NVL(AO.AW_AMT,0) * C.ACTV_BRCH_N AS OG_AW_AMT /* 조직수당 = 활성지점수 X 조직수당 테이블(실활동 매니저 지평 and 지점 순증) */ , NVL(AT.AW_AMT,0) * C.PERF_AGG_AMT - C.MNGR_DANG_GD + NVL(AO.AW_AMT,0) * C.ACTV_BRCH_N AS DSB_AMT /* 지급계 TRG_ACHV_AW_AMT + OG_AW_AMT */ FROM ( SELECT PERF.OG_CD , PERF.OG_NM , PERF.PRTNR_NM AS PRTNR_KNM , PERF.PRTNR_NO , NVL(MO.DTRC_N, 0) AS DTRC_N , NVL(OB.DTRC_N, 0) AS BASE_DTRC_N , NVL(MO.DTRC_N, 0) - NVL(OB.DTRC_N, 0) AS BRCH_NINC_CT /* 지점순증 = 당월 지점수 - 23년4월1일자 지점수 */ , NVL(ST.CNTR_BASE_TRG_IST_CT,0) AS TRG_CT /* 목표(설치) */ , PERF.PERF_CT , CASE WHEN NVL(ST.CNTR_BASE_TRG_IST_CT, 0) > 0 THEN ROUND(PERF.PERF_CT/ST.CNTR_BASE_TRG_IST_CT * 100, 1) ELSE 0 END AS TRG_ACHV_RT /* 달성률 = 실적 / 목표 * 100 */ , PERF.FEE_ACKMT_CT , PERF.ELHM_EXCP_PERF_AMT , TRUNC(PERF.ELHM_EXCP_PERF_AMT/500000) AS ELHM_EXCP_CT , PERF.NW_SELL_ACC_CT , NVL(CANC.PUR_SPR_ACC_CT,0) AS PUR_SPR_ACC_CT , PERF.NW_SELL_ACC_CT - NVL(CANC.PUR_SPR_ACC_CT,0) AS ACC_NINC_CT , PERF.FEE_ACKMT_CT + TRUNC(PERF.ELHM_EXCP_PERF_AMT/500000) - NVL(REDF.REDF_CT,0) AS PERF_AGG_AMT /* 누계 = [인정건수] + [가전외 건수] - [되물림 건수] */ , 0 AS MNGR_DANG_GD /* 관리자 위험등급 */ , PERF.WM_ACL_ACTI_CT , PERF.BFSVC_ACL_ACTI_CT , (PERF.WM_ACL_ACTI_CT+PERF.BFSVC_ACL_ACTI_CT) AS ACL_ACTI_ACHV_CT , CASE WHEN NVL(OB.DTRC_N,0) > 0 THEN ROUND((PERF.WM_ACL_ACTI_CT+PERF.BFSVC_ACL_ACTI_CT) / NVL(OB.DTRC_N,0), 2) ELSE 0 END AS ACL_ACTI_BRCH_AV_CT /* 실활동(지평) = 당월 실활동 인원 ÷ 23년 4월1일 지점수 */ , PERF.OPTN_ACHV_CT , PERF.THM1_OPTN_ACHV_CT , PERF.ACTV_BRCH_N , NVL(REDF.REDF_CT,0) AS REDF_CT FROM (
) PERF /* 실적 계산 */ LEFT OUTER JOIN (
) CANC /* 취소 계산 */ ON PERF.OG_CD = CANC.OG_CD LEFT OUTER JOIN (
) REDF /* 되물림 계산 */ ON REDF.OG_CD = PERF.OG_CD LEFT OUTER JOIN ( SELECT T.BASE_YM
, T.GNLR_LEDR_PRTNR_NO AS PRTNR_NO
, T.LCMGR_PRTNR_NO AS PRTNR_NO
, SUM(T.CNTR_BASE_TRG_IST_CT) AS CNTR_BASE_TRG_IST_CT , SUM(T.CNTR_BASE_TRG_RCP_CT) AS CNTR_BASE_TRG_RCP_CT , SUM(T.WELS_MNGER_TRG_OPTN_PSNO) AS WELS_MNGER_TRG_OPTN_PSNO , SUM(T.TOPMR_PLAR_TRG_OPTN_PSNO) AS TOPMR_PLAR_TRG_OPTN_PSNO , SUM(T.WELS_PLAR_TRG_OPTN_PSNO) AS WELS_PLAR_TRG_OPTN_PSNO , SUM(T.BRMGR_TRG_OPTN_PSNO) AS BRMGR_TRG_OPTN_PSNO FROM TB_IFIN_WELS_MCBY_SELL_TRG_BAS T WHERE T.BASE_YM = #{perfYm} GROUP BY T.BASE_YM
, T.GNLR_LEDR_PRTNR_NO
, T.LCMGR_PRTNR_NO
) ST /* 단장 목표 (BI 연계 테이블) */ ON ST.PRTNR_NO = PERF.PRTNR_NO INNER JOIN TB_OGBS_MM_OG_IZ MO ON MO.OG_ID = PERF.OG_ID AND MO.BASE_YM = #{perfYm} LEFT OUTER JOIN TB_OGBS_MM_OG_IZ OB ON OB.OG_ID = PERF.OG_ID AND OB.BASE_YM = '202304' ) C INNER JOIN TB_FEAM_MNGR_AW_BASE_INF_IZ AT /* 관리자수당기준정보내역 : 목표수당 */ ON AT.MNGR_OUTC_DV_CD = 'TAR' AND #{perfYm} BETWEEN AT.APY_STRT_YM AND AT.APY_END_YM AND AT.DTA_DL_YN = 'N' AND AT.OG_TP_CD = #{ogTpCd} AND AT.RSB_DV_CD = SUBSTR(#{rsbDvCd},4,2) AND AT.ACHV_STRT_RAT <= C.TRG_ACHV_RT AND AT.ACHV_END_RAT > C.TRG_ACHV_RT AND AT.NINC_STRT_CT <= CASE WHEN C.ACC_NINC_CT < 0 THEN 0 WHEN C.ACC_NINC_CT > 998 THEN 998 ELSE C.ACC_NINC_CT END AND AT.NINC_END_CT > CASE WHEN C.ACC_NINC_CT < 0 THEN 0 WHEN C.ACC_NINC_CT > 998 THEN 998 ELSE C.ACC_NINC_CT END INNER JOIN TB_FEAM_MNGR_AW_BASE_INF_IZ AO /* 관리자수당기준정보내역 : 조직수당 */ ON AO.MNGR_OUTC_DV_CD = 'ORG' AND #{perfYm} BETWEEN AO.APY_STRT_YM AND AO.APY_END_YM AND AO.DTA_DL_YN = 'N' AND AO.OG_TP_CD = #{ogTpCd} AND AO.RSB_DV_CD = SUBSTR(#{rsbDvCd},4,2) AND AO.ACHV_STRT_RAT <= CASE WHEN C.ACL_ACTI_BRCH_AV_CT < 0 THEN 0 ELSE C.ACL_ACTI_BRCH_AV_CT END AND AO.ACHV_END_RAT > CASE WHEN C.ACL_ACTI_BRCH_AV_CT < 0 THEN 0 ELSE C.ACL_ACTI_BRCH_AV_CT END AND AO.NINC_STRT_CT <= CASE WHEN C.BRCH_NINC_CT < 0 THEN 0 ELSE C.BRCH_NINC_CT END AND AO.NINC_END_CT > CASE WHEN C.BRCH_NINC_CT < 0 THEN 0 ELSE C.BRCH_NINC_CT END WHERE 1=1
AND C.PRTNR_NO = #{prtnrNo}
ORDER BY OG_CD

/* com.kyowon.sms.wells.web.fee.calculation.mapper.WfebOutcomeAllowanceMapper.selectOutcomeAllowancesManager */ SELECT MO.OG_CD /* 소속 */ , MO.OG_NM /* 소속명 */ , MP.PRTNR_KNM /* 성명 */ , MP.PRTNR_NO /* 파트너번호 */ , OA.DTRC_N /* 지국수 */ , OA.TRG_CT /* 목표건수 */ , OA.BASE_DTRC_N /* 기준지국수 */ , OA.PERF_CT /* 실적건수 */ , OA.TRG_ACHV_RT /* 목표달성율 */ , OA.NW_SELL_ACC_CT /* 신규판매계정수 */ , OA.PUR_SPR_ACC_CT /* 순수이탈계정수 */ , OA.ACC_NINC_CT /* 계정순증건수 */ , OA.FEE_ACKMT_CT /* 수수료인정건수 */ , OA.ELHM_EXCP_PERF_AMT /* 가전외실적금액 */ , OA.ELHM_EXCP_CT /* 가전건수 */ , OA.REDF_CT /* 되물림건수 */ , OA.PERF_AGG_AMT /* 실적누계금액 */ , OA.MNGR_DANG_GD /* 관리자위험등급 */ , OA.TRG_ACHV_AW_AMT /* 목표달성수당금액 */ , OA.BRCH_NINC_CT /* 지점순증건수 */ , OA.WM_ACL_ACTI_CT /* WM실제활동건수 */ , OA.BFSVC_ACL_ACTI_CT /* BS실제활동건수 */ , OA.ACL_ACTI_BRCH_AV_CT /* 실제활동지점평균건수 */ , OA.ACTV_BRCH_N /* 활성지점수 */ , OA.OG_AW_AMT /* 조직수당금액 */ , OA.DSB_AMT /* 지급금액 */ FROM TB_FEAM_MOGMNGR_OUTC_AW_MM_IZ OA INNER JOIN TB_OGBS_MM_PRTNR_IZ MP ON MP.OG_TP_CD = OA.OG_TP_CD AND MP.BASE_YM = OA.BASE_YM AND MP.PRTNR_NO = OA.PRTNR_NO INNER JOIN TB_OGBS_MM_OG_IZ MO ON MO.BASE_YM = MP.BASE_YM AND MO.OG_ID = MP.OG_ID WHERE 1=1 AND OA.BASE_YM = #{perfYm} AND OA.OG_TP_CD = #{ogTpCd} AND OA.RSB_DV_CD = #{rsbDvCd}
AND OA.PRTNR_NO = #{prtnrNo}
ORDER BY MO.OG_CD

/* com.kyowon.sms.wells.web.fee.calculation.mapper.WfebOutcomeAllowanceMapper.selectOutcomeAllowancesPlannerThisMonth */ SELECT C.OG_CD /* 소속 */ , C.OG_NM /* 소속명 */ , C.PRTNR_KNM /* 성명 */ , C.PRTNR_NO /* 번호 */ , C.TRG_CT /* 목표 */ , C.PERF_CT /* 실적 */ , C.TRG_ACHV_RT /* 달성률 */ , NVL(AT.AW_AMT,0) * C.PERF_CT AS TRG_ACHV_AW_AMT /* 목표달성 수당(인정건수) = 실적 X 목표달성수당 테이블(달성율 and 계정순증) 금액 */ , C.THM1_OPTN_TRG_CT /* 1차월가동목표건수 */ , C.THM1_OPTN_ACHV_CT /* 1차월가동달성건수 */ , C.THM1_OPTN_ACHV_RT /* 1차월가동달성율 */ , NVL(AO1.AW_AMT,0) * C.THM1_OPTN_ACHV_CT AS THM1_OPTN_AW_AMT /* 1차월가동수당금액 = */ , C.ACL_ACTI_TRG_CT /* 실제활동목표건수 */ , C.ACL_ACTI_ACHV_CT /* 실제활동달성건수 */ , C.ACL_ACTI_ACHV_RT /* 실제활동달성율 */ , NVL(AO2.AW_AMT,0) * C.ACL_ACTI_ACHV_CT AS ACL_ACTI_AW_AMT /* 실제활동수당금액 */ , NVL(AO1.AW_AMT,0) * C.THM1_OPTN_ACHV_CT + NVL(AO2.AW_AMT,0) * C.ACL_ACTI_ACHV_CT AS OG_ACHV_AW_SUM_AMT /* 조직달성수당합계금액 */ , 0 AS EJT_AW_AMT /* 배출수당금액 */ , NVL(AO1.AW_AMT,0) * C.THM1_OPTN_ACHV_CT + NVL(AO2.AW_AMT,0) * C.ACL_ACTI_ACHV_CT + NVL(AT.AW_AMT,0) * C.PERF_CT AS OUTC_AW_SUM_AMT /* 성과수당합계금액 */ FROM ( SELECT PERF.OG_CD , PERF.OG_NM , PERF.PRTNR_NM AS PRTNR_KNM , PERF.PRTNR_NO , NVL(MO.DTRC_N, 0) AS DTRC_N , NVL(OB.DTRC_N, 0) AS BASE_DTRC_N , NVL(MO.DTRC_N, 0) - NVL(OB.DTRC_N, 0) AS BRCH_NINC_CT /* 지점순증 = 당월 지점수 - 23년4월1일자 지점수 */ , NVL(ST.CNTR_BASE_TRG_IST_CT,0) AS TRG_CT /* 목표(설치) */ , PERF.PERF_CT + NVL(LIF.PL429CNT, 0) + NVL(LIF.PL599CNT, 0) AS PERF_CT , CASE WHEN NVL(ST.CNTR_BASE_TRG_IST_CT, 0) > 0 THEN ROUND((PERF.PERF_CT + NVL(LIF.PL429CNT, 0) + NVL(LIF.PL599CNT, 0))/ST.CNTR_BASE_TRG_IST_CT * 100, 2) ELSE 0 END AS TRG_ACHV_RT /* 달성률 = 실적 / 목표 * 100 */ , PERF.NW_SELL_ACC_CT , CASE WHEN NVL(OB.DTRC_N,0) > 0 THEN ROUND((PERF.WM_ACL_ACTI_CT+PERF.BFSVC_ACL_ACTI_CT) / NVL(OB.DTRC_N,0), 2) ELSE 0 END AS ACL_ACTI_BRCH_AV_CT /* 실활동(지평) = 당월 실활동 인원 ÷ 23년 4월1일 지점수 */ , PERF.THM1_OPTN_ACHV_CT , PERF.ACL_ACTI_ACHV_CT , NVL(TC.ACL_ACTI_TRG_CT, 0) AS ACL_ACTI_TRG_CT , NVL(TC.THM1_OPTN_TRG_CT, 0) AS THM1_OPTN_TRG_CT , NVL(TC.OPTN_TRG_CT, 0) AS OPTN_TRG_CT , CASE WHEN NVL(TC.THM1_OPTN_TRG_CT, 0) > 0 THEN ROUND(PERF.THM1_OPTN_ACHV_CT/TC.THM1_OPTN_TRG_CT * 100, 1) ELSE 0 END AS THM1_OPTN_ACHV_RT /* 달성률 = 실적 / 목표 * 100 */ , CASE WHEN NVL(TC.ACL_ACTI_TRG_CT, 0) > 0 THEN ROUND(PERF.ACL_ACTI_ACHV_CT/TC.ACL_ACTI_TRG_CT * 100, 1) ELSE 0 END AS ACL_ACTI_ACHV_RT /* 달성률 = 실적 / 목표 * 100 */ FROM (
) PERF /* 실적 계산 */ LEFT OUTER JOIN (
) TC /* 실활동, 1차월 가동 목표 */ ON TC.OG_ID = PERF.OG_ID LEFT OUTER JOIN (
) LIF /* 연계 상품(라이프) 실적 계산 */ ON LIF.OG_ID = PERF.OG_ID LEFT OUTER JOIN ( SELECT T.BASE_YM
, T.GNLR_LEDR_PRTNR_NO AS PRTNR_NO
, T.LCMGR_PRTNR_NO AS PRTNR_NO
, SUM(T.CNTR_BASE_TRG_IST_CT) AS CNTR_BASE_TRG_IST_CT , SUM(T.CNTR_BASE_TRG_RCP_CT) AS CNTR_BASE_TRG_RCP_CT , SUM(T.WELS_MNGER_TRG_OPTN_PSNO) AS WELS_MNGER_TRG_OPTN_PSNO , SUM(T.TOPMR_PLAR_TRG_OPTN_PSNO) AS TOPMR_PLAR_TRG_OPTN_PSNO , SUM(T.WELS_PLAR_TRG_OPTN_PSNO) AS WELS_PLAR_TRG_OPTN_PSNO , SUM(T.BRMGR_TRG_OPTN_PSNO) AS BRMGR_TRG_OPTN_PSNO FROM TB_IFIN_WELS_MCBY_SELL_TRG_BAS T WHERE T.BASE_YM = #{perfYm} GROUP BY T.BASE_YM
, T.GNLR_LEDR_PRTNR_NO
, T.LCMGR_PRTNR_NO
) ST /* 단장 목표 (BI 연계 테이블) */ ON ST.PRTNR_NO = PERF.PRTNR_NO INNER JOIN ( SELECT
MO.DGR1_LEVL_OG_ID AS OG_ID
MO.DGR2_LEVL_OG_ID AS OG_ID
, COUNT(1) AS DTRC_N FROM TB_OGBS_MM_OG_IZ MO WHERE MO.BASE_YM = #{perfYm} AND MO.OG_TP_CD = #{ogTpCd} AND MO.OG_LEVL_DV_CD = '3' GROUP BY
MO.DGR1_LEVL_OG_ID
MO.DGR2_LEVL_OG_ID
) MO ON MO.OG_ID = PERF.OG_ID LEFT OUTER JOIN ( SELECT
MO.DGR1_LEVL_OG_ID AS OG_ID
MO.DGR2_LEVL_OG_ID AS OG_ID
, COUNT(1) AS DTRC_N FROM TB_OGBS_MM_OG_IZ MO WHERE MO.BASE_YM = '202304' AND MO.OG_TP_CD = #{ogTpCd} AND MO.OG_LEVL_DV_CD = '3' GROUP BY
MO.DGR1_LEVL_OG_ID
MO.DGR2_LEVL_OG_ID
) OB ON OB.OG_ID = PERF.OG_ID ) C INNER JOIN TB_FEAM_MNGR_AW_BASE_INF_IZ AT /* 관리자수당기준정보내역 : 목표수당 */ ON AT.MNGR_OUTC_DV_CD = 'TAR' AND #{perfYm} BETWEEN AT.APY_STRT_YM AND AT.APY_END_YM AND AT.DTA_DL_YN = 'N' AND AT.OG_TP_CD = #{ogTpCd} AND AT.RSB_DV_CD = SUBSTR(#{rsbDvCd},4,2) AND AT.ACHV_STRT_RAT <= C.TRG_ACHV_RT AND AT.ACHV_END_RAT > C.TRG_ACHV_RT INNER JOIN TB_FEAM_MNGR_AW_BASE_INF_IZ AO1 /* 관리자수당기준정보내역 : 1차월 가동목표달성 */ ON AO1.MNGR_OUTC_DV_CD = 'OPT' AND #{perfYm} BETWEEN AO1.APY_STRT_YM AND AO1.APY_END_YM AND AO1.DTA_DL_YN = 'N' AND AO1.OG_TP_CD = #{ogTpCd} AND AO1.RSB_DV_CD = SUBSTR(#{rsbDvCd}, 4, 2) AND AO1.ACHV_STRT_RAT <= C.THM1_OPTN_ACHV_RT AND AO1.ACHV_END_RAT > C.THM1_OPTN_ACHV_RT LEFT JOIN TB_FEAM_MNGR_AW_BASE_INF_IZ AO2 /* 관리자수당기준정보내역 : 실활동목표달성 */ ON AO2.MNGR_OUTC_DV_CD = 'ACT' AND #{perfYm} BETWEEN AO2.APY_STRT_YM AND AO2.APY_END_YM AND AO2.DTA_DL_YN = 'N' AND AO2.OG_TP_CD = #{ogTpCd} AND AO2.RSB_DV_CD = SUBSTR(#{rsbDvCd}, 4, 2) AND AO2.ACHV_STRT_RAT <= C.ACL_ACTI_ACHV_RT AND AO2.ACHV_END_RAT > C.ACL_ACTI_ACHV_RT WHERE 1=1
AND C.PRTNR_NO = #{prtnrNo}
ORDER BY C.OG_CD

/* com.kyowon.sms.wells.web.fee.calculation.mapper.WfebOutcomeAllowanceMapper.selectOutcomeAllowancesPlanner */ SELECT MO.OG_NM /* 소속명 */ , MP.PRTNR_KNM /* 성명 */ , MP.PRTNR_NO /* 파트너번호 */ , OA.TRG_CT /* 목표건수 */ , OA.PERF_CT /* 실적건수 */ , OA.TRG_ACHV_RT /* 목표달성율 */ , OA.TRG_ACHV_AW_AMT /* 목표달성수당금액 */ , OA.THM1_OPTN_TRG_CT /* 1차월가동목표건수 */ , OA.THM1_OPTN_ACHV_CT /* 1차월가동달성건수 */ , OA.THM1_OPTN_ACHV_RT /* 1차월가동달성율 */ , OA.THM1_OPTN_AW_AMT /* 1차월가동수당금액 */ , OA.ACL_ACTI_TRG_CT /* 실제활동목표건수 */ , OA.ACL_ACTI_ACHV_CT /* 실제활동달성건수 */ , OA.ACL_ACTI_ACHV_RT /* 실제활동달성율 */ , OA.ACL_ACTI_AW_AMT /* 실제활동수당금액 */ , OA.OG_ACHV_AW_SUM_AMT /* 조직달성수당합계금액 */ , OA.EJT_AW_AMT /* 배출수당금액 */ , OA.OUTC_AW_SUM_AMT /* 성과수당합계금액 */ FROM TB_FEAM_POGMNGR_OUTC_AW_MM_IZ OA INNER JOIN TB_OGBS_MM_PRTNR_IZ MP ON MP.OG_TP_CD = OA.OG_TP_CD AND MP.BASE_YM = OA.BASE_YM AND MP.PRTNR_NO = OA.PRTNR_NO INNER JOIN TB_OGBS_MM_OG_IZ MO ON MO.BASE_YM = MP.BASE_YM AND MO.OG_ID = MP.OG_ID WHERE 1=1 AND OA.BASE_YM = #{perfYm} AND OA.OG_TP_CD = #{ogTpCd} AND OA.RSB_DV_CD = #{rsbDvCd}
AND OA.PRTNR_NO = #{prtnrNo}
ORDER BY MO.OG_CD

