INSERT INTO TB_FEDD_FEE_REDF_ADSB_DTL ( /* 수수료되물림재지급상세 */ FEE_REDF_ADSB_DTL_ID /* 수수료되물림재지급상세ID */ , REDF_ADSB_OC_YM /* 되물림재지급발생년월 */ , PERF_YM /* 실적년월 */ , PERF_DV_CD /* 실적구분코드 */ , CO_CD /* 회사코드 */ , OG_TP_CD /* 조직유형코드 */ , PRTNR_NO /* 파트너번호 */ , CNTR_NO /* 계약번호 */ , CNTR_SN /* 계약일련번호 */ , FEE_CD /* 수수료코드 */ , DTA_CRT_FEE_CD /* 데이터생성수수료코드 */ , REDF_ADSB_DV_CD /* 되물림재지급구분코드 */ , REDF_ADSB_TP_CD /* 되물림재지급유형코드 */ , REDF_ADSB_OC_AMT /* 되물림재지급발생금액 */ , REDF_ADSB_CTR_CNFM_AMT /* 되물림재지급조정확정금액 */
) WITH DATA AS (SELECT T1.BASE_YM , T1.PRTNR_NO , T1.OG_TP_CD , T1.BRMGR_PRTNR_NO , T1.CNTR_NO , T1.CNTR_SN , T1.LIF_CNTR_OC_TN FROM TB_IFIN_LIF_ALNC_FEE_CNTR_IZ T1 INNER JOIN TB_IFIN_LIF_ALNC_FEE_CNTR_IZ T2 ON (T1.LIF_CNTR_NO = T2.LIF_CNTR_NO AND T1.OG_TP_CD = T2.OG_TP_CD) WHERE T2.FEE_REDF_YM = #{baseYm} AND T2.LIF_CNTR_STAT_CD = '2' /* 연체 */ AND T1.LIF_CNTR_STAT_CD = '0' AND NVL(T1.FEE_DSB_YM, '0') > '0' AND T1.OG_TP_CD = #{ogTpCd} AND T2.CNFM_YN = 'Y' AND LENGTH(NVL(T1.FEE_DSB_YM, '0')) > 1 /* 지급받은 데이터 체크 */ AND NVL(T1.FEE_DSB_YM, '0') < NVL(T2.FEE_REDF_YM,'0')) SELECT ${feeRedfAdsbDtlId} AS FEE_REDF_ADSB_DTL_ID , #{baseYm} AS REDF_ADSB_OC_YM , PERF_YM , PERF_DV_CD , '2000' AS CO_CD , #{ogTpCd} AS OG_TP_CD , PRTNR_NO , CNTR_NO , CNTR_SN , FEE_CD , FEE_CD AS DTA_CRT_FEE_CD , '02' AS REDF_ADSB_DV_CD /* 되물림 */ , '0203' AS REDF_ADSB_TP_CD /* 연체되물림 */ , REDF_ADSB_OC_AMT , REDF_ADSB_OC_AMT AS REDF_ADSB_CTR_CNFM_AMT
FROM (SELECT T1.BASE_YM AS PERF_YM , '0' AS PERF_DV_CD , T1.PRTNR_NO , T1.CNTR_NO , T1.CNTR_SN , CASE WHEN T1.PRTNR_NO = T1.BRMGR_PRTNR_NO THEN #{brmgrIndvFeeCd} ELSE #{plarIndvFeeCd} END AS FEE_CD , COUNT(1) * 50000 AS REDF_ADSB_OC_AMT FROM DATA T1 INNER JOIN TB_FEAM_FEE_CALC_BAS BAS /* 해당 실적년월에 수수료를 지급받은 이력이 있는지 체크 */ ON (T1.BASE_YM = BAS.BASE_YM AND T1.OG_TP_CD = BAS.OG_TP_CD AND T1.PRTNR_NO = BAS.PRTNR_NO AND BAS.FEE_TCNT_DV_CD = '02' AND BAS.SPMT_DSB_DV_CD = '01' AND BAS.FEE_CALC_TP_CD = '01' AND BAS.FEE_DSB_MTHD_CD = '01' AND BAS.FEE_CD = CASE WHEN T1.PRTNR_NO = T1.BRMGR_PRTNR_NO THEN #{brmgrIndvFeeCd} ELSE #{plarIndvFeeCd} END) GROUP BY T1.BASE_YM, T1.PRTNR_NO, T1.CNTR_NO, T1.CNTR_SN, T1.BRMGR_PRTNR_NO UNION ALL SELECT T1.BASE_YM AS PERF_YM , '2' AS PERF_DV_CD , T1.BRMGR_PRTNR_NO AS PRTNR_NO , T1.CNTR_NO , T1.CNTR_SN , #{brmgrOgFeeCd} AS FEE_CD , COUNT(1) * 40000 AS REDF_ADSB_OC_AMT FROM DATA T1 INNER JOIN TB_FEAM_FEE_CALC_BAS BAS /* 해당 실적년월에 수수료를 지급받은 이력이 있는지 체크 */ ON (T1.BASE_YM = BAS.BASE_YM AND T1.OG_TP_CD = BAS.OG_TP_CD AND T1.BRMGR_PRTNR_NO = BAS.PRTNR_NO AND BAS.FEE_TCNT_DV_CD = '02' AND BAS.SPMT_DSB_DV_CD = '01' AND BAS.FEE_CALC_TP_CD = '01' AND BAS.FEE_DSB_MTHD_CD = '01' AND BAS.FEE_CD = #{brmgrOgFeeCd}) WHERE T1.LIF_CNTR_OC_TN = 1 AND T1.PRTNR_NO != T1.BRMGR_PRTNR_NO GROUP BY T1.BASE_YM, T1.BRMGR_PRTNR_NO, T1.CNTR_NO, T1.CNTR_SN)

INSERT INTO TB_FEDD_FEE_REDF_ADSB_DTL ( /* 수수료되물림재지급상세 */ FEE_REDF_ADSB_DTL_ID /* 수수료되물림재지급상세ID */ , REDF_ADSB_OC_YM /* 되물림재지급발생년월 */ , PERF_YM /* 실적년월 */ , PERF_DV_CD /* 실적구분코드 */ , CO_CD /* 회사코드 */ , OG_TP_CD /* 조직유형코드 */ , PRTNR_NO /* 파트너번호 */ , CNTR_NO /* 계약번호 */ , CNTR_SN /* 계약일련번호 */ , FEE_CD /* 수수료코드 */ , DTA_CRT_FEE_CD /* 데이터생성수수료코드 */ , REDF_ADSB_DV_CD /* 되물림재지급구분코드 */ , REDF_ADSB_TP_CD /* 되물림재지급유형코드 */ , REDF_ADSB_OC_AMT /* 되물림재지급발생금액 */ , REDF_ADSB_CTR_CNFM_AMT /* 되물림재지급조정확정금액 */
) SELECT ${feeRedfAdsbDtlId} AS FEE_REDF_ADSB_DTL_ID , #{baseYm} AS REDF_ADSB_OC_YM , PERF_YM , PERF_DV_CD , CO_CD , OG_TP_CD , PRTNR_NO , CNTR_NO , CNTR_SN , OG_TP_CD || '9999999' AS FEE_CD , OG_TP_CD || '9999999' AS DTA_CRT_FEE_CD , '02' AS REDF_ADSB_DV_CD /* 되물림 */ , '0203' AS REDF_ADSB_TP_CD /* 연체되물림 */ , REDF_ADSB_OC_AMT , REDF_ADSB_OC_AMT AS REDF_ADSB_CTR_CNFM_AMT
FROM ( SELECT T1.PERF_YM , '0' AS PERF_DV_CD , T1.CO_CD , T1.OG_TP_CD , T1.PRTNR_NO , T1.CNTR_NO , T1.CNTR_SN , CASE WHEN T2.SELL_TP_CD = '1' THEN T2.FEE_ACKMT_BASE_AMT * 0.04 -- 할부계약인 경우 ELSE T2.FEE_ACKMT_BASE_AMT * 2 END AS REDF_ADSB_OC_AMT -- 렌탈/정기배송 경우 FROM TB_FEAM_WELS_NRCTR_MM_CL T1 INNER JOIN TB_SSCT_CNTR_DTL T2 ON (T1.CNTR_NO = T2.CNTR_NO AND T1.CNTR_SN = T2.CNTR_SN) WHERE T1.BASE_YM = #{baseYm} AND T1.CNTR_PERF_CRT_DV_CD = #{cntrPerfCrtDvCd} /* 되물림 */ AND T1.PERF_TP_CD = '10' /* 연체) */ AND T2.FEE_ACKMT_BASE_AMT > 0 AND T1.DTA_DL_YN = 'N' AND T2.DTA_DL_YN = 'N' AND EXISTS(SELECT 1 FROM TB_FEAM_FEE_CALC_BAS BAS WHERE T1.PERF_YM = BAS.BASE_YM AND T1.OG_TP_CD = BAS.OG_TP_CD AND T1.PRTNR_NO = BAS.PRTNR_NO AND BAS.FEE_TCNT_DV_CD = '02' AND BAS.SPMT_DSB_DV_CD = '01' AND BAS.FEE_CALC_TP_CD = '01') UNION ALL SELECT T1.PERF_YM , '2' AS PERF_DV_CD , T1.CO_CD , T1.OG_TP_CD , T3.HOO_PRTNR_NO AS PRTNR_NO , T1.CNTR_NO , T1.CNTR_SN , CASE WHEN T2.SELL_TP_CD = '1' THEN T2.FEE_ACKMT_BASE_AMT * 0.02 -- 할부계약인 경우 ELSE T2.FEE_ACKMT_BASE_AMT END AS REDF_ADSB_OC_AMT -- 렌탈/정기배송 경우 FROM TB_FEAM_WELS_NRCTR_MM_CL T1 INNER JOIN TB_FEAM_MM_PRTNR_HOO_IZ T3 ON (T1.PERF_YM = T3.BASE_YM AND T1.OG_TP_CD = T3.OG_TP_CD AND T1.PRTNR_NO = T3.PRTNR_NO) INNER JOIN TB_SSCT_CNTR_DTL T2 ON (T1.CNTR_NO = T2.CNTR_NO AND T1.CNTR_SN = T2.CNTR_SN) WHERE T1.BASE_YM = #{baseYm} AND T1.CNTR_PERF_CRT_DV_CD = #{cntrPerfCrtDvCd} /* 되물림 */ AND T1.PERF_TP_CD = '10' /* 연체) */ AND T2.FEE_ACKMT_BASE_AMT > 0 AND T1.DTA_DL_YN = 'N' AND T2.DTA_DL_YN = 'N' AND T3.PRTNR_NO != T3.HOO_PRTNR_NO AND T3.PERF_DV_CD = '2' AND EXISTS(SELECT 1 FROM TB_FEAM_FEE_CALC_BAS BAS WHERE T3.BASE_YM = BAS.BASE_YM AND T3.OG_TP_CD = BAS.OG_TP_CD AND T3.HOO_PRTNR_NO = BAS.PRTNR_NO AND BAS.FEE_TCNT_DV_CD = '02' AND BAS.SPMT_DSB_DV_CD = '01' AND BAS.FEE_CALC_TP_CD = '01'))

/* 총판 기본수수료 연체 되물림 - 기본수수료(W050001) */ INSERT INTO TB_FEDD_FEE_REDF_ADSB_DTL( FEE_REDF_ADSB_DTL_ID /* 수수료되물림재지급상세ID */ , REDF_ADSB_OC_YM /* 되물림재지급발생년월 */ , PERF_YM /* 실적년월 */ , PERF_DV_CD /* 실적구분코드 */ , CO_CD /* 회사코드 */ , OG_TP_CD /* 조직유형코드 */ , PRTNR_NO /* 파트너번호 */ , CNTR_NO /* 계약번호 */ , CNTR_SN /* 계약일련번호 */ , FEE_CD /* 수수료코드 */ , DTA_CRT_FEE_CD /* 데이터생성수수료코드 */ , REDF_ADSB_DV_CD /* 되물림재지급구분코드 */ , REDF_ADSB_TP_CD /* 되물림재지급유형코드 */ , REDF_ADSB_OC_AMT /* 되물림재지급발생금액 */ , REDF_ADSB_CTR_CNFM_AMT /* 되물림재지급조정확정금액 */
) /* 최종수정부서ID */ WITH DATA AS (SELECT T01.PERF_YM /* 실적년월 */ , T10.PERF_DV_CD /* 실적구분코드 */ , HOO.PERF_DV_CD AS HOO_PERF_DV_CD /* 조직장실적구분코드 */ , T01.CO_CD /* 회사코드 */ , T01.OG_TP_CD /* 조직유형코드 */ , T01.PRTNR_NO AS HOO_PRTNR_NO /* 조직장파트너번호 */ , T10.PRTNR_NO /* 파트너번호 */ , T10.CNTR_NO /* 계약번호 */ , T10.CNTR_SN /* 계약일련번호 */ , TRUNC(NVL(T10.PERF_VAL, 0)) AS REDF_ADSB_OC_AMT /* 되물림재지급발생금액 */ FROM TB_FEAM_WELS_NRCTR_MM_CL T02 INNER JOIN TB_FEAM_NTORP_CNTR_MM_CL T10/* 순주문파트너계약월마감 */ ON (T02.PERF_YM = T10.PERF_YM AND T02.FEE_TCNT_DV_CD = T10.FEE_TCNT_DV_CD AND T02.CNTR_NO = T10.CNTR_NO AND T02.CNTR_SN = T10.CNTR_SN AND T02.OG_TP_CD = T10.OG_TP_CD AND T02.PRTNR_NO = T10.PRTNR_NO) INNER JOIN TB_FEAM_MM_PRTNR_HOO_IZ HOO /* 월파트너조직장내역 */ ON (T10.PERF_YM = HOO.BASE_YM AND T10.PRTNR_NO = HOO.PRTNR_NO AND T10.OG_TP_CD = HOO.OG_TP_CD) INNER JOIN TB_FEAM_FEE_CALC_BAS T01 /* 수수료계산계약내역 */ ON (T01.PERF_YM = T10.PERF_YM AND T01.OJ_DSB_YM = T10.PERF_YM AND T01.CO_CD = T10.CO_CD AND T01.OG_TP_CD = T10.OG_TP_CD AND T01.FEE_TCNT_DV_CD = T10.FEE_TCNT_DV_CD AND HOO.HOO_PRTNR_NO = T01.PRTNR_NO) WHERE T02.BASE_YM = #{baseYm} AND T02.OG_TP_CD = #{ogTpCd} AND T02.PERF_TP_CD = '10' /* 연체 */ AND T02.PERF_YM BETWEEN TO_CHAR(ADD_MONTHS(TO_DATE(#{baseYm}, 'YYYYMM'), -60), 'YYYYMM') AND TO_CHAR(ADD_MONTHS(TO_DATE(#{baseYm}, 'YYYYMM'), -1), 'YYYYMM') AND T02.CNTR_PERF_CRT_DV_CD = #{cntrPerfCrtDvCd} AND T02.FEE_TCNT_DV_CD = '02' AND T10.PERF_ATC_CD = 'W05P00001' AND T10.PERF_AGRG_CRT_DV_CD = '501' AND T10.DTA_DL_YN = 'N' AND T01.FEE_CD = 'W050001' AND T01.FEE_TCNT_DV_CD = '02' AND T01.FEE_CTR_CNFM_AMT > 0 AND T01.FEE_CALC_TP_CD = '01' AND T01.DTA_DL_YN = 'N' AND TRUNC(NVL(T10.PERF_VAL, 0)) > 0) SELECT ${feeRedfAdsbDtlId} AS FEE_REDF_ADSB_DTL_ID /* 수수료되물림재지급상세ID */ , #{baseYm} AS REDF_ADSB_OC_YM /* 되물림재지급발생년월 */ , PERF_YM /* 실적년월 */ , PERF_DV_CD /* 실적구분코드 */ , CO_CD /* 회사코드 */ , OG_TP_CD /* 조직유형코드 */ , PRTNR_NO /* 파트너번호 */ , CNTR_NO /* 계약번호 */ , CNTR_SN /* 계약일련번호 */ , 'W050001' AS FEE_CD /* 수수료코드 */ , 'W050001' AS DTA_CRT_FEE_CD /* 데이터생성수수료코드 */ , '02' AS REDF_ADSB_DV_CD /* 되물림재지급구분코드 */ , '0203' AS REDF_ADSB_TP_CD /* 되물림재지급유형코드 */ , REDF_ADSB_OC_AMT /* 되물림재지급발생금액 */ , REDF_ADSB_OC_AMT AS REDF_ADSB_CTR_CNFM_AMT /* 되물림재지급조정확정금액 */
FROM (SELECT PERF_YM /* 실적년월 */ , PERF_DV_CD /* 실적구분코드 */ , CO_CD /* 회사코드 */ , OG_TP_CD /* 조직유형코드 */ , PRTNR_NO /* 파트너번호 */ , CNTR_NO /* 계약번호 */ , CNTR_SN /* 계약일련번호 */ , REDF_ADSB_OC_AMT /* 되물림재지급발생금액 */ FROM DATA UNION ALL SELECT PERF_YM /* 실적년월 */ , HOO_PERF_DV_CD AS PERF_DV_CD/* 조직장실적구분코드 */ , CO_CD /* 회사코드 */ , OG_TP_CD /* 조직유형코드 */ , HOO_PRTNR_NO AS PRTNR_NO/* 조직장파트너번호 */ , CNTR_NO /* 계약번호 */ , CNTR_SN /* 계약일련번호 */ , REDF_ADSB_OC_AMT /* 되물림재지급발생금액 */ FROM DATA)
