AND O.PRTNR_NO = #{dto.prtnrNo}
ORDER BY O.DGR1_LEVL_OG_CD , O.DGR2_LEVL_OG_CD , O.DGR3_LEVL_OG_CD ,O.PRTNR_NO

/* P추진단 - 지점장 조회(동적 컬럼) */ SELECT O.BASE_YM /* 기준년월 */ , FC.PERF_YM /* 실적년월 */ , FC.CO_CD /* 회사코드 */ , O.OG_TP_CD /* 조직유형코드 */ , O.DGR1_LEVL_OG_NM /* 총괄단 조직명 */ , O.DGR2_LEVL_OG_NM /* 지역단 조직명 */ , O.DGR3_LEVL_OG_NM /* 지점 조직명 */ , O.PRTNR_NO /* 파트너번호 */ , O.DGR3_LEVL_OG_CD /* 3차레벨조직코드 - 지점 코드*/ , O.PRTNR_KNM /* 파트너명 */ , O.RSB_DV_NM /* 파트너 직급 */ , NVL(PERF.P_W01P00012, 0) AS MEET_DATES /* 미팅 출석일수 */ , NVL(PERF.O_W01P00001, 0) AS O_W01P00001 /* 조직 - 가전실적금액 */ , NVL(PERF.O_W01P00004, 0) AS O_W01P00004 /* 조직 - 가전외실적금액 */ , NVL(PERF.O_W01P00029, 0) AS O_W01P00029 /* 조직 - 합산 실적 */ , NVL(PERF.P_W01P00001, 0) AS P_W01P00001 /* 개인 - 가전 실적 */ , NVL(PERF.P_W01P00004, 0) AS P_W01P00004 /* 개인 - 가전외 실적 */ , NVL(PERF.P_W01P00009, 0) AS P_W01P00009 /* 개인 - 합산 실적 */ , NVL(PERF.P_W00P00028, 0) AS P_W00P00028 /* 개인 - 기변 실적 */ ,${feeCdFields} ,${feeSumField} , NVL(D.DDTN02, 0) AS DDTN02 /* 소득세 */ , NVL(D.DDTN03, 0) AS DDTN03 /* 주민세 */ , NVL(D.DDTN01, 0) AS DDTN01 /* 보증예치금 */ , NVL(D.DDTN04, 0) AS DDTN04 /* 가지급금 */ , NVL(D.DDTN07, 0) AS DDTN07 /* 부담공제 */ , NVL(D.DDTN05, 0) AS DDTN05 /* 고용보험 */ , (NVL(D.DDTN02, 0) + NVL(D.DDTN03, 0) + NVL(D.DDTN01, 0) + NVL(D.DDTN04, 0) + NVL(D.DDTN07, 0) + NVL(D.DDTN05, 0)) AS DDTNSUM /* 공제계 */ , NVL(DSB.DSB_OJ_AMT, 0) AS DSBAMT /* 수수료 지급대상금액 */ FROM ( SELECT * FROM ( SELECT T1.BASE_YM /* 기준년월 */ , T1.PERF_YM /* 실적년월 */ , T1.CO_CD /* 회사코드 */ , T1.OG_TP_CD /* 조직유형코드 */ , T1.PRTNR_NO /* 파트너번호 */ , NVL(SUM(T1.FEE_CTR_CNFM_AMT) ,0) AS FEE_SUM , T3.SRN_MARK_FEE_NM FROM TB_FEAM_FEE_CALC_BAS T1 INNER JOIN ( SELECT FEE_CD FROM TB_FEAM_FEE_CD WHERE 1 = 1 AND FEE_CALC_UNIT_TP_CD = '102' /* 수수료계산단위유형코드 (101 : P추진단 플래너, 102 : P추진단 지국장, 201 : M추진단-일반(15등급), 202 : M추진단 - 지점장(7등급)*/ AND USE_YN ='Y' AND DTA_DL_YN ='N'
AND #{dto.perfDt} BETWEEN APY_STRT_YM AND APY_END_YM
) T2 ON T1.FEE_CD = T2.FEE_CD INNER JOIN TB_FEAM_FEE_BASE_DTL T3 ON T1.FEE_CD = T3.FEE_CD WHERE 1 = 1
AND T1.BASE_YM = #{dto.perfDt} AND #{dto.perfDt} BETWEEN T3.APY_STRT_YM AND T3.APY_END_YM
AND T1.OG_TP_CD = 'W01' AND T1.DTA_DL_YN = 'N' AND T3.DTA_DL_YN = 'N' GROUP BY T1.BASE_YM, T1.CO_CD ,T3.SRN_MARK_FEE_NM ,T1.PERF_YM, T1.PRTNR_NO ,T1.OG_TP_CD ) PIVOT ( SUM(FEE_SUM) FOR SRN_MARK_FEE_NM IN ( ${feeCdInStr} ) ) ) FC INNER JOIN ( SELECT T1.BASE_YM /* 기준년월 */ , T1.DGR1_LEVL_OG_ID /* 1차레벨조직ID */ , T1.DGR1_LEVL_OG_CD /* 1차레벨조직코드 */ , T1.DGR1_LEVL_OG_NM /* 1차레벨조직명 */ , T1.DGR1_LEVL_DG_PRTNR_NO /* 1차레벨대표파트너번호 */ , T1.DGR1_LEVL_DG_PRTNR_NM /* 1차레벨대표파트너명 */ , T1.DGR2_LEVL_OG_ID /* 2차레벨조직ID */ , T1.DGR2_LEVL_OG_CD /* 2차레벨조직코드 */ , T1.DGR2_LEVL_OG_NM /* 2차레벨조직명 */ , T1.DGR2_LEVL_DG_PRTNR_NO /* 2차레벨대표파트너번호 */ , T1.DGR2_LEVL_DG_PRTNR_NM /* 2차레벨대표파트너명 */ , T1.DGR3_LEVL_OG_ID /* 3차레벨조직ID */ , T1.DGR3_LEVL_OG_CD /* 3차레벨조직코드 */ , T1.DGR3_LEVL_OG_NM /* 3차레벨조직명 */ , T1.DGR3_LEVL_DG_PRTNR_NO /* 3차레벨대표파트너번호 */ , T1.DGR3_LEVL_DG_PRTNR_NM /* 3차레벨대표파트너명 */ , T1.DGR4_LEVL_OG_ID /* 4차레벨조직ID */ , T1.DGR4_LEVL_OG_CD /* 4차레벨조직코드 */ , T1.DGR4_LEVL_OG_NM /* 4차레벨조직명 */ , T1.DGR4_LEVL_DG_PRTNR_NO /* 4차레벨대표파트너번호 */ , T1.DGR4_LEVL_DG_PRTNR_NM /* 4차레벨대표파트너명 */ , T1.DGR5_LEVL_OG_ID /* 5차레벨조직ID */ , T1.DGR5_LEVL_OG_CD /* 5차레벨조직코드 */ , T1.DGR5_LEVL_OG_NM /* 5차레벨조직명 */ , T1.DGR5_LEVL_DG_PRTNR_NO /* 5차레벨대표파트너번호 */ , T1.DGR5_LEVL_DG_PRTNR_NM /* 5차레벨대표파트너명 */ , T1.DTRC_N /* 지국수 */ , T2.PRTNR_NO /* 파트너번호 */ , T2.PRTNR_KNM /* 파트너명 */ , T2.RSB_DV_CD /* 직책구분코드 */ , F_CMZ_CD_NM('TNT_WELLS', 'RSB_DV_CD', T2.RSB_DV_CD) AS RSB_DV_NM /* 직책명 */ , T2.OG_TP_CD /* 조직유형코드(W01: P추진단, W02: M추진단) */ FROM TB_OGBS_MM_OG_IZ T1 INNER JOIN TB_OGBS_MM_PRTNR_IZ T2 ON T1.BASE_YM = T2.BASE_YM AND T1.OG_TP_CD = T2.OG_TP_CD AND T1.OG_ID = T2.OG_ID AND T2.DTA_DL_YN = 'N' WHERE 1 = 1 AND T1.DTA_DL_YN = 'N' AND T1.OG_TP_CD = 'W01' /* 조직유형코드(W01: P추진단, W02: M추진단) */ AND T2.RSB_DV_CD IN ('W0104') /* 직책구분코드(W0104 : 지점장) */ ) O ON FC.PERF_YM = O.BASE_YM /* 실적년월 = 관리년월*/ AND FC.PRTNR_NO = O.PRTNR_NO AND FC.OG_TP_CD = O.OG_TP_CD LEFT OUTER JOIN ( SELECT DDTN_YM , OG_TP_CD , PRTNR_NO , SUM(CASE WHEN FEE_DDTN_TP_CD = '01' THEN FEE_DDTN_CNFM_AMT END) AS DDTN01 , SUM(CASE WHEN FEE_DDTN_TP_CD = '02' THEN FEE_DDTN_CNFM_AMT END) AS DDTN02 , SUM(CASE WHEN FEE_DDTN_TP_CD = '03' THEN FEE_DDTN_CNFM_AMT END) AS DDTN03 , SUM(CASE WHEN FEE_DDTN_TP_CD = '04' THEN FEE_DDTN_CNFM_AMT END) AS DDTN04 , SUM(CASE WHEN FEE_DDTN_TP_CD = '05' THEN FEE_DDTN_CNFM_AMT END) AS DDTN05 , SUM(CASE WHEN FEE_DDTN_TP_CD = '06' THEN FEE_DDTN_CNFM_AMT END) AS DDTN06 , SUM(CASE WHEN FEE_DDTN_TP_CD = '07' THEN FEE_DDTN_CNFM_AMT END) AS DDTN07 , SUM(CASE WHEN FEE_DDTN_TP_CD = '08' THEN FEE_DDTN_CNFM_AMT END) AS DDTN08 , SUM(CASE WHEN FEE_DDTN_TP_CD = '88' THEN FEE_DDTN_CNFM_AMT END) AS DDTN88 FROM TB_FEAM_FEE_DDTN_IZ WHERE 1 = 1 AND DTA_DL_YN = 'N' GROUP BY DDTN_YM, OG_TP_CD, PRTNR_NO ) D ON O.BASE_YM = D.DDTN_YM AND O.PRTNR_NO = D.PRTNR_NO AND O.OG_TP_CD = D.OG_TP_CD LEFT OUTER JOIN ( SELECT PERF_YM , PRTNR_NO ,SUM(CASE WHEN (PERF_DV_CD = '2' AND PERF_ATC_CD = 'W01P00001') THEN PERF_VAL END) AS O_W01P00001 /* 조직 - 가전실적금액 */ ,SUM(CASE WHEN (PERF_DV_CD = '2' AND PERF_ATC_CD = 'W01P00004') THEN PERF_VAL END) AS O_W01P00004 /* 조직 - 가전외실적금액 */ ,SUM(CASE WHEN (PERF_DV_CD = '2' AND PERF_ATC_CD = 'W01P00029') THEN PERF_VAL END) AS O_W01P00029 /* 조직 - 순주문실적금액(기변실적제외) */ ,SUM(CASE WHEN (PERF_DV_CD = '0' AND PERF_ATC_CD = 'W01P00012') THEN PERF_VAL END) AS P_W01P00012 /* 개인 - 미팅 출석일수 */ ,SUM(CASE WHEN (PERF_DV_CD = '0' AND PERF_ATC_CD = 'W01P00001') THEN PERF_VAL END) AS P_W01P00001 /* 개인 - 가전실적금액 */ ,SUM(CASE WHEN (PERF_DV_CD = '0' AND PERF_ATC_CD = 'W01P00004') THEN PERF_VAL END) AS P_W01P00004 /* 개인 - 가전외실적금액 */ ,SUM(CASE WHEN (PERF_DV_CD = '0' AND PERF_ATC_CD = 'W00P00028') THEN PERF_VAL END) AS P_W00P00028 /* 개인 - 기변실적금액 */ ,SUM(CASE WHEN (PERF_DV_CD = '0' AND PERF_ATC_CD = 'W01P00009') THEN PERF_VAL END) AS P_W01P00009 /* 개인 - 순주문실적금액 */ FROM TB_FEAM_NTORP_PERF_MM_CL WHERE 1 = 1 AND DTA_DL_YN = 'N' AND PERF_DV_CD IN ('0', '2') /*실적구분코드(0:개인판매, 2:지국(지점)실적) */ GROUP BY PERF_YM, PRTNR_NO ) PERF ON FC.PERF_YM = PERF.PERF_YM AND FC.PRTNR_NO = PERF.PRTNR_NO LEFT OUTER JOIN TB_FEAM_FEE_DSB_IZ DSB ON FC.PERF_YM = DSB.DSB_YM AND FC.CO_CD = DSB.CO_CD AND FC.OG_TP_CD = DSB.OG_TP_CD AND FC.PRTNR_NO = DSB.PRTNR_NO AND DSB.DTA_DL_YN = 'N' WHERE 1 = 1 AND FC.PERF_YM = #{dto.perfDt} /* 실적년월 */
AND O.DGR1_LEVL_OG_CD = #{dto.ogLevel1} /* 조직레벨 - 총괄단 */
AND O.DGR2_LEVL_OG_CD = #{dto.ogLevel2} /* 조직레벨 - 지역단 */
AND O.DGR3_LEVL_OG_CD = #{dto.ogLevel3} /* 조직레벨 - 지점 */
AND O.PRTNR_NO = #{dto.prtnrNo}
ORDER BY O.DGR1_LEVL_OG_CD , O.DGR2_LEVL_OG_CD , O.DGR3_LEVL_OG_CD ,O.PRTNR_NO
