mapper.select:
SELECT OG.DGR1_LEVL_OG_NM AS MNGT_DIV /*총괄단*/ , OG.DGR2_LEVL_OG_NM AS RENL_GRP /*지역단*/ , OG.DGR3_LEVL_OG_NM AS BRANCH /*지점*/ , OG.PRTNR_KNM AS EMPL_NM /*성명*/ , OG.PRTNR_NO /*파트너번호*/ , OG.RSB_DV_CD AS RSB_TP /*직책*/ , OG.QLF_DV_CD AS QLF /*자격*/ , OG.FNIT_CD AS BNK /*은행*/ , OG.ACNO_ENCR AS AC_NO /*계좌번호*/ , NVL(FEE.INTBS_SUM,0) AS INTBS_SUM /*과표계*/ , NVL(FEE.DDTN_SUM,0) AS DDTN_SUM /*공제계*/ , NVL(FEE.ACL_DSB_AMT,0) AS ACL_DSB_AMT /*실지급액*/ , '0' AS AWB_INTBS_SUM /*시상과표계 현재 시상DB 미정*/ , '0' AS AWB_DDTN_SUM /*시상공제계 현재 시상DB 미정*/ , '0' AS AWB_ACL_DSB_AMT /*시상실지급액 현재 시상DB 미정*/ FROM ( SELECT B.DGR1_LEVL_OG_NM , B.DGR2_LEVL_OG_NM , B.DGR3_LEVL_OG_NM , A.PRTNR_KNM , A.PRTNR_NO , A.RSB_DV_CD , A.QLF_DV_CD , D.FNIT_CD , D.ACNO_ENCR , A.OG_TP_CD , A.OG_ID , A.PRTNR_CLSF_CD , A.PSTN_DV_CD FROM TB_OGBS_MM_PRTNR_IZ A INNER JOIN TB_OGBS_MM_OG_IZ B ON A.BASE_YM = B.BASE_YM AND A.OG_ID = B.OG_ID AND A.OG_TP_CD = B.OG_TP_CD
AND B.DGR1_LEVL_OG_ID = #{ogLevl1}
AND B.DGR2_LEVL_OG_ID = #{ogLevl2}
AND B.DGR3_LEVL_OG_ID = #{ogLevl3}
LEFT OUTER JOIN TB_OGBS_PRTNR_AC_BAS D ON A.PRTNR_NO = D.PRTNR_NO AND A.OG_TP_CD = D.OG_TP_CD WHERE A.BASE_YM = #{perfYm} AND A.OG_TP_CD = #{ogTp} /*W01~03 P,M,홈마스터*/ AND A.RSB_DV_CD = #{rsbTp} /*W204,205 지점장, 플래너*/
AND A.PRTNR_NO = #{prtnrNo}
)OG
LEFT OUTER JOIN (SELECT E.PRTNR_NO , E.OG_TP_CD , SUM(E.INTBS_AMT) AS INTBS_SUM , SUM(E.DDCTAM) AS DDTN_SUM , SUM(E.DSB_OJ_AMT) AS ACL_DSB_AMT FROM TB_FEAM_FEE_DSB_IZ E WHERE E.DSB_YM = TO_CHAR(ADD_MONTHS( #{perfYm}||'01', '1' ),'YYYYMM' ) GROUP BY E.PRTNR_NO, E.OG_TP_CD )FEE ON OG.PRTNR_NO = FEE.PRTNR_NO AND OG.OG_TP_CD = FEE.OG_TP_CD
AND OG.OG_ID = #{session.departmentId} /*업무담당자, 홈마스터는 본인 조직ID만 조회가능*/
AND OG.PRTNR_CLSF_CD = '20' /*BM은 현장 관리자 이어야 하고*/ AND OG.OG_ID = #{session.departmentId} /*본인 조직ID만 조회가능*/
/*고용형태 코드가 사업자(=파트너)이면 이하의 직급조건*/
AND OG.PRTNR_NO = #{session.userId} /*15, 10 직급은 자신의 정보만 조회*/
/*7,4,2 직급은 웰스 M조직 이면서 자신의 하위 사번 정보만 조회*/ AND OG.PRTNR_NO IN (SELECT X.PRTNR_NO FROM TG_OGBS_MM_PRTNR_IZ X INNER JOIN TG_OGBS_MM_OG_IZ Z ON X.BASE_YM = Z.BASE_YM AND X.OG_ID = Z.OG_ID AND X.OG_TP_CD = Z.OG_TP_CD WHERE X.BASE_YM = #{perfYm} AND X.OG_TP_CD = #{ogTp} AND Z.DGR3_LEVL_OG_ID = #{session.userId})
AND OG.PRTNR_NO IN (SELECT X.PRTNR_NO FROM TG_OGBS_MM_PRTNR_IZ X INNER JOIN TG_OGBS_MM_OG_IZ Z ON X.BASE_YM = Z.BASE_YM AND X.OG_ID = Z.OG_ID AND X.OG_TP_CD = Z.OG_TP_CD WHERE X.BASE_YM = #{perfYm} AND X.OG_TP_CD = #{ogTp} AND Z.DGR2_LEVL_OG_ID = #{session.userId})
AND OG.PRTNR_NO IN (SELECT X.PRTNR_NO FROM TB_OGBS_MM_PRTNR_IZ X INNER JOIN TG_OGBS_MM_OG_IZ Z ON X.BASE_YM = Z.BASE_YM AND X.OG_ID = Z.OG_ID AND X.OG_TP_CD = Z.OG_TP_CD WHERE X.BASE_YM = #{perfYm} AND X.OG_TP_CD = #{ogTp} AND Z.DGR1_LEVL_OG_ID = #{session.userId})
AND (FEE.INTBS_SUM > 0 /*지급여부가 Y 이면 과표계, 공제계,실지급액,시상지급액 이 0원 이상인것으로 (※향후 DB정해지면 시상지급액 추가)*/ OR FEE.DDTN_SUM > 0 OR FEE.ACL_DSB_AMT > 0 )
ORDER BY OG.PSTN_DV_CD, OG.OG_ID, OG.PRTNR_NO /*직급, 본부, 사번 순으로 정렬한다.*/

SELECT OG.DGR2_LEVL_OG_NM AS RENL_GRP /*지역단*/ , OG.DGR3_LEVL_OG_NM AS BRANCH /*지점*/ , OG.PRTNR_KNM AS EMPL_NM /*성명*/ , OG.PRTNR_NO /*파트너번호*/ , OG.RSB_DV_CD AS RSB_TP /*직책*/ , OG.QLF_DV_CD AS QLF /*자격*/ , OG.FNIT_CD AS BNK /*은행*/ , OG.ACNO_ENCR AS AC_NO /*계좌번호*/ , NVL(FEE.INTBS_SUM,0) AS INTBS_SUM /*과표계*/ , NVL(FEE.DDTN_SUM,0) AS DDTN_SUM /*공제계*/ , NVL(FEE.ACL_DSB_AMT,0) AS ACL_DSB_AMT /*실지급액*/ , '0' AS AWB_INTBS_SUM /*시상과표계 현재 시상DB 미정*/ , '0' AS AWB_DDTN_SUM /*시상공제계 현재 시상DB 미정*/ , '0' AS AWB_ACL_DSB_AMT /*시상실지급액 현재 시상DB 미정*/ FROM ( SELECT B.DGR2_LEVL_OG_NM , B.DGR3_LEVL_OG_NM , A.PRTNR_KNM , A.PRTNR_NO , A.RSB_DV_CD , A.QLF_DV_CD , D.FNIT_CD , D.ACNO_ENCR , A.OG_TP_CD , A.OG_ID , A.PRTNR_CLSF_CD , A.PSTN_DV_CD FROM TB_OGBS_MM_PRTNR_IZ A INNER JOIN TB_OGBS_MM_OG_IZ B ON A.BASE_YM = B.BASE_YM AND A.OG_ID = B.OG_ID AND A.OG_TP_CD = B.OG_TP_CD
AND B.DGR1_LEVL_OG_ID = #{ogLevl1}
AND B.DGR2_LEVL_OG_ID = #{ogLevl2}
AND B.DGR3_LEVL_OG_ID = #{ogLevl3}
LEFT OUTER JOIN TB_OGBS_PRTNR_AC_BAS D ON A.PRTNR_NO = D.PRTNR_NO AND A.OG_TP_CD = D.OG_TP_CD WHERE A.BASE_YM = #{mngtYm} AND A.OG_TP_CD = #{ogTpCd} /*W01~03 P,M,홈마스터*/ AND A.RSB_DV_CD = #{rsbTp} /*W204,205 지점장, 플래너*/
AND A.PRTNR_NO = #{prtnrNo}
)OG
LEFT OUTER JOIN (SELECT E.PRTNR_NO , E.OG_TP_CD , SUM(E.INTBS_AMT) AS INTBS_SUM , SUM(E.DDCTAM) AS DDTN_SUM , SUM(E.DSB_OJ_AMT) AS ACL_DSB_AMT FROM TB_FEAM_FEE_DSB_IZ E WHERE E.DSB_YM = TO_CHAR(ADD_MONTHS( #{mngtYm}||'01', '1' ),'YYYYMM' ) GROUP BY E.PRTNR_NO, E.OG_TP_CD )FEE ON OG.PRTNR_NO = FEE.PRTNR_NO AND OG.OG_TP_CD = FEE.OG_TP_CD
AND OG.OG_ID = #{session.departmentId} /*업무담당자는 본인 조직ID만 조회가능*/
AND OG.PRTNR_CLSF_CD = '20' /*BM은 현장 관리자 이어야 하고*/ AND OG.OG_ID = #{session.departmentId} /*본인 조직ID만 조회가능*/
/*고용형태 코드가 사업자(=파트너)이면 이하의 직급조건*/
AND OG.PRTNR_NO = #{session.userId} /*15, 10 직급은 자신의 정보만 조회*/
/*7,4,2 직급은 웰스 M조직 이면서 자신의 하위 사번 정보만 조회*/ AND OG.PRTNR_NO IN (SELECT X.PRTNR_NO FROM TG_OGBS_MM_PRTNR_IZ X INNER JOIN TG_OGBS_MM_OG_IZ Z ON X.BASE_YM = Z.BASE_YM AND X.OG_ID = Z.OG_ID AND X.OG_TP_CD = Z.OG_TP_CD WHERE X.BASE_YM = #{mngtYm} AND X.OG_TP_CD = #{ogTp} AND Z.DGR3_LEVL_OG_ID = #{session.userId})
AND OG.PRTNR_NO IN (SELECT X.PRTNR_NO FROM TG_OGBS_MM_PRTNR_IZ X INNER JOIN TG_OGBS_MM_OG_IZ Z ON X.BASE_YM = Z.BASE_YM AND X.OG_ID = Z.OG_ID AND X.OG_TP_CD = Z.OG_TP_CD WHERE X.BASE_YM = #{mngtYm} AND X.OG_TP_CD = #{ogTp} AND Z.DGR2_LEVL_OG_ID = #{session.userId})
AND OG.PRTNR_NO IN (SELECT X.PRTNR_NO FROM TG_OGBS_MM_PRTNR_IZ X INNER JOIN TG_OGBS_MM_OG_IZ Z ON X.BASE_YM = Z.BASE_YM AND X.OG_ID = Z.OG_ID AND X.OG_TP_CD = Z.OG_TP_CD WHERE X.BASE_YM = #{mngtYm} AND X.OG_TP_CD = #{ogTp} AND Z.DGR3_LEVL_OG_ID = #{session.userId})
AND (FEE.INTBS_SUM > 0 /*지급여부가 Y 이면 과표계, 공제계,실지급액,시상지급액 이 0원 이상인것으로 (※향후 DB정해지면 시상지급액 추가)*/ OR FEE.DDTN_SUM > 0 OR FEE.ACL_DSB_AMT > 0 )
ORDER BY OG.PSTN_DV_CD, OG.OG_ID, OG.PRTNR_NO /*직급, 본부, 사번 순으로 정렬한다.*/
