mapper.select:
SELECT RSB_DV_CD
  FROM TB_OGBS_MM_PRTNR_IZ  /* 월파트너내역 */
 WHERE DTA_DL_YN = 'N'
   AND BASE_YM = #{perfYm}
   AND OG_TP_CD = #{ogTpCd}
   AND PRTNR_NO = #{prtnrNo}

SELECT T1.BASE_YM /* 기준년월 */ , T1.DGR1_LEVL_OG_ID /* 1차레벨조직ID */ , T1.DGR1_LEVL_OG_CD /* 1차레벨조직코드 */ , T1.DGR1_LEVL_OG_NM /* 1차레벨조직명 */ , T1.DGR1_LEVL_DG_PRTNR_NO /* 1차레벨대표파트너번호 */ , T1.DGR1_LEVL_DG_PRTNR_NM /* 1차레벨대표파트너명 */ , T1.DGR2_LEVL_OG_ID /* 2차레벨조직ID */ , T1.DGR2_LEVL_OG_CD /* 2차레벨조직코드 */ , T1.DGR2_LEVL_OG_NM /* 2차레벨조직명 */ , T1.DGR2_LEVL_DG_PRTNR_NO /* 2차레벨대표파트너번호 */ , T1.DGR2_LEVL_DG_PRTNR_NM /* 2차레벨대표파트너명 */ , T1.DGR3_LEVL_OG_ID /* 3차레벨조직ID */ , T1.DGR3_LEVL_OG_CD /* 3차레벨조직코드 */ , T1.DGR3_LEVL_OG_NM /* 3차레벨조직명 */ , T1.DGR3_LEVL_DG_PRTNR_NO /* 3차레벨대표파트너번호 */ , T1.DGR3_LEVL_DG_PRTNR_NM /* 3차레벨대표파트너명 */ , T1.DGR4_LEVL_OG_ID /* 4차레벨조직ID */ , T1.DGR4_LEVL_OG_CD /* 4차레벨조직코드 */ , T1.DGR4_LEVL_OG_NM /* 4차레벨조직명 */ , T1.DGR4_LEVL_DG_PRTNR_NO /* 4차레벨대표파트너번호 */ , T1.DGR4_LEVL_DG_PRTNR_NM /* 4차레벨대표파트너명 */ , T1.DGR5_LEVL_OG_ID /* 5차레벨조직ID */ , T1.DGR5_LEVL_OG_CD /* 5차레벨조직코드 */ , T1.DGR5_LEVL_OG_NM /* 5차레벨조직명 */ , T1.DGR5_LEVL_DG_PRTNR_NO /* 5차레벨대표파트너번호 */ , T1.DGR5_LEVL_DG_PRTNR_NM /* 5차레벨대표파트너명 */ , T1.OG_CD /* 조직코드 */ , T2.PRTNR_NO /* 파트너번호 */ , T2.PRTNR_KNM /* 파트너명 */ , T2.RSB_DV_CD /* 직책구분코드 */ , T2.QLF_DV_CD /* 자격구분코드 */ , T3.CO_CD /* 회사코드 */ , T4.PYMDT /* 지급일자 */ , NVL(T3.INTBS_AMT, 0) AS INTBS_AMT /* 소득과표금액 */ , NVL(T3.DDCTAM, 0) AS DDCTAM /* 공제금액 */ , NVL(T3.DSB_OJ_AMT, 0) AS DSB_OJ_AMT /* 지급대상금액 */ , NVL(T5.AWD_INTBS_AMT, 0) AS AWD_INTBS_AMT /* 시상소득과표금액 */ , NVL(T5.AWD_ERN_WHTX, 0) AS AWD_ERN_WHTX /* 시상소득원천세 */ , NVL(T5.AWD_DSB_OJ_AMT, 0) AS AWD_DSB_OJ_AMT /* 시상실지급액 */ , T6.BNK_NM /* 은행명 */ , T6.ACNO_ENCR /* 계좌번호암호화 */ FROM TB_OGBS_MM_OG_IZ T1 /* 월조직내역 */ INNER JOIN TB_OGBS_MM_PRTNR_IZ T2 /* 월파트너내역 */ ON T1.BASE_YM = T2.BASE_YM /* 기준년월 */ AND T1.OG_TP_CD = T2.OG_TP_CD /* 조직유형코드 */ AND T1.OG_ID = T2.OG_ID /* 조직ID */ AND T2.DTA_DL_YN = 'N' INNER JOIN TB_FEAM_FEE_DSB_IZ T3 /* 수수료지급내역 */ ON T2.BASE_YM = T3.DSB_YM /* 지급년월 */ AND T2.OG_TP_CD = T3.OG_TP_CD /* 조직유형코드 */ AND T2.PRTNR_NO = T3.PRTNR_NO /* 파트너번호 */ AND T3.DTA_DL_YN = 'N' AND T3.FEE_TCNT_DV_CD = '02' /* 수수료차수구분코드 = 02 : 2차 */ AND T3.INTBS_AMT > 0 /* 소득과표금액 */ AND T3.SPMT_DSB_DV_CD = '01' /* 추가지급구분코드 = 01 : 정상지급 */ LEFT OUTER JOIN TB_FEAM_FEE_FNT_IZ T4 /* 수수료이체내역 */ ON T3.DSB_YM = T4.DSB_YM /* 지급년월 */ AND T3.CO_CD = T4.CO_CD /* 회사코드 */ AND T3.OG_TP_CD = T4.OG_TP_CD /* 조직유형코드 */ AND T3.PRTNR_NO = T4.PRTNR_NO /* 파트너번호 */ AND T4.DTA_DL_YN = 'N' LEFT OUTER JOIN ( SELECT PRTNR_NO /* 파트너번호 */ , OG_TP_CD /* 조직유형코드 */ , NVL(SUM(AWD_INTBS_AMT), 0) AS AWD_INTBS_AMT /* 시상소득과표금액 */ , NVL(SUM(AWD_ERN_WHTX), 0) AS AWD_ERN_WHTX /* 시상소득원천세 */ , NVL(SUM(AWD_INTBS_AMT), 0) - NVL(SUM(AWD_ERN_WHTX), 0) AS AWD_DSB_OJ_AMT /* 시상실지급액 */ FROM TB_PSEV_AWD_ERN_WHTX_IZ /* 시상소득원천세내역 */ WHERE 1=1 AND PERF_YM = #{perfYm} /* 조직유형코드 */ AND OG_TP_CD = #{ogTpCd} /* 조직유형코드 */
AND PRTNR_NO = #{prtnrNo} /* 파트너번호 */
GROUP BY PRTNR_NO, OG_TP_CD ) T5 ON T3.PRTNR_NO = T5.PRTNR_NO /* 파트너번호 */ AND T3.OG_TP_CD = T5.OG_TP_CD /* 조직유형코드 */
LEFT OUTER JOIN ( SELECT T1.OG_TP_CD /* 조직유형코드 */ , T1.PRTNR_NO /* 파트너번호 */ , T1.ACNO_ENCR /* 계좌번호암호화 */ , T2.BNK_NM /* 은행명 */ FROM TB_OGBS_PRTNR_AC_BAS T1 /* 파트너계좌기본 */ INNER JOIN TB_RVDW_FNIT_CD_USE_DV_IZ T2 /* 금융기관코드사용구분내역 */ ON T2.DTA_DL_YN = 'N' AND T1.FNIT_CD = T2.FNIT_CD /* 금융기관코드 */ AND T2.VNCO_DV_CD ='001' /* VAN사구분코드 */ AND T2.BNK_BIZ_DV_CD = '03' /*수수료계좌*/ AND #{perfYm} || '01' BETWEEN T2.VL_STRTDT AND T2.VL_ENDDT /* 유효시작일시, 유효종료일시 */ WHERE 1=1 AND T1.DTA_DL_YN = 'N' AND T1.OG_TP_CD = #{ogTpCd} /* 조직유형코드 */ AND T1.PRTNR_AC_USWY_CD = '1' /* 파트너계좌기본 */ AND TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS') BETWEEN T1.VL_STRT_DTM AND T1.VL_END_DTM /* 유효시작일시, 유효종료일시 */
AND T1.PRTNR_NO = #{prtnrNo} /* 파트너번호 */
) T6 ON T3.PRTNR_NO = T6.PRTNR_NO /* 파트너번호 */ AND T3.OG_TP_CD = T6.OG_TP_CD /* 조직유형코드 */ WHERE 1=1 AND T1.DTA_DL_YN = 'N' AND T1.BASE_YM = #{perfYm} /* 기준년월 */ AND T1.OG_TP_CD = #{ogTpCd} /* 조직유형코드 */ AND T2.RSB_DV_CD = #{rsbDvCd} /* 직책유형코드 */ AND T3.CO_CD = '2000' /* 회사코드 */
AND T3.PRTNR_NO = #{prtnrNo} /* 파트너번호 */
AND T1.DGR1_LEVL_OG_ID = #{ogLevl1}
AND T1.DGR2_LEVL_OG_ID = #{ogLevl2}
AND T1.DGR3_LEVL_OG_ID = #{ogLevl3}
AND T1.DGR4_LEVL_OG_ID = #{ogLevl4}
AND T1.DGR5_LEVL_OG_ID = #{ogLevl5}
AND ( T3.INTBS_AMT > 0 OR T3.DDCTAM > 0 OR T3.DSB_OJ_AMT > 0 OR T5.AWD_INTBS_AMT > 0 OR T5.AWD_ERN_WHTX > 0 OR T5.AWD_DSB_OJ_AMT > 0 )
AND ( T3.DSB_OJ_AMT <= 0 OR T5.AWD_DSB_OJ_AMT <= 0 )
ORDER BY T1.OG_CD, T2.PRTNR_NO
