mapper.select:
SELECT BASE_YM , OG_TP_CD , PRTNR_NO , OG_CD , PRTNR_KNM , RSB_DV_NM , NVL(SV01999901, 0) AS SV01999901 /*관리건수*/ , NVL(SV01999902, 0) AS SV01999902 /*방문건수*/ , NVL(SV01999903, 0) AS SV01999903 /*전월건수*/ , NVL(SV01999902, 0) + NVL(SV01999903, 0) AS TOT_SV_CNT /*완료건수*/ , NVL(SV01999909, 0) AS SV01999909 /*비율*/ , NVL(SV01999910, 0) AS SV01999910 /*수정비율*/ FROM ( SELECT A.BASE_YM , A.OG_TP_CD , A.PRTNR_NO , A.OG_CD , A.PRTNR_KNM , F_CMZ_CD_NM('TNT_WELLS', 'RSB_DV_CD', A.RSB_DV_CD) AS RSB_DV_NM , B.PRTNR_SV_PERF_ATC_CD , B.PERF_VAL FROM TB_OGBS_MM_PRTNR_IZ A /*월파트너내역*/ LEFT OUTER JOIN TB_FEAM_WPTN_SV_PERF_AGRG B /*웰스파트너서비스실적집계*/ ON A.BASE_YM = B.BASE_YM AND A.OG_TP_CD = B.OG_TP_CD AND A.PRTNR_NO = B.PRTNR_NO AND B.DTA_DL_YN = 'N' AND B.CL_DV_CD = '02' /*마감구분코드-02 순주문2차*/ AND B.PRTNR_SV_PERF_ATC_CD IN ('SV01999901', 'SV01999902', 'SV01999903', 'SV01999909', 'SV01999910') WHERE A.DTA_DL_YN = 'N' AND A.BASE_YM = #{perfYm} AND A.OG_TP_CD = 'W02'
AND A.PRTNR_NO = #{prtnrNo}
) PIVOT ( SUM(PERF_VAL) FOR PRTNR_SV_PERF_ATC_CD IN ( 'SV01999901' AS SV01999901 , 'SV01999902' AS SV01999902 , 'SV01999903' AS SV01999903 , 'SV01999909' AS SV01999909 , 'SV01999910' AS SV01999910 ) )
/*파트너번호를 입력하지 않고 전체 조회시 처리율을 수정한 사람만 조회*/ /*파트너번호를 입력 후 조회시 수정하지 않았어도 조회*/ WHERE SV01999909 != SV01999910
ORDER BY BASE_YM, OG_TP_CD, PRTNR_NO

mapper.update:
UPDATE TB_FEAM_WPTN_SV_PERF_AGRG
   SET PERF_VAL = #{perfVal}

WHERE DTA_DL_YN = 'N'
  AND BASE_YM = #{baseYm}
  AND CL_DV_CD = '02'   /*마감구분코드 02-순주문2차*/
  AND OG_TP_CD = 'W02'
  AND PRTNR_NO = #{prtnrNo}
  AND PRTNR_SV_PERF_ATC_CD = 'SV01999910'

UPDATE TB_FEAM_NTORP_PERF_MM_CL
   SET PERF_VAL = #{perfVal}

WHERE DTA_DL_YN = 'N'
  AND BASE_YM = #{baseYm}
  AND PERF_YM = #{baseYm}
  AND FEE_TCNT_DV_CD = '02'
  AND PERF_AGRG_CRT_DV_CD = '201'	/*실적집계생성구분코드-201 M추진단순주문실적생성*/
  AND PERF_ATC_CD = 'W02P00095'	/*실적항목코드-W02P00095 수정비율*/
  AND CO_CD = '2000'
  AND OG_TP_CD = 'W02'
  AND PRTNR_NO = #{prtnrNo}
  AND PERF_DV_CD = '0'	/*실적구분코드-0 개인판매*/
