mapper.select:
SELECT SUBSTR(T1.DSB_YM, 0, 4) || '-' || SUBSTR(T1.DSB_YM, 5) AS DSB_YM , T1.HMNRSC_EMPNO , T1.OG_LEVL_DV_NM , T1.OG_NM , T1.HGR_OG_CD , T1.OG_CD , T1.PRTNR_KNM , T1.BRCH_CT , T1.BAS_AW_AMT /*기본수당금액*/ , T1.HH_EXCP_AW_AMT /*시간외수당금액 */ , T1.RSB_AW_AMT /*직책수당금액*/ , T1.FXN_AW_SUM_AMT /*고정수당합계금액*/ , T1.TRG_CT /*실적목표건수*/ , T1.PERF_CT /*실적건수*/ , TRUNC(T1.PERF_ACHV_RAT, 1) AS PERF_ACHV_RAT /*실적달성비율*/ , T1.TRG_ACHV_AW_AMT /*목표달성수당금액*/ , T1.BRCH_AV_PERF_CT /*지점평균실적건수(2021년02월이전)*/ , T1.ENCRG_AW_AMT /*장려수당금액(2021년02월이후)*/ , T1.MNGR_PERF_GD_CD /*관리자실적등급코드(2021년02월이전)*/ , T1.OG_AW_AMT /*조직수당금액(2021년02월이후)*/ , T1.EVL_AW_AMT /*평가수당금액*/ , T1.PERF_AW_SUM_AMT /*실적수당합계금액*/ , T1.EXCL_DIV_AW_AMT /*우수사업부수당금액*/ , T1.ICT_AW_AMT /*인센티브수당금액*/ , T1.LDSTC_ATDC_AW_AMT /*장거리출근수당금액*/ , T1.MRNT_SPPT_AW_AMT /*월세지원수당금액*/ , T1.LECTR_ADN_AW_AMT /*강의부가수당금액*/ , T1.ETC_ADN_AW_AMT /*기타부가수당금액*/ , T1.ETC_ADN_AW_SUM_AMT /*기타부가수당합계금액*/ , T1.FXN_PERF_AW_SUM_AMT /*고정실적수당합계금액(고정급+업적수당)*/ , T1.AW_CALC_SUM_AMT /*수당계산합계금액*/ , T1.HINSR_DDCTAM /*건강보험공제금액*/ , T1.NRSN_INSR_DDCTAM /*요양보험공제금액*/ , T1.NTNL_PNSN_DDCTAM /*국민연금공제금액*/ , T1.EINSR_DDCTAM /*고용보험공제금액*/ , T1.ERNTX /*소득세*/ , T1.RSDNTX /*주민세*/ , T1.CRPT_BZNS_DDCTAM /*비정도영업조치금*/ , T1.EDDTN_AMT /*기타공제금액*/ , T1.DDTN_SUM_AMT /*공제합계금액*/ , T1.ACPY_AMT /*실지급액*/ FROM TB_FEAM_WELS_MNGR_AW_IZ T1 /*웰스관리자수당내역*/ INNER JOIN TB_OGBS_MM_PRTNR_IZ T2 /*월파트너내역*/ ON T1.DSB_YM = T2.BASE_YM AND T1.HMNRSC_EMPNO = T2.PRTNR_NO AND T2.DTA_DL_YN = 'N' WHERE T1.DTA_DL_YN = 'N' AND T1.DSB_YM = #{perfYm}
AND T1.HMNRSC_EMPNO = #{prtnrNo}
AND T2.OG_TP_CD = #{ogTpCd}
AND T2.RSB_DV_CD = #{rsbDvCd}
/*TODO 추후 권한 적용시 주석 해제*/ /*로그인한 사용자의 권한에 따른 조회*/ /*AND EXISTS ( SELECT 1 FROM OBJ_PRTNR X1 WHERE X1.OG_TP_CD = T2.OG_TP_CD AND X1.PRTNR_NO = T2.PRTNR_NO)*/ ORDER BY T1.HGR_OG_CD, T1.OG_CD

SELECT SUBSTR(T1.DSB_YM, 0, 4) || '-' || SUBSTR(T1.DSB_YM, 5) AS DSB_YM , COUNT(T1.HMNRSC_EMPNO) AS HMNRSC_EMPNO_CNT , T1.OG_LEVL_DV_NM , SUM(T1.BAS_AW_AMT) AS BAS_AW_AMT /*기본수당금액*/ , SUM(T1.HH_EXCP_AW_AMT) AS HH_EXCP_AW_AMT /*시간외수당금액 */ , SUM(T1.RSB_AW_AMT) AS RSB_AW_AMT /*직책수당금액*/ , SUM(T1.FXN_AW_SUM_AMT) AS FXN_AW_SUM_AMT /*고정수당합계금액*/ , SUM(T1.TRG_CT) AS TRG_CT /*실적목표건수*/ , SUM(T1.PERF_CT) AS PERF_CT /*실적건수*/ , SUM(TRUNC(T1.PERF_ACHV_RAT, 1)) AS PERF_ACHV_RAT /*실적달성비율*/ , SUM(T1.TRG_ACHV_AW_AMT) AS TRG_ACHV_AW_AMT /*목표달성수당금액*/ , SUM(T1.BRCH_AV_PERF_CT) AS BRCH_AV_PERF_CT /*지점평균실적건수(2021년02월이전)*/ , SUM(T1.ENCRG_AW_AMT) AS ENCRG_AW_AMT /*장려수당금액(2021년02월이후)*/ , SUM(T1.MNGR_PERF_GD_CD) AS MNGR_PERF_GD_CD /*관리자실적등급코드(2021년02월이전)*/ , SUM(T1.OG_AW_AMT) AS OG_AW_AMT /*조직수당금액(2021년02월이후)*/ , SUM(T1.EVL_AW_AMT) AS EVL_AW_AMT /*평가수당금액*/ , SUM(T1.PERF_AW_SUM_AMT) AS PERF_AW_SUM_AMT /*실적수당합계금액*/ , SUM(T1.EXCL_DIV_AW_AMT) AS EXCL_DIV_AW_AMT /*우수사업부수당금액*/ , SUM(T1.ICT_AW_AMT) AS ICT_AW_AMT /*인센티브수당금액*/ , SUM(T1.LDSTC_ATDC_AW_AMT) AS LDSTC_ATDC_AW_AMT /*장거리출근수당금액*/ , SUM(T1.MRNT_SPPT_AW_AMT) AS MRNT_SPPT_AW_AMT /*월세지원수당금액*/ , SUM(T1.LECTR_ADN_AW_AMT) AS LECTR_ADN_AW_AMT /*강의부가수당금액*/ , SUM(T1.ETC_ADN_AW_AMT) AS ETC_ADN_AW_AMT /*기타부가수당금액*/ , SUM(T1.ETC_ADN_AW_SUM_AMT) AS ETC_ADN_AW_SUM_AMT /*기타부가수당합계금액*/ , SUM(T1.FXN_PERF_AW_SUM_AMT) AS FXN_PERF_AW_SUM_AMT /*고정실적수당합계금액(고정급+업적수당)*/ , SUM(T1.AW_CALC_SUM_AMT) AS AW_CALC_SUM_AMT /*수당계산합계금액*/ , SUM(T1.HINSR_DDCTAM) AS HINSR_DDCTAM /*건강보험공제금액*/ , SUM(T1.NRSN_INSR_DDCTAM) AS NRSN_INSR_DDCTAM /*요양보험공제금액*/ , SUM(T1.NTNL_PNSN_DDCTAM) AS NTNL_PNSN_DDCTAM /*국민연금공제금액*/ , SUM(T1.EINSR_DDCTAM) AS EINSR_DDCTAM /*고용보험공제금액*/ , SUM(T1.ERNTX) AS ERNTX /*소득세*/ , SUM(T1.RSDNTX) AS RSDNTX /*주민세*/ , SUM(T1.CRPT_BZNS_DDCTAM) AS CRPT_BZNS_DDCTAM /*비정도영업조치금*/ , SUM(T1.EDDTN_AMT) AS EDDTN_AMT /*기타공제금액*/ , SUM(T1.DDTN_SUM_AMT) AS DDTN_SUM_AMT /*공제합계금액*/ , SUM(T1.ACPY_AMT) AS ACPY_AMT /*실지급액*/ FROM TB_FEAM_WELS_MNGR_AW_IZ T1 /*웰스관리자수당내역*/ INNER JOIN TB_OGBS_MM_PRTNR_IZ T2 /*월파트너내역*/ ON T1.DSB_YM = T2.BASE_YM AND T1.HMNRSC_EMPNO = T2.PRTNR_NO AND T2.DTA_DL_YN = 'N' WHERE T1.DTA_DL_YN = 'N' AND T1.DSB_YM = #{perfYm}
AND T1.HMNRSC_EMPNO = #{prtnrNo}
AND T2.OG_TP_CD = #{ogTpCd}
AND T2.RSB_DV_CD = #{rsbDvCd}
/*TODO 추후 권한 적용시 주석 해제*/ /*로그인한 사용자의 권한에 따른 조회*/ /*AND EXISTS ( SELECT 1 FROM OBJ_PRTNR X1 WHERE X1.OG_TP_CD = T2.OG_TP_CD AND X1.PRTNR_NO = T2.PRTNR_NO)*/ GROUP BY T1.DSB_YM, T1.OG_LEVL_DV_NM ORDER BY T1.DSB_YM, T1.OG_LEVL_DV_NM
