mapper.select:
SELECT OG.DGR1_LEVL_OG_ID /*총괄단*/ , OG.DGR2_LEVL_OG_ID /*지역단*/ , OG.DGR3_LEVL_OG_ID /*지점*/ , OG.DGR1_LEVL_DG_PRTNR_NO /*총괄단장*/ , OG.DGR2_LEVL_DG_PRTNR_NO /*지역단장*/ , OG.DGR3_LEVL_DG_PRTNR_NO /*지점장*/ , OG.PRTNR_NO , OG.PRTNR_KNM , T1.CNTR_NO /*계약번호*/ , T4.CNTR_CST_NO /*계약고객번호*/ , (SELECT C.CST_KNM FROM TB_CUBS_CST_BAS C WHERE C.DTA_DL_YN = 'N' AND C.CST_NO = T4.CNTR_CST_NO ) AS CST_KNM /*고객명*/ , T1.BASE_PD_CD /*상품코드*/ , T2.PD_NM /*상품명*/ , BAS.SV_FEE_PD_DV_CD /*BS상품군*/ , F_CMZ_CD_NM('TNT_WELLS', 'SV_FEE_PD_DV_CD', BAS.SV_FEE_PD_DV_CD) AS SV_FEE_PD_DV_NM /*BS상품군*/ , NVL(BAS.SV_FEE_BASE_AMT, 0) AS SV_FEE_BASE_AMT /*BS기본수수료*/ , NVL(T1.FEE_CALC_AMT, 0) AS FEE_CALC_AMT /*방문수수료*/ , T1.VST_RGLVL_GD_CD /*방문급지*/ , CASE T1.VST_RGLVL_GD_CD WHEN '1' THEN CASE OG.OG_TP_CD WHEN 'W02' THEN 'N' ELSE 'A' END WHEN '2' THEN CASE OG.OG_TP_CD WHEN 'W02' THEN 'G' ELSE 'B' END WHEN '3' THEN CASE OG.OG_TP_CD WHEN 'W02' THEN 'W1' ELSE 'C' END WHEN '4' THEN CASE OG.OG_TP_CD WHEN 'W02' THEN 'W2' ELSE 'D' END WHEN '5' THEN 'W3' WHEN '6' THEN 'S1' WHEN '7' THEN 'S2' ELSE '*' END AS VST_RGLVL_GD_NM /*방문급지*/ , T1.WK_EXCN_DT /*방문일자*/ , CASE WHEN T1.VST_TP_DV_CD = '2' THEN /*방문취소*/ CASE WHEN T1.WK_CAN_RSON_CD = '01' THEN '취소' WHEN T1.WK_CAN_RSON_CD = '02' THEN ' 매출중지' WHEN T1.WK_CAN_RSON_CD = '03' THEN ' 배정취소' ELSE '' END ELSE '' END AS CAN_YN /*취소여부*/ FROM TB_FEAM_WELS_SV_PERF_IZ T1 /*웰스서비스실적내역*/ LEFT OUTER JOIN ( SELECT A.PD_CD , A.PD_NM , A.PD_CLSF_ID , B.REF_PD_CLSF_VAL FROM TB_PDBS_PD_BAS A INNER JOIN TB_PDBS_PD_CLSF_BAS B ON A.PD_CLSF_ID = B.PD_CLSF_ID AND B.DTA_DL_YN = 'N' WHERE A.DTA_DL_YN = 'N' ) T2 /*상품*/ ON T1.BASE_PD_CD = T2.PD_CD LEFT OUTER JOIN LATERAL ( /* * 웰스팜 기기 + 모종 결합 계약인 경우, 웰스팜 기기 방문에 수수료 지급 * => 정기배송 패키지 상품(모종)이고 결합 계약인 것을 제외함 * */ SELECT NULL AS HCR_DV_CD , CASE WHEN C3.CNTR_REL_DTL_CD = '216' /*모종결합*/ THEN 'Y' ELSE 'N' END AS COMBI_YN /*결합제외여부*/ , C2.CNTR_CST_NO /*계약고객번호*/ FROM TB_SSCT_CNTR_DTL C1 /*계약상세*/ INNER JOIN TB_SSCT_CNTR_BAS C2 /*계약기본*/ ON C1.CNTR_NO = C2.CNTR_NO AND C2.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_SSCT_CNTR_REL C3 /*계약관계*/ ON C3.OJ_DTL_CNTR_NO = C1.CNTR_NO AND C3.OJ_DTL_CNTR_SN = C1.CNTR_SN AND C3.DTA_DL_YN = 'N' WHERE C1.DTA_DL_YN = 'N' AND C1.CNTR_NO = T1.CNTR_NO AND C1.CNTR_SN = T1.CNTR_SN AND C1.SELL_TP_CD = '6' /*정기배송*/ UNION ALL /*멤버십*/ SELECT C3.HCR_DV_CD , 'N' AS COMBI_YN /*결합여부*/ , C2.CNTR_CST_NO /*계약고객번호*/ FROM TB_SSCT_CNTR_DTL C1 /*계약상세*/ INNER JOIN TB_SSCT_CNTR_BAS C2 /*계약기본*/ ON C1.CNTR_NO = C2.CNTR_NO AND C2.DTA_DL_YN = 'N' INNER JOIN TB_SSCT_CNTR_WELLS_DTL C3 ON C1.CNTR_NO = C3.CNTR_NO AND C1.CNTR_SN = C3.CNTR_SN WHERE C1.CNTR_NO = T1.CNTR_NO AND C1.CNTR_SN = T1.CNTR_SN AND C1.SELL_TP_CD = '3' /*멤버십*/ AND C1.DTA_DL_YN = 'N' UNION ALL /* * 렌탈 * 웰스팜 기기이고 결합이 아니면 제외 * */ SELECT NULL AS HCR_DV_CD , CASE WHEN T2.REF_PD_CLSF_VAL LIKE '05001003%' AND C3.CNTR_REL_DTL_CD != '216' THEN 'Y' ELSE 'N' END AS COMBI_YN /*결합제외여부*/ , C2.CNTR_CST_NO /*계약고객번호*/ FROM TB_SSCT_CNTR_DTL C1 /*계약상세*/ INNER JOIN TB_SSCT_CNTR_BAS C2 /*계약기본*/ ON C1.CNTR_NO = C2.CNTR_NO AND C2.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_SSCT_CNTR_REL C3 /*계약관계*/ ON C3.BASE_DTL_CNTR_NO = C1.CNTR_NO AND C3.BASE_DTL_CNTR_SN = C1.CNTR_SN AND C3.DTA_DL_YN = 'N' INNER JOIN TB_SSCT_CNTR_WELLS_DTL C4 /*계약WELLS상세*/ ON C4.CNTR_NO = C1.CNTR_NO AND C4.CNTR_SN = C1.CNTR_SN AND C4.DTA_DL_YN = 'N' WHERE C1.DTA_DL_YN = 'N' AND C1.CNTR_NO = T1.CNTR_NO AND C1.CNTR_SN = T1.CNTR_SN AND C1.SELL_TP_CD = '2' /*렌탈/리스*/ UNION ALL /* * 할부판매(일시불) * 웰스팜 기기이고 결합이 아니면 제외 * */ SELECT C4.HCR_DV_CD , CASE WHEN T2.REF_PD_CLSF_VAL LIKE '05001003%' AND C3.CNTR_REL_DTL_CD != '216' THEN 'Y' ELSE 'N' END AS COMBI_YN /*결합제외여부*/ , C2.CNTR_CST_NO /*계약고객번호*/ FROM TB_SSCT_CNTR_DTL C1 /*계약상세*/ INNER JOIN TB_SSCT_CNTR_BAS C2 /*계약기본*/ ON C1.CNTR_NO = C2.CNTR_NO AND C2.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_SSCT_CNTR_REL C3 /*계약관계*/ ON C3.BASE_DTL_CNTR_NO = C1.CNTR_NO AND C3.BASE_DTL_CNTR_SN = C1.CNTR_SN AND C3.DTA_DL_YN = 'N' INNER JOIN TB_SSCT_CNTR_WELLS_DTL C4 /*계약WELLS상세*/ ON C4.CNTR_NO = C1.CNTR_NO AND C4.CNTR_SN = C1.CNTR_SN AND C4.DTA_DL_YN = 'N' WHERE C1.DTA_DL_YN = 'N' AND C1.CNTR_NO = T1.CNTR_NO AND C1.CNTR_SN = T1.CNTR_SN AND C1.SELL_TP_CD = '1' /*일시불*/ ) T4 ON 1=1 LEFT OUTER JOIN ( SELECT A.BASE_YM , A.OG_TP_CD , A.DGR1_LEVL_OG_ID , A.DGR2_LEVL_OG_ID , A.DGR3_LEVL_OG_ID , A.DGR1_LEVL_DG_PRTNR_NO , A.DGR2_LEVL_DG_PRTNR_NO , A.DGR3_LEVL_DG_PRTNR_NO , B.PRTNR_NO , B.PRTNR_KNM FROM TB_OGBS_MM_OG_IZ A /* 월조직내역 */ INNER JOIN TB_OGBS_MM_PRTNR_IZ B /* 월파트너내역 */ ON A.BASE_YM = B.BASE_YM AND A.OG_TP_CD = B.OG_TP_CD AND A.OG_ID = B.OG_ID AND B.DTA_DL_YN = 'N' WHERE A.DTA_DL_YN = 'N' AND A.BASE_YM = #{baseYm} AND A.OG_TP_CD IN ('W02', 'W03') /*M조직, 홈마스터*/ ) OG ON T1.BASE_YM = OG.BASE_YM AND T1.OG_TP_CD = OG.OG_TP_CD AND T1.PRTNR_NO = OG.PRTNR_NO LEFT OUTER JOIN LATERAL ( SELECT SV_FEE_BASE_AMT /*BS기본수수료*/ , SV_FEE_PD_DV_CD /*BS상품그룹*/ FROM TB_FEAM_WELS_SV_FEE_BAS C WHERE BASE_PD_CD = T1.BASE_PD_CD AND VST_MCN = 0 AND SV_FEE_DV_CD = CASE WHEN OG.OG_TP_CD = 'W03' THEN '2' ELSE '1' END AND HCR_DV_CD = CASE WHEN T2.REF_PD_CLSF_VAL NOT LIKE '06%' THEN HCR_DV_CD ELSE NVL(T4.HCR_DV_CD, '') END AND #{baseYm} BETWEEN APY_STRT_YM AND APY_END_YM ORDER BY BASE_PD_CD , VST_MCN , SV_FEE_DV_CD , HCR_DV_CD, BASE_CH_TCNT ) BAS ON 1=1 WHERE T1.DTA_DL_YN = 'N' AND T1.BASE_YM = #{baseYm} AND T1.CL_DV_CD = '02' AND T1.OG_TP_CD IN ('W02', 'W03') /*M조직, 홈마스터*/ AND (T1.WK_EXCN_DT IS NOT NULL OR T1.WK_EXCN_DT != '0') /*작업수행일자*/ AND T4.COMBI_YN != 'Y' /*모종이고 결합이면 제외, 기기이고 결합이 아니면 제외*/
AND OG.DGR1_LEVL_OG_ID = #{ogLevlDvCd1}
AND OG.DGR2_LEVL_OG_ID = #{ogLevlDvCd2}
AND OG.DGR3_LEVL_OG_ID = #{ogLevlDvCd3}
/*총괄단 기준 조회*/ AND OG.DGR1_LEVL_DG_PRTNR_NO = #{prtnrNo}
/*지역단 기준 조회*/ AND OG.DGR2_LEVL_DG_PRTNR_NO = #{prtnrNo}
/*지점 기준 조회*/ AND OG.DGR3_LEVL_DG_PRTNR_NO = #{prtnrNo}
/*개인 기준 조회*/ AND OG.PRTNR_NO = #{prtnrNo}
ORDER BY OG.PRTNR_NO, T1.CNTR_NO
