mapper.insert:
MERGE INTO TB_FEAM_FEE_CALC_BAS A
USING DUAL
ON( A.PRTNR_NO = #{prtnrNo}
AND A.BASE_YM = #{baseYm}
AND A.OG_TP_CD = #{ogTpCd}
AND A.FEE_CD = #{feeCd}
AND A.FEE_CALC_AMT = #{feeCalcAmt}
   )
WHEN MATCHED THEN
UPDATE SET A.FEE_CTR_CNFM_AMT = #{feeCtrCnfmAmt}
         , A.FEE_CTR_RSON_CN = #{feeCtrRsonCn}
         , A.FNL_MDFC_DTM = TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
         , A.FNL_MDFC_USR_ID = #{session.userId}
         , A.FNL_MDFC_PRG_ID = 'KSS_WELLS'
         , A.FNL_MDFC_DEPT_ID = #{session.departmentId}
WHEN NOT MATCHED THEN
INSERT ( A.BASE_YM
       , A.PERF_YM
       , A.OJ_DSB_YM
       , A.CO_CD
       , A.OG_TP_CD
       , A.PRTNR_NO
       , A.FEE_CD
       , A.FEE_TCNT_DV_CD
       , A.SPMT_DSB_DV_CD
       , A.DTA_CRT_FEE_CD
       , A.FEE_CALC_AMT
       , A.FEE_CTR_CNFM_AMT
       , A.FEE_CTR_RSON_CN

) VALUES ( #{baseYm}
         , #{baseYm}
         , #{baseYm}
         , '2000'
         , #{ogTpCd}
         , #{prtnrNo}
         , #{feeCd}
         , '02'
         , '01'
         , #{feeCd}
         , 0
         , #{feeCtrCnfmAmt}
         , #{feeCtrRsonCn}

)
