CNFM_AMT > 0 -- 고정 AND B.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_OGPS_PLAR_QLF_CH_IZ C ON C.og_tp_Cd = A01.OG_TP_CD AND C.PRTNR_NO = A01.PRTNR_NO AND TO_CHAR(LAST_DAY(TO_DATE(A01.PERF_YM||'01','YYYYMMDD') ),'YYYYMMDD' ) BETWEEN C.STRTDT AND C.ENDDT AND C.QLF_DV_CD = '3' AND NVL(C.QLF_APLC_DV_CD,'0') IN ('1','2') -- 승격,자격해제(과거) 3 : 보류 는 제외 AND C.DTA_DL_YN = 'N' LEFT OUTER JOIN ( SELECT A1.BASE_YM , A1.OG_TP_CD , A1.PRTNR_NO, NVL(A1.OPNG_CD,'0') AS OPNG_CD FROM TB_FEAM_WELS_MNGER_OPNG_BAS A1 JOIN ( SELECT B0.BASE_YM,B0.OG_TP_CD ,B0.PRTNR_NO ,MAX(B0.TCNT_DV_CD) AS TCNT_DV_CD FROM TB_FEAM_WELS_MNGER_OPNG_BAS B0 WHERE 1 = 1 GROUP BY B0.BASE_YM,B0.OG_TP_CD ,B0.PRTNR_NO ) B1 ON A1.BASE_YM = B1.BASE_YM AND A1.OG_TP_CD = B1.OG_TP_CD AND A1.PRTNR_NO = B1.PRTNR_NO AND A1.TCNT_DV_CD = B1.TCNT_DV_CD ) D ON D.BASE_YM = A01.PERF_YM AND D.OG_TP_CD = A01.OG_TP_CD AND D.PRTNR_NO = A01.PRTNR_NO LEFT OUTER JOIN TB_FEDD_FEE_REDF_ADSB_BAS Z -- 되물림 실적 ON Z.REDF_ADSB_OC_YM < A01.BASE_YM -- 이전에 되물림된 건들 AND Z.PERF_YM = A01.PERF_YM AND Z.FEE_CD = 'W020081' AND Z.OG_TP_CD = A01.OG_TP_CD AND Z.PRTNR_NO = A01.PRTNR_NO AND Z.REDF_ADSB_DV_CD = '02' AND Z.REDF_ADSB_TP_CD = '0202' AND Z.REDF_ADSB_CTR_CNFM_AMT > 0 -- 실제 반영된 되물림 금액 AND Z.DTA_DL_YN = 'N' WHERE 1 = 1 AND A01.BASE_YM = #{baseYm} -- 변수 변수 변수 변수 AND A01.PERF_YM BETWEEN TO_CHAR(ADD_MONTHS(TO_DATE(#{baseYm}, 'YYYYMM'), -60), 'YYYYMM') AND TO_CHAR(ADD_MONTHS(TO_DATE(#{baseYm}, 'YYYYMM'), -1), 'YYYYMM') AND A01.PERF_YM BETWEEN #{apyStrtYm} AND #{apyEndYm} AND A01.PERF_AGRG_CRT_DV_CD = #{perfAgrgCrtDvCd} AND A01.FEE_TCNT_DV_CD = '02' -- 고정 / 2차만 AND A01.PERF_ATC_CD = 'W02P00002' -- 고정 / 판매인정건수 AND A01.CO_CD = '2000' -- 고정 AND A01.OG_TP_CD = 'W02' -- 고정 AND A01.PERF_DV_CD = '0' -- 고정 / 개인 AND A01.DTA_DL_YN = 'N' AND A.QLF_DV_CD = '3' /* 당월 자격 확인 */ GROUP BY A01.BASE_YM ,A01.PERF_YM ,A01.PERF_DV_CD ,A01.CO_CD ,A01.OG_TP_CD ,A01.PRTNR_NO ,A01.PERF_VAL ,A.QLF_DV_CD ,B.FEE_CD ,B.FEE_CTR_CNFM_AMT -- 최초 수수료 금액 ,C.STRTDT ,D.OPNG_CD ) WMTOT UNION ALL /* 23년4