N C.og_tp_Cd = A01.OG_TP_CD AND C.PRTNR_NO = A01.PRTNR_NO AND TO_CHAR(LAST_DAY(TO_DATE(A01.PERF_YM||'01','YYYYMMDD') ),'YYYYMMDD' ) BETWEEN C.STRTDT AND C.ENDDT AND C.QLF_DV_CD = '1' AND NVL(C.QLF_APLC_DV_CD,'0') IN ('1','2') -- 승격,자격해제(과거) 3 : 보류 는 제외 AND C.DTA_DL_YN = 'N' WHERE 1 = 1 AND A01.BASE_YM = #{baseYm} -- 변수 변수 변수 변수 AND A01.PERF_YM BETWEEN TO_CHAR(ADD_MONTHS(TO_DATE(#{baseYm}, 'YYYYMM'), -60), 'YYYYMM') AND TO_CHAR(ADD_MONTHS(TO_DATE(#{baseYm}, 'YYYYMM'), -1), 'YYYYMM') AND A01.PERF_YM BETWEEN #{apyStrtYm} AND #{apyEndYm} AND A01.PERF_AGRG_CRT_DV_CD = #{perfAgrgCrtDvCd} AND A01.FEE_TCNT_DV_CD = '02' -- 고정 / 2차만 AND A01.PERF_ATC_CD = 'W02P00002' -- 고정 / 판매인정건수 AND A01.CO_CD = '2000' -- 고정 AND A01.OG_TP_CD = 'W02' -- 고정 AND A01.PERF_DV_CD = '0' -- 고정 / 개인 AND A01.DTA_DL_YN = 'N' AND A.QLF_DV_CD = '1' /* 당월 자격 확인 */ GROUP BY A01.BASE_YM ,A01.PERF_YM ,A01.PERF_DV_CD ,A01.CO_CD ,A01.OG_TP_CD ,A01.PRTNR_NO ,A01.PERF_VAL ,A.QLF_DV_CD ,NVL(SUBSTR(C.STRTDT,1,6),'199901') ,B.FEE_CD ,B.FEE_CTR_CNFM_AMT ) SUTOT ) SRC WHERE 1 = 1 AND SRC.REDF_ADSB_OC_AMT > 0

