4,5) THEN -- 차수 CASE WHEN WMIMSI.PERF_VAL >= 6 THEN 250000 WHEN WMIMSI.PERF_VAL >= 3 THEN 150000 ELSE 0 END ELSE 0 END ELSE 0 END ELSE 0 END AS FEE_CALC_AMT_NEW FROM ( SELECT A01.BASE_YM ,A01.PERF_YM ,A01.PERF_DV_CD ,A01.CO_CD ,A01.OG_TP_CD ,A01.PRTNR_NO ,A01.PERF_VAL ,B.FEE_CD ,B.FEE_CTR_CNFM_AMT -- 수수료 금액 ,NVL(FF.QLF_DV_CD,NVL(GG.QLF_DV_CD,'0')) AS QLF_DV_CD -- 자격 ,NVL(CEIL(MONTHS_BETWEEN(TO_DATE(A01.PERF_YM||'01','YYYYMMDD'),TO_DATE(FF.STRTDT,'YYYYMMDD'))) + 1, CEIL(MONTHS_BETWEEN(TO_DATE(A01.PERF_YM||'01','YYYYMMDD'),TO_DATE(GG.STRTDT,'YYYYMMDD'))) + 1 ) AS NMN_N -- 차월수 ,NVL(EE.OPNG_CD,'0') AS OPNG_CD FROM TB_FEAM_NTORP_PERF_MM_CL A01 -- 파트너별실적 -- W02P00002 JOIN TB_OGBS_MM_PRTNR_IZ A ON A.BASE_YM = A01.PERF_YM AND A.OG_TP_CD = A01.OG_TP_CD AND A.PRTNR_NO = A01.PRTNR_NO AND A.PSTN_DV_CD = '15' JOIN TB_FEAM_FEE_CALC_BAS B -- 최초 수수료 ON B.BASE_YM = A01.PERF_YM -- 기준과 실적 연동 AND B.PERF_YM = A01.PERF_YM AND B.OJ_DSB_YM = A01.PERF_YM AND B.CO_CD = A01.CO_CD AND B.OG_TP_CD = A01.OG_TP_CD AND B.PRTNR_NO = A01.PRTNR_NO AND B.FEE_CD = 'W020081' -- 고정 AND B.FEE_TCNT_DV_CD = '02' -- 고정 / 2차 AND B.FEE_CALC_TP_CD = '01' -- 고정 / 수수료 AND B.FEE_CTR_CNFM_AMT > 0 -- 고정 AND B.DTA_DL_YN = 'N' LEFT OUTER JOIN ( SELECT A.BASE_YM , A.OG_TP_CD , A.PRTNR_NO, NVL(A.OPNG_CD,'0') AS OPNG_CD FROM TB_FEAM_WELS_MNGER_OPNG_BAS A JOIN ( SELECT B.BASE_YM,B.OG_TP_CD ,B.PRTNR_NO ,MAX(B.TCNT_DV_CD) AS TCNT_DV_CD FROM TB_FEAM_WELS_MNGER_OPNG_BAS B WHERE 1 = 1 GROUP BY B.BASE_YM,B.OG_TP_CD ,B.PRTNR_NO ) B ON A.BASE_YM = B.BASE_YM AND A.OG_TP_CD = B.OG_TP_CD AND A.PRTNR_NO = B.PRTNR_NO AND A.TCNT_DV_CD = B.TCNT_DV_CD ) EE ON A01.PERF_YM = EE.BASE_YM AND A01.OG_TP_CD = EE.OG_TP_CD AND A01.PRTNR_NO = EE.PRTNR_NO LEFT OUTER JOIN ( SELECT A00.BASE_YM, A00.PERF_YM, A00.FEE_TCNT_DV_CD, A00.PERF_AGRG_CRT_DV_CD, A00.PERF_ATC_CD, A00.CO_CD ,A.OG_TP_CD,A.PRTNR_NO ,A.QLF_DV_CD,CASE WHEN A.CV_DT IS NOT NULL THEN NVL(B.STRTDT,A.STRTDT) ELSE A.STRTDT END AS STRTDT FROM TB_OGPS_PLAR_QLF_CH_IZ A JOIN TB_FEAM_NTORP_PERF_