V_CD IN ('1') /* 23년 4월 자격 확인 */ AND NVL(D.QLF_DV_CD,'2') NOT IN ('3','6') /* 차월 자격 확인 */ GROUP BY A01.BASE_YM ,A01.PERF_YM ,A01.PERF_DV_CD ,A01.CO_CD ,A01.OG_TP_CD ,A01.PRTNR_NO ,A01.PERF_VAL ,A.QLF_DV_CD ,C.QLF_DV_CD ,C.STRTDT ,B.FEE_CD ,B.FEE_CTR_CNFM_AMT ) SUTOT ) SRC WHERE 1 = 1 AND SRC.REDF_ADSB_OC_AMT > 0
