'1') AND NVL(A.QLF_APLC_DV_CD,'0') IN ('1','2') -- 승격,자격해제(과거) 3 : 보류 는 제외 ) ZZ ON AA.OG_TP_CD = ZZ.OG_TP_CD AND AA.PRTNR_NO = ZZ.PRTNR_NO LEFT OUTER JOIN TB_FEAM_MACUP_PERF_CL T01 ON AA.BASE_YM = T01.BASE_YM AND #{mmAcuPerfAgrgCrtDvCd} = T01.MM_ACU_PERF_AGRG_CRT_DV_CD AND AA.OG_TP_CD = T01.OG_TP_CD AND AA.PRTNR_NO = T01.PRTNR_NO AND 'W02P00002' = T01.PERF_ATC_CD -- 고정 판매건수 AND '0' = T01.PERF_DV_CD -- 고정 개인실적 LEFT OUTER JOIN TB_FEAM_PRTNR_PERF_MM_ACU_CL S01 ON AA.BASE_YM = S01.BASE_YM AND #{mmAcuPerfAgrgCrtDvCd} = S01.MM_ACU_PERF_AGRG_CRT_DV_CD AND AA.OG_TP_CD = S01.OG_TP_CD AND AA.PRTNR_NO = S01.PRTNR_NO AND 'W02P00116' = S01.PERF_ATC_CD -- 고정 보수교육최종(129)수료여부 WHERE 1 = 1 AND AA.BASE_YM = #{baseYm} -- 변수 기준년월 BASE_YM = 202305
AND AA.PRTNR_NO = #{prtnrNo}
AND AA.OG_TP_CD = 'W02' AND AA.PSTN_DV_CD = '15' AND AA.QLF_DV_CD NOT IN ('3', '6') /* 당월 자격 확인 */ AND NVL(GG.QLF_DV_CD, '2') NOT IN ('3', '6') /* 차월 자격 확인 */ AND ZZ.QLF_DV_CD = '1' /* 4월 자격 수석 플래너 */ AND AA.PERF_EXCD_OJ_YN = 'N' ) SUB) RESULT WHERE 1 = 1 AND RESULT.FEE_CALC_AMT > 0

/* BS장려(판매자)수수료(W020122) 계산 SQL (소스 : LC56U111, 컬럼 : AKSD41) */ INSERT INTO TB_FEAM_FEE_SML_BAS ( /* 수수료시뮬레이션기본 */ BASE_YM /* 기준년월 */ , PERF_YM /* 실적년월 */ , OJ_DSB_YM /* 대상지급년월 */ , CO_CD /* 회사코드 */ , OG_TP_CD /* 조직유형코드 */ , PRTNR_NO /* 파트너번호 */ , FEE_CD /* 수수료코드 */ , DTA_CRT_FEE_CD /* 데이터생성수수료코드 */ , FEE_DSB_MTHD_CD /* 수수료지급방식코드 */ , SML_CRT_TP_CD /* 시뮬레이션생성유형코드 */ , FEE_CALC_AMT /* 수수료계산금액 */ , FNL_FEE_YN /* 최종수수료여부 */
) SELECT AAA.BASE_YM , AAA.BASE_YM AS PERF_YM , AAA.BASE_YM AS OJ_DSB_YM , #{coCd} AS CO_CD , AAA.OG_TP_CD , AAA.PRTNR_NO , #{feeCd} AS FEE_CD , #{dtaCrtFeeCd} AS DTA_CRT_FEE_CD , '01' AS FEE_DSB_MTHD_CD -- 수수료지급방식코드 , #{smlCrtTpCd} AS SML_CRT_TP_CD , AAA.FEE_CALC_AMT , #{fnlFeeYn} AS FNL_FEE_YN
FROM (SELECT AA.BASE_YM , AA.OG_TP_CD , AA.PRTNR_NO , TRUNC( CASE WHEN AA.TXT_BIGO = 'A' THEN AA.CNT_BS_UNDER * 4000 + CNT_BS_OVER * 2500 WHEN AA.TXT_BIGO = 'B' THEN AA.CNT_BS_UNDER * 3800 + CNT_BS_OVER * 2200 WHEN AA.TXT_BIGO = 'C' THEN AA.CNT_BS_UNDER * 3500 + CNT_BS_OVER * 2000 WHEN AA.TXT_BIGO = 'D' THEN AA.CNT_BS_UNDER * 2800 + CNT_BS_OVER * 1500 WHEN AA.TXT_BIGO = 'E' THEN AA.CNT_BS_UNDER * 2000 + CNT_BS_OVER * 900 WHEN AA.TXT_BIGO = 'F' THEN AA.CNT_BS_UNDER * 1200 + CNT_BS_OVER * 700 WHEN AA.TXT_BIGO = 'G' THEN AA.CNT_BS_UNDER * 500 + CNT_BS_OVER * 300 WHEN AA.TXT_BIGO = 'H' THEN AA.CNT_BS_UNDER * 200 ELSE 0 END * CNT_RATE ) AS FEE_CALC_AMT FROM (SELECT A.BASE_YM , A.OG_TP_CD , A.PRTNR_NO , NVL(B.PERF_VAL, 0) AS CNT_SUNZEON -- 순증건수 , NVL(C.PERF_VAL, 0) AS CNT_FSVST -- 허위방문건수 , NVL(D.PERF_VAL, 0) AS CNT_BS -- BS건수 , NVL(E.PERF_VAL, 0) AS CNT_PAN -- 가전(판매)건수 , MONTHS_BETWEEN(TO_DATE(A.BASE_YM||'01','YYYYMMDD'),TO_DATE(NVL(F.STRTDT,A.BASE_YM||'01'),'YYYYMMDD')) + 1 AS CNT_MON , CASE WHEN NVL(D.PERF_VAL, 0) >= 250 THEN 250 ELSE NVL(D.PERF_VAL, 0) END AS CNT_BS_UNDER , CASE WHEN NVL(D.PERF_VAL, 0) >= 250 THEN NVL(D.PERF_VAL, 0) - 250 ELSE 0 END AS CNT_BS_OVER , CASE WHEN NVL(E.PERF_VAL, 0) >= 20 AND NVL(C.PERF_VAL, 0) < 1 THEN 'A' -- 20건 이상 WHEN NVL(E.PERF_VAL, 0) >= 20 AND NVL(C.PERF_VAL, 0) >= 1 THEN 'B' -- 한칸 하위 WHEN NVL(E.PERF_VAL, 0) >= 15 AND NVL(C.PERF_VAL, 0) < 1 THEN 'B' -- 15건 이상 WHEN NVL(E.PERF_VAL, 0) >= 15 AND NVL(C.PERF_VAL, 0) >= 1 THEN 'C' -- 한칸 하위 WHEN NVL(E.PERF_VAL, 0) >= 12 AND NVL(C.PERF_VAL, 0) < 1 THEN 'C' -- 12건 이상 WHEN NVL(E.PERF_VAL, 0) >= 12 AND NVL(C.PERF_VAL, 0) >= 1 THEN 'D' -- 한칸 하위 WHEN NVL(E.PERF_VAL, 0) >= 10 AND NVL(C.PERF_VAL, 0) < 1 THEN 'D' -- 10건 이상 WHEN NVL(E.PERF_VAL, 0) >= 10 AND NVL(C.PERF_VAL, 0) >= 1 THEN 'E' -- 한칸 하위 WHEN NVL(E.PERF_VAL, 0) >= 8 AND NVL(C.PERF_VAL, 0) < 1 THEN 'E' -- 8건 이상 WHEN NVL(E.PERF_VAL, 0) >= 8 AND NVL(C.PERF_VAL, 0) >= 1 THEN 'F' -- 한칸 하위 WHEN NVL(E.PERF_VAL, 0) >= 6 AND NVL(C.PERF_VAL, 0) < 1 THEN 'F' -- 6건 이상 WHEN NVL(E.PERF_VAL, 0) >= 6 AND NVL(C.PERF_VAL, 0) >= 1 THEN 'G' -- 한칸 하위 WHEN NVL(E.PERF_VAL, 0) >= 4 AND NVL(C.PERF_VAL, 0) < 1 THEN 'G' -- 4건 이상 WHEN NVL(E.PERF_VAL, 0) >= 4 AND NVL(C.PERF_VAL, 0) >= 1 THEN 'H' -- 한칸 하위 WHEN NVL(E.PERF_VAL, 0) >= 2 AND NVL(C.PERF_VAL, 0) < 1 THEN 'H' -- 2건 이상 WHEN NVL(E.PERF_VAL, 0) >= 2 AND NVL(C.PERF_VAL, 0) >= 1 THEN 'I' -- 한칸 하위 ELSE 'I' END AS TXT_BIGO , CASE WHEN CEIL(MONTHS_BETWEEN(TO_DATE(A.BASE_YM||'01','YYYYMMDD'),TO_DATE(NVL(F.STRTDT,A.BASE_YM||'01'),'YYYYMMDD'))) + 1 <= 6 THEN 1 ELSE (CASE WHEN NVL(B.PERF_VAL, 0) >= 2 THEN 1.1 WHEN NVL(B.PERF_VAL, 0) >= 0 THEN 1 WHEN NVL(B.PERF_VAL, 0) >= -2 THEN 0.9 ELSE 0.8 END) END AS CNT_RATE FROM TB_OGBS_MM_PRTNR_IZ A /* M조직 기본 */ LEFT OUTER JOIN TB_FEAM_MACUP_PERF_CL B ON A.BASE_YM = B.BASE_YM AND #{mmAcuPerfAgrgCrtDvCd} = B.MM_ACU_PERF_AGRG_CRT_DV_CD AND A.OG_TP_CD = B.OG_TP_CD AND A.PRTNR_NO = B.PRTNR_NO AND 'W02P00113' = B.PERF_ATC_CD -- 순증 AND '0' = B.PERF_DV_CD -- 고정 개인실적 LEFT OUTER JOIN TB_FEAM_MACUP_PERF_CL C ON A.BASE_YM = C.BASE_YM AND #{mmAcuPerfAgrgCrtDvCd} = C.MM_ACU_PERF_AGRG_CRT_DV_CD AND A.OG_TP_CD = C.OG_TP_CD AND A.PRTNR_NO = C.PRTNR_NO AND 'W02P00097' = C.PERF_ATC_CD -- 허위방문 AND '0' = C.PERF_DV_CD -- 고정 개인실적 LEFT OUTER JOIN TB_FEAM_MACUP_PERF_CL D ON A.BASE_YM = D.BASE_YM AND #{mmAcuPerfAgrgCrtDvCd} = D.MM_ACU_PERF_AGRG_CRT_DV_CD AND A.OG_TP_CD = D.OG_TP_CD AND A.PRTNR_NO = D.PRTNR_NO AND 'W02P00132' = D.PERF_ATC_CD -- 고정 BS건수 AND '0' = D.PERF_DV_CD -- 고정 개인실적 LEFT OUTER JOIN TB_FEAM_MACUP_PERF_CL E ON A.BASE_YM = E.BASE_YM AND #{mmAcuPerfAgrgCrtDvCd} = E.MM_ACU_PERF_AGRG_CRT_DV_CD AND A.OG_TP_CD = E.OG_TP_CD AND A.PRTNR_NO = E.PRTNR_NO AND 'W02P00002' = E.PERF_ATC_CD -- 고정 판매건수 AND '0' = E.PERF_DV_CD -- 고정 개인실적 LEFT OUTER JOIN (SELECT A.og_tp_Cd,A.prtnr_no ,CASE WHEN A.CV_DT IS NOT NULL THEN NVL(B.STRTDT,A.STRTDT) ELSE A.STRTDT END AS STRTDT FROM WSMDBS.TB_OGPS_PLAR_QLF_CH_IZ A LEFT OUTER JOIN TB_OGPS_PLAR_QLF_CH_IZ B ON A.og_tp_Cd = B.og_tp