mapper.select:
SELECT COUNT(1)
  FROM TB_FEAM_WELS_FEE_PDCT_TP_BAS
 WHERE BASE_PD_CD = #{basePdCd}
   AND APY_STRT_YM = #{apyStrtYm}
   AND APY_END_YM = #{apyEndYm}

mapper.insert:
INSERT INTO TB_FEAM_WELS_FEE_PDCT_TP_BAS (  /* 웰스수수료제품유형기본 */
       BASE_PD_CD       /* 기준상품코드 */
     , APY_STRT_YM      /* 적용시작년월 */
     , APY_END_YM       /* 적용종료년월 */
     , FEE_PDCT_TP_CD1  /* 수수료제품유형코드1 */
     , FEE_PDCT_TP_CD2  /* 수수료제품유형코드2 */
     , DTA_DL_YN        /* 데이터삭제여부 */
     )
VALUES (
       #{basePdCd}
     , #{apyStrtYm}
     , #{apyEndYm}
     , #{feePdctTpCd1}
     , #{feePdctTpCd2}
     , 'N'
  )
