mapper.insert:
  INSERT INTO TB_OGBS_WK_GRP_BLG_DTL (  /* 작업그룹소속상세 */
         OG_TP_CD
       , PRTNR_NO
       , APY_SEQN
       , VL_STRTDT
       , VL_ENDDT
       , WK_GRP_CD
       , EGER_RSB_CD
       , WKCR_CD
       , RMK_CN
       , CRAL_LOCARA_TNO
       , MEXNO_ENCR
       , CRAL_IDV_TNO
       , AV_CPBLT_DV_CD
       , THEO_EVL_TP_CD
       , DTA_DL_YN
       )
VALUES (
        #{ogTpCd}
      , #{prtnrNo}
      , (SELECT NVL(MAX(APY_SEQN), 0) +1
           FROM TB_OGBS_WK_GRP_BLG_DTL
          WHERE OG_TP_CD = #{ogTpCd}
            AND PRTNR_NO = #{prtnrNo}
        )
      , #{vlStrtdt}
      , #{vlEnddt}
      , #{wkGrpCd}
      , #{egerRsbCd}
      , #{wkcrCd}
      , #{rmkCn}
      , #{cralLocaraTno}
      , #{mexnoEncr}
      , #{cralIdvTno}
      , #{avCpbltDvCd}
      , #{theoEvlTpCd}
      , #{dtaDlYn}
       )

mapper.update:
UPDATE TB_OGBS_MM_PRTNR_IZ
   SET WK_GRP_CD = #{wkGrpCd}
     , WK_GRP_NM = #{wkGrpCdNm}
     , WKCR_NO = #{wkcrCd}

 WHERE 1=1
   AND OG_TP_CD = #{ogTpCd}
   AND PRTNR_NO = #{prtnrNo}
   AND BASE_YM = SUBSTR(#{vlStrtdt},0,6)

mapper.insert:
  INSERT INTO TB_OGPS_EGER_GD_RGST_IZ(
        OG_TP_CD
      , PRTNR_NO
      , APY_SEQN
      , PRTNR_GD_CD
      , APY_STRTDT
      , APY_ENDDT
      , RMK_CN
      , DTA_DL_YN
      )
VALUES (
        #{ogTpCd}
      , #{prtnrNo}
      , (SELECT NVL(MAX(APY_SEQN), 0) +1
           FROM TB_OGPS_EGER_GD_RGST_IZ
          WHERE OG_TP_CD = #{ogTpCd}
            AND PRTNR_NO = #{prtnrNo}
        )
      , #{prtnrGdCd}
      , #{apyStrtDt}
      , #{apyEnddt}
      , #{rmkCn}
      , #{dtaDlYn}
       )

MERGE INTO TB_OGPS_EGER_GD_RGST_IZ A USING (
SELECT #{item.ogTpCd} AS OG_TP_CD , #{item.prtnrNo} AS PRTNR_NO , NULL AS APY_SEQN , #{item.prtnrGdCd} AS PRTNR_GD_CD , #{item.apyStrtDt} AS APY_STRTDT , #{item.apyEnddt} AS APY_ENDDT , NULL AS RMK_CN , 'N' AS DTA_DL_YN FROM DUAL
)T1 ON (A.PRTNR_NO = T1.PRTNR_NO AND A.OG_TP_CD = T1.OG_TP_CD AND A.APY_STRTDT = #{apyStrtDt}) WHEN MATCHED THEN UPDATE SET APY_ENDDT = #{apyEnddt} , PRTNR_GD_CD = #{prtnrGdCd} , RMK_CN = #{rmkCn} , DTA_DL_YN = #{dtaDlYn}
WHEN NOT MATCHED THEN INSERT ( OG_TP_CD , PRTNR_NO , APY_SEQN , PRTNR_GD_CD , APY_STRTDT , APY_ENDDT , RMK_CN , DTA_DL_YN ) VALUES ( T1.OG_TP_CD , T1.PRTNR_NO , (SELECT NVL(MAX(APY_SEQN), 0) +1 FROM TB_OGPS_EGER_GD_RGST_IZ WHERE OG_TP_CD = #{ogTpCd} AND PRTNR_NO = #{prtnrNo} ) , T1.PRTNR_GD_CD , T1.APY_STRTDT , T1.APY_ENDDT , T1.RMK_CN , T1.DTA_DL_YN )

mapper.update:
UPDATE TB_OGBS_MM_PRTNR_IZ
SET ROL_DV_CD = #{prtnrGdCd}

WHERE 1=1
AND OG_TP_CD = #{ogTpCd}
AND PRTNR_NO = #{prtnrNo}
AND BASE_YM = SUBSTR(#{apyStrtDt},0,6)

UPDATE TB_OGBS_PRTNR_DTL
SET ROL_DV_CD = #{prtnrGdCd}

WHERE 1=1
AND OG_TP_CD = #{ogTpCd}
AND PRTNR_NO = #{prtnrNo}
