mapper.select:
SELECT B.OG_CD /* 조직코드 */ , B.OG_NM /* 조직명 */ , A.PRTNR_NO /* 파트너번호 */ , A.PRTNR_KNM /* 파트너명 */ , CASE WHEN PSTN_DV_CD IN ('86', '87', '88') THEN F_CMZ_CD_NM('TNT_WELLS', 'SAP_PSTN_DV_CD', A.PSTN_DV_CD) ELSE F_CMZ_CD_NM('TNT_WELLS', 'HIR_FOM_CD', A.HIR_FOM_CD) END AS PSTN_DV_NM /* 직무명 */ , F.MLNG_CNTN AS RSB_DV_NM /* 직책구분코드명 */ , A.WK_GRP_CD /* 작업그룹코드 */ , A.WK_GRP_NM , CASE WHEN BIZ_AGNT_PRTNR_NO IS NULL THEN 'N' ELSE 'Y' END AS BIZ_AGNT_YN /* 업무대행여부 */ , C.WRK_DT AS WRK_DT /* 근무일자 */ , CASE WHEN E.PHLD_YN='Y' THEN TO_CHAR(TO_DATE(E.BASE_Y||E.BASE_MM||E.BASE_D, 'YYYY-MM-DD'), 'DY','NLS_DATE_LANGUAGE=KOREAN') ||'(공휴일)' ELSE TO_CHAR(TO_DATE(E.BASE_Y||E.BASE_MM||E.BASE_D, 'YYYY-MM-DD'), 'DY','NLS_DATE_LANGUAGE=KOREAN') END AS DOW_DV /* 근무요일 */ , CASE WHEN C.EGER_WRK_STAT_CD IS NULL THEN '000' ELSE C.EGER_WRK_STAT_CD END AS EGER_WRK_STAT_CD /* 엔지니어근무상태코드 */ , CASE WHEN C.EGER_WRK_STAT_CD <> '000' THEN F_CMZ_CD_NM('TNT_WELLS', 'EGER_WRK_STAT_CD', C.EGER_WRK_STAT_CD) ELSE F_CMZ_CD_NM('TNT_WELLS', 'EGER_WRK_STAT_CD', '000') END EGER_WRK_STAT_NM /* 엔지니어근무상태코드명 */ , C.RMK_CN /* 비고내용 */ , D.VCN_STRT_DT /* 휴가시작일자 */ , D.VCN_END_DT /* 휴가종료일자 */ , D.BIZ_AGNT_PRTNR_NO /* 업무대행파트너번호 */ , D.PRTNR_KNM AS BIZ_AGNT_NM /* 파트너명 */ , C.PCP_PRTNR_NO /* 처리자파트너번호 */ , H.PRTNR_KNM AS PCP_PRTNR_NM /* 파트너명 */ , C.PROCS_DTM /* 처리일시 */ FROM TB_OGBS_MM_PRTNR_IZ A /* --> 월파트너내역 */ LEFT OUTER JOIN TB_OGBS_MM_OG_IZ B /* --> 월조직내역 */ ON B.OG_TP_CD = A.OG_TP_CD AND B.BASE_YM = A.BASE_YM AND B.OG_ID = A.OG_ID INNER JOIN TB_OGPS_EGER_ATDC_IZ C /* -->엔지니어출근내역 */ ON C.OG_TP_CD = A.OG_TP_CD AND C.PRTNR_NO = A.PRTNR_NO AND C.WRK_DT = #{baseDt} LEFT OUTER JOIN ( SELECT D1.*, G.PRTNR_KNM FROM TB_OGPS_EGER_VCN_IZ D1 /* --> 엔지니어휴가내역 */ INNER JOIN TB_OGBS_PRTNR_BAS G /* --> 파트너기본(업무대행파트너) */ ON G.OG_TP_CD = D1.OG_TP_CD AND G.PRTNR_NO = D1.BIZ_AGNT_PRTNR_NO WHERE #{baseDt} BETWEEN D1.VCN_STRT_DT AND D1.VCN_END_DT ) D ON D.OG_TP_CD = A.OG_TP_CD AND D.PRTNR_NO = A.PRTNR_NO LEFT OUTER JOIN TB_SVPD_SV_CLND_BAS E /* --> 서비스달력기본*/ ON #{baseDt} = E.BASE_Y || E.BASE_MM || E.BASE_D LEFT OUTER JOIN ( SELECT T1.CD_VLD_VAL, T1.ARYL_ORD, T2.MLNG_CNTN --, T2.* FROM T_CMZ_CD_D T1 LEFT OUTER JOIN T_CMZ_MLNG_D T2 ON T1.TENANT_ID = T2.TENANT_ID AND T1.CD_DTL_MLNG_ID = T2.MLNG_ID AND T2.LNG_ID = 'ko' WHERE 1 = 1 AND (T1.TENANT_ID = 'TNT_BASE' OR T1.TENANT_ID = 'TNT_WELLS') AND T1.CD_ID = 'RSB_DV_CD' ) F ON F.CD_VLD_VAL = A.RSB_DV_CD /* 직책명 및 직잭별 소트용*/ LEFT OUTER JOIN ( SELECT H1.OG_TP_CD , H1.PRTNR_NO , H1.PRTNR_KNM , RANK() OVER (PARTITION BY H1.PRTNR_NO ORDER BY PRTNR_NO, OG_TP_CD DESC) RANK_P FROM TB_OGBS_PRTNR_BAS H1 WHERE H1.OG_TP_CD IN ('W06', 'HR1') ) H ON H.PRTNR_NO = C.PCP_PRTNR_NO AND H.RANK_P = 1 /* 처리자명 */ WHERE A.OG_TP_CD = 'W06' AND A.BZ_STAT_CD = 1 AND A.BASE_YM = SUBSTR(#{baseDt}, 1, 6) AND B.OG_ID IN (
)
AND B.DGR1_LEVL_OG_ID = #{ogLevlDvCd1}
ORDER BY A.OG_CD, F.ARYL_ORD DESC , A.PRTNR_NO

mapper.update:
UPDATE TB_OGPS_EGER_ATDC_IZ SET EGER_WRK_STAT_CD = #{egerWrkStatCd} /*엔지니어근무상태코드*/ , RMK_CN = #{rmkCn} /*비고내용*/ , PCP_OG_TP_CD = #{session.ogTpCd} /*처리자조직유형코드*/ , PCP_PRTNR_NO = #{session.employeeIDNumber} /*처리자사번*/ , PROCS_DTM = TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS') /*처리일시*/
WHERE 1=1 AND OG_TP_CD = 'W06' AND PRTNR_NO = #{prtnrNo}
AND WRK_DT = #{wrkDt}
AND WRK_DT BETWEEN #{vcnStrtDt} AND #{vcnEndDt}

