       , CRAL_IDV_TNO                   /* 휴대개별전화번호 */
       , AV_CPBLT_DV_CD                 /* 평균역량구분코드 */
       , THEO_EVL_TP_CD                 /* 이론평가유형코드 */
       , DTA_DL_YN                      /* 데이터삭제여부 */
       )
