mapper.select:
SELECT T4.DGR1_LEVL_OG_NM /* 총괄단 */ , T4.DGR2_LEVL_OG_NM /* 지역단 */ , T4.OG_CD /* 소속 */ , T3.BLD_NM /* 빌딩명 */ , T1.PRTNR_NO /* 번호 */ , T1.PRTNR_KNM /* 성명 */ , T2.RSB_DV_CD /* 직책 */ , F_CMZ_CD_NM(#{session.tenantId}, 'RSB_DV_CD', T2.RSB_DV_CD) AS RSB_DV_NM /* 직책명 */ , T1.BIZ_USE_IDV_TNO /* 업무사용개별전화번호 */ , T1.BIZ_USE_EXNO_ENCR /* 업무사용전화국번호암호화 */ , T1.BIZ_USE_LOCARA_TNO /* 업무사용지역전화번호 */ , T1.BRYY_MMDD /* 주민번호(생년월일) */ , T0.RCRT_WRTE_DT /* 리쿠르팅일자 */ , T2.FNL_CLTN_DT /* 최종해약일자 */ , T5.EDU143 /* Pre스타트업 */ , T5.EDU96 /* 스타트업 */ , T2.QLF_DV_CD /* 자격구분코드 */ , F_CMZ_CD_NM(#{session.tenantId}, 'QLF_DV_CD', T2.QLF_DV_CD) AS QLF_DV_NM /* 자격명 */ FROM TB_OGBS_PRTNR_BAS T1 INNER JOIN TB_OGBS_PRTNR_DTL T2 ON T2.OG_TP_CD = T1.OG_TP_CD AND T2.PRTNR_NO = T1.PRTNR_NO AND T2.DTA_DL_YN = 'N' INNER JOIN TB_OGBS_MM_PRTNR_IZ T0 ON T0.BASE_YM = TO_CHAR(SYSDATE, 'YYYYMM') AND T0.OG_TP_CD = T1.OG_TP_CD AND T0.PRTNR_NO = T1.PRTNR_NO AND T0.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_OGBS_BLD_BAS T3 ON T3.RSPP_OG_TP_CD = T1.OG_TP_CD AND T3.RSPP_PRTNR_NO = T1.PRTNR_NO AND T3.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_OGBS_MM_OG_IZ T4 ON T4.BASE_YM = TO_CHAR(SYSDATE, 'YYYYMM') AND T4.OG_TP_CD = T1.OG_TP_CD AND T4.OG_ID = T1.OG_ID AND T4.DTA_DL_YN = 'N' LEFT OUTER JOIN (SELECT PRTNR_NO , MAX(CASE WHEN EDUC_CRSE_NO = '143' THEN EDUC_CPC_ACKMT_DT ELSE '' END) AS EDU143 /* Pre스타트업 */ , MAX(CASE WHEN EDUC_CRSE_NO = '96' THEN EDUC_CPC_ACKMT_DT ELSE '' END) AS EDU96 /* 스타트업 */ FROM TB_PSCA_MCPTN_EDUC_IZ WHERE EDUC_CRSE_CRT_BASE_YM <= '202307' AND OG_TP_CD = 'W02' AND EDUC_CRSE_NO IN ('143', '96') AND EDUC_CPC_ACKMT_YN = 'Y' AND DTA_DL_YN = 'N' GROUP BY PRTNR_NO) T5 ON T5.PRTNR_NO = T1.PRTNR_NO WHERE 1=1 AND T1.DTA_DL_YN = 'N' AND T1.OG_TP_CD = 'W02'
AND T1.PRTNR_KNM LIKE #{prtnrKnm} || '%'
AND T1.PRTNR_NO = #{prtnrNo}
AND T2.QLF_DV_CD = #{qlfDvCd}
ORDER BY T4.OG_CD, T1.PRTNR_NO, T1.PRTNR_KNM

mapper.update:
UPDATE TB_OGPS_TOPMR_PLAR_APLC_IZ
   SET DTA_DL_YN = 'Y'  /* 데이터삭제여부 */

 WHERE 1=1
   AND MNGT_YM = #{mngtYm}
   AND APLC_SN = #{aplcSn}
   AND OG_TP_CD = 'W01'
   AND PRTNR_NO = #{prtnrNo}

mapper.select:
SELECT COUNT(1)
  FROM TB_OGPS_PLAR_QLF_CH_IZ
 WHERE 1=1
   AND DTA_DL_YN = 'N'
   AND MNGT_YM = #{mngtYm}
   AND OG_TP_CD = 'W02'
   AND PRTNR_NO = #{prtnrNo}
   AND QLF_DV_CD = #{qlfDvCd}

SELECT COUNT(1)
  FROM TB_OGPS_TOPMR_PLAR_APLC_IZ
 WHERE 1=1
   AND DTA_DL_YN = 'N'
   AND MNGT_YM = #{mngtYm}
   AND APLC_SN = #{aplcSn}
   AND OG_TP_CD = 'W01'
   AND PRTNR_NO = #{prtnrNo}

mapper.insert:
INSERT INTO TB_OGPS_TOPMR_PLAR_APLC_IZ (
     , MNGT_YM              /*관리년월*/
     , APLC_SN              /*신청일련번호*/
     , OG_TP_CD             /*조직유형코드*/
     , PRTNR_NO             /*파트너번호*/
     , QLF_DV_CD            /*자격구분코드*/
     , RFDT                 /*반영일자*/
     , UPGR_DMTN_DV_CD      /*승급강등구분코드*/
     , UPGR_YM              /*승급년월*/
     , DMTN_YM              /*강등년월*/
     , CNTR_DT              /*계약일자*/
     , CLTN_DT              /*해약일자*/
     , RCNTR_DT             /*재계약일자*/
     , UPGR_MCN             /*승급개월수*/
     , DTA_DL_YN		    /*데이터삭제여부*/

) VALUES (
       #{mngtYm}
     , (SELECT NVL(MAX(TO_NUMBER(APLC_SN)), 0)+1 FROM TB_OGPS_TOPMR_PLAR_APLC_IZ)
     , #{ogTpCd}
     , #{prtnrNo}
     , #{qlfDvCd}
     , #{rfdt}
     , #{upgrDmtnDvCd}
     , #{upgrYm}
     , #{dmtnYm}
     , #{cntrDt}
     , #{cltnDt}
     , #{rcntrDt}
     , #{upgrMcn}
     , #{dtaDlYn}
     , 'N'

)
