mapper.update:
INSERT INTO TB_PDBS_RGBS_WK_BASE_BAS (  /* 정기BS작업기준기본 */
          SV_PD_CD         /* 서비스상품코드 */
        , PDCT_PD_CD       /* 제품상품코드 */
        , DTL_SN           /* 상세일련번호 */
        , SV_BIZ_DCLSF_CD  /* 서비스업무세분류코드 */
        , VST_DV_CD        /* 방문구분코드 */
        , FILT_CHNG_LV_CD  /* 필터교체단계코드 */
        , PART_PD_CD       /* 부품상품코드 */
        , PART_USE_QTY     /* 부품사용수량 */
        , SV_PRD_MM_N      /* 서비스주기월수 */
        , SV_STRTMM_N      /* 서비스시작월수 */
        , SV_TMS           /* 서비스횟수 */
        , TOT_STPL_MCN     /* 총약정개월수 */
        , EXCD_MM_VAL      /* 제외월값 */
        , IST_MM           /* 설치월 */
        , STRT_WK_Y_VAL    /* 시작작업년도값 */
        , WK_MM            /* 작업월 */
        , DTA_DL_YN        /* 데이터삭제여부 */

    ) VALUES (
        #{info.svPdCd}
        , #{info.pdctPdCd}
        , NVL(( SELECT MAX(DTL_SN) + 1
              FROM TB_PDBS_RGBS_WK_BASE_BAS 
             WHERE SV_PD_CD = #{info.svPdCd}
               AND PDCT_PD_CD = #{info.pdctPdCd} 
          ), 1)
        , #{info.svBizDclsfCd}
        , #{info.vstDvCd}
        , #{info.filtChngLvCd}
        , #{info.partPdCd}
        , #{info.partUseQty}
        , #{info.svPrdMmN}
        , #{info.svStrtmmN}
        , #{info.svTms}
        , #{info.totStplMcn}
        , #{info.excdMmVal}
        , #{info.istMm}
        , #{info.strtWkYVal}
        , #{info.wkMm}
        , NVL(#{info.dtaDlYn}, 'N')

    )

mapper.insert:
INSERT INTO TB_PDBS_RGBS_WK_BASE_DTL (  /* 정기BS작업기준상세 */
          SV_PD_CD         /* 서비스상품코드 */
        , PDCT_PD_CD       /* 제품상품코드 */
        , DTL_SN           /* 상세일련번호 */
        , VST_NMN_N        /* 방문차월수 */
        , VST_DV_CD        /* 방문구분코드 */
        , SV_BIZ_DCLSF_CD  /* 서비스업무세분류코드 */
        , FILT_CHNG_LV_CD  /* 필터교체단계코드 */
        , PART_PD_CD       /* 부품상품코드 */
        , PART_USE_QTY     /* 부품사용수량 */
        , VST_NMN_PRD_VAL  /* 방문차월주기값 */
        , IST_MM           /* 설치월 */
        , STRT_WK_Y_VAL    /* 시작작업년도값 */
        , WK_MM            /* 작업월 */
        , TOT_STPL_MCN     /* 총약정개월수 */
        , CSTMW_PDCT_YN    /* 맞춤형제품여부 */
        , DTA_DL_YN        /* 데이터삭제여부 */

    ) VALUES (
        #{info.svPdCd}
        , #{info.pdctPdCd}
        , NVL(( SELECT MAX(DTL_SN) + 1
              FROM TB_PDBS_RGBS_WK_BASE_DTL 
             WHERE SV_PD_CD = #{info.svPdCd}
               AND PDCT_PD_CD = #{info.pdctPdCd} 
          ), 1)
        , #{info.vstNmnN}
        , #{info.vstDvCd}
        , #{info.svBizDclsfCd}
        , #{info.filtChngLvCd}
        , #{info.partPdCd}
        , #{info.partUseQty}
        , #{info.vstNmnPrdVal}
        , #{info.istMm}
        , #{info.strtWkYVal}
        , #{info.wkMm}
        , #{info.totStplMcn}
        , #{info.cstmwPdctYn}
        , NVL(#{info.dtaDlYn}, 'N')

    )

mapper.update:
MERGE INTO TB_PDBS_LVLH_CSTMW_FILT_BAS T1 /* 생활맞춤형필터기준 */ USING ( SELECT #{svPdCd} AS SV_PD_CD /* 서비스상품코드 */ , #{pdctPdCd} AS PDCT_PD_CD /* 제품상품코드 */ , #{partPdCd} AS PART_PD_CD /* 부품상품코드 */ , TO_NUMBER(NVL(#{dtlSn}, 0)) AS DTL_SN /* 상세일련번호 */ , #{vstDvCd} AS VST_DV_CD /* 방문구분코드 */ , #{prdMmVal} AS PRD_MM_VAL /* 주기월값 */ , #{chPdctPdCd} AS CH_PDCT_PD_CD /* 변경제품상품코드 */ , #{filtCmuCdv} AS FILT_CMU_CDV /* 필터공용코드값 */ , #{filtCmuNm} AS FILT_CMU_NM /* 필터공용명 */ , #{filtCmuEpl} AS FILT_CMU_EPL /* 필터공용설명 */ , NVL(#{dtaDlYn}, 'N') AS DTA_DL_YN /* 데이터삭제여부 */ FROM DUAL ) T2 ON (T1.SV_PD_CD = T2.SV_PD_CD /* 서비스상품코드 */ AND T1.PDCT_PD_CD = T2.PDCT_PD_CD /* 제품상품코드 */ AND T1.PART_PD_CD = T2.PART_PD_CD /* 부품상품코드 */ AND T1.DTL_SN = T2.DTL_SN /* 상세일련번호 */ ) WHEN MATCHED THEN UPDATE SET VST_DV_CD = T2.VST_DV_CD , PRD_MM_VAL = T2.PRD_MM_VAL , CH_PDCT_PD_CD = T2.CH_PDCT_PD_CD , FILT_CMU_CDV = T2.FILT_CMU_CDV , FILT_CMU_NM = T2.FILT_CMU_NM , FILT_CMU_EPL = T2.FILT_CMU_EPL
WHEN NOT MATCHED THEN INSERT ( SV_PD_CD , PDCT_PD_CD , PART_PD_CD , DTL_SN , VST_DV_CD , PRD_MM_VAL , CH_PDCT_PD_CD , FILT_CMU_CDV , FILT_CMU_NM , FILT_CMU_EPL , DTA_DL_YN
) VALUES ( T2.SV_PD_CD , T2.PDCT_PD_CD , T2.PART_PD_CD , NVL(( SELECT MAX(DTL_SN) + 1 FROM TB_PDBS_LVLH_CSTMW_FILT_BAS WHERE SV_PD_CD = T2.SV_PD_CD AND PDCT_PD_CD = T2.PDCT_PD_CD AND PART_PD_CD = T2.PART_PD_CD ), 1) , T2.VST_DV_CD , T2.PRD_MM_VAL , T2.CH_PDCT_PD_CD , T2.FILT_CMU_CDV , T2.FILT_CMU_NM , T2.FILT_CMU_EPL , T2.DTA_DL_YN )

UPDATE TB_PDBS_RGBS_WK_BASE_BAS
   SET DTA_DL_YN = 'Y'

 WHERE SV_PD_CD = #{svPdCd}

   AND PDCT_PD_CD = #{pdctPdCd}

   AND DTA_DL_YN = 'N'

UPDATE TB_PDBS_RGBS_WK_BASE_DTL
   SET DTA_DL_YN = 'Y'

 WHERE SV_PD_CD = #{svPdCd}

   AND PDCT_PD_CD = #{pdctPdCd}


   AND DTL_SN = #{dtlSn}

   AND DTA_DL_YN = 'N'

UPDATE TB_PDBS_LVLH_CSTMW_FILT_BAS
   SET DTA_DL_YN = 'Y'

 WHERE SV_PD_CD = #{svPdCd}

   AND PDCT_PD_CD = #{pdctPdCd}


   AND PART_PD_CD = #{partPdCd}


   AND DTL_SN = #{dtlSn}

   AND DTA_DL_YN = 'N'

mapper.select:
SELECT VST_DV_CD || ',' || PRD_MM_VAL || ',' || CH_PDCT_PD_CD
  FROM TB_PDBS_LVLH_CSTMW_FILT_BAS 
 WHERE DTA_DL_YN = 'N'
   AND SV_PD_CD = #{svPdCd}
   AND PDCT_PD_CD = #{pdctPdCd}
   AND PART_PD_CD = #{partPdCd}
   AND VST_DV_CD = #{vstDvCd}
   AND PRD_MM_VAL = #{prdMmVal}
   AND CH_PDCT_PD_CD = #{chPdctPdCd}
   AND ROWNUM = 1
