mapper.select:
SELECT T1.ROWID AS RID , T1.PD_CD /* 상품코드 */ , T2.PD_NM /* 상품명 */ , T1.RSTL_BASE_TP_CD /* 재약정기준유형코드 */ , T1.STPL_TN /* 약정회차 */ , T1.RSTL_SELL_CHNL_DV_CD /* 재약정판매채널구분코드 */ , T1.APY_STRTDT /* 적용시작일자 */ , T1.APY_ENDDT /* 적용종료일자 */ , T1.RSTL_MCN /* 재약정개월수 */ , T1.MIN_RENTAL_AMT /* 최소렌탈금액 */ , T1.STPL_DSC_AMT /* 약정할인금액 */ , T1.RCP_STRT_MCN /* 접수시작개월수 */ , T1.RCP_END_MCN /* 접수종료개월수 */ , T1.RSTL_DUTY_MCN /* 재약정의무개월수 */ , T1.ACKMT_AMT /* 인정금액 */ , T1.ACKMT_CT /* 인정건수 */ , T1.FEE_ACKMT_BASE_AMT /* 수수료인정기준금액 */ , T1.FEE_FXAM_YN /* 수수료정액여부 */ , T1.DTA_DL_YN /* 데이터삭제여부 */ , T1.FST_RGST_DTM /* 최초등록일시 */ , T1.FST_RGST_USR_ID /* 최초등록사용자ID */ , T1.FNL_MDFC_DTM /* 최종수정일시 */ , T1.FNL_MDFC_USR_ID /* 최종수정사용자ID */ , F_CMZ_USR_NM(T1.FST_RGST_USR_ID) FST_RGST_USR_NM /* 등록자 */ , F_CMZ_USR_NM(T1.FNL_MDFC_USR_ID) FNL_MDFC_USR_NM FROM TB_PDBS_PD_RSTL_BAS T1 /* 재약정기준 TBL */ JOIN TB_PDBS_PD_BAS T2 ON T2.PD_CD = T1.PD_CD WHERE T1.DTA_DL_YN = 'N' AND T1.VL_END_DTM = '99991231235959'
AND UPPER(T2.PD_NM) LIKE '%' || UPPER(#{pdNm}) || '%' /* 상품명 */
AND T1.PD_CD = #{pdCd} /* 상품코드 */
AND T1.APY_ENDDT >= #{startDate}
AND T1.APY_STRTDT <= #{startDate}
AND T1.APY_ENDDT >= #{endDate}
AND T1.APY_STRTDT <= #{endDate}
ORDER BY T1.FNL_MDFC_DTM DESC, FNL_MDFC_DTM DESC

mapper.insert:
INSERT INTO TB_PDBS_PD_RSTL_BAS (  /* 재약정기준 TBL */
      PD_CD                        /* 상품코드 */
    , RSTL_BASE_TP_CD              /* 재약정기준유형코드 */
    , STPL_TN                      /* 약정회차 */
    , RSTL_SELL_CHNL_DV_CD         /* 재약정판매채널구분코드 */
    , APY_STRTDT                   /* 적용시작일자 */
    , APY_ENDDT                    /* 적용종료일자 */
    , RSTL_MCN                     /* 재약정개월수 */
    , MIN_RENTAL_AMT               /* 최소렌탈금액 */
    , STPL_DSC_AMT                 /* 약정할인금액 */
    , RCP_STRT_MCN                 /* 접수시작개월수 */
    , RCP_END_MCN                  /* 접수종료개월수 */
    , RSTL_DUTY_MCN                /* 재약정의무개월수 */
    , ACKMT_AMT
    , ACKMT_CT
    , FEE_ACKMT_BASE_AMT
    , FEE_FXAM_YN
    , DTA_DL_YN                    /* 데이터삭제여부 */
    , VL_STRT_DTM
    , VL_END_DTM

) VALUES (
      #{pdCd}
    , #{rstlBaseTpCd}
    , #{stplTn}
    , #{rstlSellChnlDvCd}
    , #{apyStrtdt}
    , #{apyEnddt}
    , #{rstlMcn}
    , #{minRentalAmt}
    , #{stplDscAmt}
    , #{rcpStrtMcn}
    , #{rcpEndMcn}
    , #{rstlDutyMcn}
    , #{ackmtAmt}
    , #{ackmtCt}
    , #{feeAckmtBaseAmt}
    , #{feeFxamYn}
    , 'N'
    , TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
    , '99991231235959'

)
