mapper.sql:
SELECT PB.PD_NM AS PDCT_PD_NM /* 제품상품명 */ , NVL2(C1.PD_CLSF_NM, C1.PD_CLSF_NM, '') || NVL2(C2.PD_CLSF_NM, ' > ' || C2.PD_CLSF_NM, '') || NVL2(C3.PD_CLSF_NM, ' > ' || C3.PD_CLSF_NM, '') AS PD_CLSF_NM /* 상품분류 */ , PB.PD_TP_DTL_CD , P2.PD_NM AS BASE_PD_NM /* 기준상품코드명 */
, T1.RGLR_SPP_SDING_PRC_ID /* 정기배송모종가격ID */ , T1.PDCT_PD_CD /* 제품상품코드 */ , T1.RGLR_SPP_MCHN_KND_CD /* 정기배송기기종류코드 */ , T1.RGLR_SPP_MCHN_TP_CD /* 정기배송기기유형코드 */ , T1.RGLR_SPP_PRC_DV_CD /* 정기배송가격구분코드 */ , T1.BASE_PD_CD /* 기준상품코드 */ , T1.PD_PRC_TCNT /* 상품가격차수 */ , T1.APY_STRTDT /* 적용시작일자 */ , T1.APY_ENDDT /* 적용종료일자 */ , T1.SDING_QTY /* 모종수량 */ , T1.SELL_AMT /* 판매금액 */ , T1.SPL_AMT /* 공급금액 */ , T1.VAT /* 부가가치세 */ , T1.AS_SELL_AMT /* AS판매금액 */ , T1.USE_YN /* 사용여부 */ , T1.DTA_DL_YN /* 데이터삭제여부 */ , T1.FST_RGST_DTM /* 최초등록일시 */ , T1.FST_RGST_USR_ID /* 최초등록사용자ID */ , T1.FST_RGST_PRG_ID /* 최초등록프로그램ID */ , T1.FST_RGST_DEPT_ID /* 최초등록부서ID */ , T1.FNL_MDFC_DTM /* 최종수정일시 */ , T1.FNL_MDFC_USR_ID /* 최종수정사용자ID */ , T1.FNL_MDFC_PRG_ID /* 최종수정프로그램ID */ , T1.FNL_MDFC_DEPT_ID /* 최종수정부서ID */ , MU.USR_NM AS FNL_MDFC_USR_NM /* 최종수정자 */ FROM TB_PDBS_SDING_PRC_BAS T1 /* 모종가격기본 */ LEFT JOIN TB_PDBS_PD_BAS PB /* 제품상품코드 */ ON PB.PD_CD = T1.PDCT_PD_CD
LEFT JOIN TB_PDBS_PD_BAS P2 /* 기준상품코드 */ ON P2.PD_CD = T1.BASE_PD_CD
LEFT JOIN T_CMP_USR_ACO_M MU ON MU.USR_ID = T1.FNL_MDFC_USR_ID AND MU.DEL_YN = 'N' LEFT OUTER JOIN TB_PDBS_PD_CLSF_BAS C1 /* 대분류 */ ON PB.PD_HCLSF_ID = C1.PD_CLSF_ID
LEFT OUTER JOIN TB_PDBS_PD_CLSF_BAS C2 /* 중분류 */ ON PB.PD_MCLSF_ID = C2.PD_CLSF_ID
LEFT OUTER JOIN TB_PDBS_PD_CLSF_BAS C3 /* 소분류 */ ON PB.PD_LCLSF_ID = C3.PD_CLSF_ID
WHERE T1.DTA_DL_YN = 'N'
AND T1.APY_ENDDT >= #{apyStrtdt}
AND T1.APY_STRTDT <= #{apyStrtdt}
AND T1.APY_ENDDT >= #{apyEnddt}
AND T1.APY_STRTDT <= #{apyEnddt}
AND UPPER(PB.PD_NM) LIKE UPPER(#{pdctPdNm})
AND UPPER(PB.PD_CD) LIKE UPPER(#{pdctPdCd})
AND T1.RGLR_SPP_MCHN_TP_CD = #{rglrSppMchnTpCd}
AND T1.RGLR_SPP_PRC_DV_CD = #{rglrSppPrcDvCd}

mapper.insert:

   SELECT 'PDSM' || LPAD(  SQ_PDBS_SDING_PRC_BAS$RGLR_SPP_SDING_PRC_ID.NEXTVAL  , 11, 0) RGLR_SPP_SDING_PRC_ID FROM DUAL

 INSERT INTO TB_PDBS_SDING_PRC_BAS 
 (  /* 모종가격기본 */
       RGLR_SPP_SDING_PRC_ID /* 정기배송모종가격ID */
     , PDCT_PD_CD            /* 제품상품코드 */
     , RGLR_SPP_MCHN_KND_CD  /* 정기배송기기종류코드 */
     , RGLR_SPP_MCHN_TP_CD   /* 정기배송기기유형코드 */
     , RGLR_SPP_PRC_DV_CD    /* 정기배송가격구분코드 */
     , BASE_PD_CD            /* 기준상품코드 */
     , PD_PRC_TCNT           /* 상품가격차수 */
     , APY_STRTDT            /* 적용시작일자 */
     , APY_ENDDT             /* 적용종료일자 */
     , SDING_QTY             /* 모종수량 */
     , SELL_AMT              /* 판매금액 */
     , SPL_AMT               /* 공급금액 */
     , VAT                   /* 부가가치세 */
     , AS_SELL_AMT           /* AS판매금액 */
     , USE_YN                /* 사용여부 */
     , DTA_DL_YN             /* 데이터삭제여부 */
     )
VALUES (
       #{info.rglrSppSdingPrcId}
     , #{info.pdctPdCd}
     , #{info.rglrSppMchnKndCd}
     , #{info.rglrSppMchnTpCd}
     , #{info.rglrSppPrcDvCd}
     , #{info.basePdCd}
     , NVL(
            (
            SELECT MAX(PD_PRC_TCNT) + 1 
              FROM TB_PDBS_SDING_PRC_BAS
             WHERE DTA_DL_YN = 'N'
               AND PDCT_PD_CD = #{info.pdctPdCd}
               AND RGLR_SPP_MCHN_KND_CD = #{info.rglrSppMchnKndCd}
               AND RGLR_SPP_MCHN_TP_CD = #{info.rglrSppMchnTpCd}
               AND RGLR_SPP_PRC_DV_CD = #{info.rglrSppPrcDvCd}

                    AND BASE_PD_CD = #{info.basePdCd} 
                    AND BASE_PD_CD IS NULL  

            )
       , 1)  
     , #{info.apyStrtdt}
     , #{info.apyEnddt}
     , #{info.sdingQty}
     , #{info.sellAmt}
     , #{info.splAmt}
     , #{info.vat}
     , #{info.asSellAmt}
     , #{info.useYn}
     , NVL(#{info.dtaDlYn}, 'N')

 )

mapper.update:
UPDATE TB_PDBS_SDING_PRC_CH_HIST H1
   SET HIST_END_DTM = TO_CHAR(TO_DATE(#{histStrtDtm}, 'YYYYMMDDHH24MISS') + interval '-1' second, 'YYYYMMDDHH24MISS')
 WHERE RGLR_SPP_SDING_PRC_ID = #{rglrSppSdingPrcId}
   AND HIST_END_DTM = #{histEndDtm}
   AND HIST_STRT_DTM != #{histStrtDtm}

mapper.select:
SELECT PDCT_PD_CD || ',' || RGLR_SPP_MCHN_KND_CD || ',' || RGLR_SPP_MCHN_TP_CD || ',' || RGLR_SPP_PRC_DV_CD
       || ',' || #{info.apyStrtdt}
       || ',' || #{info.apyEnddt}
       || ',' || BASE_PD_CD
  FROM TB_PDBS_SDING_PRC_BAS 
 WHERE DTA_DL_YN = 'N'
   AND PDCT_PD_CD = #{info.pdctPdCd}
   AND RGLR_SPP_MCHN_KND_CD = #{info.rglrSppMchnKndCd}
   AND RGLR_SPP_MCHN_TP_CD = #{info.rglrSppMchnTpCd}
   AND RGLR_SPP_PRC_DV_CD = #{info.rglrSppPrcDvCd}
   AND APY_ENDDT >= #{info.apyStrtdt}
   AND APY_STRTDT <= #{info.apyEnddt}

        AND BASE_PD_CD = #{info.basePdCd} 
        AND BASE_PD_CD IS NULL  


   AND RGLR_SPP_SDING_PRC_ID NOT IN (

        #{val}

   )

   AND ROWNUM = 1
