mapper.update:
MERGE INTO TB_PDBS_PD_BYTP_VARB_BAS T1 /* 상품유형별변수기본 */ USING ( SELECT #{sellTpCd} AS SELL_TP_CD /* 판매유형코드 */ , #{varbSn} AS VARB_SN /* 변수일련번호 */ , #{choFxnDvCd} AS CHO_FXN_DV_CD /* 선택고정구분코드 */ , #{rgltnVarbId} AS RGLTN_VARB_ID /* 규칙변수ID */ , #{sortOdr} AS SORT_ODR /* 정렬순서 */ , NVL(#{tempSaveYn}, 'N') AS TEMP_SAVE_YN /* 임시저장여부 */ , NVL(#{dtaDlYn}, 'N') AS DTA_DL_YN /* 데이터삭제여부 */ FROM DUAL ) T2 ON (T1.SELL_TP_CD = T2.SELL_TP_CD /* 판매유형코드 */ AND T1.VARB_SN = T2.VARB_SN /* 변수일련번호 */ ) WHEN MATCHED THEN UPDATE SET CHO_FXN_DV_CD = T2.CHO_FXN_DV_CD , RGLTN_VARB_ID = T2.RGLTN_VARB_ID , SORT_ODR = T2.SORT_ODR , TEMP_SAVE_YN = T2.TEMP_SAVE_YN , DTA_DL_YN = T2.DTA_DL_YN
WHEN NOT MATCHED THEN INSERT ( SELL_TP_CD , VARB_SN , CHO_FXN_DV_CD , RGLTN_VARB_ID , SORT_ODR , TEMP_SAVE_YN , DTA_DL_YN
) VALUES ( T2.SELL_TP_CD , NVL( ( SELECT MAX(VARB_SN) + 1 FROM TB_PDBS_PD_BYTP_VARB_BAS WHERE SELL_TP_CD = #{sellTpCd} ), '1' ) , T2.CHO_FXN_DV_CD , T2.RGLTN_VARB_ID , T2.SORT_ODR , T2.TEMP_SAVE_YN , T2.DTA_DL_YN
)

UPDATE TB_PDBS_PD_BYTP_VARB_BAS
   SET DTA_DL_YN = 'Y'

 WHERE DTA_DL_YN = 'N'
   AND SELL_TP_CD = #{sellTpCd}
   AND VARB_SN = #{varbSn}
