mapper.select:
SELECT SVPD4.PDCT_PD_CD AS PD_CD /*품목코드*/ , PDBS1.PD_ABBR_NM AS PD_NM /*상품명*/ , SVPD4.ITM_PD_CD , (SELECT PD_ABBR_NM AS PD_NM FROM TB_PDBS_PD_BAS T WHERE T.PD_CD = SVPD4.ITM_PD_CD) AS ITM_PD_NM , (SELECT T.SAP_MAT_CD FROM TB_PDBS_PD_BAS T WHERE T.PD_CD = SVPD4.ITM_PD_CD) AS SAP_MAT_CD , CNSL_TP_LCSF_CD_NM , CNSL_CN , ITM_RCMD_RNK , ITM_RCMD_QTY , TX.CNSL_TP_HCSF_CD , TX.CNSL_TP_MCSF_CD , TX.CNSL_TP_LCSF_CD FROM TB_SVPD_CST_SV_RCMD_ITM_IZ SVPD4 LEFT OUTER JOIN CCSDBS.TB_CLASS_C TX /*상담유형소분류*/ ON USE_FG = 'Y' AND JOB_CD = 'L001' AND TX.CNSL_TP_HCSF_CD = SVPD4.CNSL_TP_HCLSF_CD AND TX.CNSL_TP_MCSF_CD = SVPD4.CNSL_TP_MCLSF_CD AND TX.CNSL_TP_LCSF_CD = SVPD4.CNSL_TP_LCLSF_CD INNER JOIN TB_PDBS_PD_BAS PDBS1 /*상품기본*/ ON PDBS1.DTA_DL_YN = 'N' AND SVPD4.PDCT_PD_CD = PDBS1.PD_CD WHERE SVPD4.DTA_DL_YN = 'N'
AND SVPD4.PDCT_PD_CD = #{pdCd}
AND TX.CNSL_TP_HCSF_CD = #{classA}
AND TX.CNSL_TP_MCSF_CD = #{classB}
AND TX.CNSL_TP_LCSF_CD = #{classC}
ORDER BY PDCT_PD_CD DESC, ITM_RCMD_RNK ASC
