mapper.select:
SELECT WEEK_NUM , START_DT||END_DT AS CODE_ID , WEEK_NUM||'주차('||TO_CHAR(TO_DATE(START_DT, 'YYYYMMDD'), 'YYYY-MM-DD') ||'~'|| TO_CHAR(TO_DATE(END_DT, 'YYYYMMDD'), 'YYYY-MM-DD')||')' AS CODE_NAME , '일'||TO_CHAR(TO_DATE(START_DT, 'YYYYMMDD'), 'YYYY-MM-DD') AS DAY_S1 , CASE WHEN TO_CHAR(TO_DATE(START_DT, 'YYYYMMDD') + INTERVAL '1' DAY, 'YYYY-MM-DD') <= TO_CHAR(TO_DATE(END_DT, 'YYYYMMDD'), 'YYYY-MM-DD') THEN '월'||TO_CHAR(TO_DATE(START_DT, 'YYYYMMDD') + INTERVAL '1' DAY, 'YYYY-MM-DD') ELSE '' END AS DAY_S2 , CASE WHEN TO_CHAR(TO_DATE(START_DT, 'YYYYMMDD') + INTERVAL '2' DAY, 'YYYY-MM-DD') <= TO_CHAR(TO_DATE(END_DT, 'YYYYMMDD'), 'YYYY-MM-DD') THEN '화'||TO_CHAR(TO_DATE(START_DT, 'YYYYMMDD') + INTERVAL '2' DAY, 'YYYY-MM-DD') ELSE '' END AS DAY_S3 , CASE WHEN TO_CHAR(TO_DATE(START_DT, 'YYYYMMDD') + INTERVAL '3' DAY, 'YYYY-MM-DD') <= TO_CHAR(TO_DATE(END_DT, 'YYYYMMDD'), 'YYYY-MM-DD') THEN '수'||TO_CHAR(TO_DATE(START_DT, 'YYYYMMDD') + INTERVAL '3' DAY, 'YYYY-MM-DD') ELSE '' END AS DAY_S4 , CASE WHEN TO_CHAR(TO_DATE(START_DT, 'YYYYMMDD') + INTERVAL '4' DAY, 'YYYY-MM-DD') <= TO_CHAR(TO_DATE(END_DT, 'YYYYMMDD'), 'YYYY-MM-DD') THEN '목'||TO_CHAR(TO_DATE(START_DT, 'YYYYMMDD') + INTERVAL '4' DAY, 'YYYY-MM-DD') ELSE '' END AS DAY_S5 , CASE WHEN TO_CHAR(TO_DATE(START_DT, 'YYYYMMDD') + INTERVAL '5' DAY, 'YYYY-MM-DD') <= TO_CHAR(TO_DATE(END_DT, 'YYYYMMDD'), 'YYYY-MM-DD') THEN '금'||TO_CHAR(TO_DATE(START_DT, 'YYYYMMDD') + INTERVAL '5' DAY, 'YYYY-MM-DD') ELSE '' END AS DAY_S6 , CASE WHEN TO_CHAR(TO_DATE(START_DT, 'YYYYMMDD') + INTERVAL '6' DAY, 'YYYY-MM-DD') <= TO_CHAR(TO_DATE(END_DT, 'YYYYMMDD'), 'YYYY-MM-DD') THEN '토'||TO_CHAR(TO_DATE(END_DT, 'YYYYMMDD'), 'YYYY-MM-DD') ELSE '' END AS DAY_S7 , TO_CHAR(TO_DATE(START_DT, 'YYYYMMDD'), 'YYYYMMDD') AS DAY_C1 , CASE WHEN TO_CHAR(TO_DATE(START_DT, 'YYYYMMDD') + INTERVAL '1' DAY, 'YYYY-MM-DD') <= TO_CHAR(TO_DATE(END_DT, 'YYYYMMDD'), 'YYYY-MM-DD') THEN TO_CHAR(TO_DATE(START_DT, 'YYYYMMDD') + INTERVAL '1' DAY, 'YYYYMMDD') ELSE '99991231' END AS DAY_C2 , CASE WHEN TO_CHAR(TO_DATE(START_DT, 'YYYYMMDD') + INTERVAL '2' DAY, 'YYYY-MM-DD') <= TO_CHAR(TO_DATE(END_DT, 'YYYYMMDD'), 'YYYY-MM-DD') THEN TO_CHAR(TO_DATE(START_DT, 'YYYYMMDD') + INTERVAL '2' DAY, 'YYYYMMDD') ELSE '99991231' END AS DAY_C3 , CASE WHEN TO_CHAR(TO_DATE(START_DT, 'YYYYMMDD') + INTERVAL '3' DAY, 'YYYY-MM-DD') <= TO_CHAR(TO_DATE(END_DT, 'YYYYMMDD'), 'YYYY-MM-DD') THEN TO_CHAR(TO_DATE(START_DT, 'YYYYMMDD') + INTERVAL '3' DAY, 'YYYYMMDD') ELSE '99991231' END AS DAY_C4 , CASE WHEN TO_CHAR(TO_DATE(START_DT, 'YYYYMMDD') + INTERVAL '4' DAY, 'YYYY-MM-DD') <= TO_CHAR(TO_DATE(END_DT, 'YYYYMMDD'), 'YYYY-MM-DD') THEN TO_CHAR(TO_DATE(START_DT, 'YYYYMMDD') + INTERVAL '4' DAY, 'YYYYMMDD') ELSE '99991231' END AS DAY_C5 , CASE WHEN TO_CHAR(TO_DATE(START_DT, 'YYYYMMDD') + INTERVAL '5' DAY, 'YYYY-MM-DD') <= TO_CHAR(TO_DATE(END_DT, 'YYYYMMDD'), 'YYYY-MM-DD') THEN TO_CHAR(TO_DATE(START_DT, 'YYYYMMDD') + INTERVAL '5' DAY, 'YYYYMMDD') ELSE '99991231' END AS DAY_C6 , CASE WHEN TO_CHAR(TO_DATE(START_DT, 'YYYYMMDD') + INTERVAL '6' DAY, 'YYYY-MM-DD') <= TO_CHAR(TO_DATE(END_DT, 'YYYYMMDD'), 'YYYY-MM-DD') THEN TO_CHAR(TO_DATE(END_DT, 'YYYYMMDD'), 'YYYYMMDD') ELSE '99991231' END AS DAY_C7
FROM ( SELECT WEEK_NUM, TO_CHAR(START_DT, 'YYYYMMDD') AS START_DT, TO_CHAR(END_DT, 'YYYYMMDD') AS END_DT FROM ( SELECT DENSE_RANK() OVER(ORDER BY TRUNC(dt, 'd')) WEEK_NUM , MIN(dt) START_DT , MAX(dt) END_DT FROM ( SELECT TO_DATE(#{managementYear}||'01', 'yyyymm') + LEVEL - 1 dt FROM dual CONNECT BY LEVEL <= TO_CHAR(TO_DATE(#{managementYear}||'1231', 'yyyymmdd'), 'ddd')
) GROUP BY TRUNC(dt, 'd') ) )
