mapper.delete:
/*무결성 제약 조건 오류 방지*/
DELETE FROM TB_SVPD_CST_SV_RGBSPR_IZ
 WHERE CNTR_NO = #{cntrNo}
   AND CNTR_SN = #{cntrSn}
   AND WK_DT IS NULL

mapper.update:
/*BS 사전생성을 위해서 강제 입력한 설치일자 삭제*/
UPDATE TB_SVPD_CST_SV_EXCN_DL_IZ
   SET IST_DT = ''
 WHERE CNTR_NO = #{cntrNo}
   AND CNTR_SN = #{cntrSn}
   AND SL_DT IS NULL

/*취소일자 업데이트*/
UPDATE TB_SVPD_CST_SV_EXCN_DL_IZ
   SET CAN_DT = #{canDt}
 WHERE CNTR_NO = #{cntrNo}
   AND CNTR_SN = #{cntrSn}

mapper.delete:
/*스케쥴을 삭제 한다*/
DELETE FROM TB_SVPD_CST_SV_RGBSPR_IZ
 WHERE CNTR_NO = #{cntrNo}
   AND CNTR_SN = '00'
   AND VST_DUEDT >= TO_CHAR(SYSDATE, 'YYYYMM') || '01'
   AND WK_DT IS NULL
