mapper.select:
SELECT TB.* FROM ( SELECT TB_1.CNTR_NO AS CNTR_NO /* 계약번호 */ , TB_1.CNTR_SN AS CNTR_SN /* 계약일련번호 */ , TB_1.CH_SN AS CH_SN /* 변경일련번호 */ , TB_3.RCGVP_KNM AS RCGVP_KNM /* 수령자한글명 */ , TB_4.NEW_ADR_ZIP AS NEW_ADR_ZIP /* 신주소우편번호 */ , TB_4.RNADR AS RNADR /* 도로명주소 */ , TB_4.RDADR AS RDADR /* 도로명상세주소 */ , TB_3.CRAL_LOCARA_TNO AS CRAL_LOCARA_TNO /* 휴대지역전화번호 */ , TB_3.MEXNO_ENCR AS MEXNO_ENCR , TB_3.CRAL_IDV_TNO AS CRAL_IDV_TNO /* 휴대개별전화번호 */ , TB_3.LOCARA_TNO AS LOCARA_TNO /* 지역전화번호 */ , TB_3.EXNO_ENCR AS EXNO_ENCR , TB_3.IDV_TNO AS IDV_TNO /* 개별전화번호 */ , TB_2.SELL_TP_CD AS SELL_TP_CD /* 판매유형코드 */ , TB_1.FXN_PRTNR_DV_CD AS FXN_PRTNR_DV_CD /* 고정파트너구분코드 */ , TB_1.FXN_PRTNR_NO AS FXN_PRTNR_NO /* 고정파트너번호 */ , TB_1.APY_STRT_YM AS APY_STRT_YM /* 적용시작년월 */ , TB_1.CH_RSON_CN AS CH_RSON_CN /* 변경사유내용 */ , TB_1.FNL_MDFC_DTM AS FNL_MDFC_DTM /* 최종수정일시 */ , TB_1.FNL_MDFC_USR_ID AS FNL_MDFC_USR_ID /* 최종수정사용자ID */ , RANK() OVER(PARTITION BY TB_1.CNTR_NO, TB_1.CNTR_SN ORDER BY TB_1.CH_SN DESC NULLS LAST) AS RNK FROM TB_SVPD_CNTR_FXN_PRTNR_IZ TB_1 , TB_SSCT_CNTR_DTL TB_2 , TB_SSCT_CNTR_ADRPC_BAS TB_3 , TB_GBCO_ADR_BAS TB_4 , TB_OGBS_MM_PRTNR_IZ TB_5 , TB_OGBS_MM_OG_IZ TB_6 , TB_SSCT_CNTR_ADR_REL TB_7 WHERE 1=1 AND TB_1.CNTR_NO = TB_2.CNTR_NO AND TB_1.CNTR_SN = TB_2.CNTR_SN AND TB_1.CNTR_NO = TB_3.CNTR_NO AND TB_5.BASE_YM = #{baseYm} AND TB_1.FXN_PRTNR_OG_TP_CD = TB_5.OG_TP_CD AND TB_1.FXN_PRTNR_NO = TB_5.PRTNR_NO AND TB_5.BASE_YM = TB_6.BASE_YM AND TB_5.OG_ID = TB_6.OG_ID AND TB_1.APY_STRT_YM >= #{baseYm} AND TB_1.CNTR_NO = TB_7.DTL_CNTR_NO AND TB_1.CNTR_SN = TB_7.DTL_CNTR_SN AND TB_7.ADRPC_TP_CD = '3' /* 설치주소 */ AND TB_3.ADR_ID = TB_4.ADR_ID AND TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS') BETWEEN TB_7.VL_STRT_DTM AND TB_7.VL_END_DTM AND TB_3.CNTR_ADRPC_ID = TB_7.CNTR_ADRPC_ID
AND TB_1.FXN_PRTNR_DV_CD = #{fxnPrtnrDvCd}
AND TB_2.SELL_TP_CD = #{sellTpCd}
AND TB_1.FXN_PRTNR_NO = #{fxnPrtnrNo}
AND TB_6.DGR1_LEVL_OG_ID = #{dgr1LevlOgId}
AND TB_6.DGR2_LEVL_OG_ID = #{dgr2LevlOgId}
) TB WHERE 1=1 AND RNK = 1

mapper.update:
UPDATE TB_SVPD_CNTR_FXN_PRTNR_IZ
SET DTA_DL_YN = 'Y'

WHERE 1=1
AND CNTR_NO = #{cntrNo}
AND CNTR_SN = #{cntrSn}

mapper.insert:
INSERT INTO TB_SVPD_CNTR_FXN_PRTNR_IZ (
CNTR_NO
, CNTR_SN
, CH_SN
, FXN_PRTNR_DV_CD
, FXN_PRTNR_NO
, CH_MNGR_DV_CD
, CH_RSON_CN
, APY_STRT_YM
, DTA_DL_YN

) VALUES (
#{cntrNo}
, #{cntrSn}
, (
SELECT NVL(MAX(TEMP.CH_SN) + 1, 1) AS CH_SN
FROM TB_SVPD_CNTR_FXN_PRTNR_IZ TEMP
WHERE 1=1
AND TEMP.CNTR_NO = #{cntrNo}
AND TEMP.CNTR_SN = #{cntrSn}
)
, #{fxnPrtnrDvCd}
, #{fxnPrtnrNo}
, #{chMngrDvCd}
, #{chRsonCn}
, #{apyStrtYm}
, 'N'

)
