SELECT /*+ leading(D3 D2) usae_nl(D3 D2) */ DISTINCT '' AS CHK , D1.FR2P_LGLD_CD /* 앞2자리법정동코드 */ , D1.CTPV_NM /* 시도명 */ , D1.CTCTY_NM /* 시군구명 */ , D1.LAWC_EMD_NM /* 법정읍면동명 */ , D1.AMTD_NM /* 행정동명 */ , D2.LOCARA_SN /* 순번 */ , (SELECT LISTAGG(AD1.NEW_ADR_ZIP, ', ') WITHIN GROUP (ORDER BY AD1.NEW_ADR_ZIP) FROM TB_SVPD_EGER_ASN_ADR_IZ AD1 WHERE 1=1 AND AD1.FR2P_LGLD_CD = D2.FR2P_LGLD_CD AND AD1.CTPV_NM = D2.CTPV_NM AND AD1.CTCTY_NM = D2.CTCTY_NM AND AD1.LAWC_EMD_NM = D2.LAWC_EMD_NM AND AD1.AMTD_NM = D2.AMTD_NM AND AD1.EMD_SN = (SELECT MAX(EMD_SN) FROM TB_SVPD_EGER_ASN_ADR_IZ WHERE NEW_ADR_ZIP = AD1.NEW_ADR_ZIP ) ) AS ZIP_LIST /* 우편번호 List */ , D2.APY_STRTDT /* 적용시작일자 */ , D2.APY_ENDDT /* 적용종료일자 */ , D2.RPB_LOCARA_CD /* 책임지역코드 */ , D3.RPB_LOCARA_GRP_CD /* 책임지역그룹코드 */ , D3.VST_DOW_VAL /* 방문요일코드 */ , F_CMZ_CD_NM(#{session.tenantId}, 'LOCARA_VST_PRD_CD', D3.VST_DOW_VAL, #{session.langId}) AS VST_DOW_NM /* 방문요일명 */ , D3.MMT_AV_LDTM /* 이동평균소요시간 */ , D4.OG_NM /* 조직명 */ , D3.ICHR_PRTNR_NO /* 담당파트너번호 */ , D4.PRTNR_KNM /* 파트너한글명 */ , D3.LOCARA_CEN_STRU_ADR /* 지역중심건물주소 */ , C1.CD_VLD_VAL AS ORDER_NO /* 정렬기준 */ FROM TB_SVPD_EGER_ASN_ADR_IZ D1 /* 엔지니어배정주소내역 */ INNER JOIN TB_SVPD_RPB_LOCARA_MNGT_ADR_IZ D2 /* 책임지역관리주소내역 */ ON D1.FR2P_LGLD_CD = D2.FR2P_LGLD_CD AND D1.CTPV_NM = D2.CTPV_NM AND D1.CTCTY_NM = D2.CTCTY_NM AND D1.LAWC_EMD_NM = D2.LAWC_EMD_NM AND D1.AMTD_NM = D2.AMTD_NM AND D2.ADR_ODR_NO = (SELECT MAX(L1.ADR_ODR_NO) FROM TB_SVPD_RPB_LOCARA_MNGT_ADR_IZ L1 WHERE 1=1 AND L1.FR2P_LGLD_CD = D2.FR2P_LGLD_CD AND L1.CTPV_NM = D2.CTPV_NM AND L1.CTCTY_NM = D2.CTCTY_NM AND L1.LAWC_EMD_NM = D2.LAWC_EMD_NM AND L1.AMTD_NM = D2.AMTD_NM) AND D2.DTA_DL_YN = 'N' INNER JOIN TB_SVPD_RPB_LOCARA_PSIC_IZ D3 /* 책임지역담당자내역 */ ON D2.RPB_LOCARA_CD = D3.RPB_LOCARA_CD AND D3.IZ_SN = (SELECT MAX(L2.IZ_SN) FROM TB_SVPD_RPB_LOCARA_PSIC_IZ L2 WHERE 1=1 AND L2.RPB_LOCARA_CD = D3.RPB_LOCARA_CD AND L2.WK_GRP_CD = D3.WK_GRP_CD AND #{applyDate} BETWEEN L2.APY_STRTDT AND L2.APY_ENDDT) AND D3.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_OGBS_MM_PRTNR_IZ D4 /* 월파트너내역 */ ON D4.BASE_YM = SUBSTR(D3.APY_STRTDT, 1, 6) AND D4.PRTNR_NO = D3.ICHR_PRTNR_NO AND D4.OG_TP_CD = D3.ICHR_PRTNR_OG_TP_CD AND D4.DTA_DL_YN = 'N' INNER JOIN T_CMZ_CD_D C1 /* 코드상세 */ ON D1.FR2P_LGLD_CD = C1.USER_DFN_02 AND C1.CD_ID = 'LOCARA_CD' WHERE 1=1 AND D1.DTA_DL_YN = 'N' AND D1.EMD_SN = (SELECT /*+ UNNEST */ MAX(EMD_SN) FROM TB_SVPD_EGER_ASN_ADR_IZ WHERE NEW_ADR_ZIP = D1.NEW_ADR_ZIP) AND #{applyDate} BETWEEN D2.APY_STRTDT AND D2.APY_ENDDT AND #{applyDate} BETWEEN D3.APY_STRTDT AND D3.APY_ENDDT AND D3.WK_GRP_CD = #{wkGrpCd}
AND D4.OG_ID = #{ogId}
AND D2.RPB_LOCARA_CD >= #{locaraCdFrom}
AND D2.RPB_LOCARA_CD <= #{locaraCdTo}
AND D1.NEW_ADR_ZIP >= #{zipFrom}
AND D1.NEW_ADR_ZIP <= #{zipTo}
AND D1.FR2P_LGLD_CD = #{fr2pLgldCd}
AND D1.CTCTY_NM = #{ctctyNm}
ORDER BY C1.CD_VLD_VAL, D2.RPB_LOCARA_CD ,D1.FR2P_LGLD_CD, D1.CTPV_NM, D1.CTCTY_NM, D1.LAWC_EMD_NM, D1.AMTD_NM

SELECT COUNT(1)
  FROM TB_SVPD_RPB_LOCARA_PSIC_IZ /* 책임지역담당자내역 */
 WHERE RPB_LOCARA_CD = #{chLocaraCd}

