mapper.select:
WITH TEMP01 AS ( SELECT DISTINCT W1.PD_CD /* 선택한 작업그룹에 해당하는 상품 리스트 */ FROM TB_SVPD_BPD_BIZ_TP_WK_GRP_IZ W1 /* 상품별업무유형별작업그룹내역 */ WHERE 1 = 1 AND W1.WK_GRP_CD = #{wkGrpCd} AND TO_CHAR(SYSDATE, 'YYYYMMDD') BETWEEN W1.APY_STRTDT AND W1.APY_ENDDT AND W1.IZ_SN = (SELECT MAX(IZ_SN) FROM TB_SVPD_BPD_BIZ_TP_WK_GRP_IZ WHERE 1 = 1 AND PD_CD = W1.PD_CD AND WK_GRP_CD = W1.WK_GRP_CD AND SV_BIZ_DCLSF_CD = W1.SV_BIZ_DCLSF_CD AND TO_CHAR(SYSDATE, 'YYYYMMDD') BETWEEN APY_STRTDT AND APY_ENDDT AND DTA_DL_YN = 'N') ), TEMP1 AS ( /* 지역별 계정 수 */ SELECT A2.NEW_ADR_ZIP , COUNT(1) AS MGT_CNT FROM (SELECT (CASE WHEN M1.CNTR_ADRPC_ID IS NULL THEN D1.CNTR_ADRPC_ID ELSE M1.CNTR_ADRPC_ID END) AS CNTR_ADRPC_ID FROM TB_SVPD_MCBY_CST_SV_OJ_IZ M1 /* 월별고객서비스대상내역 */ LEFT OUTER JOIN TB_SVPD_CST_SV_EXCN_DL_IZ D1 /* 고객서비스수행삭제내역 */ ON D1.CNTR_NO = M1.CNTR_NO AND D1.CNTR_SN = M1.CNTR_SN AND D1.DTA_DL_YN = 'N' INNER JOIN TB_SSCT_CNTR_DTL C1 /* 계약상세 */ ON C1.CNTR_NO = M1.CNTR_NO AND C1.CNTR_SN = M1.CNTR_SN AND C1.DTA_DL_YN = 'N' INNER JOIN TEMP01 P1 ON P1.PD_CD = C1.BASE_PD_CD WHERE M1.DTA_DL_YN = 'N' AND M1.MNGT_YM = TO_CHAR(SYSDATE, 'YYYYMM') AND (M1.REQD_DT IS NULL OR M1.CAN_DT IS NULL OR M1.CPS_DT IS NULL) ) T1 LEFT OUTER JOIN TB_SSCT_CNTR_ADRPC_BAS A1 /* 계약주소지기본 */ ON A1.CNTR_ADRPC_ID = T1.CNTR_ADRPC_ID AND A1.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_GBCO_ADR_BAS A2 /* 주소지기본 */ ON A2.ADR_ID = A1.ADR_ID AND A2.DTA_DL_YN = 'N' GROUP BY A2.NEW_ADR_ZIP ORDER BY A2.NEW_ADR_ZIP ), TEMP2 AS ( /* 월별 수임 건수 (조회월 이전 3개월 평균) */ SELECT NEW_ADR_ZIP , ROUND(SUM(WRK_CNT) / 3, 2) AS WRK_CNT FROM (SELECT T4.NEW_ADR_ZIP , COUNT(1) AS WRK_CNT FROM TB_SVPD_CST_SVAS_IST_ASN_IZ T1 /* 고객서비스AS설치배정내역 */ INNER JOIN TB_SVPD_CST_SVAS_IST_OJ_IZ T2 /* 고객서비스AS설치대상내역 */ ON T2.CST_SV_ASN_NO = T1.CST_SV_ASN_NO AND T2.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_SSCT_CNTR_ADRPC_BAS T3 /* 계약주소지기본 */ ON T3.CNTR_ADRPC_ID = T2.CNTR_ADRPC_ID AND T3.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_GBCO_ADR_BAS T4 /* 주소지기본 */ ON T4.ADR_ID = T3.ADR_ID AND T4.DTA_DL_YN = 'N' WHERE T1.DTA_DL_YN = 'N' AND T1.WK_PRGS_STAT_CD = '20' /* 작업진행상태 = '작업완료' */ AND T1.WK_EXCN_DT BETWEEN TO_CHAR(ADD_MONTHS(SYSDATE, -3), 'YYYYMMDD') || '01' AND TO_CHAR(ADD_MONTHS(SYSDATE, -1), 'YYYYMM') || '31' AND T1.WK_GRP_CD = #{wkGrpCd} GROUP BY T4.NEW_ADR_ZIP UNION ALL SELECT T3.NEW_ADR_ZIP , COUNT(1) AS WRK_CNT FROM TB_SVPD_CST_SV_BFSVC_ASN_IZ T1 /* 고객서비스BS배정내역 */ INNER JOIN TB_SVPD_CST_SV_BFSVC_OJ_IZ T2 /* 고객서비스BS대상내역 */ ON T2.CST_SV_ASN_NO = T1.CST_SV_ASN_NO AND T2.DTA_DL_YN = 'N' INNER JOIN TB_GBCO_ADR_BAS T3 /* 주소지기본 */ ON T3.ADR_ID = T2.ADR_ID AND T3.DTA_DL_YN = 'N' WHERE T1.DTA_DL_YN = 'N' AND T1.VST_PRGS_STAT_CD = '20' /* 작업진행상태 = '작업완료' */ AND T1.CNFM_PSIC_DV_CD = '2' AND T1.WK_EXCN_DT BETWEEN TO_CHAR(ADD_MONTHS(SYSDATE, -3), 'YYYYMMDD') || '01' AND TO_CHAR(ADD_MONTHS(SYSDATE, -1), 'YYYYMM') || '31' AND T1.WK_GRP_CD = #{wkGrpCd} GROUP BY T3.NEW_ADR_ZIP ) GROUP BY NEW_ADR_ZIP ), TEMP3 AS ( /* 지역별 리스트 */ SELECT T1.NEW_ADR_ZIP , T1.EMD_SN , T1.FR2P_LGLD_CD , T1.CTPV_NM , T1.CTCTY_NM , T1.LAWC_EMD_NM , T1.AMTD_NM , T1.LAWC_EMD_NM || ' / ' || T1.AMTD_NM AS MNGT_AMTD /* 관리행정동 */ , T2.RPB_LOCARA_CD /* 책임지역코드 */ , T3.RPB_LOCARA_GRP_CD /* 책임지역그룹코드 */ , T4.OG_NM /* 조직명 */ , T3.ICHR_PRTNR_NO /* 담당파트너번호 */ , T4.PRTNR_KNM /* 파트너한글명 */ , T3.VST_DOW_VAL /* 방문요일값 */ , T1.KYNOR_LOCARA_YN /* 경북지역여부 */ , T1.ILD_YN /* 섬여부 */ , T1.PDLV_NO /* 출고지번호 */ FROM (SELECT M1.NEW_ADR_ZIP, M1.EMD_SN, M1.FR2P_LGLD_CD, M1.CTPV_NM, M1.CTCTY_NM, M1.LAWC_EMD_NM, M1.AMTD_NM, M1.KYNOR_LOCARA_YN, M1.ILD_YN, M1.PDLV_NO FROM TB_SVPD_EGER_ASN_ADR_IZ M1 WHERE M1.EMD_SN = (SELECT MAX(EMD_SN) FROM TB_SVPD_EGER_ASN_ADR_IZ WHERE NEW_ADR_ZIP = M1.NEW_ADR_ZIP AND DTA_DL_YN = 'N') ) T1 /* 엔지니어배정용주소내역 */ INNER JOIN TB_SVPD_RPB_LOCARA_MNGT_ADR_IZ T2 /* 책임지역별관리주소내역 */ ON T2.FR2P_LGLD_CD = T1.FR2P_LGLD_CD AND T2.CTPV_NM = T1.CTPV_NM AND T2.CTCTY_NM = T1.CTCTY_NM AND T2.LAWC_EMD_NM = T1.LAWC_EMD_NM AND T2.AMTD_NM = T1.AMTD_NM AND T2.DTA_DL_YN = 'N' AND #{applyDate} BETWEEN T2.APY_STRTDT AND T2.APY_ENDDT INNER JOIN (SELECT M3.WK_GRP_CD, M3.RPB_LOCARA_CD, M3.IZ_SN, M3.RPB_LOCARA_GRP_CD, M3.VST_DOW_VAL, M3.APY_STRTDT, M3.APY_ENDDT, M3.ICHR_PRTNR_OG_TP_CD, M3.ICHR_PRTNR_NO FROM TB_SVPD_RPB_LOCARA_PSIC_IZ M3 WHERE M3.IZ_SN = (SELECT MAX(IZ_SN) FROM TB_SVPD_RPB_LOCARA_PSIC_IZ WHERE WK_GRP_CD = M3.WK_GRP_CD AND RPB_LOCARA_CD = M3.RPB_LOCARA_CD AND #{applyDate} BETWEEN APY_STRTDT AND APY_ENDDT AND DTA_DL_YN = 'N') ) T3 /* 책임지역별담당자내역 */ ON T3.RPB_LOCARA_CD = T2.RPB_LOCARA_CD INNER JOIN (SELECT DISTINCT M4.BASE_YM, M4.OG_TP_CD, M4.PRTNR_NO, M4.PRTNR_KNM, M4.OG_ID, M4.OG_NM FROM TB_OGBS_MM_PRTNR_IZ M4 WHERE M4.BASE_YM = TO_CHAR(SYSDATE, 'YYYYMM') AND DTA_DL_YN = 'N' ) T4 /* 월파트너내역 */ ON T4.PRTNR_NO = T3.ICHR_PRTNR_NO AND T4.OG_TP_CD = T3.ICHR_PRTNR_OG_TP_CD WHERE 1 = 1 AND T3.WK_GRP_CD = #{wkGrpCd} AND #{applyDate} BETWEEN T2.APY_STRTDT AND T2.APY_ENDDT AND #{applyDate} BETWEEN T3.APY_STRTDT AND T3.APY_ENDDT AND T2.LOCARA_SN = (SELECT MAX(LOCARA_SN) FROM TB_SVPD_RPB_LOCARA_MNGT_ADR_IZ WHERE RPB_LOCARA_CD = T2.RPB_LOCARA_CD AND FR2P_LGLD_CD = T2.FR2P_LGLD_CD AND CTPV_NM = T2.CTPV_NM AND CTCTY_NM = T2.CTCTY_NM AND LAWC_EMD_NM = T2.LAWC_EMD_NM AND AMTD_NM = T2.AMTD_NM AND #{applyDate} BETWEEN APY_STRTDT AND APY_ENDDT AND DTA_DL_YN = 'N')
AND T1.NEW_ADR_ZIP >= #{zipFrom}
AND T1.NEW_ADR_ZIP <= #{zipTo}
AND T1.CTPV_NM = #{ctpvNm}
AND T1.CTCTY_NM = #{ctctyNm}
) SELECT T3.NEW_ADR_ZIP /* 신주소우편번호 */ , T3.EMD_SN /* 읍면동일련번호 */ , T1.MGT_CNT /* 지역별서비스계정수 */ , T2.WRK_CNT /* 월별수임건수 (조회월 이전3개월 평균) */ , T3.FR2P_LGLD_CD /* 앞2자리법정동코드 */ , T3.CTPV_NM /* 시도명 */ , T3.CTCTY_NM /* 시군구명 */ , T3.LAWC_EMD_NM /* 법정읍면동명 */ , T3.AMTD_NM /* 행정동명 */ , T3.MNGT_AMTD /* 관리행정동 */ , T3.RPB_LOCARA_CD /* 책임지역코드 */ , T3.RPB_LOCARA_GRP_CD /* 책임지역그룹코드 */ , T3.OG_NM /* 조직명 */ , T3.ICHR_PRTNR_NO /* 담당파트너번호 */ , T3.PRTNR_KNM /* 파트너한글명 */ , T3.VST_DOW_VAL /* 방문요일값 */ , T3.KYNOR_LOCARA_YN /* 경북지역여부 */ , T3.ILD_YN /* 섬여부 */ , T3.PDLV_NO /* 출고지번호 */ FROM TEMP3 T3 LEFT OUTER JOIN TEMP1 T1 ON T1.NEW_ADR_ZIP = T3.NEW_ADR_ZIP LEFT OUTER JOIN TEMP2 T2 ON T2.NEW_ADR_ZIP = T3.NEW_ADR_ZIP ORDER BY T3.NEW_ADR_ZIP

mapper.insert:
INSERT INTO TB_SVPD_EGER_ASN_ADR_IZ ( /* 엔지니어배정주소내역 */
       NEW_ADR_ZIP      /* 신주소우편번호 */
     , EMD_SN           /* 읍면동일련번호 */
     , FR2P_LGLD_CD     /* 앞2자리법정동코드 */
     , LAWC_EMD_NM      /* 법정읍면동명 */
     , AMTD_NM          /* 행정동명 */
     , KYNOR_LOCARA_YN  /* 경북지역여부 */
     , CTPV_NM          /* 시도명 */
     , CTCTY_NM         /* 시군구명 */
     , ILD_YN           /* 섬여부 */
     , PDLV_NO          /* 출고지번호 */
     , DTA_DL_YN        /* 데이터삭제여부 */
     )
VALUES (
       #{newAdrZip}
     , (SELECT MAX(EMD_SN) + 1
          FROM TB_SVPD_EGER_ASN_ADR_IZ
         WHERE 1 = 1
           AND NEW_ADR_ZIP = #{newAdrZip}
           AND DTA_DL_YN = 'N')
     , #{fr2pLgldCd}
     , #{lawcEmdNm}
     , #{amtdNm}
     , #{kynorLocaraYn}
     , #{ctpvNm}
     , #{ctctyNm}
     , #{ildYn}
     , #{pdlvNo}
     , 'N'
      )
