mapper.select:
SELECT T1.PD_CD /* 상품코드 */ , IZ_SN , BSTR_CS_AMT , APY_STRTDT , APY_ENDDT , RMK_CN , '' AS FLAG , P1.SAP_MAT_CD /* SAP자재코드 */ , P1.PD_NM /* 상품명 */ FROM TB_SVPD_RCAS_BSTR_COST_BASE_IZ T1 /* 유상AS출장비기준내역 */ INNER JOIN TB_PDBS_PD_BAS P1 /* 상품기본 */ ON P1.PD_CD = T1.PD_CD AND P1.DTA_DL_YN = 'N' WHERE T1.DTA_DL_YN = 'N'
AND P1.PD_CD = #{pdCd}
AND TO_CHAR( SYSDATE, 'YYYYMMDD') BETWEEN APY_STRTDT AND APY_ENDDT

mapper.delete:
UPDATE TB_SVPD_RCAS_BSTR_COST_BASE_IZ SET
       DTA_DL_YN = 'Y'

 WHERE PD_CD = #{pdCd}
   AND IZ_SN = #{izSn}

mapper.update:
UPDATE TB_SVPD_RCAS_BSTR_COST_BASE_IZ
   SET APY_STRTDT = #{apyStrtdt}

 WHERE PD_CD = #{pdCd}
   AND IZ_SN = #{izSn}
   AND DTA_DL_YN = 'N'

UPDATE TB_SVPD_RCAS_BSTR_COST_BASE_IZ
   SET APY_ENDDT = #{apyEnddt}

 WHERE PD_CD = #{pdCd}
   AND IZ_SN = #{izSn}
   AND DTA_DL_YN = 'N'

UPDATE TB_SVPD_RCAS_BSTR_COST_BASE_IZ
   SET BSTR_CS_AMT = #{bstrCsAmt}
     , APY_STRTDT = #{apyStrtdt}
     , APY_ENDDT = #{apyEnddt}
     , RMK_CN = #{rmkCn}

 WHERE PD_CD = #{pdCd}
   AND IZ_SN = #{izSn}
   AND DTA_DL_YN = 'N'

mapper.select:
SELECT NVL(MAX(IZ_SN),0) + 1 AS IZ_SN
  FROM TB_SVPD_RCAS_BSTR_COST_BASE_IZ
 WHERE PD_CD = #{pdCd}

mapper.insert:
INSERT INTO TB_SVPD_RCAS_BSTR_COST_BASE_IZ (
       PD_CD
     , IZ_SN
     , BSTR_CS_AMT
     , APY_STRTDT
     , APY_ENDDT
     , RMK_CN
     , DTA_DL_YN

) VALUES (
       #{pdCd}
     , #{izSn}
     , #{bstrCsAmt}
     , #{apyStrtdt}
     , #{apyEnddt}
     , #{rmkCn}
     , 'N'

)
