mapper.select:
SELECT T1.PD_CD /* 상품코드 */ , IZ_SN , BSTR_CS_AMT , APY_STRTDT , APY_ENDDT , RMK_CN , '' AS FLAG , P1.SAP_MAT_CD /* SAP자재코드 */ , P1.PD_ABBR_NM AS PD_NM /* 상품명 */ , CASE WHEN (SELECT IZ_SN FROM TB_SVPD_RCAS_BSTR_COST_BASE_IZ T WHERE T.DTA_DL_YN = 'N' AND T.PD_CD = T1.PD_CD AND TO_CHAR(SYSDATE,'YYYYMMDD') BETWEEN T.APY_STRTDT AND T.APY_ENDDT) = T1.IZ_SN THEN 'Y' ELSE 'N' END AS IS_LAST
FROM TB_SVPD_RCAS_BSTR_COST_BASE_IZ T1 /* 유상AS출장비기준내역 */ INNER JOIN TB_PDBS_PD_BAS P1 /* 상품기본 */ ON P1.PD_CD = T1.PD_CD AND P1.DTA_DL_YN = 'N' INNER JOIN TB_PDBS_PD_ECOM_PRP_DTL P2 /* 상품각사속성상세 */ ON P2.PD_CD = T1.PD_CD AND P2.PD_EXTS_PRP_GRP_CD = 'PART' AND P2.DTA_DL_YN = 'N' WHERE T1.DTA_DL_YN = 'N'
AND P2.PD_PRP_VAL20 = #{pdGrpCd}
AND P1.PD_CD = #{pdCd}
AND TO_CHAR(SYSDATE,'YYYYMMDD') BETWEEN T1.APY_STRTDT AND T1.APY_ENDDT
ORDER BY T1.PD_CD, IZ_SN DESC

mapper.delete:
UPDATE TB_SVPD_RCAS_BSTR_COST_BASE_IZ SET
       DTA_DL_YN = 'Y'

 WHERE PD_CD = #{pdCd}
   AND IZ_SN = #{izSn}

mapper.update:
UPDATE TB_SVPD_RCAS_BSTR_COST_BASE_IZ
   SET APY_STRTDT = #{apyStrtdt}

 WHERE PD_CD = #{pdCd}
   AND IZ_SN = #{izSn}
   AND DTA_DL_YN = 'N'

UPDATE TB_SVPD_RCAS_BSTR_COST_BASE_IZ
   SET APY_ENDDT = #{apyEnddt}

 WHERE PD_CD = #{pdCd}
   AND IZ_SN = #{izSn}
   AND DTA_DL_YN = 'N'

UPDATE TB_SVPD_RCAS_BSTR_COST_BASE_IZ
   SET BSTR_CS_AMT = #{bstrCsAmt}
     , APY_STRTDT = #{apyStrtdt}
     , APY_ENDDT = #{apyEnddt}
     , RMK_CN = #{rmkCn}

 WHERE PD_CD = #{pdCd}
   AND IZ_SN = #{izSn}
   AND DTA_DL_YN = 'N'

mapper.select:
SELECT NVL(MAX(IZ_SN),0) + 1 AS IZ_SN
  FROM TB_SVPD_RCAS_BSTR_COST_BASE_IZ
 WHERE PD_CD = #{pdCd}

mapper.insert:
INSERT INTO TB_SVPD_RCAS_BSTR_COST_BASE_IZ (
       PD_CD
     , IZ_SN
     , BSTR_CS_AMT
     , APY_STRTDT
     , APY_ENDDT
     , RMK_CN
     , DTA_DL_YN

) VALUES (
       #{pdCd}
     , #{izSn}
     , #{bstrCsAmt}
     , #{apyStrtdt}
     , #{apyEnddt}
     , #{rmkCn}
     , 'N'

)
