mapper.update:
UPDATE TB_SVPD_SV_CLND_BAS
   SET DF_YN = #{dfYn}
     , RMK_CN = #{rmkCn}

 WHERE 1=1
   AND BASE_Y = #{baseY}
   AND BASE_MM = #{baseMm}
   AND BASE_D = #{baseD}

MERGE INTO TB_SVPD_BNDT_WRK_PSIC_RGST_IZ
USING DUAL
ON (
        SV_CNR_OG_ID = #{svCnrOgId}
    AND BASE_Y = #{baseY}
    AND BASE_MM = #{baseMm}
    AND BASE_D = #{baseD}
)
WHEN MATCHED THEN
    UPDATE
       SET BNDT_WRK_PSIC_NO = #{bndtWrkPsicNo}

WHEN NOT MATCHED THEN
    INSERT (
          SV_CNR_OG_ID
        , BASE_Y
        , BASE_MM
        , BASE_D
        , BNDT_WRK_PSIC_NO
        , DTA_DL_YN

    ) VALUES (
          #{svCnrOgId}
        , #{baseY}
        , #{baseMm}
        , #{baseD}
        , #{bndtWrkPsicNo}
        , 'N'

    )
