mapper.select:
SELECT P1.SAP_MAT_CD /*SAP코드*/ , T1.USE_MAT_PD_CD AS USE_MAT_PD_CD /*품목코드*/ , (SELECT X.PD_NM FROM TB_PDBS_PD_BAS X WHERE X.PD_CD = T1.USE_MAT_PD_CD) AS PD_NM/*품목명*/ , T1.APY_STRTDT /*적용시작일*/ , T1.APY_ENDDT /*적용종료일*/ , NVL(T1.CSMR_UPRC_AMT,0) AS CSMR_UPRC_AMT /*소비자가*/ , NVL(T1.WHLS_UPRC_AMT,0) AS WHLS_UPRC_AMT /*도매단가*/ , NVL(T1.INSI_UPRC_AMT,0) AS INSI_UPRC_AMT /*도매단가*/ , NVL(T1.TCFEE_AMT,0) AS TCFEE_AMT /*기술료*/ , NVL(T1.CSMR_UPRC_AMT + T1.TCFEE_AMT,0) AS SUM_AMT /*합계(소비자가+기술료)*/ , NVL(T1.IZ_SN, 0) AS IZ_SN /* 내역일련번호 */ , T1.PDCT_PD_CD /* 기준상품코드 */ FROM TB_PDBS_PD_BAS P1 INNER JOIN TB_PDBS_PD_ECOM_PRP_DTL P2 ON P1.PD_CD = P2.PD_CD AND P2.PD_EXTS_PRP_GRP_CD = 'PART' AND P2.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_SVPD_RCAS_ITM_PRC_IZ T1 ON P1.PD_CD = T1.PDCT_PD_CD /* 현재적용자료 체크시 */
AND TO_CHAR( SYSDATE, 'YYYYMMDD') BETWEEN T1.APY_STRTDT AND T1.APY_ENDDT
WHERE 1=1 /* 공통부품 언체크시 */
/* 전체 조회 */ AND P2.PD_PRP_VAL20 = #{hgrPdCd}
/* 상품명 선택 시 조회 */ AND T1.PDCT_PD_CD = #{pdCd}
AND P1.PD_CD != '0000000000'
/* 공통부품 체크시 */
AND (( P2.PD_PRP_VAL21 IN ('00','01')
OR P2.PD_PRP_VAL21 = #{hgrPdCd} || '0'
)
OR T1.PDCT_PD_CD = #{pdCd}
) AND P1.PD_CD != '0000000000'
ORDER BY P1.PD_CD ASC, T1.IZ_SN ASC

SELECT T1.SVPD_PD_CD AS CODE_ID , T1.SVPD_NM_ABBR1 || '(' || T1.SVPD_PD_CD || ')' AS CODE_NAME FROM () T1 WHERE T1.SVPD_ITEM_KND = '4'
AND T1.SVPD_ITEM_GR = #{hgrPdCd}
ORDER BY T1.SVPD_ITEM_CD, T1.SVPD_PART_CD

mapper.update:
UPDATE TB_SVPD_RCAS_ITM_PRC_IZ
   SET CSMR_UPRC_AMT = ${csmrUprcAmt} /* 소비자단가금액 */
     , WHLS_UPRC_AMT = ${whlsUprcAmt} /* 도매단가금액 */
     , INSI_UPRC_AMT = ${insiUprcAmt} /* 내부단가금액*/
     , TCFEE_AMT = ${tcfeeAmt} /* 기술료금액 */
     , APY_STRTDT = #{apyStrtdt} /* 적용시작일 */
     , APY_ENDDT = #{apyEnddt} /* 적용종료일 */

 WHERE PDCT_PD_CD = #{pdctPdCd}
   AND USE_MAT_PD_CD = #{useMatPdCd}
   AND IZ_SN = ${izSn}
