INSERT INTO TB_SVPD_HCF_AS_AK_HIST ( /* 홈카페AS요청이력 */
      CNTR_NO
    , CNTR_SN
    , AK_SN
    , HIST_CH_DTM
    , AS_AK_DV_CD
    , RGST_DT
    , AK_CHDT
    , BFCH_PD_CD
    , AFCH_PD_CD
    , CHO_CAPSL_CN
    , MTR_PROCS_STAT_CD
    , CNFMDT

) VALUES (
      #{cntrNo}
    , #{cntrSn}
    , (SELECT NVL(MAX(AK_SN), 0) + 1 AS MAX_AK_SN
         FROM TB_SVPD_HCF_AS_AK_IZ
        WHERE DTA_DL_YN = 'N'
          AND CNTR_NO = #{cntrNo}
          AND CNTR_SN = #{cntrSn})
    , TO_CHAR(SYSDATE,'YYYYMMDDHH24MISS') + ${histChDtm}
    , #{asAkDvCd}
    , TO_CHAR(SYSDATE, 'YYYYMMDD')
    , #{akChdt}
    , #{bfchPdCd}
    , #{afchPdCd}
    , NULL
    , #{mtrProcsStatCd}
    , NULL
    , TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
    , NVL(#{lgnUsrId}, #{session.userId})
    , #{session.pageId}
    , NVL((SELECT U1.OG_ID
             FROM T_CMY_USR_M U1 /* 사용자 기본 */
            WHERE U1.USR_ID = #{lgnUsrId}), NVL(#{session.ogId}, 'NULL'))
    , TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
    , #{session.userId}
    , #{session.pageId}
    , NVL(#{session.ogId}, 'NULL')
)

