mapper.select:
SELECT DISTINCT SSCT1.CNTR_NO -- 계약번호 , CUBS1.CST_NO -- 고객번호 , CUBS1.CST_KNM -- 고객한글명 , SSCT1.CNTR_CNFM_DTM AS CNTR_DT -- 계약일자 , PDBS1.PD_CD -- 상품코드 , PDBS1.PD_NM -- 상품명 , SSCT3.CRAL_LOCARA_TNO -- 휴대지역전화번호 , SSCT3.MEXNO_ENCR -- 휴대전화국번호암호화 , SSCT3.CRAL_IDV_TNO -- 휴대개별전화번호 , SSCT3.LOCARA_TNO -- 지역전화번호 , SSCT3.EXNO_ENCR -- 전화국번호암호화 , SSCT3.IDV_TNO -- 개별전화번호 , GBCO1.NEW_ADR_ZIP -- 신주소우편번호 , GBCO1.RNADR -- 도로명주소 , GBCO1.RDADR -- 도로명상세주소 , SSCT3.ADR_DV_CD -- 주소구분코드 , GBCO1.LTN_ADR AS ADDR , SSCT3.RCGVP_KNM -- 수령자한글명 , CUBS1.BRYY_MMDD -- 생년월일 , CUBS1.SEX_DV_CD -- 성별구분코드 , SSCT1.CNTR_CST_NO-- 계약고객번호 FROM TB_SSCT_CNTR_BAS SSCT1 /*계약기본*/ INNER JOIN TB_SSCT_CNTR_DTL SSCT2 /*계약상세*/ ON SSCT1.CNTR_NO = SSCT2.CNTR_NO LEFT OUTER JOIN TB_SSCT_CNTR_ADRPC_BAS SSCT3 /*계약주소지기본*/ ON SSCT3.CNTR_NO = SSCT1.CNTR_NO AND SSCT3.CNTR_CST_NO = SSCT1.CNTR_CST_NO LEFT OUTER JOIN TB_CUBS_CST_BAS CUBS1 /*고객기본*/ ON CUBS1.CST_NO = SSCT1.CNTR_CST_NO LEFT OUTER JOIN TB_PDBS_PD_BAS PDBS1 /*상품기본*/ ON PDBS1.PD_CD = SSCT2.BASE_PD_CD LEFT OUTER JOIN TB_PDBS_PD_REL PDBS2 /*상품관계*/ ON PDBS2.BASE_PD_CD = SSCT2.BASE_PD_CD LEFT OUTER JOIN TB_GBCO_ADR_BAS GBCO1/*주소기본*/ ON CUBS1.ADR_ID = GBCO1.ADR_ID WHERE SSCT1.DTA_DL_YN = 'N'
AND SSCT1.CNTR_NO = #{cntrNo}
AND SSCT2.CNTR_SN = #{cntrSn}
AND SSCT1.CNTR_CST_NO = #{cntrCstNo}
AND CUBS1.CST_KNM = #{cstKnm}
AND CUBS1.CRAL_LOCARA_TNO = #{cralLocaraTno}
AND CUBS1.MEXNO_ENCR = #{mexnoEncr}
AND CUBS1.CRAL_IDV_TNO = #{cralIdvTno}
AND PDBS2.PD_GRP_ID = #{pdGrpId}
AND CUBS1.ADR_ID = #{adrId}

SELECT SVPD1.CNTR_NO , SVPD1.IN_CHNL_DV_CD , SVPD1.SV_BIZ_HCLSF_CD , TO_DATE(SUBSTR(SVPD1.RCPDT, 0, 8)) AS RCPDT , AS_IST_OJ_NO , SVPD1.SV_BIZ_DCLSF_CD , F_CMZ_CD_NM('TNT_WELLS', 'SV_BIZ_DCLSF_CD', SVPD1.SV_BIZ_DCLSF_CD, 'ko') AS SV_BIZ_DCLSF_NM , CNSL_MO_CN , CASE WHEN WK_PRGS_STAT_CD = '00' AND NVL(WK_ACPTE_STAT_CD, 'N') = 'N' THEN 'Y' ELSE 'N' END AS CLTN_YN -- 해약여부 , VST_CNFMDT AS VST_CNFM_DT , VST_CNFM_HH AS VST_CNFM_M , VST_DUEDT AS VST_EXP_DT , VST_EXP_HH AS VST_EXP_M , CUBS1.CRAL_LOCARA_TNO , CUBS1.MEXNO_ENCR , CUBS1.CRAL_IDV_TNO , SSCT1.LOCARA_TNO , SSCT1.EXNO_ENCR , SSCT1.IDV_TNO , GBCO1.NEW_ADR_ZIP , GBCO1.RNADR , GBCO1.RDADR , SSCT1.ADR_DV_CD FROM TB_SVPD_CST_SVAS_IST_OJ_IZ SVPD1 /*고객서비스AS설치대상내역*/ LEFT OUTER JOIN TB_SVPD_CST_SVAS_IST_ASN_IZ SVPD2 /*고객서비스AS설치배정내역*/ ON SVPD1.CNTR_NO = SVPD2.CNTR_NO LEFT OUTER JOIN TB_CUBS_CST_BAS CUBS1 /*고객기본*/ ON SVPD1.CNTR_CST_NO = CUBS1.CST_NO LEFT OUTER JOIN TB_SSCT_CNTR_ADRPC_BAS SSCT1 /*계약주소지기본*/ ON SSCT1.DTA_DL_YN = 'N' AND SVPD1.CNTR_NO = SSCT1.CNTR_NO AND SVPD1.CNTR_CST_NO = SSCT1.CNTR_CST_NO LEFT OUTER JOIN TB_GBCO_ADR_BAS GBCO1 /*주소기본*/ ON GBCO1.DTA_DL_YN = 'N' AND SSCT1.ADR_ID = GBCO1.ADR_ID LEFT OUTER JOIN TB_OGBS_PRTNR_BAS OGBS1 /*파트너기본*/ ON CUBS1.PRTNR_NO = OGBS1.PRTNR_NO WHERE SVPD1.SV_BIZ_HCLSF_CD = '3' -- AS
AND SVPD1.CNTR_NO = #{cntrNo}
AND SVPD1.CNTR_SN = #{cntrSn}

SELECT TP_CD , TO_CHAR(TO_DATE(RCPDT), 'yyyy-MM-dd') AS RCPDT , CNTR_NO , CNTR_SN , PD_CD , PD_NM , BC_NO , WK_TP , AS_RSON , VST_RQDT , WK_EXCN_DT , WK_PRGS_STAT , PEXT_SDING , CH_SDING , APY_DT FROM ( SELECT '1' TP_CD , 'AS접수' AS WK_TP , SSCT1.CNTR_NO , SSCT2.CNTR_SN , SVPD1.RCPDT -- 접수일자 , SVPD1.PD_CD , (SELECT PD_NM FROM TB_PDBS_PD_BAS T WHERE T.PD_CD = SVPD1.PD_CD) PD_NM , SVPD4.BC_NO , SVPD1.CNSL_MO_CN AS AS_RSON , TO_CHAR(TO_DATE(SVPD1.VST_RQDT), 'yyyy-MM-dd') VST_RQDT , TO_CHAR(TO_DATE(SVPD2.WK_EXCN_DT), 'yyyy-MM-dd') WK_EXCN_DT , NULL PEXT_SDING , NULL CH_SDING , F_CMZ_CD_NM('TNT_WELLS', 'WK_PRGS_STAT_CD', SVPD2.WK_PRGS_STAT_CD, 'ko') AS WK_PRGS_STAT , NULL AS APY_DT FROM TB_SSCT_CNTR_BAS SSCT1 /*계약기본*/ INNER JOIN TB_SSCT_CNTR_DTL SSCT2 /*계약상세*/ ON SSCT1.CNTR_NO = SSCT2.CNTR_NO INNER JOIN TB_SVPD_CST_SVAS_IST_OJ_IZ SVPD1 /*고객서비스AS설치대상내역*/ ON SVPD1.CNTR_NO = SSCT1.CNTR_NO AND SVPD1.CNTR_SN = SSCT2.CNTR_SN AND SSCT1.CNTR_CST_NO = SVPD1.CNTR_CST_NO INNER JOIN TB_SVPD_CST_SVAS_IST_ASN_IZ SVPD2 /*고객서비스AS설치배정내역*/ ON SSCT2.CNTR_NO = SVPD2.CNTR_NO AND SSCT2.CNTR_SN = SVPD2.CNTR_SN AND SSCT1.CNTR_CST_NO = SVPD2.CNTR_CST_NO INNER JOIN TB_SVPD_CST_SV_WK_RS_IZ SVPD3 /*고객서비스작업결과내역*/ ON SSCT2.CNTR_NO = SVPD3.CNTR_NO AND SSCT2.CNTR_SN = SVPD3.CNTR_SN AND SVPD1.CST_SV_ASN_NO = SVPD3.CST_SV_ASN_NO INNER JOIN TB_SVPD_CST_SV_EXCN_IZ SVPD4 /*고객서비스수행내역*/ ON SSCT2.CNTR_NO = SVPD4.CNTR_NO AND SSCT2.CNTR_NO = SVPD4.CNTR_NO UNION ALL SELECT '2' TP_CD , '모종변경' AS WK_TP , SSCT1.CNTR_NO , SSCT2.CNTR_SN , SSSO1.CH_RCP_DTM AS RCPDT -- 접수일자 , SVPD4.PDCT_PD_CD AS PD_CD , (SELECT PD_NM FROM TB_PDBS_PD_BAS T WHERE T.PD_CD = SVPD4.PDCT_PD_CD) PD_NM , SVPD4.BC_NO , '' AS AS_RSON , TO_CHAR(TO_DATE(SSSO1.CH_APY_STRTDT), 'yyyy-MM-dd') VST_RQDT , '' WK_EXCN_DT , SSSO2.HGR_PD_CD AS PEXT_SDING , CASE WHEN SPP_CH_SN = '1' THEN SSCT2.HGR_PD_CD ELSE SSSO2.HGR_PD_CD END AS CH_SDING , F_CMZ_CD_NM('TNT_WELLS', 'WK_PRGS_STAT_CD', SVPD3.WK_PRGS_STAT_CD, 'ko') AS WK_PRGS_STAT_NM , CH_APY_STRTDT AS APY_DT FROM TB_SSCT_CNTR_BAS SSCT1 /*계약기본*/ INNER JOIN TB_SSCT_CNTR_DTL SSCT2 /*계약상세*/ ON SSCT1.CNTR_NO = SSCT2.CNTR_NO INNER JOIN TB_SVPD_CST_SV_WK_RS_IZ SVPD3 /*고객서비스작업결과내역*/ ON SSCT2.CNTR_NO = SVPD3.CNTR_NO AND SSCT2.CNTR_SN = SVPD3.CNTR_SN INNER JOIN TB_SVPD_CST_SV_EXCN_IZ SVPD4 /*고객서비스수행내역*/ ON SSCT2.CNTR_NO = SVPD4.CNTR_NO AND SSCT2.CNTR_NO = SVPD4.CNTR_NO INNER JOIN TB_SSSO_SPP_CH_RCP_BAS SSSO1 /*배송변경접수기본*/ ON SSCT1.CNTR_NO = SSSO1.CNTR_NO INNER JOIN TB_SSSO_SPP_CH_RCP_DTL SSSO2 /*배송변경접수상세*/ ON SSSO1.SPP_CH_RCP_ID = SSSO2.SPP_CH_RCP_ID ) WHERE 1=1
AND RCPDT BETWEEN #{rcpDtStart} AND #{rcpdtEnd}
AND PD_CD = #{pdCd}
AND CNTR_NO = #{cntrNo}
AND BC_NO = #{bcNo}
ORDER BY RCPDT DESC
