mapper.select:
WITH PART_MASTER AS (
) SELECT TS1.ISTLL_KNM AS CUST_NM , TS1.IN_CHNL_DV_CD AS IN_GB , F_CMZ_CD_NM('TNT_WELLS', 'IN_CHNL_DV_CD', TS1.IN_CHNL_DV_CD, 'ko') AS IN_GB_NM , TC1.SELL_TP_CD AS SELL_TP_CD , F_CMZ_CD_NM('TNT_WELLS', 'SELL_TP_CD', TC1.SELL_TP_CD, 'ko') AS SELL_TP_CD_NM , TS1.CNTR_NO AS CNTR_NO , TS1.CNTR_SN AS CNTR_SN , TS3.PDCT_PD_CD AS PD_CD , TP1.SVPD_NM_ABBR1 AS PD_CD_NM , TP1.SVPD_ITEM_GR AS GDS_GR , TS1.SV_BIZ_HCLSF_CD AS GUBUN , TS1.SV_BIZ_HCLSF_CD AS SV_BIZ_HCLSF_CD , F_CMZ_CD_NM('TNT_WELLS', 'SV_BIZ_HCLSF_CD', TS1.SV_BIZ_HCLSF_CD, 'ko') AS SV_BIZ_HCLSF_CD_NM , SUBSTR(TS1.SV_BIZ_DCLSF_CD, 1, 2) AS SV_BIZ_MCLSF_CD , F_CMZ_CD_NM('TNT_WELLS', 'SV_BIZ_MCLSF_CD', SUBSTR(TS1.SV_BIZ_DCLSF_CD, 1, 2), 'ko') AS SV_BIZ_MCLSF_CD_NM , SUBSTR(TS1.SV_BIZ_DCLSF_CD, 1, 3) AS SV_BIZ_LCLSF_CD , F_CMZ_CD_NM('TNT_WELLS', 'SV_BIZ_LCLSF_CD', SUBSTR(TS1.SV_BIZ_DCLSF_CD, 1, 3), 'ko') AS SV_BIZ_LCLSF_CD_NM , TS1.SV_BIZ_DCLSF_CD AS SV_BIZ_DCLSF_CD , F_CMZ_CD_NM('TNT_WELLS', 'SV_BIZ_DCLSF_CD', TS1.SV_BIZ_DCLSF_CD, 'ko') AS SV_BIZ_DCLSF_CD_NM , TS1.RCP_ICHR_PRTNR_NO AS REG_ID , TO1.PRTNR_KNM AS REG_KNM , TS1.CNSL_MO_CN AS REG_MEMO , CASE WHEN TS1.VST_RQDT IS NOT NULL THEN TS1.VST_RQDT || TS1.VST_AK_HH END AS VST_SCH_DT_TM , CASE WHEN TS1.FNL_RCPDT IS NOT NULL THEN TS1.FNL_RCPDT || TS1.FNL_RCP_HH END AS RECD_DT_TM , CASE WHEN TS2.WK_ACPTE_DT IS NOT NULL THEN TS2.WK_ACPTE_DT || TS2.WK_ACPTE_HH END AS ACC_DT_TM , CASE WHEN TS2.ARV_DT IS NOT NULL THEN TS2.ARV_DT || TS2.ARV_HH END AS ARR_DT_TM , CASE WHEN TS2.VST_CNFMDT IS NOT NULL THEN TS2.VST_CNFMDT || TS2.VST_CNFM_HH END AS CFRM_DT_TM , CASE WHEN TS2.WK_EXCN_DT IS NOT NULL THEN TS2.WK_EXCN_DT || TS2.WK_EXCN_HH END AS WRK_DT_TM , TO2.OG_TP_CD AS PRTNR_OG_TP_CD , TO2.PRTNR_NO AS PRTNR_NO , TO2.PRTNR_KNM AS PRTNR_KNM , CASE WHEN TO2.OG_CD = '71321' THEN '031' ELSE TO3.CRAL_LOCARA_TNO END AS TEL_NO1 , CASE WHEN TO2.OG_CD = '71321' THEN '953' ELSE TO3.MEXNO_ENCR END AS TEL_NO2 , CASE WHEN TO2.OG_CD = '71321' THEN '0816' ELSE TO3.CRAL_IDV_TNO END AS TEL_NO3 , TO5.OG_CD AS OG_CD , CASE WHEN TO2.OG_TP_CD = 'W02' THEN SUBSTR(TO4.OG_CD, 1, 3) || TO4.BLD_NM ELSE TO4.OG_NM END AS OG_NAME , CASE WHEN TO2.OG_TP_CD = 'W02' THEN SUBSTR(TO4.OG_CD, 1, 3) || '-' || TO4.BLD_NM ELSE TO4.OG_NM END AS JURISDICTION_OG_NM , TS4.REFRI_DV_CD AS CHAG_GB , TS4.CLN_YN AS RET_YN , NVL(TS4.BC_NO, TS3.BC_NO) AS BC_NO , TS4.SV_PROCS_CN AS PROC_TXT , TS2.WK_CAN_RSON_CD AS CANC_NM , TS2.WK_CAN_MO_CN AS CANC_MEMO , CASE WHEN (SELECT COUNT(1) FROM TB_SVST_SV_WK_OSTR_IZ WHERE CST_SV_ASN_NO = TS1.CST_SV_ASN_NO AND CNTR_NO = TS1.CNTR_NO AND CNTR_SN = TS1.CNTR_SN AND DTA_DL_YN = 'N') > 0 THEN 'Y' ELSE 'N' END AS PART_USE_YN , '' AS FILTER_CD_1 , '' AS FILTER_CD_2 , '' AS FILTER_CD_3 , '' AS FILTER_CD_4 , '' AS FILTER_CD_5 , '' AS FILTER_CD_6 , '' AS FILTER_CD_1_NM , '' AS FILTER_CD_2_NM , '' AS FILTER_CD_3_NM , '' AS FILTER_CD_4_NM , '' AS FILTER_CD_5_NM , '' AS FILTER_CD_6_NM , '' AS PART_CD_1 , '' AS PART_CD_2 , '' AS PART_CD_3 , '' AS CHULGO_C , '' AS CHULGO_C_YN , '' AS CHULGO_U , '' AS CHULGO_U_YN , TS5.BIL_OJ_AMT AS REQ_AMT_TOT , CASE WHEN TP1.SVPD_ITEM_GR = '11' THEN TS6.RECAP_CS_AMT ELSE TS5.BIL_CTR_SUM_AMT END AS ACC_TOT , CASE WHEN TC2.CNTR_DTL_STAT_CD = '202' THEN TC2.HIST_STRT_DTM END AS STOP_DT , TS1.MTR_STAT_CD AS RCP_STAT_CD , F_CMZ_CD_NM('TNT_WELLS', 'MTR_STAT_CD', TS1.MTR_STAT_CD, 'ko') AS RCP_STAT_CD_NM , TO2.CLTN_DT AS CLTN_DT , CASE WHEN TC3.FRISU_AS_PTRM_UNIT_CD = '10' THEN TC3.FRISU_AS_PTRM_N * 12 WHEN TC3.FRISU_AS_PTRM_UNIT_CD = '30' THEN CEIL(TC3.FRISU_AS_PTRM_N * 7 / 30) WHEN TC3.FRISU_AS_PTRM_UNIT_CD = '40' THEN CEIL(TC3.FRISU_AS_PTRM_N / 30) ELSE TC3.FRISU_AS_PTRM_N END AS AS_MTHS , CASE WHEN TC3.FRISU_BFSVC_PTRM_UNIT_CD = '10' THEN TC3.FRISU_BFSVC_PTRM_N / 12 WHEN TC3.FRISU_BFSVC_PTRM_UNIT_CD = '30' THEN CEIL(TC3.FRISU_BFSVC_PTRM_N * 7 / 30) WHEN TC3.FRISU_BFSVC_PTRM_UNIT_CD = '40' THEN CEIL(TC3.FRISU_BFSVC_PTRM_N / 30) ELSE TC3.FRISU_BFSVC_PTRM_N END AS BS_MTHS , TS7.SPP_PROCS_BZS_CD AS IN_DLV_COMP , TS7.SPP_ORD_IVC_NO AS IN_INVOICE , TS8.SPP_PROCS_BZS_CD AS OUT_DLV_COMP , TS8.SPP_ORD_IVC_NO AS OUT_INVOICE , TS1.AS_IST_OJ_NO AS CST_SV_ASN_NO_1 , '' AS CST_SV_ASN_NO_2 , 0 AS FS_VST_CNT FROM TB_SVPD_CST_SVAS_IST_OJ_IZ TS1 INNER JOIN TB_SVPD_CST_SVAS_IST_ASN_IZ TS2 ON TS2.CST_SV_ASN_NO = TS1.CST_SV_ASN_NO AND TS2.CNTR_NO = TS1.CNTR_NO AND TS2.CNTR_SN = TS1.CNTR_SN AND TS1.DTA_DL_YN = 'N' AND TS2.DTA_DL_YN = 'N' INNER JOIN TB_SVPD_CST_SV_EXCN_IZ TS3 ON TS3.CNTR_NO = TS1.CNTR_NO AND TS3.CNTR_SN = TS1.CNTR_SN AND TS3.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_SVPD_CST_SV_WK_RS_IZ TS4 ON TS4.CST_SV_ASN_NO = TS1.CST_SV_ASN_NO AND TS4.DTA_DL_YN = 'N' INNER JOIN TB_SSCT_CNTR_DTL TC1 ON TC1.CNTR_NO = TS1.CNTR_NO AND TC1.CNTR_SN = TS1.CNTR_SN AND TC1.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_SSCT_CNTR_DTL_STAT_CH_HIST TC2 ON TC2.CNTR_NO = TS1.CNTR_NO AND TC2.CNTR_SN = TS1.CNTR_SN AND TC2.DTA_DL_YN = 'N' AND TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS') BETWEEN TC2.HIST_STRT_DTM AND TC2.HIST_END_DTM LEFT OUTER JOIN TB_SSCT_CNTR_WELLS_DTL TC3 ON TC3.CNTR_NO = TS1.CNTR_NO AND TC3.CNTR_SN = TS1.CNTR_SN AND TC3.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_SVPD_SV_CS_BIL_IZ TS5 ON TS5.CNTR_NO = TS1.CNTR_NO AND TS5.CNTR_SN = TS1.CNTR_SN AND TS5.CST_SV_ASN_NO = TS1.CST_SV_ASN_NO AND TS5.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_SVPD_SDING_SPP_PLAN_IZ TS6 ON TS6.CNTR_NO = TS1.CNTR_NO AND TS6.CNTR_SN = TS1.CNTR_SN AND TS6.SV_BIZ_HCLSF_CD = TS1.SV_BIZ_HCLSF_CD AND TS6.SV_BIZ_DCLSF_CD = TS1.SV_BIZ_DCLSF_CD AND TS6.SPP_ORD_NO = TS1.AS_IST_OJ_NO AND TS6.SPP_PLAN_SN = (SELECT MAX(SPP_PLAN_SN) FROM TB_SVPD_SDING_SPP_PLAN_IZ WHERE CNTR_NO = TS6.CNTR_NO AND CNTR_SN = TS6.CNTR_SN AND SV_BIZ_HCLSF_CD = TS6.SV_BIZ_HCLSF_CD AND SV_BIZ_DCLSF_CD = TS6.SV_BIZ_DCLSF_CD AND SPP_ORD_NO = TS6.SPP_ORD_NO AND DTA_DL_YN = 'N') LEFT OUTER JOIN TB_SVST_OTSC_PDCT_IOST_IZ TS7 ON TS7.CST_SV_ASN_NO = TS1.CST_SV_ASN_NO AND TS7.ITM_IOST_DV_CD = '1' AND TS7.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_SVST_OTSC_PDCT_IOST_IZ TS8 ON TS8.CST_SV_ASN_NO = TS1.CST_SV_ASN_NO AND TS8.ITM_IOST_DV_CD = '2' AND TS8.DTA_DL_YN = 'N' LEFT OUTER JOIN PART_MASTER TP1 ON TP1.SVPD_PD_CD = TS3.PDCT_PD_CD LEFT OUTER JOIN TB_OGBS_MM_PRTNR_IZ TO1 ON TO1.OG_TP_CD = TS1.RCP_OG_TP_CD AND TO1.PRTNR_NO = TS1.RCP_ICHR_PRTNR_NO AND TO1.BASE_YM = SUBSTR(TS1.FNL_RCPDT, 1, 6) AND TO1.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_OGBS_MM_PRTNR_IZ TO2 ON TO2.OG_TP_CD = (CASE WHEN TS4.OG_TP_CD IS NOT NULL AND TS4.PRTNR_NO IS NOT NULL THEN TS4.OG_TP_CD WHEN TS2.WK_PRTNR_OG_TP_CD IS NOT NULL AND TS2.WK_PRTNR_NO IS NOT NULL THEN TS2.WK_PRTNR_OG_TP_CD WHEN TS2.ICHR_OG_TP_CD IS NOT NULL AND TS2.ICHR_PRTNR_NO IS NOT NULL THEN TS2.ICHR_OG_TP_CD END) AND TO2.PRTNR_NO = (CASE WHEN TS4.OG_TP_CD IS NOT NULL AND TS4.PRTNR_NO IS NOT NULL THEN TS4.PRTNR_NO WHEN TS2.WK_PRTNR_OG_TP_CD IS NOT NULL AND TS2.WK_PRTNR_NO IS NOT NULL THEN TS2.WK_PRTNR_NO WHEN TS2.ICHR_OG_TP_CD IS NOT NULL AND TS2.ICHR_PRTNR_NO IS NOT NULL THEN TS2.ICHR_PRTNR_NO END) AND TO2.BASE_YM = (CASE WHEN TS4.OG_TP_CD IS NOT NULL AND TS4.PRTNR_NO IS NOT NULL THEN SUBSTR(TS4.VST_FSH_DT, 1, 6) WHEN TS2.WK_PRTNR_OG_TP_CD IS NOT NULL AND TS2.WK_PRTNR_NO IS NOT NULL THEN SUBSTR(TS2.ASN_DT, 1, 6) WHEN TS2.ICHR_OG_TP_CD IS NOT NULL AND TS2.ICHR_PRTNR_NO IS NOT NULL THEN SUBSTR(TS2.ASN_DT, 1, 6) END) LEFT OUTER JOIN TB_OGBS_PRTNR_BAS TO3 ON TO3.OG_TP_CD = TO2.OG_TP_CD AND TO3.PRTNR_NO = TO2.PRTNR_NO LEFT OUTER JOIN TB_OGBS_MM_OG_IZ TO4 ON TO4.OG_ID = TO2.OG_ID AND TO4.BASE_YM = TO2.BASE_YM LEFT OUTER JOIN TB_OGBS_MM_OG_IZ TO5 ON TO5.OG_ID = NVL(TS4.OG_ID, TS2.SV_CNR_OG_ID) AND TO5.BASE_YM = (CASE WHEN TS4.OG_ID IS NOT NULL THEN SUBSTR(TS4.VST_FSH_DT, 1, 6) ELSE SUBSTR(TS2.ASN_DT, 1, 6) END) WHERE 1=1 AND TS1.CNTR_NO = #{CNTR_NO} AND TS1.CNTR_SN = #{CNTR_SN} AND TS1.SV_BIZ_HCLSF_CD = #{GUBUN}
AND TS2.WK_EXCN_DT IS NOT NULL
AND TS2.WK_EXCN_DT IS NULL
UNION ALL SELECT TC5.RCGVP_KNM AS CUST_NM , '' AS IN_GB , '' AS IN_GB_NM , TC1.SELL_TP_CD AS SELL_TP_CD , F_CMZ_CD_NM('TNT_WELLS', 'SELL_TP_CD', TC1.SELL_TP_CD, 'ko') AS SELL_TP_CD_NM , TS1.CNTR_NO AS CNTR_NO , TS1.CNTR_SN AS CNTR_SN , TS3.PDCT_PD_CD AS PD_CD , TP1.SVPD_NM_ABBR1 AS PD_CD_NM , TP1.SVPD_ITEM_GR AS GDS_GR , SUBSTR(TS1.SV_BIZ_DCLSF_CD, 1, 1) AS GUBUN , SUBSTR(TS1.SV_BIZ_DCLSF_CD, 1, 1) AS SV_BIZ_HCLSF_CD , F_CMZ_CD_NM('TNT_WELLS', 'SV_BIZ_MCLSF_CD', SUBSTR(TS1.SV_BIZ_DCLSF_CD, 1, 1), 'ko') AS SV_BIZ_HCLSF_CD_NM , SUBSTR(TS1.SV_BIZ_DCLSF_CD, 1, 2) AS SV_BIZ_MCLSF_CD , F_CMZ_CD_NM('TNT_WELLS', 'SV_BIZ_MCLSF_CD', SUBSTR(TS1.SV_BIZ_DCLSF_CD, 1, 2), 'ko') AS SV_BIZ_MCLSF_CD_NM , SUBSTR(TS1.SV_BIZ_DCLSF_CD, 1, 3) AS SV_BIZ_LCLSF_CD , F_CMZ_CD_NM('TNT_WELLS', 'SV_BIZ_LCLSF_CD', SUBSTR(TS1.SV_BIZ_DCLSF_CD, 1, 3), 'ko') AS SV_BIZ_LCLSF_CD_NM , TS1.SV_BIZ_DCLSF_CD AS SV_BIZ_DCLSF_CD , F_CMZ_CD_NM('TNT_WELLS', 'SV_BIZ_DCLSF_CD', TS1.SV_BIZ_DCLSF_CD, 'ko') AS SV_BIZ_DCLSF_CD_NM , 'BS' AS REG_ID , '' AS REG_KNM , '' AS REG_MEMO , CASE WHEN TS2.VST_CNFMDT IS NOT NULL THEN TS2.VST_CNFMDT || TS2.VST_CNFM_HH END AS VST_SCH_DT_TM , CASE WHEN TS1.VST_DUEDT IS NOT NULL THEN TS1.VST_DUEDT || '000000' END AS RECD_DT_TM , '' AS ACC_DT_TM , '' AS ARR_DT_TM , CASE WHEN TS2.VST_CNFMDT IS NOT NULL THEN TS2.VST_CNFMDT || TS2.VST_CNFM_HH END AS CFRM_DT_TM , CASE WHEN TS4.VST_FSH_DT IS NOT NULL THEN TS4.VST_FSH_DT || TS4.VST_FSH_HH END AS WRK_DT_TM , TO1.OG_TP_CD AS PRTNR_OG_TP_CD , TO1.PRTNR_NO AS PRTNR_NO , TO1.PRTNR_KNM AS PRTNR_KNM , CASE WHEN TO1.OG_CD = '71321' THEN '031' ELSE TO2.CRAL_LOCARA_TNO END AS TEL_NO1 , CASE WHEN TO1.OG_CD = '71321' THEN '953' ELSE TO2.MEXNO_ENCR END AS TEL_NO2 , CASE WHEN TO1.OG_CD = '71321' THEN '0816' ELSE TO2.CRAL_IDV_TNO END AS TEL_NO3 , TO3.OG_CD AS OG_CD , CASE WHEN TO1.OG_TP_CD = 'W02' AND TO3.BLD_NM IS NOT NULL THEN TO3.BLD_NM ELSE TO3.OG_NM END AS OG_NAME , CASE WHEN TO1.OG_TP_CD = 'W02' THEN SUBSTR(TO3.OG_CD, 1, 3) || '-' || TO3.BLD_NM ELS