' AS PROC_TXT , '' AS CANC_NM , '' AS CANC_MEMO , CASE WHEN (SELECT COUNT(1) FROM TB_SVPD_CST_SV_RGBSPR_IZ WHERE CNTR_NO = TS1.CNTR_NO AND CNTR_SN = TS1.CNTR_SN AND VST_DUEDT = TS1.VST_DUEDT AND DTA_DL_YN = 'N') > 0 THEN 'Y' ELSE 'N' END AS PART_USE_YN , TF1.PART_PD_CD AS FILTER_CD_1 , TF2.PART_PD_CD AS FILTER_CD_2 , TF3.PART_PD_CD AS FILTER_CD_3 , TF4.PART_PD_CD AS FILTER_CD_4 , TF5.PART_PD_CD AS FILTER_CD_5 , TF6.PART_PD_CD AS FILTER_CD_6 , TF1.PART_PD_ABBR_NM AS FILTER_CD_1_NM , TF2.PART_PD_ABBR_NM AS FILTER_CD_2_NM , TF3.PART_PD_ABBR_NM AS FILTER_CD_3_NM , TF4.PART_PD_ABBR_NM AS FILTER_CD_4_NM , TF5.PART_PD_ABBR_NM AS FILTER_CD_5_NM , TF6.PART_PD_ABBR_NM AS FILTER_CD_6_NM , TF7.PART_PD_CD AS PART_CD_1 , TF8.PART_PD_CD AS PART_CD_2 , TF9.PART_PD_CD AS PART_CD_3 , '' AS CHULGO_C , '' AS CHULGO_C_YN , '' AS CHULGO_U , '' AS CHULGO_U_YN , 0 AS REQ_AMT_TOT , 0 AS ACC_TOT , CASE WHEN TC2.CNTR_DTL_STAT_CD = '202' THEN TC2.HIST_STRT_DTM END AS STOP_DT , '' AS RCP_STAT_CD , '예정' AS RCP_STAT_CD_NM , TO1.CLTN_DT AS CLTN_DT , CASE WHEN TC3.FRISU_AS_PTRM_UNIT_CD = '10' THEN TC3.FRISU_AS_PTRM_N * 12 WHEN TC3.FRISU_AS_PTRM_UNIT_CD = '30' THEN CEIL(TC3.FRISU_AS_PTRM_N * 7 / 30) WHEN TC3.FRISU_AS_PTRM_UNIT_CD = '40' THEN CEIL(TC3.FRISU_AS_PTRM_N / 30) ELSE TC3.FRISU_AS_PTRM_N END AS AS_MTHS , CASE WHEN TC3.FRISU_BFSVC_PTRM_UNIT_CD = '10' THEN TC3.FRISU_BFSVC_PTRM_N / 12 WHEN TC3.FRISU_BFSVC_PTRM_UNIT_CD = '30' THEN CEIL(TC3.FRISU_BFSVC_PTRM_N * 7 / 30) WHEN TC3.FRISU_BFSVC_PTRM_UNIT_CD = '40' THEN CEIL(TC3.FRISU_BFSVC_PTRM_N / 30) ELSE TC3.FRISU_BFSVC_PTRM_N END AS BS_MTHS , '' AS IN_DLV_COMP , '' AS IN_INVOICE , '' AS OUT_DLV_COMP , '' AS OUT_INVOICE , '' AS CST_SV_ASN_NO_1 , '' AS CST_SV_ASN_NO_2 , 0 AS FS_VST_CNT FROM TB_SVPD_CST_SV_RGBSPR_IZ TS1 INNER JOIN TB_SVPD_MCBY_CST_SV_OJ_IZ TS2 ON TS2.CNTR_NO = TS1.CNTR_NO AND TS2.CNTR_SN = TS1.CNTR_SN AND TS1.DTA_DL_YN = 'N' AND TS2.DTA_DL_YN = 'N' AND TS2.MNGT_YM = (SELECT MAX(MNGT_YM) FROM TB_SVPD_MCBY_CST_SV_OJ_IZ WHERE CNTR_NO =