mapper.insert:
INSERT INTO TB_SVPD_SDING_AS_AK_HIST (
       CNTR_NO
     , CNTR_SN
     , AK_SN
     , HIST_CH_DTM
     , RGST_DT
     , AS_AK_DV_CD
     , AK_CHDT
     , BFCH_PD_CD
     , AFCH_PD_CD
     , MTR_PROCS_STAT_CD
     , CNFMDT
     , MAT_NM
     , CSMR_UPRC_AMT

) VALUES (
       #{cntrNo}
     , #{cntrSn}
     , #{akSn}
     , #{histChDtm}
     , TO_CHAR(SYSDATE,'YYYYMMDD')
     , #{asAkDvCd}
     , #{akChdt}
     , #{bfchPdCd}
     , #{afchPdCd}
     , #{mtrProcsStatCd}
     , null
     , #{matNm}
     , #{csmrUprcAmt}

)

mapper.delete:
DELETE FROM TB_SVPD_SDING_AS_AK_IZ
WHERE CNTR_NO = #{cntrNo}
  AND CNTR_SN = #{cntrSn}
  AND AK_SN = #{akSn}

mapper.select:
SELECT COUNT(*) AS COUNT
  FROM TB_SVPD_SDING_AS_AK_IZ SVPD1 /*모종AS요청내역*/
 WHERE SVPD1.CNTR_NO = #{cntrNo}
   AND SVPD1.CNTR_SN = #{cntrSn}
   AND AK_SN = #{akSn}
   AND AS_AK_DV_CD = #{asAkDvCd}
   AND SUBSTR(AK_CHDT, 1, 6) = SUBSTR(#{akChdt}, 1, 6)
   AND CNFMDT IS NULL

mapper.update:
UPDATE TB_SVPD_SDING_AS_AK_IZ
   SET AK_CHDT = #{akChdt}
     , BFCH_PD_CD = #{bfchPdCd}
     , AFCH_PD_CD = #{afchPdCd}
     , MTR_PROCS_STAT_CD = #{mtrProcsStatCd}

 WHERE CNTR_NO = #{cntrNo}
   AND CNTR_SN = #{cntrSn}
   AND AK_SN = #{akSn}
   AND AS_AK_DV_CD = #{asAkDvCd}
   AND SUBSTR(AK_CHDT, 1, 6) = SUBSTR(#{akChdt}, 1, 6)
   AND CNFMDT IS NULL

mapper.insert:
INSERT INTO TB_SVPD_SDING_AS_AK_IZ (
       CNTR_NO
     , CNTR_SN
     , AK_SN
     , AS_AK_DV_CD
     , AK_CHDT
     , BFCH_PD_CD
     , AFCH_PD_CD
     , MTR_PROCS_STAT_CD
     , CSMR_UPRC_AMT

) VALUES (
       #{cntrNo}
     , #{cntrSn}
     , (SELECT LPAD(NVL(MAX(AK_SN), 0) + 1, 2, '0') AS MAX_AK_SN FROM TB_SVPD_SDING_AS_AK_IZ WHERE 1 = 1 AND CNTR_NO = #{cntrNo} AND CNTR_SN = #{cntrSn})
     , #{asAkDvCd}
     , TO_CHAR(SYSDATE, 'YYYYMMDD')
     , #{bfchPdCd}
     , #{afchPdCd}
     , #{mtrProcsStatCd}
     , 0

)

mapper.select:
SELECT COUNT(*) AS COUNT
  FROM TB_SVPD_CST_SV_BFSVC_OJ_IZ /*고객서비스BS대상내역*/
 WHERE CNTR_NO = #{cntrNo}
   AND CNTR_SN = #{cntrSn}
   AND ASN_OJ_YM = #{asnOjYm}
