mapper.insert:
INSERT INTO TB_SVPD_SDING_AS_AK_HIST ( /* 모종AS요청이력 */
       CNTR_NO
     , CNTR_SN
     , AK_SN
     , HIST_CH_DTM
     , RGST_DT
     , AS_AK_DV_CD
     , AK_CHDT
     , BFCH_PD_CD
     , AFCH_PD_CD
     , MTR_PROCS_STAT_CD
     , CNFMDT
     , CHO_CAPSL_CN
     , CSMR_UPRC_AMT
     , DTA_DL_YN

) VALUES (
       #{cntrNo}
     , #{cntrSn}
     , #{akSn}
     , TO_CHAR(SYSDATE,'YYYYMMDDHH24MISS') + ${histChDtm}
     , TO_CHAR(SYSDATE,'YYYYMMDD')
     , #{asAkDvCd}
     , #{akChdt}
     , #{bfchPdCd}
     , #{afchPdCd}
     , #{mtrProcsStatCd}
     , null
     , #{choCapslCn}
     , #{csmrUprcAmt}
     , 'N'

)

mapper.delete:
UPDATE TB_SVPD_SDING_AS_AK_IZ /* 모종AS요청내역 */
   SET DTA_DL_YN = 'Y'
 WHERE CNTR_NO = #{cntrNo}
   AND CNTR_SN = #{cntrSn}
   AND AS_AK_DV_CD = #{asAkDvCd}
   AND SUBSTR(AK_CHDT, 1, 6) = SUBSTR(#{akChdt}, 1, 6)
   AND CNFMDT IS NULL

mapper.select:
SELECT COUNT(*) AS COUNT
  FROM TB_SVPD_SDING_AS_AK_IZ SVPD1 /* 모종AS요청내역 */
 WHERE SVPD1.DTA_DL_YN = 'N'
   AND SVPD1.CNTR_NO = #{cntrNo}
   AND SVPD1.CNTR_SN = #{cntrSn}
   AND AS_AK_DV_CD = #{asAkDvCd}
   AND SUBSTR(AK_CHDT, 1, 6) = SUBSTR(#{akChdt}, 1, 6)
   AND CNFMDT IS NULL

mapper.update:
UPDATE TB_SVPD_SDING_AS_AK_IZ /* 모종AS요청내역 */
   SET AK_CHDT = #{akChdt}
     , BFCH_PD_CD = #{bfchPdCd}
     , AFCH_PD_CD = #{afchPdCd}
     , MTR_PROCS_STAT_CD = #{mtrProcsStatCd}
     , CHO_CAPSL_CN = #{choCapslCn}

 WHERE CNTR_NO = #{cntrNo}
   AND CNTR_SN = #{cntrSn}
   AND AS_AK_DV_CD = #{asAkDvCd}
   AND SUBSTR(AK_CHDT, 1, 6) = SUBSTR(#{akChdt}, 1, 6)
   AND CNFMDT IS NULL

mapper.select:
SELECT NVL(MAX(AK_SN), 0) + 1 AS MAX_AK_SN
  FROM TB_SVPD_SDING_AS_AK_IZ /* 모종AS요청내역 */
 WHERE CNTR_NO = #{cntrNo}
   AND CNTR_SN = #{cntrSn}

