mapper.select:
SELECT (
        SELECT COUNT(*) AS RESULTCODE
          FROM TB_SVPD_CST_SV_EXCN_IZ TEMP
         WHERE 1=1
           AND TEMP.BC_NO = #{qrcd}
       ) AS RESULTCODE
     , (
        SELECT COUNT(*) AS REGI
          FROM () TEMP
         WHERE 1=1
           AND TEMP.SVPD_BAR_CD = #{qrcd}
       ) AS REGI
  FROM DUAL
